-- CREATE DB, TABLES AND TRIGGERS --

/* Create Project Database */
CREATE DATABASE ConstitutionsDB;
USE ConstitutionsDB;

/* Create Table With State-Level characteristics */
CREATE TABLE IF NOT EXISTS states (
state_id varchar(2) NOT NULL,
total_constitutions INT NOT NULL,
region varchar(255) NOT NULL,
culture varchar(255) NOT NULL,
initiative BOOL NOT NULL, 
referendum BOOL NOT NULL,
PRIMARY KEY (state_id)
);

/* Create Table With Constitution-Level characteristics */
CREATE TABLE IF NOT EXISTS constitutions (
constitution_id varchar(255) NOT NULL,
state_id varchar(2) NOT NULL, 
date_of_adoption DATE NOT NULL,	
year_of_adoption INT(4),	
period_of_adoption varchar(255) NOT NULL, 
PRIMARY KEY (constitution_id),
CONSTRAINT fk_states FOREIGN KEY (state_id)
REFERENCES states(state_id)
);

/* Create Table With Section-Level Data*/
CREATE TABLE IF NOT EXISTS sections (
section_id varchar(255) NOT NULL, 
constitution_id varchar(255) NOT NULL,
section_year INT(4), 
article_num varchar(5) NOT NULL, 
section_num varchar(5) NOT NULL, 
part_num varchar(5) NOT NULL, 
section_topic varchar(255) NOT NULL, 
section_text LONGTEXT,
is_deleted int NOT NULL DEFAULT 0,
created_by  varchar(255) NOT NULL,
updated_by  varchar(255) NOT NULL, 
PRIMARY KEY (section_id),
CONSTRAINT fk_constitutions FOREIGN KEY (constitution_id)
REFERENCES constitutions(constitution_id)
);

/* Create Sections History/Audit Trail table to code constitutional amendments*/
create table sections_history(
id int NOT NULL AUTO_INCREMENT, 
section_id varchar(255) NOT NULL, 
column_name varchar(255),
old_value LONGTEXT,
new_value LONGTEXT,
done_by varchar(255) NOT NULL,
done_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
PRIMARY KEY (id));

