INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_1_0_1819', 'Alabama1819', '1819', '1', '1', '0', 'Bill of Rights', 'That all freemen, when they form a social compact, are equal in rights; and that no man, or set of men, are entitled to exclusive, separate public emoluments or privileges, but in consideration of public services.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_2_0_1819', 'Alabama1819', '1819', '1', '2', '0', 'Bill of Rights', 'All political power is inherent in the people, and all free governments are founded on their authority, and instituted for their benefit; and, therefore, they have, at all times an unalienable and indefeasible right to alter, reform, or abolish their form of government, in such manner as they may think expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_3_0_1819', 'Alabama1819', '1819', '1', '3', '0', 'Bill of Rights', 'No person within this state shall, upon, any pretence, be deprived of the inestimable privilege of worshipping God in the manner most agreeable to his own conscience; nor be compelled to attend any place of worship, nor shall any one ever be obliged to pay any tythes, taxes, or other rate, for the building or repairing any place of worship, or for the maintenance of any minister or ministry.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_4_0_1819', 'Alabama1819', '1819', '1', '4', '0', 'Bill of Rights', 'No human authority ought, in any case whatever, to control or interfere with the rights of conscience.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_5_0_1819', 'Alabama1819', '1819', '1', '5', '0', 'Bill of Rights', 'No person shall be hurt, molested, or restrained in his religious profession, sentiments, or persuasion, provided he does not disturb others in their religious worship.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_6_0_1819', 'Alabama1819', '1819', '1', '6', '0', 'Bill of Rights', 'The civil rights, privileges, or capacities of any citizen, shall in no way be diminished, or enlarged, on account of his religious principles.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_7_0_1819', 'Alabama1819', '1819', '1', '7', '0', 'Bill of Rights', 'There shall be no establishment of religion by law; no preference shall ever be given by law to any religious sect, society, denomination, or mode of worship; and no religious test shall ever be required as a qualification to any office or public trust under this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_8_0_1819', 'Alabama1819', '1819', '1', '8', '0', 'Bill of Rights', 'Every citizen may freely speak, write, and publish his sentiments on all subjects, being responsible for the abuse of that liberty.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_9_0_1819', 'Alabama1819', '1819', '1', '9', '0', 'Bill of Rights', 'The people shall be secure in their persons, houses, papers, and possessions, from unreasonable seizures or searches; and no warrant to search any place, or to seize any person or thing, shall issue, without describing them as nearly as may be, nor without probable cause, supported by oath or affirmation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_10_0_1819', 'Alabama1819', '1819', '1', '10', '0', 'Bill of Rights', 'In all criminal prosecutions, the accused has a right to be heard by himself, and counsel; to demand the nature and cause of the accusation, and have a copy thereof; to be confronted by the witnesses against him; to have compulsory process for obtaining witnesses in his favor, and, in all prosecutions, by indictment or information, a speedy public trial, by an impartial jury of the county or district in which the offence shall have been committed: he shall not be compelled to give evidence against himself, nor shall he be deprived of his life, liberty, or property, but by due course of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_11_0_1819', 'Alabama1819', '1819', '1', '11', '0', 'Bill of Rights', 'No person, shall be accused, arrested, or detained, except in cases ascertained by law, and according to the forms which the same has prescribed; and no person shall be punished, but in virtue of a law, established and promulgated prior to the offence, and legally applied.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_12_0_1819', 'Alabama1819', '1819', '1', '12', '0', 'Bill of Rights', 'No person shall, for any indictable offence, be proceeded against criminally, by information; except in cases arising in the land or naval forces, or the militia when in actual service, or by leave of the court for oppression or misdemeanor in office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_13_0_1819', 'Alabama1819', '1819', '1', '13', '0', 'Bill of Rights', 'No person shall, for the same offence, be twice put in jeopardy of life or limb; nor shall any persons property be taken or applied to public use, unless just compensation be made therefor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_14_0_1819', 'Alabama1819', '1819', '1', '14', '0', 'Bill of Rights', 'All courts shall be open, and every person, for an injury done him in his lands, goods, person, or reputation, shall have remedy by due course of law, and right and justice administered, without sale, denial, or delay.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_15_0_1819', 'Alabama1819', '1819', '1', '15', '0', 'Bill of Rights', 'No power of suspending laws shall he exercised, except by the General Assembly, or its authority.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_16_0_1819', 'Alabama1819', '1819', '1', '16', '0', 'Bill of Rights', 'Excessive bail shall not be required, nor excessive fines imposed, nor cruel punishments inflicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_17_0_1819', 'Alabama1819', '1819', '1', '17', '0', 'Bill of Rights', 'All persons shall, before conviction, be bailable by sufficient securities, except for capital offences, when the proof is evident, or the presumption great: and the privilege of the writ of habeas corpus shall not be suspended, unless when, in cases of rebellion, or invasion, the public safety may require it.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_18_0_1819', 'Alabama1819', '1819', '1', '18', '0', 'Bill of Rights', 'The person of a debtor, where there is not strong presumption of fraud, shall not be detained in prison, after delivering up his estate for the benefit of his creditors, in such manner as shall be prescribed by la w.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_19_0_1819', 'Alabama1819', '1819', '1', '19', '0', 'Bill of Rights', 'No ex post facto law, nor law impairing the obligation of contracts, shall be made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_20_0_1819', 'Alabama1819', '1819', '1', '20', '0', 'Bill of Rights', 'No person shall be attainted of treason or felony by the General Assembly. No attainder shall work corruption of blood, nor forfeiture of estate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_21_0_1819', 'Alabama1819', '1819', '1', '21', '0', 'Bill of Rights', 'The estates of suicides shall descend or vest as in cases of natural death; if any person shall be killed by casualty, there shall be no forfeiture by reason thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_22_0_1819', 'Alabama1819', '1819', '1', '22', '0', 'Bill of Rights', 'The citizens have a right, in a peaceable manner, to assemble together for their common good, and to apply to those invested with the powers of government, for redress of grievances, or other proper purposes, by petition, address, or remonstrance.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_23_0_1819', 'Alabama1819', '1819', '1', '23', '0', 'Bill of Rights', 'Every citizen has a right to bear arms in defence of himself and the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_24_0_1819', 'Alabama1819', '1819', '1', '24', '0', 'Bill of Rights', 'No standing army shall be kept up without the consent of the General Assembly; and, in that case, no appropriation of money for its support shall be for a longer term than one year; and the military shall, in all cases, and at all times, be in strict subordination to the civil power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_25_0_1819', 'Alabama1819', '1819', '1', '25', '0', 'Bill of Rights', 'No soldier shall, in time of peace, be quartered in any house, without the consent of the owner; nor in time of war, but in a manner to be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_26_0_1819', 'Alabama1819', '1819', '1', '26', '0', 'Bill of Rights', 'No title of nobility, or hereditary distinction, privilege honor, or emolument, shall ever be granted or conferred in this State; nor shall any office be created, the appointment of which shall be for a longer term than during good behaviour.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_27_0_1819', 'Alabama1819', '1819', '1', '27', '0', 'Bill of Rights', 'Emigration from this state shall not be prohibited, nor shall any citizen be exiled.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_28_0_1819', 'Alabama1819', '1819', '1', '28', '0', 'Bill of Rights', 'The right of trial by jury shall remain inviolate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_29_0_1819', 'Alabama1819', '1819', '1', '29', '0', 'Bill of Rights', 'No person shall be debarred from prosecuting or defending any civil cause, for or against him or herself, before any tribunal in this State, by him or herself or counsel.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_30_0_1819', 'Alabama1819', '1819', '1', '30', '0', 'Bill of Rights', 'This enumeration of certain rights shall not be construed to deny or disparage others retained by the people: and, to guard against any encroachments on the rights herein retained, or any transgression of any of the high powers herein delegated, we declare, that every thing in this article is excepted out of the general powers of government, and shall forever remain inviolate; and that all laws contrary thereto, or to the following provisions, shall be void.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_1_0_1819', 'Alabama1819', '1819', '2', '1', '0', 'Separation of Powers', 'The powers of the government of the State of Alabama shall be divided into three distinct departments; and each of them confided to a separate body of magistracy, to wit: those which are legislative, to one; those which are executive, to another; and those which are judicial, to another.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_2_0_1819', 'Alabama1819', '1819', '2', '2', '0', 'Separation of Powers', 'No person, or collection of persons, being of one of those departments, shall exercise any power, properly belonging to either of the others, except in the instances herein after expressly directed or permitted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_1_0_1819', 'Alabama1819', '1819', '3', '1', '0', 'Legislative', 'The Legislative power of this State shall be vested in two distinct branches: the one to be styled the Senate, the other the House of Representatives, and both together, the General Assembly of the State of Alabama; and the style of their Laws shall be, Be it enacted by the Senate and House of Representatives of the State of Alabama, in General Assembly convened.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_2_0_1819', 'Alabama1819', '1819', '3', '2', '0', 'Legislative', 'The members of the House of Representatives shall be chosen by the qualified electors, and shall serve for the term of one year, from the day of the commencement of the general election, add no longer.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_3_0_1819', 'Alabama1819', '1819', '3', '3', '0', 'Legislative', 'The Representatives shall be chosen every year, on the first Monday and the day following in August, until otherwise directed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_4_0_1819', 'Alabama1819', '1819', '3', '4', '0', 'Legislative', 'No person shall be a representative unless he be a white man, a citizen of the United States, and shall have been an inhabitant of this State two years next preceding his election, and the last year thereof a resident of the county, city, or town, for which he shall be chosen, and shall have attained the age of twenty-one years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_5_0_1819', 'Alabama1819', '1819', '3', '5', '0', 'Legislative', 'Every white male person of the age of twenty-one years, or upwards, who shall be a citizen of the United States, and shall have resided in this State one year next preceding an election, and the last three months within the county, city, or town, in which he offers to vote, shall be deemed a qualified elector: Provided, that no soldier, seaman, or marine, in the regular army or navy of the United States, shall be entitled to vote at any election in this State; and provided, also, that no elector shall be entitled to vote except in the county, city, or town, (entitled to separate representation,) in which he may reside at the time of the election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_6_0_1819', 'Alabama1819', '1819', '3', '6', '0', 'Legislative', 'Electors shall, in all cases, except in those of treason, felony, or breach of the peace, be privileged from arrest during their attendance at elections, and in going to and returning from the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_7_0_1819', 'Alabama1819', '1819', '3', '7', '0', 'Legislative', 'In all elections by the people, the electors shall vote by ballot until the General Assembly shall otherwise direct.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_8_0_1819', 'Alabama1819', '1819', '3', '8', '0', 'Legislative', 'Elections for Representatives for the several counties shall be held at the place of holding their respective courts, and at such other places as may be prescribed by law: Provided that, when it shall appear to the General Assembly that any city or town shall have a number of white inhabitants equal to the ratio then fixed, such city or town shall have a separate representation, according to the number of white inhabitants therein; which shall be retained so long as such city or town shall contain a number of white inhabitants equal to the ratio which may from time to time be fixed by law; and thereafter, and during the existence of the right of separate representation, in such city or town, elections for the county in which such city or town (entitled to such separate representation) is situated, shall not be held in such city or town; but it is understood and hereby declared, that no city or town shall be entitled to separate representation unless the number of white inhabitants in the county in which such city or town is situated, residing out of the limits of said city or town, be equal to the existing ratio; or unless the residuum or fraction of such city or town shall, when added to the white inhabitants of the county residing out of the limits of said city or town, he equal to the ratio fixed by law for one representative; and provided, that, if the residuum or fraction of any city or town, entitled to separate representation, shall, when added to the residuum of the county in which it may lie, be equal to the ratio fixed by law for one representative, then the aforesaid county, city, or town, having the largest residuum, shall be entitled to such representation: and provided, also, that when there are two or more counties adjoining, which have residuums or fractions over and above the ratio then fixed by law, if said residuums or fractions, when added together, will amount to such ratio, in that case one representative shall be added to that county having the largest residuum.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_9_0_1819', 'Alabama1819', '1819', '3', '9', '0', 'Legislative', 'The General Assembly shall, at their first meeting and in the years one thousand eight hundred and twenty, one thousand eight hundred and twenty-three, one thousand eight hundred and twenty-six, and every six years thereafter, cause an enumeration to be made of all the inhabitants of the State, and the whole number of the representatives shall, at the first session held, after making every such enumeration, be fixed by the General Assembly, and apportioned among the several counties, cities, or towns, entitled to separate representation, according to their respective numbers of white inhabitants; and the said apportionment, when made, shall not be subject to alteration, until after the next census shall be taken. The House of Representatives shall not consist of less than forty-four, nor more than sixty members, until the number of white inhabitants shall be one hundred thousand, and, after that event, the whole number of representatives shall never be less than sixty nor more than one hundred; Provided, however, that each county shall be entitled to at least one representative.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_10_0_1819', 'Alabama1819', '1819', '3', '10', '0', 'Legislative', 'The General Assembly shall, at the first session after making every such enumeration, fix by law the whole number of senators, and shall divide the state into the same number of districts, as nearly equal in the number of white inhabitants as may be, each of which districts shall be entitled to one senator and no more; provided that the whole number of senators shall never be less than one fourth, nor more than one third of the whole number of representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_11_0_1819', 'Alabama1819', '1819', '3', '11', '0', 'Legislative', 'When a senatorial district shall be composed of two or more counties, the counties of which such district consists shall not be entirely separated by any county belonging to another district; and no county shall be divided in forming a district.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_12_0_1819', 'Alabama1819', '1819', '3', '12', '0', 'Legislative', 'Senators shall be chosen by the qualified electors, for the term of three years, at the same time, in the same manner, and at the same places, where they may vote for Members of the House of Representatives; and no person shall be a Senator unless he be a white man, a citizen of the United States, and shall have been an inhabitant of this State two years next preceding his election, and the last year thereof a resident of the district for which he shall be chosen, and shall have attained to the age of twenty-seven years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_13_0_1819', 'Alabama1819', '1819', '3', '13', '0', 'Legislative', 'The Senators chosen according to the apportionment under the census ordered to be taken in one thousand eight hundred and twenty-six, when convened, shall be divided by lot into three classes, as nearly equal as may be. The seats of the Senators of the first class shall be vacated at the expiration of the first year, those of the second class at the expiration of the second year, and those of the third class at the expiration of the third year, so that one third may be annually chosen thereafter, and a rotation thereby kept up perpetually. Such mode of classifying new additional Senators shall be observed as will, as nearly as possible, preserve an equality of members in each class.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_14_0_1819', 'Alabama1819', '1819', '3', '14', '0', 'Legislative', 'The House of Representatives, when assembled, shall choose a Speaker, and its other officers; and the Senate shall annually choose a President and its other officers. Each house shall judge of the qualifications, elections, and returns, of its own members; but a contested election shall be determined in such a manner as shall be directed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_15_0_1819', 'Alabama1819', '1819', '3', '15', '0', 'Legislative', 'A majority of each house shall constitute a quorum to do business, but a smaller number may adjourn from day to day, and may compel the attendance of absent members, in such manner, and under such penalties, as each House may provide.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_16_0_1819', 'Alabama1819', '1819', '3', '16', '0', 'Legislative', 'Each House may determine the rules of its own proceedings, punish members for disorderly behavior, and, with the consent of two thirds, expel a member; but not a second time for the same cause; and shall have all other powers necessary for branch of the legislature of a free and independent State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_17_0_1819', 'Alabama1819', '1819', '3', '17', '0', 'Legislative', 'Each House, during the session, may punish, by imprisonment, any person, not a member, for disrespectful or disorderly behavior in its presence, or for obstructing any of its proceedings: provided, that such imprisonment shall not, at any one time, exceed forty-eight hours.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_18_0_1819', 'Alabama1819', '1819', '3', '18', '0', 'Legislative', 'Each house shall keep a journal of its proceedings, and cause the same to be published immediately after its adjournment, excepting such parts as, in its judgment, may require secrecy; and the yeas and nays of the members of either house, on any question, shall, at the desire of any two members present, be entered on the journals. And any member of either house shall have liberty to dissent from or protest against any act or resolution which he may think injurious to the public or an individual, and have the reasons of his dissent entered on the Journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_19_0_1819', 'Alabama1819', '1819', '3', '19', '0', 'Legislative', 'Senators and Representatives shall, in all cases, except treason, felony, or breach of the peace, be privileged from arrest during the session of the General Assembly, and in going to and returning from the same; allowing one day for every twenty miles such member may reside from the place at which the General Assembly is convened; nor shall any member be liable to answer for any thing spoken in debate in either House, in any court or place elsewhere.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_20_0_1819', 'Alabama1819', '1819', '3', '20', '0', 'Legislative', 'When vacancies happen in either House, the Governor, or the person exercising the powers of the Governor, shall issue writs of election to fill such vacancies.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_21_0_1819', 'Alabama1819', '1819', '3', '21', '0', 'Legislative', 'The doors of each House shall be open, except on such occasions as, in the opinion of the House, may require secrecy.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_22_0_1819', 'Alabama1819', '1819', '3', '22', '0', 'Legislative', 'Neither house shall, without the consent of the other, adjourn for more than three days, nor to any other place than that in which they may be sitting.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_23_0_1819', 'Alabama1819', '1819', '3', '23', '0', 'Legislative', 'Bills may originate in either House, and be amended, altered, or rejected, by the other; but no bill shall have the force of a law until on three several days it be read in each House, and free discussion be allowed thereon, unless, in cases of urgency, four-fifths of the House in which the bill shall be depending may deem it expedient to dispense with this rule; and every bill, having passed both Houses, shall be signed by the Speaker and President of their respective Houses: provided, that all bills for raising revenue shall originate in the House of Representatives, but the Senate may amend or reject them as other bills.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_24_0_1819', 'Alabama1819', '1819', '3', '24', '0', 'Legislative', 'Each member or the General Assembly shall receive from the public treasury such compensation for his services as may be fixed by law; but no increase of compensation shall take effect during the session at which such increase shall have been made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_25_0_1819', 'Alabama1819', '1819', '3', '25', '0', 'Legislative', 'No Senator or representative shall, during the term for which he shall have been elected, be appointed to any civil office of profit under this state, which shall have been created, or the emoluments of which shall have been increased, during said term; except such offices as may be filled by elections by the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_26_0_1819', 'Alabama1819', '1819', '3', '26', '0', 'Legislative', 'No person holding any lucrative office under the United States, (the office of postmaster excepted,) this state, or any other power, shall be eligible to the General Assembly: provided, that offices in the militia, to which there is attached no annual salary, or the office of justice of the peace, or that of the quorum or county court, while it has no salary, shall not be deemed lucrative.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_27_0_1819', 'Alabama1819', '1819', '3', '27', '0', 'Legislative', 'No person who may hereafter be a collector or holder of public moneys, shall have a seat in either house of the General Assembly, or be eligible to any office of trust or profit under this State, until he shall have accounted for, and paid into the treasury, all sums for which he may be accountable.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_28_0_1819', 'Alabama1819', '1819', '3', '28', '0', 'Legislative', 'The first election for Senators and Representatives shall be general throughout the State; and shall be held on the third Monday and Tuesday in September next.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_29_0_1819', 'Alabama1819', '1819', '3', '29', '0', 'Legislative', 'The first session of the General Assembly shall commence on the fourth Monday in October next, and be held at the town of Huntsville, and all subsequent sessions at the town of Cahawba, until the end of the first session of the General Assembly, to be held in the year one thousand eight hundred and twenty-five: during that session the General Assembly shall have power to designate by law (to which the Executive concurrence shall not be required) the permanent seat of Government, which shall not thereafter be changed; Provided, however, that, unless such designation be then made by law, the government shall continue permanently at the town of Cahawba; and provided, also, that the General Assembly shall make no appropriations previous to the year one thousand eight hundred and twenty-five, for the building of any other State House than that now provided for by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_1_0_1819', 'Alabama1819', '1819', '4', '1', '0', 'Executive', 'The supreme executive power of this State shall be vested in a chief magistrate, who shall be styled the Governor of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_2_0_1819', 'Alabama1819', '1819', '4', '2', '0', 'Executive', 'The Governor shall be elected by the qualified electors, at the time and places when they shall respectively vote for Representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_3_0_1819', 'Alabama1819', '1819', '4', '3', '0', 'Executive', 'The returns of every election for Governor shall be sealed up, and transmitted to the seat of government, directed to the Speaker of the House of Representatives, who shall, during the first week of the session, open and publish them in presence of both Houses of the General Assembly. The person having the highest number of votes shall be Governor; but if two, or more, shall be equal and the highest in votes, one of them shall be chosen Governor by the joint vote of both Houses. Contested elections for Governor shall be determined by both Houses of the General Assembly, in such manner as shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_4_0_1819', 'Alabama1819', '1819', '4', '4', '0', 'Executive', 'The Governor shall hold his office for the term of two years from the time of his installation, and until his successor shall be duly qualified; but shall not be eligible for more than four years in every term of six years; he shall be at least thirty years of age, shall be a native citizen of the United States, and shall have resided in this state at least four years next preceding the day of his election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_5_0_1819', 'Alabama1819', '1819', '4', '5', '0', 'Executive', 'He shall, at stated times, receive a compensation for his services, which shall not be increased or diminished during the term for which he shall have been elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_6_0_1819', 'Alabama1819', '1819', '4', '6', '0', 'Executive', 'He shall be commander in chief of the army and navy of this State, and of the militia thereof, except when they shall be called into the service of the United States. And when acting in the service of the United States, the General Assembly shall fix his rank.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_7_0_1819', 'Alabama1819', '1819', '4', '7', '0', 'Executive', 'He may require information, in writing, from the officers in the executive department, on any subject relating to the duties of their respective offices.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_8_0_1819', 'Alabama1819', '1819', '4', '8', '0', 'Executive', 'He may, by proclamation, on extraordinary occasions, convene the General Assembly at the seat of government, or at a different place, if that shall have become, since their last adjournment, dangerous from an enemy, or from contagious disorders; and in case of disagreement between the two houses with respect to the time of adjournment, he may adjourn them to such time as he shall think proper, not beyond the day of the next annual meeting of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_9_0_1819', 'Alabama1819', '1819', '4', '9', '0', 'Executive', 'He shall, from time to time, give to the General Assembly information of the state of the government, and recommend to their consideration such measures as he may deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_10_0_1819', 'Alabama1819', '1819', '4', '10', '0', 'Executive', 'He shall take care that all the laws be faithfully executed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_11_0_1819', 'Alabama1819', '1819', '4', '11', '0', 'Executive', 'In all criminal and penal cases, except in those of treason and impeachment, he shall have power to grant reprieves and pardons, and remit fines and forfeitures, under such rules and regulation as shall be prescribed by law. In cases of treason, he shall have power, by and with the advice and consent of the Senate, to grant reprieves and pardons; and he may, in the recess of the Senate, respite the sentence until the end of the next session of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_12_0_1819', 'Alabama1819', '1819', '4', '12', '0', 'Executive', 'There shall be a seal of this State, which shall be kept by the Governor, and used by him officially; and the present seal of the Territory shall be the seal of the State, until otherwise directed by the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_13_0_1819', 'Alabama1819', '1819', '4', '13', '0', 'Executive', 'All commissions shall be in the name and by the authority of the State of Alabama, be sealed with the state seal, signed by the Governor, and attested by the Secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_14_0_1819', 'Alabama1819', '1819', '4', '14', '0', 'Executive', 'There shall be a Secretary of State appointed by joint vote of both Houses of the General Assembly, who shall continue in office during the term of two years. He shall keep a fair register of all official acts and proceedings of the Governor, and shall, when required, lay the same, and all papers, minutes, and vouchers, relative thereto, before the General Assembly; and shall perform such other duties as may be required of him by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_15_0_1819', 'Alabama1819', '1819', '4', '15', '0', 'Executive', 'Vacancies that may happen in offices, the appointment to which is vested in the General Assembly, shall be filled by the Governor, during the recess of the General Assembly, by granting commissions, which shall expire at the end of the next session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_16_0_1819', 'Alabama1819', '1819', '4', '16', '0', 'Executive', 'Every bill which shall have passed both Houses of the General Assembly shall be presented to the Governor; if he approve he shall sign it, but, if not, he shall return it, with his objections, to the House in which it shall have originated, who shall enter the objections, at large, upon the journals, and proceed to reconsider it; if, after such reconsideration, a majority of the whole number elected to that House shall agree to pass the bill, it shall be sent, with the objections, to the other House, by which it shall likewise be reconsidered, if approved by a majority of the whole number elected to that House, it shall become a law; but in such cases the votes of both Houses shall be determined by yeas and nays, and the names of the members voting for or against the bill shall be entered on the journals of each House respectively. If any bill shall not be returned by the Governor within five days, Sundays excepted, after it shall have been presented to him, the same shall be a law in like manner as if he had signed it, unless the General Assembly, by their adjournment, prevent its return, in which case, it shall not be a law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_17_0_1819', 'Alabama1819', '1819', '4', '17', '0', 'Executive', 'Every order, resolution, or vote, to which the concurrence of both Houses may be necessary, except on questions of adjournment, shall be presented to the Governor, and, before it shall take effect, be approved by him, or, being disapproved, shall be repassed by both Houses, according to the rules and limitations prescribed in the case of a bill.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_18_0_1819', 'Alabama1819', '1819', '4', '18', '0', 'Executive', 'In case of the impeachment of the Governor, his removal from office, death, refusal to qualify, resignation, or absence from the state, the President of the Senate shall exercise all the power and authority appertaining to the office of Governor, until the time pointed out by this constitution for the election of Governor shall arrive, unless the General Assembly shall provide, by law, for the election of a Governor, to fill such vacancy, or until the Governor absent or impeached shall return or be acquitted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_19_0_1819', 'Alabama1819', '1819', '4', '19', '0', 'Executive', 'If, during the vacancy of the office of Governor, the President of the Senate shall be impeached, removed from office, refuse to qualify, resign, die, or be absent from the state, the Speaker of the House of Representatives shall, in like manner, administer the government.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_20_0_1819', 'Alabama1819', '1819', '4', '20', '0', 'Executive', 'The President of the Senate and Speaker of the House of Representatives, during the time they respectively administer the government, shall receive the same compensation which the Governor would have received, had he been employed in the duties of his office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_21_0_1819', 'Alabama1819', '1819', '4', '21', '0', 'Executive', 'The Governor shall always reside, during the session of the General Assembly, at the place where their session may be held, and at all other times, wherever, in their opinion, the public good require.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_22_0_1819', 'Alabama1819', '1819', '4', '22', '0', 'Executive', 'No person shall hold the office of Governor, and any other office or commission, civil or military, either in this state, or under any state, or the United States, or any other power, at one and the may same time.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_23_0_1819', 'Alabama1819', '1819', '4', '23', '0', 'Executive', 'A state Treasurer and a Comptroller of public accounts shall be annually elected, by joint vote of both Houses of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_24_0_1819', 'Alabama1819', '1819', '4', '24', '0', 'Executive', 'A sheriff shall be elected in each county by the qualified electors thereof, who shall hold his office for the term of three years, unless sooner removed, and who shall not be eligible to serve either as principal or deputy for the three succeeding years. Should a vacancy occur subsequent to an election, it shall be filled by the Governor, as in other cases, and the person so appointed shall continue in office until the next general election, when such vacancy shall be filled by the qualified electors, and the sheriff then elected shall continue in office for three years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_1_0_1819', 'Alabama1819', '1819', '5', '1', '0', 'Judicial', 'The judicial power of this state shall be vested in one Supreme Court, circuit courts to be held in each county in the state, and such inferior courts of law and equity, to consist of not more than five members, as the General Assembly may, from time to time, direct, ordain, and establish.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_2_0_1819', 'Alabama1819', '1819', '5', '2', '0', 'Judicial', 'The supreme court, except in cases otherwise directed by this constitution, shall have appellate jurisdiction only, which shall be co-extensive with the state, under such restrictions and regulations, not repugnant to this constitution, as may, from time to time, be prescribed by law; Provided, that the supreme court shall have power to issue writs of injunction, mandamus, quo-warranto, habeas corpus, and such other remedial and original writs, as may be necessary to give it a general superintendence and control of inferior jurisdiction.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_3_0_1819', 'Alabama1819', '1819', '5', '3', '0', 'Judicial', 'Until the General Assembly shall otherwise prescribe, the powers of the supreme court shall be vested in, and its duties shall be performed by, the Judges of the several circuit courts, within this state: and they or a majority of them shall hold such sessions of the Supreme court, and at such times, as may be directed by law: Provided, that no Judge of the Supreme Court shall be appointed before the commencement of the first session of the General Assembly, which shall be begun and held after the first day of January, in the year one thousand eight hundred and twenty-five.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_4_0_1819', 'Alabama1819', '1819', '5', '4', '0', 'Judicial', 'The Supreme Court shall be holden at the seat of government, but may adjourn to a different place, if that shall have become dangerous from an enemy or from disease.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_5_0_1819', 'Alabama1819', '1819', '5', '5', '0', 'Judicial', 'The State shall be divided into convenient circuits, and each circuit shall contain not less than three, nor more than six counties: and for each circuit there shall be appointed a judge, who shall, after his appointment, reside in the circuit for which he may be appointed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_6_0_1819', 'Alabama1819', '1819', '5', '6', '0', 'Judicial', 'The Circuit Court shall have original jurisdiction in all matters, civil and criminal, within this State, not otherwise excepted in this constitution: but, in civil cases, only when the matter or sum in controversy exceeds fifty dollars.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_7_0_1819', 'Alabama1819', '1819', '5', '7', '0', 'Judicial', 'A Circuit Court shall be held in each county in the State, at least twice in every year, and the judges of the several circuit courts may hold courts for each other, when they may deem it expedient, and shall do so when directed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_8_0_1819', 'Alabama1819', '1819', '5', '8', '0', 'Judicial', 'The General Assembly shall have power to establish a court or courts of chancery with original and appellate equity jurisdiction: and, until the establishment of such court or courts, the said jurisdiction shall be vested in the judges of the circuit courts respectively: Provided that the judges of the several circuit courts shall have power to issue writs of injunction, returnable into the courts of chancery.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_9_0_1819', 'Alabama1819', '1819', '5', '9', '0', 'Judicial', 'The General Assembly shall have power to establish, in each county within this State, a court of probate, for the granting of letters testamentary and of administration, and for orphans business.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_10_0_1819', 'Alabama1819', '1819', '5', '10', '0', 'Judicial', 'A competent number of Justices of the Peace shall be appointed, in and for each county, in such mode, and for such term of office, as the General Assembly may direct. Their jurisdiction in civil cases shall be limited to causes in which the amount in controversy shall not exceed fifty dollars. And in all cases tried by a Justice of the Peace, right of appeal shall be secured, under such rules and regulations as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_11_0_1819', 'Alabama1819', '1819', '5', '11', '0', 'Judicial', 'Judges of the Supreme and Circuit Courts, and Courts of Chancery, shall, at stated times, receive for their services a compensation, which shall be fixed by law, and shall not be diminished during their continuance in office: but they shall receive no fees or perquisites of office, nor hold any other office of profit or trust under this State, the United States, or any other power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_12_0_1819', 'Alabama1819', '1819', '5', '12', '0', 'Judicial', 'Chancellors, Judges of the Supreme Court, Judges of the Circuit Courts, and Judges of the Inferior Courts, shall be elected by joint vote of both Houses of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_13_0_1819', 'Alabama1819', '1819', '5', '13', '0', 'Judicial', 'The Judges of the several Courts in this State shall hold their offices during good behavior; and, for wilful neglect of duty, or other reasonable cause, which shall not be sufficient ground for impeachment, the Governor shall remove any of them, on the address of two thirds of each House of the General Assembly; provided, however, that the cause or causes for which such removal shall be required, shall be stated at length in such address, and entered on the journals of each House; and provided further, that the cause or causes shall be notified to the judge so intended to be removed, and he shall be admitted to a hearing in his own defence, before any vote for such address shall pass; and in all such cases the vote shall be taken by yeas and nays, and entered on the journals of each House respectively; and provided, also, that the judges of the several circuit courts who shall be appointed before the commencement of the first session of the General Assembly, which shall be begun and held after the first day of January in the year of our Lord one thousand eight hundred and twenty-five, shall only hold their offices during good behavior, until the end of the said session, at which time their commissions shall expire.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_14_0_1819', 'Alabama1819', '1819', '5', '14', '0', 'Judicial', 'No person who shall have arrived at the age of seventy years shall be appointed to, or continue in, the office of Judge in this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_15_0_1819', 'Alabama1819', '1819', '5', '15', '0', 'Judicial', 'The clerks of the circuit and inferior courts in this State shall be elected by the qualified electors in each county, for the term of four years, and may be removed from office for such causes, and in such manner as may be prescribed by law; and, should a vacancy occur subsequent to an election, it shall be filled by the Judge or Judges of the Court in which such vacancy exists; and the person so appointed shall hold his office until the next general election; provided, however, that, after the year one thousand eight hundred and twenty-six, the General Assembly may prescribe a different mode of appointment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_16_0_1819', 'Alabama1819', '1819', '5', '16', '0', 'Judicial', 'The Judges of the Supreme Court shall, by virtue of their offices, be conservators of the peace throughout the State; and also the Judges of the Circuit Courts in their respective districts, and Judges of the inferior Courts in their respective counties.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_17_0_1819', 'Alabama1819', '1819', '5', '17', '0', 'Judicial', 'The style of all process shall be the State of Alabama, and all prosecutions shall be carried on in the name and by the authority of the State of Alabama, and shall conclude against the peace and dignity of the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_18_0_1819', 'Alabama1819', '1819', '5', '18', '0', 'Judicial', 'There shall be an Attorney General for the State, and as many Solicitors as the General Assembly may deem necessary, to be elected by joint vote thereof, who shall hold their offices for the term of four years, and shall receive for their services a compensation, which shall not be diminished during their continuance in office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_1_0_1819', 'Alabama1819', '1819', '6', '1', '0', 'General Provisions', 'The members of the General Assembly, and all officers, executive and judicial, before they enter on the execution of their respective offices, shall take the following oath or affirmation, to wit: I solemnly swear (or affirm, as the case may be) that I will support the Constitution of the United States, and Constitution of the State of Alabama, so long as I continue a citizen thereof, and that I will faithfully discharge, to the best of my abilities, the duties of -------- according to law: so help me God.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_2_0_1819', 'Alabama1819', '1819', '6', '2', '0', 'General Provisions', 'Treason against the State shall consist only in levying war against it, or in adhering to its enemies, giving them aid and comfort. No person shall be convicted of treason unless on the testimony of two witnesses to the same overt act, or his own confession in open court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_3_0_1819', 'Alabama1819', '1819', '6', '3', '0', 'General Provisions', 'The General Assembly shall have power to pass such penal laws, to suppress the evil practice of Duelling, extending to disqualification from office or the tenure thereof, as they may deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_4_0_1819', 'Alabama1819', '1819', '6', '4', '0', 'General Provisions', 'Every person shall be disqualified from holding any office or place of honor or profit, under the authority of the State, who shall be convicted of having given or offered any bribe to procure his election or appointment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_5_0_1819', 'Alabama1819', '1819', '6', '5', '0', 'General Provisions', 'Laws shall be made to exclude from office, from suffrage, and from serving as Jurors, those who shall hereafter be convicted of bribery, perjury, forgery, or other high crimes or misdemeanors. The privilege of free suffrage shall be supported by laws regulating elections, and prohibiting, under adequate penalties, all undue influence thereon, from power, bribery, tumult, or other improper conduct.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_6_0_1819', 'Alabama1819', '1819', '6', '6', '0', 'General Provisions', 'In all elections by the General Assembly, the members thereof shall vote viva voce, and the votes shall be entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_7_0_1819', 'Alabama1819', '1819', '6', '7', '0', 'General Provisions', 'No money shall be drawn from the Treasury, but in consequence of an appropriation made by law; and a regular statement and account of the receipts and expenditures of all public moneys shall be published annually.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_8_0_1819', 'Alabama1819', '1819', '6', '8', '0', 'General Provisions', 'All lands liable to taxation in this State shall be taxed in proportion to their value.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_9_0_1819', 'Alabama1819', '1819', '6', '9', '0', 'General Provisions', 'The General Assembly shall direct, by law, in what manner, and in what courts, suits may be brought against the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_10_0_1819', 'Alabama1819', '1819', '6', '10', '0', 'General Provisions', 'It shall be the duty of the General Assembly to regulate, by law, the cases in which deductions shall be made from the salaries of public officers, for neglect of duty in their official capacities, and the amount of such deduction.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_11_0_1819', 'Alabama1819', '1819', '6', '11', '0', 'General Provisions', 'Absence on business of this State, or of the United States, or on a visit, or necessary private business, shall not cause a forfeiture of a residence once obtained.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_12_0_1819', 'Alabama1819', '1819', '6', '12', '0', 'General Provisions', 'No Member of Congress, nor any person holding any office of profit or trust under the United States, (the office of Postmaster excepted) or either of them, or any foreign power, shall hold or exercise any office of profit under this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_13_0_1819', 'Alabama1819', '1819', '6', '13', '0', 'General Provisions', 'Divorces from the bonds of matrimony shall not be granted, but in cases provided for by law, by suit in Chancery: and no decree for such divorce shall have effect until the same shall be sanctioned by two thirds of both Houses of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_14_0_1819', 'Alabama1819', '1819', '6', '14', '0', 'General Provisions', 'In prosecutions for the publishing of papers investigating the official conduct of officers or men in public capacity, or when the matter published is proper for public information, the truth thereof may be given in evidence; and, in all indictments for libels, the jury shall have a right to determine the law and the facts, under the direction of the courts.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_15_0_1819', 'Alabama1819', '1819', '6', '15', '0', 'General Provisions', 'Returns of all elections for officers who are to be commissioned by the Governor, and for members of the General Assembly, shall be made to the Secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_16_0_1819', 'Alabama1819', '1819', '6', '16', '0', 'General Provisions', 'No new county shall be established by the General Assembly, which shall reduce the county or counties, or either of them, from which it shall be taken to a less content that nine hundred square miles; nor shall any county be laid off of less contents. Every new county, as to the right of suffrage and representation, shall be considered as a part of the county or counties from which it was taken, until entitled by numbers to the right of separate representation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_17_0_1819', 'Alabama1819', '1819', '6', '17', '0', 'General Provisions', 'The General Assembly shall, at their first session, which may be holden in the year eighteen hundred and twenty-eight, or at several counties within the limits of this State, to which the Indian title shall have been extinguished, in such manner as they may deem expedient; which boundaries shall not be afterwards altered, unless by the agreement of two thirds of both branches of the General Assembly, and in all cases of ceded Territory acquired by the State, the General Assembly may make such arrangements and designations of the boundaries of counties within such ceded Territory as they may deem expedient, which shall only be altered in like manner; provided that no county hereafter to be formed shall be of less extent than nine hundred square miles.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_18_0_1819', 'Alabama1819', '1819', '6', '18', '0', 'General Provisions', 'It shall be the duty of the General Assembly to pass such laws as may be necessary and proper, to decide differences by arbitrators, to be appointed by the parties, who may choose that summary mode of adjustment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_19_0_1819', 'Alabama1819', '1819', '6', '19', '0', 'General Provisions', 'It shall be the duty of the General Assembly, as soon as circumstances will permit, to form a penal code, founded on principles of reformation, and not of vindictive justice.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_20_0_1819', 'Alabama1819', '1819', '6', '20', '0', 'General Provisions', 'Within five years after the adoption of this Constitution, the body of our laws, civil and criminal, shall be revised, digested, and arranged under proper heads, and promulgated in such manner as the General Assembly may direct: and a like revision, digest, and promulgation, shall be made within every subsequent period of ten years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_21_0_1819', 'Alabama1819', '1819', '6', '21', '0', 'General Provisions', 'The General Assembly shall make provision by law for obtaining correct knowledge of the several objects proper for improvement in relation to the navigable waters, and to the roads in this State, and for making a systematic and economical application of the means appropriated to those objects.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_22_0_1819', 'Alabama1819', '1819', '6', '22', '0', 'General Provisions', 'In the event of the annexation of any foreign territory to this State, by a cession from the United States, laws may be passed, extending to the inhabitants of such territory all the rights and privileges which may be required by the terms of such cession; any thing in this Constitution to the contrary notwithstanding.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_1_0_1819', 'Alabama1819', '1819', '9003', '1', '0', 'Schedule', 'That no inconvenience may arise from a change of Territorial to a permanent State Government, it is declared that all rights, actions, prosecutions, claims, and contracts, as well of individuals as of bodies corporate, shall continue as if no such change had taken place: and all process which shall, before the third Monday in September next, be issued in the name of the Alabama Territory, shall be as valid as if issued in the name of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_2_0_1819', 'Alabama1819', '1819', '9003', '2', '0', 'Schedule', 'All fines, penalties, forfeitures, and escheats accruing to the Alabama Territory, shall accrue to the use of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_3_0_1819', 'Alabama1819', '1819', '9003', '3', '0', 'Schedule', 'The validity of all bonds and recognizances, executed to the Governor of the Alabama Territory, shall not be impaired by the change of government, but may be sued for and recovered in the name of the Governor of the State of Alabama and his successors in office: and all criminal or penal actions arising or now depending within the limits of this State, shall be prosecuted to judgment and execution in the name of said State; all causes of action arising to individuals, and all suits at law or in equity, now depending in the several Courts within the limits of this State and not already barred by law, may be commenced in, or transferred to, such Courts as may have jurisdiction thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_4_0_1819', 'Alabama1819', '1819', '9003', '4', '0', 'Schedule', 'All officers, civil or military, now holding commissions under the authority of the United States or of the Alabama Territory within this State, shall continue to hold and exercise their respective offices under the authority of this State, until they shall be superseded under the authority of this Constitution, and shall receive from the Treasury of this State the same compensation which they heretofore received, in proportion to the time they shall be so employed. The Governor Shall have power to fill vacancies by commissions, to expire so soon as elections or appointments can be made to such offices, by authority of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_5_0_1819', 'Alabama1819', '1819', '9003', '5', '0', 'Schedule', 'All laws and parts of laws, now in force in the Alabama Territory, which are not repugnant to the provisions of this Constitution, shall continue and remain in force as the laws of this State, until they expire by their own limitation, or shall be altered, or repealed by the Legislature thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_6_0_1819', 'Alabama1819', '1819', '9003', '6', '0', 'Schedule', 'Every white male person above the age of twenty one years, who shall be a citizen of the United States, and resident in this State at the time of the adoption of this Constitution, shall be deemed a qualified elector at the first election to be holden in this State. And every white male person who shall reside within the limits of this State at the time of the adoption of this Constitution, and shall be otherwise qualified, shall be entitled to hold any office or place of honor, trust, or profit under this State; any thing in this Constitution to the contrary notwithstanding.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_7_0_1819', 'Alabama1819', '1819', '9003', '7', '0', 'Schedule', 'The President of this Convention shall issue writs of election directed to the Sheriffs of the several counties, requiring them to cause an election to be held for a Governor, Representative to the Congress of the United States, Members of the General Assembly, Clerks of the several Courts and Sheriffs of the respective counties, at the respective places of election and in said counties, on the third Monday and the day following in September next, which elections shall be conducted in the manner prescribed by the existing election laws of the Alabama Territory; and the said Governor and Members of the General Assembly, then duly elected, shall continue to discharge the duties of their respective offices, for the time prescribed by this Constitution, and until their successors shall be duly qualified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_8_0_1819', 'Alabama1819', '1819', '9003', '8', '0', 'Schedule', 'Until the first enumeration shall be made, as directed by this Constitution, the county of Autauga shall be entitled to two representatives; the county of Baldwin to one representative; the county of Blount to three representatives; the county of Cahawba to one representative; the county of Clark to two representatives; the county of Conecuh to two representatives; the county of Cotaco to two representatives; the county of Dallas to two representatives; the county of Franklin to two representatives; the county of Lauderdale to two representatives; the county of Lawrence to two representatives; the county of Limestone to three representatives; the county of Madison to eight representatives; the county of Marengo to one representative; the county of Marion to one representative; the county of Monroe to five representatives; the county of Montgomery to three representatives; the county of Mobile to one representative; the county of St. Clair to one representative; the county of Shelby to two representatives; the county of Tuscaloosa to three representatives; and the county of Washington to two representatives. And each county shall be entitled to one senator, who shall serve for one term.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_9_0_1819', 'Alabama1819', '1819', '9003', '9', '0', 'Schedule', 'The oaths of office, herein directed to be taken, may be administered by any Justice of the Peace, until the General Assembly shall otherwise direct.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_1_0_1819', 'Alabama1819', '1819', '9008', '1', '0', 'Militia', 'The General Assembly shall provide, by law, for organizing and disciplining the militia of this state, in such manner as they shall deem expedient, not incompatible with the constitution and laws of the United States in relation thereto.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_2_0_1819', 'Alabama1819', '1819', '9008', '2', '0', 'Militia', 'Any person who conscientiously scruples to bear arms shall not be compelled to do so, but shall pay an equivalent for personal service.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_3_0_1819', 'Alabama1819', '1819', '9008', '3', '0', 'Militia', 'The Governor shall have power to call forth the militia to execute the laws of the state, to suppress insurrections, and repel invasions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_4_0_1819', 'Alabama1819', '1819', '9008', '4', '0', 'Militia', 'All officers of the militia shall be elected or appointed in such manner as may be prescribed by law: Provided, that the General Assembly shall not make any such elections or appointment, other than those of Adjutants General and Quarter Masters General.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_5_0_1819', 'Alabama1819', '1819', '9008', '5', '0', 'Militia', 'The Governor shall appoint his Aids-de-camp; Majors General, their Aids-de-camp, and all other division staff officers; Brigadiers General shall appoint their Aids, and all other brigade staff officer; and Colonels shall appoint their regimental staff officers.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_6_0_1819', 'Alabama1819', '1819', '9008', '6', '0', 'Militia', 'The General Assembly shall fix, by law, the method of dividing the militia into divisions, brigades, regiments, battalions, and companies: and shall fix the rank of all staff officers.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9009_1_0_1819', 'Alabama1819', '1819', '9009', '1', '0', 'Impeachment', 'The House of Representatives shall have the sole power of impeaching.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9009_2_0_1819', 'Alabama1819', '1819', '9009', '2', '0', 'Impeachment', 'All impeachments shall be tried by the Senate: when sitting for that purpose, the Senators shall be on oath or affirmation: and no person shall be convicted without the concurrence of two-thirds of the members present.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9009_3_0_1819', 'Alabama1819', '1819', '9009', '3', '0', 'Impeachment', 'The Governor and all civil officers shall be liable to impeachment for any misdemeanor in office; but judgment in such cases shall not extend further than removal from office, and to disqualification to hold any office of honor, trust, or profit under the State; but the party convicted shall nevertheless be liable and subject to indictment, trial, and punishment, according to law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9010_1_0_1819', 'Alabama1819', '1819', '9010', '1', '0', 'Banks', 'One State Bank may be established, with such number of branches as the General Assembly may, from time to time, deem expedient: Provided, that no branch bank shall be established, nor bank charter renewed, under the authority of this State, without the concurrence of two thirds of both Houses of the General Assembly; and provided, also, that not more than one bank nor branch bank shall be established, nor bank charter renewed, at any one session of the General Assembly; nor shall any bank or branch bank be established, or bank charter renewed, but in conformity with the following rules: 1. At least two-fifths of the capital stock shall be reserved for the State. 2. A proportion of power in the direction of the bank shall be reserved to the State, equal at least to its proportion of stock therein. 3. The State, and the individual stockholders, shall be liable, respectively, for the debts of the bank, in proportion to their stock holden therein. 4. The remedy for collecting debts shall be reciprocal, for and against the bank. 5. No bank shall commence operations until half of the capital stock subscribed for, be actually paid in gold or silver, which amount shall, in no case, be less than one hundred thousand dollars. 6. In case any bank or branch bank shall neglect or refuse to pay, on demand, any bill, note, or obligation, issued by the corporation, according to the promise therein expressed, the holder of any such note, bill, or obligation, issued by the corporation, according to the promise therein expressed, the holder of any such note, bill, or obligation, shall be entitled to receive and recover interest thereon, until the same shall be paid or specie payments are resumed by said bank, at the rate of twelve per cent per annum from the date of such demand, unless the General Assembly shall sanction such suspension of specie payments; and the General Assembly shall have power, after such neglect or refusal, to adopt such measures as they may deem proper, to protect and secure the rights of all concerned: and to declare the charter of such bank forfeited. 7. After the establishment of a general state bank, the banks of this State now existing may be admitted as branches thereof, upon such terms as the Legislature and the said banks may agree, subject nevertheless to the preceding rules.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9011_1_0_1819', 'Alabama1819', '1819', '9011', '1', '0', 'Slavery', 'The General Assembly shall have no power to pass laws for the emancipation of slaves, without the consent of their owners, or without paying their owners, previous to such emancipation, a full equivalent in money for the slaves so emancipated. They shall have no power to prevent emigrants to this State from bringing with them such persons as are deemed slaves by the laws of any one of the United States, so long as any person of the same age or description shall be continued in slavery by the laws of this State: Provided, that such person or slave be the bona fide property of such emigrants; and provided, also, that laws may be passed to prohibit the introduction into this State of slaves, who have committed high crimes in other States or Territories. They shall have power to pass laws to permit the owners of slaves to emancipate them, saving the rights of creditors, and preventing them from becoming a public charge. They shall have full power to prevent slaves from being brought into this State as merchandize, and also to oblige the owners of slaves to treat them with humanity, to provide for them necessary food and clothing, to abstain from all injuries to them extending to life or limb, and, in case of their neglect, or refusal to comply with the directions of such laws, to have such slave or slaves sold for the benefit of the owner or owners.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9011_2_0_1819', 'Alabama1819', '1819', '9011', '2', '0', 'Slavery', 'In the prosecution of slaves for crimes, of a higher grade than petit larceny, the General Assembly shall have no power to deprive them of an impartial trial by a petit jury.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9011_3_0_1819', 'Alabama1819', '1819', '9011', '3', '0', 'Slavery', 'Any person who shall maliciously dismember or deprive a slave of life, shall suffer such punishment as would be inflicted in case the like offence had been committed on a free white person, and on the like proof; except in case of insurrection of such slave.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9025_1_0_1819', 'Alabama1819', '1819', '9025', '1', '0', 'Amendments', 'The General Assembly, whenever two-thirds of each house shall deem it necessary, may propose amendments to this Constitution; which proposed amendments shall be duly published in print, at least three months before the next general election of Representatives, for the consideration of the people, and it shall be the duty of the several returning officers at the next general election which shall be held for Representatives, to open a poll for, and make a return to the Secretary of State for the time being, of the names of all those voting for representatives, who have voted on such proposed amendments; and if thereupon it shall appear that a majority of all the citizens of this state, voting for representatives, have voted in favor of such proposed amendments; and two thirds of each House of the next General Assembly shall, after such an election, and before another, ratify the same amendments by yeas and nays, they shall be valid, to all intents and purposes, as parts of this Constitution: Provided, that the said proposed amendments shall at each of the said sessions have been read three times, on three several days, in each House.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_1_0_1861', 'Alabama1861', '1861', '1', '1', '0', 'Bill of Rights', 'That all freemen, when they form a social compact, are equal in rights; and that no man or set of men are entitled to exclusive, separate public emoluments or privileges, but in consideration of public services.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_2_0_1861', 'Alabama1861', '1861', '1', '2', '0', 'Bill of Rights', 'All political power is inherent in the people, and all free governments are founded on their authority, and instituted for their benefit; and, therefore, they have at all times an inalienable and indefeasible right to alter, reform, or abolish their form of government, in such manner as they may think expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_3_0_1861', 'Alabama1861', '1861', '1', '3', '0', 'Bill of Rights', 'No person within this State shall, upon any pretence be deprived of the inestimable privilege of worshiping God in the manner most agreeable to his own conscience; nor be compelled to attend any place of worship; nor shall any one ever be obliged to pay any tithes, taxes or other rate, for the building or repairing any place of worship, or for the maintenance of any minister or ministry.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_4_0_1861', 'Alabama1861', '1861', '1', '4', '0', 'Bill of Rights', 'No human authority ought, in any case whatever, to control or interfere with the rights of conscience.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_5_0_1861', 'Alabama1861', '1861', '1', '5', '0', 'Bill of Rights', 'No person shall be hurt, molested or restrained in his religious profession, sentiments or persuasions, provided he does not disturb others in their religious worship.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_6_0_1861', 'Alabama1861', '1861', '1', '6', '0', 'Bill of Rights', 'The civil rights, privileges, or capacities of any citizen, shall in no way be diminished or enlarged, on account of his religious principles.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_7_0_1861', 'Alabama1861', '1861', '1', '7', '0', 'Bill of Rights', 'There shall be no establishment of religion by law; no preference shall ever be given by law to any religious sect, society, denomination, or mode of worship; and no religious test shall ever be required as a qualification to any office or public trust under this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_8_0_1861', 'Alabama1861', '1861', '1', '8', '0', 'Bill of Rights', 'Every citizen may freely speak, write and publish his sentiments on all subjects, being responsible for the abuse of that liberty.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_9_0_1861', 'Alabama1861', '1861', '1', '9', '0', 'Bill of Rights', 'The people shall be secure in their persons, houses, papers, and possessions from unreasonable seizures or searches; and no warrant to search any place, or to seize any person or thing, shall issue without describing them as nearly as may be, nor without probable cause, supported by oath or affirmation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_10_0_1861', 'Alabama1861', '1861', '1', '10', '0', 'Bill of Rights', 'In all criminal prosecutions, the accused has a right to be heard by himself and counsel; to demand the nature and cause of the accusation, and have a copy thereof; to be confronted by the witnesses against him; to have compulsory process for obtaining witnesses in his favor; and, in all prosecutions, by indictment or information, a speedy public trial by an impartial jury of the county or district in which the offense shall have been committed: he shall not be compelled to give evidence against himself, nor shall he be deprived of his life, liberty, or property, but by due course of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_11_0_1861', 'Alabama1861', '1861', '1', '11', '0', 'Bill of Rights', 'No person shall be accused, arrested, or detained, except in cases ascertained by law, and according to the forms which the same has prescribed; and no person shall be punished, but in virtue of a law, established and promulgated prior to the offense, and legally applied.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_12_0_1861', 'Alabama1861', '1861', '1', '12', '0', 'Bill of Rights', 'No person shall, for any indictable offense, be proceeded against criminally by information; except in cases arising in the land and naval forces, or the militia when in actual service, or by leave of the court, for oppression or misdemeanor in office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_13_0_1861', 'Alabama1861', '1861', '1', '13', '0', 'Bill of Rights', 'No person shall, for the same offense, be twice put in jeopardy of life or limb; nor shall any persons property be taken or applied to public use, unless just compensation be made therefor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_14_0_1861', 'Alabama1861', '1861', '1', '14', '0', 'Bill of Rights', 'All courts shall be open, and every person, for an injury done him, in his lands, goods, person or reputation, shall have remedy by due course of law, and right and justice administered without sale, denial or delay.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_15_0_1861', 'Alabama1861', '1861', '1', '15', '0', 'Bill of Rights', 'No power of suspending laws shall be exercised, except by the general assembly, or its authority.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_16_0_1861', 'Alabama1861', '1861', '1', '16', '0', 'Bill of Rights', 'Excessive bail shall not be required, nor excessive fines imposed, nor cruel punishments inflicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_17_0_1861', 'Alabama1861', '1861', '1', '17', '0', 'Bill of Rights', 'All persons shall, before conviction, be bailable by sufficient securities, except for capital offenses, when the proof is evident, or the presumption great; and the privilege of the writ of habeas corpus shall not be suspended, unless when, in cases of rebellion, or invasion, the public safety may require it.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_18_0_1861', 'Alabama1861', '1861', '1', '18', '0', 'Bill of Rights', 'The person of a debtor, where there is not strong presumption of fraud, shall not be detained in prison, after delivering up his estate for the benefit of his creditors, in such manner as shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_19_0_1861', 'Alabama1861', '1861', '1', '19', '0', 'Bill of Rights', 'No ex post facto law, nor law impairing the obligations of contracts shall be made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_20_0_1861', 'Alabama1861', '1861', '1', '20', '0', 'Bill of Rights', 'No person shall be attainted of treason or felony by the general assembly. No attainder shall work corruption of blood, nor forfeiture of estate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_21_0_1861', 'Alabama1861', '1861', '1', '21', '0', 'Bill of Rights', 'The estates of suicides shall descend or vest as in cases of natural death; if any person shall be killed by casualty, there shall be no forfeiture by reason thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_22_0_1861', 'Alabama1861', '1861', '1', '22', '0', 'Bill of Rights', 'The citizens have a right, in a peaceable manner, to assemble together for their common good, and to apply to those invested with the powers of government for redress of grievances, or other proper purposes, by petition, address, or remonstrance.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_23_0_1861', 'Alabama1861', '1861', '1', '23', '0', 'Bill of Rights', 'Every citizen has a right to bear arms in defense of himself and the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_24_0_1861', 'Alabama1861', '1861', '1', '24', '0', 'Bill of Rights', 'No standing army shall be kept up without the consent of the general assembly; and, in that case, no appropriation of money for its support shall be for a longer term than one year; and the military shall, in all cases, and at all times, be in strict subordination to the civil power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_25_0_1861', 'Alabama1861', '1861', '1', '25', '0', 'Bill of Rights', 'No soldier shall, in time of peace, be quartered in any house, without the consent of the owner; nor in time of war, but in a manner to be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_26_0_1861', 'Alabama1861', '1861', '1', '26', '0', 'Bill of Rights', 'No title of nobility, or hereditary distinction, privilege, honor, or emolument, shall ever be granted or conferred in this State; nor shall any office be created, the appointment of which shall be for a longer term than during good behavior.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_27_0_1861', 'Alabama1861', '1861', '1', '27', '0', 'Bill of Rights', 'Emigration from this State shall not be prohibited, nor shall any citizen be exiled.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_28_0_1861', 'Alabama1861', '1861', '1', '28', '0', 'Bill of Rights', 'The right of trial by jury shall remain inviolate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_29_0_1861', 'Alabama1861', '1861', '1', '29', '0', 'Bill of Rights', 'No person shall be debarred from prosecuting or defending any civil cause, for or against him or herself, before any tribunal in this State, by him or herself, or counsel.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_30_0_1861', 'Alabama1861', '1861', '1', '30', '0', 'Bill of Rights', 'This enumeration of certain rights shall not be construed to deny or disparage others retained by the people: and to guard against any encroachments on the rights herein retained, or any transgression of any of the high powers herein delegated, we declare, that every thing in this article is excepted out of the general powers of government, and shall forever remain inviolate; and that all laws contrary thereto, or to the following provisions, shall be void.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_1_0_1861', 'Alabama1861', '1861', '2', '1', '0', 'Separation of Powers', 'The powers of the government of the State of Alabama shall be divided into three distinct departments; and each of them confided to a separate body of magistracy, to-wit: those which are legislative to one; those which are executive, to another; and those which are judicial to another.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_2_0_1861', 'Alabama1861', '1861', '2', '2', '0', 'Separation of Powers', 'No person, or collection of persons, being of one of those departments, shall exercise any power properly belonging to either of the others, except in the instances hereinafter expressly directed or permitted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_1_0_1861', 'Alabama1861', '1861', '3', '1', '0', 'Legislative', 'The legislative power of this State shall be vested in two distinct branches; the one to be styled the Senate, the other the House of Representatives, and both together The General Assembly of the State of Alabama; and the style of their laws shall be, Be it enacted by the Senate and House of Representatives of the State of Alabama in General Assembly convened.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_2_0_1861', 'Alabama1861', '1861', '3', '2', '0', 'Legislative', 'The members of the House of Representatives shall be chosen by the qualified electors, and shall serve for the term of two years from the day of the commencement of the general election, and no longer.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_3_0_1861', 'Alabama1861', '1861', '3', '3', '0', 'Legislative', 'The representatives shall be chosen every two years, on the first Monday in August, until otherwise directed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_4_0_1861', 'Alabama1861', '1861', '3', '4', '0', 'Legislative', 'No person shall be a representative, unless he be a white man, a citizen of the State of Alabama, and shall have been an inhabitant of this State two years next preceding his election, and the last year thereof, a resident of the county, city, or town, for which he shall be chosen, and shall have attained the age of twenty-one years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_5_0_1861', 'Alabama1861', '1861', '3', '5', '0', 'Legislative', 'Every male white person of the age of twenty-one years, or upwards, who shall be a citizen of this State, and shall have resided therein one year next preceding an election, and the last three months within a county, city, or town, in which he offers to vote, shall be deemed a qualified elector, but no elector shall be entitled to vote except in the county, city, or town, (entitled to separate representation) in which he may reside at the time of the election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_6_0_1861', 'Alabama1861', '1861', '3', '6', '0', 'Legislative', 'Electors shall, in all cases, except in those of treason, felony, or breach of the peace, be privileged from arrest during their attendance at elections, and in going to and returning from the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_7_0_1861', 'Alabama1861', '1861', '3', '7', '0', 'Legislative', 'In all elections by the people, the electors shall vote by ballot, until the general assembly shall otherwise direct.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_8_0_1861', 'Alabama1861', '1861', '3', '8', '0', 'Legislative', 'Elections for representatives for the several counties shall be held at the place of holding their respective courts, and at such other places as may be prescribed by law: Provided, that when it shall appear to the general assembly, that any city or town shall have a number of white inhabitants equal to the ratio then fixed, such city or town shall have a separate representation, according to the number of white inhabitants therein; which shall be retained so long as such city or town shall contain a number of white inhabitants, equal to the ratio which may from time to time be fixed by law; and thereafter, and during the existence of the right of separate representation, in such city or town, elections for the county in which such city or town (entitled to such separate representation) is situated, shall not be held in such city or town: but it is understood, and hereby declared, that no city or town shall be entitled to separate representation, unless the number of white inhabitants in the county in which such city or town is situated, residing out of the limits of such city or town, be equal to the existing ratio or unless the residuum or fraction of such city or town shall, when added to the white inhabitants of the county, residing out of the limits of said city or town, be equal to the ratio fixed by law for one representative; and, provided, that, if the residuum or fraction of any city or town, entitled to separate representation, shall, when added to the residuum of the county in which it may lie, be equal to the ratio fixed by law for one representative, then the aforesaid county, city or town, having the largest residuum, shall be entitled to such representation: and, provided, also, that where there are two or more counties adjoining, which have residuums or fractions over and above the ratio then fixed by law, if said residuums or fractions, when added together, will amount to such ratio, in that case one representative shall be added to that county having the largest residuum.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_9_0_1861', 'Alabama1861', '1861', '3', '9', '0', 'Legislative', 'The General Assembly shall cause an enumeration to made in the year eighteen hundred and fifty, and eighteen hundred and fifty-five, and every ten years thereafter, of all the white inhabitants of this State; and the whole number of representatives shall, at the first regular session after such enumeration, be apportioned among the several counties, cities, or towns, entitled to separate representation, according to their respective number of white inhabitants, and the said apportionment, when made, shall not be subject to alteration until after the next census shall be taken.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_10_0_1861', 'Alabama1861', '1861', '3', '10', '0', 'Legislative', 'The general assembly shall, at the first session after making every such enumeration, fix by law the whole number of senators, and shall divide the State into the same number of districts, as nearly equal in the number of white inhabitants as may be, each of which districts shall be entitled to one senator, and no more: Provided , that the whole number of senators shall never be less than one-fourth, nor never more than one-third, of the whole number of representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_11_0_1861', 'Alabama1861', '1861', '3', '11', '0', 'Legislative', 'When a senatorial district shall be composed of two or more counties, the counties of which such district consists, shall not be entirely separated by any county belonging to another district; and no county shall be divided in forming a district.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_12_0_1861', 'Alabama1861', '1861', '3', '12', '0', 'Legislative', 'Senators shall be chosen by the qualified electors, for the term of four years, at the same time, and in the same manner, and at the places where they may vote for members of the House of Representatives; and no person shall be a senator, unless he be a white man, a citizen of the State, and an inhabitant thereof two years next preceding his election, and the last year thereof, a resident of the district for which he shall be chosen; and shall have attained to the age of twenty-seven years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_13_0_1861', 'Alabama1861', '1861', '3', '13', '0', 'Legislative', 'Senators shall be chosen for the term of four years; yet, at the general election after every new apportionment, elections shall be held anew in every senatorial district; and the senators elected, when convened at the first session, shall be divided by lot into two classes, as nearly equal as may be: the seats of those of the first class shall be vacated at the expiration of two years, and those of the second class at the expiration of four years, dating in both cases from the day of election, so that one half may be biennially chosen, except as above provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_14_0_1861', 'Alabama1861', '1861', '3', '14', '0', 'Legislative', 'The House of Representatives, when assembled, shall choose a speaker, and its other officers; and the Senate shall choose a President and its other officers, biennially: each house shall judge of the qualifications, elections and returns of its own members; but a contested election shall be determined in such manner as shall be directed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_15_0_1861', 'Alabama1861', '1861', '3', '15', '0', 'Legislative', 'A majority of each house constitute a quorum to do business, but a smaller number may adjourn from day to day, and may compel the attendance of absent members, in such manner and under such penalties, as each house may provide.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_16_0_1861', 'Alabama1861', '1861', '3', '16', '0', 'Legislative', 'Each house may determine the rules of its own proceedings, punish members for disorderly behavior, and, with the consent of two-thirds, expel a member; but not a second time for the same cause; and shall have all other powers necessary for a branch of the legislature of a free and independent State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_17_0_1861', 'Alabama1861', '1861', '3', '17', '0', 'Legislative', 'Each house, during the session, may punish by imprisonment, any person, not a member, for disrepectful or disorderly behavior in its presence, or for obstructing any of its proceedings: Provided , that such imprisonment shall not at any one time, exceed forty-eight hours.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_18_0_1861', 'Alabama1861', '1861', '3', '18', '0', 'Legislative', 'Each house shall keep a journal of its proceedings, and cause the same to be published immediately after its adjournment, excepting such parts as, in its judgment, may require secrecy; and the yeas and nays of the members of either house, on any question, shall at the desire of one tenth of the members, be entered on the journals. Any member of either house shall have liberty to dissent from, or protest against, any act or resolution, which he may think injurious to the public or an individual, and have the reasons of his dissent entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_19_0_1861', 'Alabama1861', '1861', '3', '19', '0', 'Legislative', 'Senators and Representatives shall, in all cases, except treason, felony or breach of the peace, be privileged from arrest, during the session of the General Assembly, and in going to and returning from the same; allowing one day for every twenty miles such member may reside from the place at which the General Assembly is convened; nor shall any member be liable to answer for any thing spoken in debate in either house, in any court or place elsewhere.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_20_0_1861', 'Alabama1861', '1861', '3', '20', '0', 'Legislative', 'When vacancies happen in either house, the Governor, or the person exercising the powers of the Governor, shall issue writs of election to fill vacancies.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_21_0_1861', 'Alabama1861', '1861', '3', '21', '0', 'Legislative', 'The doors of each house shall be open, except on such occasions, as in the opinion of the house, may require secrecy.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_22_0_1861', 'Alabama1861', '1861', '3', '22', '0', 'Legislative', 'Neither house shall, without the consent of the other, adjourn for more than three days, nor to any other place than that in which they may be sitting.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_23_0_1861', 'Alabama1861', '1861', '3', '23', '0', 'Legislative', 'Bills may originate in either house, and be amended, altered, or rejected, by the other; but no bill shall have the force of a law, until on three several days it be read in each house; and free discussion be allowed thereon; unless, in case of urgency, four-fifths of the house, in which the bill shall be depending, may deem it expedient to dispense with this rule: and every bill, having passed both houses, shall be signed by the Speaker and President of their respective houses: Provided , that all bills for raising revenue shall originate in the House of Representatives, but the Senate may amend or reject them as other bills.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_24_0_1861', 'Alabama1861', '1861', '3', '24', '0', 'Legislative', 'Each member of the General Assembly shall receive from the public treasury, such compensation for his services, as may be fixed by law; but no increase of compensation shall take effect during the session at which such increase shall have been made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_25_0_1861', 'Alabama1861', '1861', '3', '25', '0', 'Legislative', 'No Senator or Representative shall during the term for which he shall have been elected, be appointed to any civil office of profit under this State; except such offices as may be filled by elections by the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_26_0_1861', 'Alabama1861', '1861', '3', '26', '0', 'Legislative', 'No person holding any lucrative office under this State, or any other power, the office of Postmaster, offices in the militia to which there is attached no annual salary, justices of the peace, commissioners of the Court of County Commissioners, notary public, and commissioner of deeds excepted, shall be eligible to the General Assembly of this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_27_0_1861', 'Alabama1861', '1861', '3', '27', '0', 'Legislative', 'No person who may hereafter be a collector or holder of public moneys, shall have a seat in either house of the General Assembly, or be eligible to any office of trust or profit under this State, until he shall have accounted for, and paid into the treasury, all sums for which he may be accountable.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_28_0_1861', 'Alabama1861', '1861', '3', '28', '0', 'Legislative', 'The General Assembly shall meet annually, on such day as may be provided by law, and may continue in session not more than thirty days. The next session of the General Assembly shall commence on the second Monday in November, eighteen hundred and sixty-one.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_29_0_1861', 'Alabama1861', '1861', '3', '29', '0', 'Legislative', 'No special law shall be enacted for the benefit of individuals or private corporations, in cases which are provided for by a general law, or where the relief sought, can be given by any court of this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_30_0_1861', 'Alabama1861', '1861', '3', '30', '0', 'Legislative', 'Private property shall not be taken for private use, or for the use of corporations, other than municipal, without the consent of the owner; but the right of way may be secured by law, to persons and corporations, over the lands of persons and corporations; also, the right to establish depots, stations and turn-outs to works of public improvement: Provided , just compensation is made to the owner of such land.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_31_0_1861', 'Alabama1861', '1861', '3', '31', '0', 'Legislative', 'No power to levy taxes shall be delegated to individuals or private corporations.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_32_0_1861', 'Alabama1861', '1861', '3', '32', '0', 'Legislative', 'Taxes shall not be levied for the benefit of individuals or corporations, other than municipal corporations, without the consent of the tax-payer; but this section shall not be so construed as to effect the power of the General Assembly to perfect or secure any right or privilege arising under any existing law of this State; and no right or privilege arising under any existing law of this State, shall be affected by this section.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_33_0_1861', 'Alabama1861', '1861', '3', '33', '0', 'Legislative', 'The General Assembly shall not borrow or raise money on the credit of the State, except for purposes of military defense against actual or threatened invasion, rebellion, or insurrection, unless two-thirds of the members elected to each house, shall concur; nor shall the debts or liabilities of any corporation, person or persons, or other State be guaranteed; or any money, credit or thing loaned, or given away, unless by a like concurrence of each house, voting, in cases provided for in this section, by ayes and nays to be placed upon the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_1_0_1861', 'Alabama1861', '1861', '4', '1', '0', 'Executive', 'The supreme executive power of this State shall be vested in a chief magistrate, who shall be styled the Governor of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_2_0_1861', 'Alabama1861', '1861', '4', '2', '0', 'Executive', 'The Governor shall be elected by the qualified electors, at the time and places when they shall respectively vote for Representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_3_0_1861', 'Alabama1861', '1861', '4', '3', '0', 'Executive', 'The returns of every election for Governor, shall be sealed up, and transmitted to the seat of government, directed to the speaker of the House of Representatives, who shall, during the first week of the session, open and publish them in presence of both houses of the General Assembly. The person having the highest number of votes shall be Governor, but if two or more shall be equal and highest in votes, one of them shall be chosen Governor by the joint vote of both houses. Contested elections for Governor shall be determined by both houses of the General Assembly, in such manner as shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_4_0_1861', 'Alabama1861', '1861', '4', '4', '0', 'Executive', 'The Governor shall hold his office for the term of two years, from the time of his installation, and until his successor shall be qualified; but shall not be eligible for more than four years in any term of six years; he shall be at least thirty years of age, shall be a citizen of the State of Alabama, and native of one of the States of the Territories, lately styled the United States of America.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_5_0_1861', 'Alabama1861', '1861', '4', '5', '0', 'Executive', 'He shall, at stated times, receive a compensation for his services, which shall not be increased or diminished during the term for which he shall have been elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_6_0_1861', 'Alabama1861', '1861', '4', '6', '0', 'Executive', 'He shall be commander-in-chief of the army and navy of this State, except when acting with any other power, in which case, the General Assembly shall fix his rank.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_7_0_1861', 'Alabama1861', '1861', '4', '7', '0', 'Executive', 'He may require information in writing from the officers of the executive department, on any subject relating to the duties of their respective offices.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_8_0_1861', 'Alabama1861', '1861', '4', '8', '0', 'Executive', 'He may, by proclamation, on extraordinary occasions, convene the General Assembly at the seat of government, or at a different place, if that shall have become, since their last adjournment, dangerous from an enemy , or from contagious disorders; in case of disagreement between the two houses, with respect to the time of adjournment, he may adhourn them to such time as he shall think proper, not beyond the day of the next annual meeting of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_9_0_1861', 'Alabama1861', '1861', '4', '9', '0', 'Executive', 'He shall, from time to time, give to the General Assembly, information of the state of the government, and recommend to their consideration such measures as he may deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_10_0_1861', 'Alabama1861', '1861', '4', '10', '0', 'Executive', 'He shall take care that the laws be faithfully executed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_11_0_1861', 'Alabama1861', '1861', '4', '11', '0', 'Executive', 'In all criminal and penal cases, except in those of treason and impeachment, he shall have power to grant reprieves and pardons, and remit fines and forfeitures, under such rules and regulations as shall be prescribed by law. In cases of treason, he shall have power, by, and with the advice and consent of the Senate, to grant reprieves and pardons; and he may in the recess of the Senate, respite the sentence until the end of the next session of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_12_0_1861', 'Alabama1861', '1861', '4', '12', '0', 'Executive', 'There shall be a Seal of this State, which shall be kept by the Governor, and used by him officially.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_13_0_1861', 'Alabama1861', '1861', '4', '13', '0', 'Executive', 'All commissions shall be in the name, and by the authority of the State of Alabama, be sealed with the State Seal, signed by the Governor, and attested by the Secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_14_0_1861', 'Alabama1861', '1861', '4', '14', '0', 'Executive', 'There shall be a Secretary of State, appointed by a joint vote of both houses of the General Assembly, who shall continue in office during the term of two years. He shall keep a fair register of all official acts and proceedings of the Governor, and shall, when required, lay the same, and all papers, minutes and vouchers relative thereto, before the General Assembly; and shall perform such other duties as may be required of him by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_15_0_1861', 'Alabama1861', '1861', '4', '15', '0', 'Executive', 'Vacancies that may happen in offices, the appointment of which is vested in the General Assembly, shall be filled by the Governor, during the recess of the General Assembly, by granting commissions, which shall expire at the end of the next session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_16_0_1861', 'Alabama1861', '1861', '4', '16', '0', 'Executive', 'Every bill which shall have passed both houses of the General Assembly, shall be presented to the Governor; if he approve, he shall sign it, but if not, he shall return it with his objections, to the house in which it shall have originated, who shall enter the objections at large upon the journals, and proceed to reconsider it; if after such reconsideration, a majority of the whole number elected to that house shall agree to pass the bill, it shall be sent, with the objections, to the other house, by which it shall likewise be reconsidered; if approved by a majority of the whole number elected to that house, it shall become a law: but in such cases, the votes of both houses shall be determined by yeas and nays , and the names of the members voting for or against the bill shall be entered on the journals of each house respectively: if any bill shall not be returned by the Governor within five days, Sundays excepted, after it shall have been presented to him, the same shall be a law in like manner as if he had signed it, unless the General Assembly, by their adjournment, prevent its return, in which case it shall not be a law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_17_0_1861', 'Alabama1861', '1861', '4', '17', '0', 'Executive', 'Every order, resolution, or vote, to which the concurrence of both houses may be necessary, except on questions of adjournment, shall be presented to the Governor, and, before it shall take effect, be approved by him, or being disapproved, shall be repassed by both houses, according to the rules and limitations prescribed in the case of a bill.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_18_0_1861', 'Alabama1861', '1861', '4', '18', '0', 'Executive', 'In case of the impeachment of the Governor, his removal from office, death, refusal to qualify, resignation, or absence from the State, the President of the Senate shall exercise all the power and authority appertaining to the office of Governor, until the time, pointed out by this Constitution for the election of Governor, shall arrive, unless the General Assembly shall provide by law for the election of a Governor to fill such vacancy, or until the Governor absent or impeached, shall return or be acquitted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_19_0_1861', 'Alabama1861', '1861', '4', '19', '0', 'Executive', 'If, during the vacancy of the office of Governor, the President of the Senate shall be impeached, removed from office, refuse to qualify, resign, die, or be absent from the State, the Speaker of the House of Representatives, shall, in like manner, administer the government. If there is no President of the Senate, or no Speaker of the House of Representatives, the Secretary of State shall exercise all the duties and powers appertaining to the office of Governor, until the office of Governor is filled, as provided by this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_20_0_1861', 'Alabama1861', '1861', '4', '20', '0', 'Executive', 'The President of the Senate and Speaker of the House of Representatives, and the Secretary of State, during the times they respectively administer the government, shall receive the same compensation which the Governor would have received, had he been employed in the duties of his office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_21_0_1861', 'Alabama1861', '1861', '4', '21', '0', 'Executive', 'The Governor shall always reside during the session of the General Assembly, at the place where their session may be held, and at all other times, wherever, in their opinion, public good may require.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_22_0_1861', 'Alabama1861', '1861', '4', '22', '0', 'Executive', 'No person shall hold the office of Governor, and any other office or commission, civil or military, either in this State, or under any State, or any other power, at one and the same time.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_23_0_1861', 'Alabama1861', '1861', '4', '23', '0', 'Executive', 'A State Treasurer and a Comptroller of public accounts, shall be biennially elected by joint vote of both houses of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_24_0_1861', 'Alabama1861', '1861', '4', '24', '0', 'Executive', 'A sheriff shall be elected in each county by the qualified electors thereof, who shall hold his office for the term of three years, unless sooner removed, and who shall not be eligible to serve either as principal or deputy, for the three succeeding years. Should a vacancy occur subsequent to an election, it shall be filled by the Governor, as in other cases, and the person so appointed shall continue in office until the next general election, when such vacancy shall be filled by the qualified electors, and the sheriff then elected shall continue in office for three years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_1_0_1861', 'Alabama1861', '1861', '5', '1', '0', 'Judicial', 'The judicial power of this State shall be vested in one Supreme Court, Circuit Courts to be held in each county in the State, and such inferior courts of law and equity, to consist of not more than five members, as the General Assembly may, from time to time, direct, ordain and establish.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_2_0_1861', 'Alabama1861', '1861', '5', '2', '0', 'Judicial', 'The Supreme Court, except in cases otherwise directed by this Constitution, shall have appellate jurisdiction only, which shall be co-extensive with the State, under such restrictions and regulations, not repugnant to this Constitution, as may from time to time, be prescribed by law: Provided , that the Supreme Court shall have power to issue writs of injunction, mandamus, quo warranto, habeas corpus , and such other remedial and original writs as may be necessary to give it a general superintendence and control of inferior jurisdiction.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_3_0_1861', 'Alabama1861', '1861', '5', '3', '0', 'Judicial', 'The Supreme Court shall be holden at the seat of government, but may adjourn to a different place, if that shall have become dangerous from an enemy or from disease.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_4_0_1861', 'Alabama1861', '1861', '5', '4', '0', 'Judicial', 'The State shall be divided into convenient circuits, and each circuit shall contain not less than three, nor more than six counties; and for each circuit there shall be appointed a Judge, who shall, after his appointment, reside in the circuit for which he may be appointed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_5_0_1861', 'Alabama1861', '1861', '5', '5', '0', 'Judicial', 'The Circuit Court shall have original jurisdiction in all matters, civil and criminal, within this State, not otherwise excepted in this Constitution; but in civil cases, only where the matter or sum in controversy exceeds fifty dollars.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_6_0_1861', 'Alabama1861', '1861', '5', '6', '0', 'Judicial', 'A Circuit Court shall be held in each county in the State, at least twice in every year, and the judges of the several Circuit Courts may hold courts for each other, when they may deem it expedient, and shall do so when directed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_7_0_1861', 'Alabama1861', '1861', '5', '7', '0', 'Judicial', 'The General Assembly shall have power to establish a Court or Courts of Chancery, with original and appellate jurisdiction; Provided, that the judges of the several circuit courts shall have power to issue writs of injunction, returnable into the Courts of Chancery.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_8_0_1861', 'Alabama1861', '1861', '5', '8', '0', 'Judicial', 'The General Assembly shall have power to establish, in each county within this State, a Court of Probate, for the granting of letters testamentary, and of administration, and for orphans business.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_9_0_1861', 'Alabama1861', '1861', '5', '9', '0', 'Judicial', 'A competent number of Justices of the Peace shall be appointed in and for each county, in such mode and for such term of office as the General Assembly may direct. Their jurisdiction in civil cases shall be limited to causes in which the amount in controversy shall not exceed fifty dollars; and in all cases tried by a Justice of the Peace, right of appeal shall be secured, under such rules and regulations as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_10_0_1861', 'Alabama1861', '1861', '5', '10', '0', 'Judicial', 'Judges of the Supreme and Circuit Courts, and Courts of Chancery, shall, at stated times, receive for their services a compensation, which shall be fixed by law, and shall not be diminished during their continuance in office; but they shall receive no fees or perquisites of office, nor hold any other office of profit or trust under this State, or any other power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_11_0_1861', 'Alabama1861', '1861', '5', '11', '0', 'Judicial', 'Chancellors and Judges of the Supreme Court shall be elected by joint vote of both houses of the General Assembly; but at and after the session of the General Assembly, to be held in the winter of the year eighteen hundred and forty-nine-fifty, the General Assembly shall provide by law for the election of judges of the circuit Courts, by the qualified electors of their circuits respectively, and for the election of Judges of the Courts of Probate, and other inferior courts, (not including Chancellors) by the qualified electors of the counties, cities, or districts, for which such courts may be respectively established; the first Monday in November in any year shall be the day for any election of such judges by the people, or such other day not to be within a less period than two months of the general election for Governor, members of the General Assembly, or members of Congress, as the General Assembly may by law prescribe; but no change to be made in any circuit, or district, or in the mode or time of electing, shall affect the right of any Judge to hold office during the term prescribed by the Constitution, except at the first election thereof, to be made by the people, after the ratification of these amendments or either of them, which elections shall then, all be had on the same day throughout the State, and the terms of the Judges then to be elected, shall commence on that day; vacancies in the office of Judge, shall be filled by the Governor, and the persons appointed thereto by him, shall hold until the next first Monday in November, or other election day of Judges, and until the election and qualification of their successors respectively; and the General Assembly have power to annex to the offices of any of the Judges of the inferior courts the duties of clerks of such courts respectively.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_12_0_1861', 'Alabama1861', '1861', '5', '12', '0', 'Judicial', 'The Judges of the several courts of this State, shall hold their offices for the term of six years; and for wilful neglect of duty, or other reasonable cause, which shall not be sufficient ground for impeachment, the Governor shall remove any of them on the address of two-thirds of each house of the General Assembly; Provided , however, that the cause or causes for which such removal shall be required, shall be stated at length in such address, and entered on the journals of each house; And provided, further, That the cause or causes shall be notified to the Judge so intended to be removed, and he shall be admitted to a hearing in his own defense, before any vote for such address shall pass; and in all such cases the vote shall be taken by yeas and nays , and entered on the journal of each house, respectively; And provided, also, That the Judges now in office may hold their offices until the session of the General Assembly, which shall be held in the year one thousand eight hundred and thirty-three, and until their successors shall be elected and qualified, unless removed by address or impeachment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_13_0_1861', 'Alabama1861', '1861', '5', '13', '0', 'Judicial', 'No person who shall have arrived at the age of seventy years shall be appointed to, or continue in, the office of Judge in this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_14_0_1861', 'Alabama1861', '1861', '5', '14', '0', 'Judicial', 'Clerks of the Circuit and Inferior Courts in this State, shall be elected by the qualified electors in each county, for the term of four years, and may be removed from office for such causes and in such manner as may be prescribed by law; and should a vacancy occur, subsequent to an election, it shall be filled by the Judge or Judges of the court in which such vacancy exists; and the person so appointed shall hold his office until the next general election; Provided , however, that after the year one thousand eight hundred and twenty-six, the General Assembly may prescribe a different mode of appointment, but shall not make such appointment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_15_0_1861', 'Alabama1861', '1861', '5', '15', '0', 'Judicial', 'The Judges of the Supreme Court shall, by virtue of their offices, be conservators of the peace throughout the State; as also the Judges of the Circuit Courts in their respective districts, and Judges of the inferior courts in their respective counties.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_16_0_1861', 'Alabama1861', '1861', '5', '16', '0', 'Judicial', 'The style of all process shall be The State of Alabama, and all prosecutions shall be carried on in the name, and by the authority of the State of Alabama, and shall conclude against the peace and dignity of the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_17_0_1861', 'Alabama1861', '1861', '5', '17', '0', 'Judicial', 'There shall be an Attorney General from the State, and as many solicitors as the General Assembly may deem necessary, to be elected by a joint vote thereof, who shall hold their offices for the term of four years, and shall receive for their services a compensation, which shall not be diminished during their continuance in office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_1_0_1861', 'Alabama1861', '1861', '6', '1', '0', 'General Provisions', 'The members of the General Assembly, and all officers, executive and judicial, before they enter on the execution of their respective offices, shall take the following oath or affirmation, to wit: I do solemnly swear [or affirm, as the case may be] that I will support the Constitution of the State of Alabama so long as I continue a citizen thereof, and that I will faithfully discharge, to the best of my abilities, the duties of_______________ according to law, so help me God!', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_2_0_1861', 'Alabama1861', '1861', '6', '2', '0', 'General Provisions', 'Treason against the State shall consist only in levying war against it, or in adhering to its enemies, giving them aid and comfort. No person shall be convicted of treason, unless on the testimony of two witnesses to the same overt act, or his own confession in open court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_3_0_1861', 'Alabama1861', '1861', '6', '3', '0', 'General Provisions', 'The General Assembly shall have power to pass such penal laws to suppress the evil practice of duelling, extending to disqualification from office, or the tenure thereof, as they may deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_4_0_1861', 'Alabama1861', '1861', '6', '4', '0', 'General Provisions', 'Every person shall be disqualified from holding any office or place of honor or profit, under the authority of the State, who shall be convicted of having given or offered any bribe to procure his election or appointment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_5_0_1861', 'Alabama1861', '1861', '6', '5', '0', 'General Provisions', 'Laws shall be made to exclude from office, from suffrage, and from serving as jurors, those who shall hereafter be convicted of bribery, perjury, forgery, or other high crimes, or misdemeanors. The privelege of free suffrage shall be supported by laws regulating elections, and prohibiting under adequate penalties, all undue influence thereon from power, bribery, tumult, or improper conduct.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_6_0_1861', 'Alabama1861', '1861', '6', '6', '0', 'General Provisions', 'In all elections by the General Assembly, the members thereof shall vote viva voce , and the votes shall be entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_7_0_1861', 'Alabama1861', '1861', '6', '7', '0', 'General Provisions', 'No money shall be drawn from the treasury, but in consequence of an appropriation made by law; and a regular statement and account of receipts and expenditures of all public moneys shall be published annually.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_8_0_1861', 'Alabama1861', '1861', '6', '8', '0', 'General Provisions', 'All lands liable to taxation in this State, shall be taxed in proportion to their value.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_9_0_1861', 'Alabama1861', '1861', '6', '9', '0', 'General Provisions', 'The General Assembly shall direct, by law, in what manner, and in what courts, suits may be brought against the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_10_0_1861', 'Alabama1861', '1861', '6', '10', '0', 'General Provisions', 'It shall be the duty of the General Assembly to regulate, by law, the cases in which deductions shall be made from the salaries of public officers, for neglect of duty in their official capacities, and the amount of such deduction.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_11_0_1861', 'Alabama1861', '1861', '6', '11', '0', 'General Provisions', 'Temporary absence from this State, shall not cause a forfeiture of a residence once obtained.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_12_0_1861', 'Alabama1861', '1861', '6', '12', '0', 'General Provisions', 'No member or delegate to any Congress of other States or powers, nor persons holding any office of profit or trust under any foreign power, shall hold or exercise any office of profit under this State: Provided , that this section does not apply to any deputy, delegate or commissioner elected by this convention.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_13_0_1861', 'Alabama1861', '1861', '6', '13', '0', 'General Provisions', 'Divorces from the bonds of Matrimony shall not be granted, but in cases provided for by law in chancery. But decrees for divorce shall be final, unless appealed from within three months from the date of the enrollment thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_14_0_1861', 'Alabama1861', '1861', '6', '14', '0', 'General Provisions', 'In prosecutions for the publishing of papers investigating the official conduct of officers, or men in public capacity, or when the matter published is proper for public information, the truth thereof may be given in evidence; and in all indictments for libels, the jury shall have the right to determine the law and the facts under the direction of the courts.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_15_0_1861', 'Alabama1861', '1861', '6', '15', '0', 'General Provisions', 'Returns of all elections for officers who are to be commissioned by the Governor, and for members of the General Assembly, shall be made to the Secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_16_0_1861', 'Alabama1861', '1861', '6', '16', '0', 'General Provisions', 'The General Assembly may, by a vote of two-thirds of both branches thereof, arrange and designate boundaries for the several counties of this State, which shall not be altered, except by a like vote of the General Assembly. But no new county shall be hereafter formed of less extent than nine hundred square miles, nor shall it contain, at the time, less than one hundredth part of the population of the State, and no existing county shall be hereafter reduced below such area or population by the formation of a new county.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_17_0_1861', 'Alabama1861', '1861', '6', '17', '0', 'General Provisions', 'It shall be the duty of the General Assembly to pass such laws as may be necessary and proper to decide differences by arbitrators, to be appointed by the parties, who may choose that summary mode of adjustment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_18_0_1861', 'Alabama1861', '1861', '6', '18', '0', 'General Provisions', 'It shall be the duty of the General Assembly, as soon as circumstances will permit, to form a penal code, founded on principles of reformation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_19_0_1861', 'Alabama1861', '1861', '6', '19', '0', 'General Provisions', 'Within five days after the adoption of this Constitution, the body or our laws, civil and criminal, shall be revised, digested and arranged, under proper heads, and promulgated, in such manner as the General Assembly may direct: and a like revision, digest, and promulgation, shall be made within every subsequent period of ten years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_20_0_1861', 'Alabama1861', '1861', '6', '20', '0', 'General Provisions', 'The General Assembly shall make provisions by law for obtaining correct knowledge of the several objects proper for improvement in relation to the navigable waters, and to the roads in this State, and for making a systematic and economical application of the means appropriated to those objects.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_21_0_1861', 'Alabama1861', '1861', '6', '21', '0', 'General Provisions', 'In the event of the annexation of any foreign territory to this State, laws may be passed, extending to the inhabitants of such territory, all the rights and privileges which may be required by the terms of such acquisition: anything in this constitution to the contrary notwithstanding.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9001_1_0_1861', 'Alabama1861', '1861', '9001', '1', '0', 'Preamble', 'PREAMBLE Revised by order of the Convention of the People, assembled at Montgomery on the Seventh Day of January, AD, 1861. We the People of the State of Alabama, having separated ourselves from the Government known as the United States of America, and being now by our representatives in Convention assembled, and acting in our sovereign and independent character; in order to establish justice, insure domestic tranquillity, and secure the blessings of liberty to ourselves and our posterity - invoking the favor and guidance of Almighty God - do ordain and establish the following Constitution and form of Government for the State of Alabama: And the boundaries of this State are established to be: Beginning where the thirty-first degree of North latitude crosses the Perdido river; thence East, to the boundary line of the State of Georgia; thence along said line to the Southern boundary of the State of Tennessee; thence West, along the Southern boundary line of the State of Tennessee, crossing the Tennessee river, and on to the intersection of said river by said line; thence up said river to the mouth of Big Bear creek; thence by a direct line to the North-west corner of Washington county in this State, as originally formed; thence Southerly along the line of Mississippi to the Gulf of Mexico; thence Eastwardly, including all Islands within six leagues of the shore, to the Perdido river; and thence up the same to the beginning.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_1_1_1861', 'Alabama1861', '1861', '9003', '1', '1', 'Schedule', 'That no inconvience may arise from a change of territorial to a permanent State government, it is declared that all rights, actions, prosecutions, claims, and contracts, as well of individuals, as of bodies corporate, shall continue as if no such change had taken place; and all process, which shall, before the third Monday in September next, be issued in the name of the Alabama territory, shall be as valid as if issued in the name of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_2_0_1861', 'Alabama1861', '1861', '9003', '2', '0', 'Schedule', 'All fines, penalties, forfeitures, and escheats, accruing to the Alabama territory, shall accrue to the use of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_3_0_1861', 'Alabama1861', '1861', '9003', '3', '0', 'Schedule', 'The validity of all bonds and recognizances, executed to the Governor of the Alabama territory, shall not be impaired by the change of government, by may be sued for and recovered in the name of the Governor of the State of Alabama, and his successors in office; and all criminal and penal actions, arising or now depending within the limits of this State, shall be prosecuted to judgment and execution in the name of said State, all causes of action arising to individuals, and all suits at law or in equityn now depending in the several courts, within the limits of this State, and not already barred by law, may be commenced in, or transferred to, such courts as may have jurisdiction thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_4_0_1861', 'Alabama1861', '1861', '9003', '4', '0', 'Schedule', 'All officers, civil or military, now holding commissions under the authority of the United States, or of the Alabama territory, within this State, shall continue to hold and exercise their respective offices under the authority of this State, until they shall be superseded under the authority of this Constitution, and shall receive from the treasury of this State, the same compensation which they heretofore received, in proportion to the time they shall be so employed. The Governor shall have power to fill vacancies by commissions, to expire so soon as elections or appointments can be made to such offices, by authority of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_5_0_1861', 'Alabama1861', '1861', '9003', '5', '0', 'Schedule', 'All laws and parts of laws, now in force in the Alabama territory, which are not repugnant to the provisions of this Constitution, shall continue and remain in force as the laws of this State, until they expire by their own limitation, or shall be altered, or repealed, by the Legislature thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9007_1_0_1861', 'Alabama1861', '1861', '9007', '1', '0', 'Education', 'Schools, and the means of education, shall forever be encouraged in this State; and the General Assembly shall take measures to preserve, from unnecessary waste or damage, such lands as have been granted by the United States for the use of schools, within each township in this State, and apply the funds, which may be raised from such lands, in strict conformity to the object of such grant. The General Assembly shall take like measures for the improvement of such lands as have been granted by the United States to this State, for the support of a seminary of learning, and the moneys which may be raised from such lands, by rent, lease, or sale, or from any other quarter, for the purpose aforesaid, shall be and remain a fund for the exclusive support of a State University, for the promotion of the arts, literature and the sciences; and it shall be the duty of the General Assembly, as early as may be, to provide effectual means for the improvement and permanent security of the funds and endowments of such institution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_1_0_1861', 'Alabama1861', '1861', '9008', '1', '0', 'Militia', 'The General Assembly shall provide by law for organizing and disciplining the militia of this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_2_0_1861', 'Alabama1861', '1861', '9008', '2', '0', 'Militia', 'Any person who conscientiously scruples to bear arms shall not be compelled to do so, but shall pay an equivalent for personal service.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_3_0_1861', 'Alabama1861', '1861', '9008', '3', '0', 'Militia', 'The Governor shall have power to call forth the militia to execute the laws of the State, to suppress insurrections, and repel invasions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_4_0_1861', 'Alabama1861', '1861', '9008', '4', '0', 'Militia', 'All officers of the militia shall be elected or appointed in such manner as may be prescribed by law: Provided , that the General Assembly shall not make any such elections or appointments, other than those of adjutants-general, and quarter-masters general.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_5_0_1861', 'Alabama1861', '1861', '9008', '5', '0', 'Militia', 'The Governor shall appoint his aids-de-camp; majors general, their aids-de-camp, and all division and staff officers; brigadiers general shall appoint their aids, and all other brigade staff officers; and colonels shall appoint their regimental staff officers.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9008_6_0_1861', 'Alabama1861', '1861', '9008', '6', '0', 'Militia', 'The General Assembly shall fix, by law, the method of dividing the militia into divisions, brigades, regiments, battalions, and companies; and shall fix the rank of all staff officers.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9009_1_0_1861', 'Alabama1861', '1861', '9009', '1', '0', 'Impeachment', 'The House of Representatives shall have the sole power of impeaching.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9009_2_0_1861', 'Alabama1861', '1861', '9009', '2', '0', 'Impeachment', 'All impeachments shall be tried by the Senate; when sitting for that purpose, the Senators shall be on oath or afffirmation; and no person shall be convicted without the concurrence of two-thirds of the members present.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9009_3_0_1861', 'Alabama1861', '1861', '9009', '3', '0', 'Impeachment', 'The Governor and all civil officers shall be liable to impeachment for any misdemeanor in office; but judgment in such cases shall not extend further than removal from office, and to disqualification to hold any office of honor, trust or profit under the State; but the party convicted shall, nevertheless, be liable and subject to indictment, trial, and punishment, according to law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9010_1_0_1861', 'Alabama1861', '1861', '9010', '1', '0', 'Banks', 'No bank shall be established, nor bank charter renewed under the authority of this State, without the concurrence of two-thirds of each house of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9010_2_0_1861', 'Alabama1861', '1861', '9010', '2', '0', 'Banks', 'Not more than one bank shall be established, nor bank charter renewed at any one session of the General Assembly, nor shall any bank be established, or bank charter renewed, but in conformity with the following rules. 1. The stockholders shall be liable respectively for the debts of the bank in proportion to their stock holden therein. 2. The remedy for collecting debts shall be reciprocal for and against the bank. 3. No bank shall commence operations until half of the capital stock subscribed for be actually paid in gold or silver, which amount shall, in no case, be less than one hundred thousand dollars. 4. Should any bank neglect or refuse to pay on demand any bill, note or obligation, issued by the corporation, according to the promise therein expressed, the holder of any such note, bill, or obligation shall be entitled to receive and recover interest thereon until the same shall be paid, or specie payments are resumed by said bank, at the rate of twelve per cent. per annun from the date of such demand, unless the General Assembly shall sanction such suspension of specie payments by a vote ot two-thirds of each house of the General Assembly. 5. Whenever any bank suspends specie payments, the charter is thereby forfeited, unless such suspension is legalized, as is provided by the preceding rule at the then next ensuing session of the General Assembly after such suspension.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9011_1_0_1861', 'Alabama1861', '1861', '9011', '1', '0', 'Slavery', 'No slave in this State shall be emancipated by any act done to take effect in this State, or any other country.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9011_2_0_1861', 'Alabama1861', '1861', '9011', '2', '0', 'Slavery', 'The humane treatment of slaves shall be secured by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9011_3_0_1861', 'Alabama1861', '1861', '9011', '3', '0', 'Slavery', 'Laws may be enacted to prohibit the introduction into this State, of slaves who have committed high crimes in other States or territories, and to regulate or prevent the introduction of slaves into this State as merchandise.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9011_4_0_1861', 'Alabama1861', '1861', '9011', '4', '0', 'Slavery', 'In the prosecution of slaves for crimes, of a higher grade than petit larceny, the General Assembly shall have no power to deprive them of an impartial trial by a petiti jury.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9011_5_0_1861', 'Alabama1861', '1861', '9011', '5', '0', 'Slavery', 'Any person who shall maliciously dismember or deprive a slave of life, shall suffer such punishment as would be inflicted in case the like offense had been committed on a free white person, and on the like proof, except in case of insurrection of such slave.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9012_1_0_1861', 'Alabama1861', '1861', '9012', '1', '0', 'Amendments', 'The General Assembly, whenever two-thirds of each House shall deem it necessary, may propose amendments to this Constitution, which proposed amendments, shall be duly published in print, at least three months before the next general election of Representatives, for the consideration of the people, and it shall be the duty of the several returning officers, at the next general election which shall be held for Representatives, to open a poll for, and make a return to the Secretary of State, for the time being, of the names of all those voting for Representatives, who have voted on such proposed amendments, and if, thereupon, it shall appear that a majority of all the citizens of this State, voting for Representatives, have voted in favor of such proposed amendments, and two-thirds of each House of the next General Assembly, shall, after such an election, and before another, ratify the same amendments by yeas and nays , they shall be valid, to all intents and purposes, as parts of this Constitution: Provided , That the said proposed amendments shall, at each of the said sessions, have been read three times, on three several days, in each House; Provided further , That a Convention of the people of the State may be called by a vote of two-thirds of each branch of the General Assembly, under such rules and regulations as the Legislature may prescribe, to amend the Constitution or for any other purpose.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_1_1_1861', 'Alabama1861', '1861', '9013', '1', '1', 'Secession', 'Be it declared and ordained by the people of the State of Alabama in Convention assembled , That the State of Alabama now withdraws, and is hereby withdrawn from the Union known as the United States of America, and henceforth ceases to be one of said United States, and is, and of right ought to be a Sovereign and Independent State .', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_1_2_1861', 'Alabama1861', '1861', '9013', '1', '2', 'Secession', 'No law enacted by the authority of the State of Alabama, in force on the 11th day of January, AD, 1861, and consistent with the Constitution of this State, and not inconsistent with the ordinances of this Convention, is affected by the ordinance known as the Ordinance of Secession, adopted on said day, and entitled An Ordinance to dissolve the Union between the State of Alabama and other States, united under the compact styled the Constitution of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_1_3_1861', 'Alabama1861', '1861', '9013', '1', '3', 'Secession', 'Be it ordained by the people of the State of Alabama in Convention assembled , That no change made in the Constitution of the State of Alabama, by this Convention, shall have the effect to divest any right, title, or legal trust existing at the time of making such change. But all such changes shall have a prospective and not a retrospective effect, unless otherwise declared in the change itself.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_2_1_1861', 'Alabama1861', '1861', '9013', '2', '1', 'Secession', 'Be it further declared and ordained by the people of the State of Alabama in Convention assembled , That all the powers over the territory of said State, and over the people thereof, heretofore delegated to the government of the United States of America, be, and they are hereby withdrawn from said government, and are hereby resumed and vested in the people of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_2_2_1861', 'Alabama1861', '1861', '9013', '2', '2', 'Secession', 'No office, civil or military, created by this State, or under the authority of its laws, in force on the eleventh day of January, AD, 1861, and no officer lawfully exercising the powers or duties of such office, is affected by said Ordinance of Secession, except the offices of the members of the House of Representatives, and of the Senators of the Congress of the United States of America, and these are abrogated.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_2_3_1861', 'Alabama1861', '1861', '9013', '2', '3', 'Secession', 'Be it ordained by the people of Alabama in Convention assembled , That, an ordinance adopted by the people of this State, in Convention, at Huntsville, on the second day of August, one thousand eight hundred and nineteen, disclaiming forever all right to the waste or unappropriated lands lying within this State, is hereby repealed; but the navigable waters of this State shall remain forever free to the citizens of this State, and of such States as may unite with the State of Alabama, in a Southern slaveholding Confederacy. But no right heretofore obtained, by any person or corporation, to erect a bridge, or bridges, across the navigable waters of this State, shall be affected by this ordinance; Provided , That the navigation of such rivers be not obstructed thereby.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_3_1_1861', 'Alabama1861', '1861', '9013', '3', '1', 'Secession', 'Be it ordained by the people of Alabama in Convention Assembled, That the Constitution framed and adopted on the 11th day of March, 1861, by the Deputies from the States of South Carolina, Georgia, Florida, Alabama, Mississippi, Louisiana, and Texas, in convention assembled, at Montgomery, Alabama, be, and the same is hereby Approved, Ratified and Adopted , as the Federal Constitution for the people of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_3_2_1861', 'Alabama1861', '1861', '9013', '3', '2', 'Secession', 'No offense against the laws of this State, committed before or since the adoption of said Ordinance of Secession, is affected by said ordinance, and no offender against said laws, is relieved or discharged from the consequences of such offense by said ordinance; and no amercement, fine, penalty, forfeiture, escheat, bond, or recognizance, accruing or enuring, in whole or in part, to the State of Alabama, whether in action or in judgment, is affected by said ordinance.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_4_2_1861', 'Alabama1861', '1861', '9013', '4', '2', 'Secession', 'No bond issued by authority of the laws of this State, or bills or coin lawfully used as money in this State, and no bond, obligation, debt or duty, due or owing to this State, or enuring, in whole or in part, to this State, before or since the adoption of said ordinance, is affected thereby.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_5_2_1861', 'Alabama1861', '1861', '9013', '5', '2', 'Secession', 'No process or proceeding of any Court of this State, is affected by said Ordinance of Secession.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_6_2_1861', 'Alabama1861', '1861', '9013', '6', '2', 'Secession', 'No right, title, franchise, easement, license or privilege given, granted or conferred to, or upon any person or body corporate, under and by authority of the laws of this State, and no right of possession of property, action or prosecution, title, claim, contract, agreement, obligation, debt or duty, of any person or body corporate, is affected by said Ordinance of Secession, unless the same is inconsistent with said ordinance, or is affected by some other ordinance of this Convention.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9013_7_2_1861', 'Alabama1861', '1861', '9013', '7', '2', 'Secession', 'No rights acquired, or vested in any body corporate under the Constitution of the United States, or under any act of Congress passed in pursuance thereof, or under any law of this State, and not incompatible with said Ordinance of Secession, is affected by said ordinance.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_1_0_1865', 'Alabama1865', '1865', '1', '1', '0', 'Bill of Rights', 'That no man, and no set of men, are entitled to exclusive separate public emoluments or privileges, but in consideration of public services.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_2_0_1865', 'Alabama1865', '1865', '1', '2', '0', 'Bill of Rights', 'That all political power is inherent in the people, and all free governments are founded on their authority, and instituted for their benefit ; and that, therefore, they have at all times an inalienable and indefeasible right to alter, reform, or abolish their form of government, in such manner as they may deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_3_0_1865', 'Alabama1865', '1865', '1', '3', '0', 'Bill of Rights', 'That no person within this State shall, upon any pretence whatever, be deprived of the inestimable privilege of worshipping God in the manner most agreeable to his own conscience ; nor be hurt, molested, or restrained in his religious profession. Sentiments, or persuasions, provided he does not disturb others in their religious worship.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_4_0_1865', 'Alabama1865', '1865', '1', '4', '0', 'Bill of Rights', 'That no religion shall be established by law ; that no preference shall be given by law to any religious sect, society, denomination, or mode of worship ; that no one shall be compelled by law to attend any place of worship, nor to pay any tithes, taxes, or other rate, for building or repairing any place of worship, or for maintaining any minister or ministry ; that no religious test shall be required as a qualification to any office or public trust under this State ; and that the civil rights, privileges, and capacities of any citizen shall not be in any manner affected by his religious principles.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_5_0_1865', 'Alabama1865', '1865', '1', '5', '0', 'Bill of Rights', 'That every citizen may freely speak, write, and publish his sentiments on all subjects being responsible for the abuse of that liberty.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_6_0_1865', 'Alabama1865', '1865', '1', '6', '0', 'Bill of Rights', 'That the people shall be secure in their persons, houses, papers, and possessions, from unreasonable seizures or searches ; and that no warrant shall issue to search any place, or to seize any person or thing, without describing them as nearly as may be, nor without probable cause, supported by oath or affirmation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_7_0_1865', 'Alabama1865', '1865', '1', '7', '0', 'Bill of Rights', 'That in all criminal prosecutions, the accused has a right to be heard by himself and counsel, to demand the nature and cause of the accusation, to have a copy thereof, to be confronted by the witnesses against him, to have compulsory process for obtaining witnesses in his favor, and, in all prosecutions by indictment or information, a speedy public trial by an impartial jury of the county or district in which the offence was committed ; and that he shall not be compelled to give evidence against himself, nor be deprived of his life, liberty, or property, but by due course of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_8_0_1865', 'Alabama1865', '1865', '1', '8', '0', 'Bill of Rights', 'That no person shall be accused; arrested, or detained, except in cases ascertained by law, and according to the forms which the same has prescribed ; and that no person shall be punished, but by virtue of a law established and promulgated prior to the offence, and legally applied.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_9_0_1865', 'Alabama1865', '1865', '1', '9', '0', 'Bill of Rights', 'That no person shall, for any indictable offence, be proceeded against criminally by information ; except in cases arising in the land and naval forces, or in the militia when in actual service, or, by leave of the court, for oppression or misdemeanor in office. Provided, that in cases of petit larceny, assault and battery, affray, unlawful assemblies, vagrancy, and other misdemeanors, the General Assembly may by law dispense with a grand jury, and authorize such prosecutions before justices of the peace, or such other inferior courts as may be by law established ; and the proceedings in such cases shall be regulated by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_10_0_1865', 'Alabama1865', '1865', '1', '10', '0', 'Bill of Rights', 'That no person shall, for the same offence, be twice put in jeopardy of life or limb.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_11_0_1865', 'Alabama1865', '1865', '1', '11', '0', 'Bill of Rights', 'That no person shall be debarred from prosecuting or defending, before any tribunal in this State, by himself or counsel, any civil cause to which he is a party.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_12_0_1865', 'Alabama1865', '1865', '1', '12', '0', 'Bill of Rights', 'That the right of trial by jury shall remain inviolate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_13_0_1865', 'Alabama1865', '1865', '1', '13', '0', 'Bill of Rights', 'That in prosecutions for the publication of papers investigating the official conduct of officers or men in public capacity, or when the matter published is proper for public information, the truth thereof may be given in evidence ; and that in all indictments for libels, the jury shall have the right to determine the law and the facts, under the direction of the court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_14_0_1865', 'Alabama1865', '1865', '1', '14', '0', 'Bill of Rights', 'That all courts shall be open ; and that every person, for any injury done him, in his lands, goods, person or reputation, shall have a remedy by due course of law, and right and justice administered, without sale, denial, or delay.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_15_0_1865', 'Alabama1865', '1865', '1', '15', '0', 'Bill of Rights', 'That suits may be brought against the State, in such manner, and in such courts, as may be by law provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_16_0_1865', 'Alabama1865', '1865', '1', '16', '0', 'Bill of Rights', 'That excessive fines shall not be imposed, nor cruel punishments be inflicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_17_0_1865', 'Alabama1865', '1865', '1', '17', '0', 'Bill of Rights', 'That all persons shall, before conviction, be bailable by sufficient sureties, except for capital offences, when the proof is evident, or the presumption great ; and that excessive bail shall no , in any case, be required.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_18_0_1865', 'Alabama1865', '1865', '1', '18', '0', 'Bill of Rights', 'That the privilege of the writ of habeas corpus shall not be suspended, unless when, in cases of rebellion or invasion, the public safety may require it.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_19_0_1865', 'Alabama1865', '1865', '1', '19', '0', 'Bill of Rights', 'That treason against the state shall consist only in levying war against it, or adhering to its enemies, giving them aid and comfort ; and that no person shall be convicted of treason, unless on the testimony of two witnesses to the same overt act, or his own confession in open court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_20_0_1865', 'Alabama1865', '1865', '1', '20', '0', 'Bill of Rights', 'That no person shall be attainted of treason by the General Assembly ; and that no conviction shall work corruption of blood, or forfeiture of estate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_21_0_1865', 'Alabama1865', '1865', '1', '21', '0', 'Bill of Rights', 'That the estates of suicides shall descend, or vest, as in cases of natural death ; and that, if any person shall be killed by casualty, there shall be no forfeiture by reason thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_22_0_1865', 'Alabama1865', '1865', '1', '22', '0', 'Bill of Rights', 'That the person of a debtor, when there is not a strong presumption of fraud, shall not be detained in prison, after delivering up his estate, for the benefit of his creditors, in such manner as shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_23_0_1865', 'Alabama1865', '1865', '1', '23', '0', 'Bill of Rights', 'That no power of suspending laws shall be exercised, except by the General Assembly, or by its authority.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_24_0_1865', 'Alabama1865', '1865', '1', '24', '0', 'Bill of Rights', 'That no ex-post-facto law impairing the obligation of contracts, shall be made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_25_0_1865', 'Alabama1865', '1865', '1', '25', '0', 'Bill of Rights', 'That private property shall not be taken or applied for public use, unless just compensation be made therefore ; nor shall private property be taken for private use, or for the use of corporations other than municipal, without the consent of the owner. Provided, however, that laws may be made securing to persons or corporations the right of way over the lands of other persons or corporations, and, for works of internal improvement, the right to establish depots, stations, and turn-outs ; but just compensation shall, in such cases, be first made to the owner.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_26_0_1865', 'Alabama1865', '1865', '1', '26', '0', 'Bill of Rights', 'That the citizens have a right, in a peaceable manner, to assemble together for their common good, and to apply to those invested with the powers of government for redress of grievances, or other proper purposes, by petition, address, or remonstrance.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_27_0_1865', 'Alabama1865', '1865', '1', '27', '0', 'Bill of Rights', 'That every citizen has a right to bear arms in defence of himself and the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_28_0_1865', 'Alabama1865', '1865', '1', '28', '0', 'Bill of Rights', 'That no person, who conscientiously scruples to bear arms, shall be compelled to do so but may pay an equivalent for personal service.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_29_0_1865', 'Alabama1865', '1865', '1', '29', '0', 'Bill of Rights', 'That no standing army shall be kept up, without the consent of the General Assembly; and in that case, no appropriation for its support shall be for a longer term than one year ; and that the military shall, in all cases, and at all times, be in strict subordination to the civil power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_30_0_1865', 'Alabama1865', '1865', '1', '30', '0', 'Bill of Rights', 'That no soldier shall, in time of peace, be quartered in any house, without the consent of the owner ; nor in time of war, but in a manner to be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_31_0_1865', 'Alabama1865', '1865', '1', '31', '0', 'Bill of Rights', 'That no title of nobility, or hereditary distinction, privilege, honor, or emolument, shall ever be granted or conferred in this State ; and that no office shall be created, the appointment of which shall be for a longer term than during good behavior.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_32_0_1865', 'Alabama1865', '1865', '1', '32', '0', 'Bill of Rights', 'That emigration from this State shall not be prohibited, and that no citizen shall be exiled.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_33_0_1865', 'Alabama1865', '1865', '1', '33', '0', 'Bill of Rights', 'That temporary absence from the State shall not cause a forfeiture of residence once obtained.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_34_0_1865', 'Alabama1865', '1865', '1', '34', '0', 'Bill of Rights', 'That hereafter there shall be in this State neither slavery, nor involuntary servitude, otherwise than for the punishment of crime, whereof the party shall have been duly convicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_35_0_1865', 'Alabama1865', '1865', '1', '35', '0', 'Bill of Rights', 'That the right of suffrage shall be protected by laws regulating elections, and prohibiting, under adequate penalties, all undue influence from power, briber, tumult, or other improper conduct.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_36_0_1865', 'Alabama1865', '1865', '1', '36', '0', 'Bill of Rights', 'This enumeration of certain rights shall not be construed to deny or disparage others retained by the people ; and to guard against any encroachment on the rights hereby retained, or any transgression of any of the high powers by this constitution delegated, we declare, that everything in this article is excepted out of the general powers of government, and shall forever remain inviolate, and that all contrary thereto, or to the following provisions, shall be void.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_1_0_1865', 'Alabama1865', '1865', '2', '1', '0', 'Boundaries', 'The boundaries of this State are established and declared to be as follows that is to say : Beginning at the point where the thirty-first degree of north latitude crosses the Perdido river ; thence east, to the western boundary line of the State of Georgia ; thence along said line to the southern boundary line of the State of Tennessee ; thence west, along the southern boundary line of the State of Tennessee, crossing the Tennessee river, and on to the second intersection of said river by said line ; thence up said river to the mouth of Big Bear creek ; thence by a direct line, to the north-west corner of Washington county in this State, as originally formed ; thence southerly, along the line of the State of Mississippi, to the Gulf of Mexico ; thence eastwardly, including all islands within six leagues of the shore, to the Perdido river ; and thence up the said river, to the beginning.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_2_0_1865', 'Alabama1865', '1865', '2', '2', '0', 'Boundaries', 'The General Assembly may, by a vote of two thirds of both branches thereof, arrange and designate boundaries for the several counties of this State, which boundaries shall not be altered except by a like vote ; but no new county shall be hereafter formed of less extent than six hundred square miles, nor shall any existing county be reduced to a less extent than six hundred square miles ; and no new county shall be formed not containing a sufficient number of inhabitants to entitle it to one representative under the existing ratio of representation, nor unless the counties from which it is taken shall be left with the required number entitling them to separate representation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_1_0_1865', 'Alabama1865', '1865', '3', '1', '0', 'Separation of Powers', 'The powers of the government of the State of Alabama shall be divided into three distinct departments, each of which shall be confided to a separate body of magistracy-to wit ; those which are legislative to one, those which are executive to another, and those which are judicial to another.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_2_0_1865', 'Alabama1865', '1865', '3', '2', '0', 'Separation of Powers', 'No person, or collection of persons, being of one of those departments, shall exercise any power properly belonging to either of the others, except in the instances hereinafter expressly directed or permitted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_1_0_1865', 'Alabama1865', '1865', '4', '1', '0', 'Legislative', 'The legislative power of this State shall be vested in two distinct branches ; the one to be styled the Senate, and the other the House of Representatives. And both together the General Assembly of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_2_0_1865', 'Alabama1865', '1865', '4', '2', '0', 'Legislative', 'All laws shall be passed by original bill ; and their style shall be, Be it enacted by the Senate and House of Representative of the State of Alabama, in General Assembly convened. Each law shall embrace but one subject, which shall be described in the title ; and no law, nor any section of any law, shall be revised or amended by reference only to its title and number, but the law or section revised or amended shall itself be set forth at full length.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_3_0_1865', 'Alabama1865', '1865', '4', '3', '0', 'Legislative', 'Members of both houses of the General Assembly shall be chosen by the qualified electors ; and the regulations for holding such elections shall, as to time, place, and manner, be the same for each house, and shall be prescribed by law. After the special election to be held on the first Monday in November, 1865, such elections shall, until otherwise directed by law, take place on the first Monday in August.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_4_0_1865', 'Alabama1865', '1865', '4', '4', '0', 'Legislative', 'No person who holds any lucrative office under the United States, or under this State, or under any other State or government (except post-masters, officers in the militia, to whose office no annual salary is attached, justices of the peace, members of the court of county commissioners, notaries public, and commissioners of deeds, excepted); no person who has been convicted of having given or offered any bribe to procure his election ; no person who has been convicted of bribery, forgery, perjury, or other high crime or misdemeanor which may be by law declared to disqualify him ; and no person who has been a collector or holder of public moneys and has failed to account for and pay over into the treasury all sums for which he may be by law accountable, shall be eligible to the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_5_0_1865', 'Alabama1865', '1865', '4', '5', '0', 'Legislative', 'Representatives shall be chosen for the term of two years ; and no person shall be a representative, who is not a white man, twenty-one years of age, a citizen of the United States and who has not been an inhabitant of this State for the two years next preceding the election, and for the last year thereof a resident of the county for which he is chosen.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_6_0_1865', 'Alabama1865', '1865', '4', '6', '0', 'Legislative', 'The house of representatives shall consist of not more than one hundred members, who shall be apportioned by the General Assembly among the several counties of the State according to the number of white inhabitants in them respectively ; and, to this end, the General Assembly shall cause an enumeration of all the inhabitants of the State to be made in the year one thousand eight hundred and sixty-six, and again in the year one thousand eight hundred and seventy-five, and every ten years thereafter, and shall make an apportionment of the representatives among the several counties at the first regular session after each enumeration ; which apportionment, when made, shall not be subjected to alteration, until after the next census shall have been taken, Provided, that each county shall be entitled to at least one representative. Provided further, that where two or more adjoining counties shall each have a residuum or fraction over and above the ratio then fixed by law, which fractions, when added together, equal or exceed that ratio, in that case, the county having the largest fraction shall be entitled to one additional representative.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_7_0_1865', 'Alabama1865', '1865', '4', '7', '0', 'Legislative', 'The whole number of senators shall be not less than one fourth, nor more than one third, of the whole number of representatives ; and it shall be the duty of the General Assembly, at its first session after the making of each enumeration, as provided by the last preceding section, to fix by law the number of senators, and to divide the State into as many senatorial districts as there are senators ; which districts shall be as nearly equal to each other as may be in the number of white inhabitant, and each shall be entitled to one senator and no more. Provided, that, in the formation of said districts, no county shall be divided, and no two or more counties, which are separated entirely by a county belonging to another district, shall be joined into one district. And provided further, that the senatorial districts, when formed, shall not be changed until after the next census shall have been taken.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_8_0_1865', 'Alabama1865', '1865', '4', '8', '0', 'Legislative', 'No person shall be a senator, who is not a white man, at least twenty-seven years of age, a citizen of the United States, and who has not been an inhabitant of this State for two years next preceding the election, and for the last year thereof a resident in the district for which he is chosen.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_9_0_1865', 'Alabama1865', '1865', '4', '9', '0', 'Legislative', 'Senators shall be chosen for the term of four years ; yet, at the first general election after each new apportionment, elections shall be held anew in all the senatorial districts ; and the senators elected, when convened at the next ensuing session of the General Assembly, shall be divided by lot into two classes, as nearly equal to each other as may be ; the seats of the senators of the first class shall be vacated at the expiration of two years, and those of the second class at the expiration of four years from the day of election, so that (except as above provided) one half of the senators may be chosen biennially.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_10_0_1865', 'Alabama1865', '1865', '4', '10', '0', 'Legislative', 'The General Assembly shall meet annually, on such day as may be by law prescribed; and shall not remain in session longer than thirty days, unless by a vote of two thirds of each house.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_11_0_1865', 'Alabama1865', '1865', '4', '11', '0', 'Legislative', 'At the first regular or called session after each general election for representatives, the senate shall choose a president and its other officers, and the house of representatives shall choose a speaker and its other officers ; and the officers so chosen shall be entitled to hold their respective offices until the next general election for representatives. Each house shall judge of the qualifications, elections, and returns of its own members ; but a contested election shall be determined in such manner as may be by law provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_12_0_1865', 'Alabama1865', '1865', '4', '12', '0', 'Legislative', 'A majority of each house shall constitute a quorum to do business ; but a smaller number may adjourn from day to day, and may compel the attendance of absent members, in such manner, and under such penalties, as each house may provide.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_13_0_1865', 'Alabama1865', '1865', '4', '13', '0', 'Legislative', 'Each house may determine the rules of its own proceedings, punish members for disorderly behavior, and, with the consent of two thirds, expel a member, but not a second time for the same offence; and shall have all other powers necessary for a branch of the legislature of a free and independent state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_14_0_1865', 'Alabama1865', '1865', '4', '14', '0', 'Legislative', 'Each house may, during the session, punish by imprisonment any person, not a member, for disrespectful or disorderly behavior in its presence, or for obstructing any of its proceedings. Provided, that such imprisonment shall not, at any one time, exceed forty-eight hours.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_15_0_1865', 'Alabama1865', '1865', '4', '15', '0', 'Legislative', 'Each house shall keep a journal of its own proceedings, and cause the same to be published immediately after its adjournment, excepting such parts as, in its judgement, may require secrecy ; and the yeas and nays of the members of either house, on any question, shall, at the desire of any two members present, be entered on the journals. Any member of either house shall have liberty to dissent from, and protest against, any act or resolution which he may think injurious to the public or to an individual, and have the reasons of his dissent entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_16_0_1865', 'Alabama1865', '1865', '4', '16', '0', 'Legislative', 'The doors of each house shall be open, except on such occasions as, in the opinion of the house, may require secrecy.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_17_0_1865', 'Alabama1865', '1865', '4', '17', '0', 'Legislative', 'Neither house shall, without the consent of the other, adjourn for more than three days, nor to any other place than that in which they may be sitting.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_18_0_1865', 'Alabama1865', '1865', '4', '18', '0', 'Legislative', 'Bills may originate in either house, and be amended, altered, or rejected by the other ; but no bill shall have the force of a law, until it be read in each house on three several days, and free discussion thereon be allowed ; unless, in case of urgency, four fifths of the house in which the bill may be depending shall deem it expedient to dispense with this rule ; and every bill, having passed both houses, shall be signed by the speaker and president of the respective houses. Provided, that all bills for raising revenue shall originate in the house of representatives, but may be amended or rejected by the senate as other bills.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_19_0_1865', 'Alabama1865', '1865', '4', '19', '0', 'Legislative', 'In all elections by the General Assembly, the members shall vote viva voce, and the votes shall be entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_20_0_1865', 'Alabama1865', '1865', '4', '20', '0', 'Legislative', 'No senator or representative shall, during the term for which he was elected, be elected or appointed to any civil office of profit under this State, except such offices as may be filled by elections by the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_21_0_1865', 'Alabama1865', '1865', '4', '21', '0', 'Legislative', 'Senators and representatives shall, in all cases except treason, felony, or breach of the peace, be privileged from arrest, during the session of the General Assembly, and in going to and returning from the same, allowing one day or every twenty miles such member may reside from the place at which the General Assembly is convened ; nor shall any member be liable to answer for anything spoken in debate in either house, in any court or place elsewhere.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_22_0_1865', 'Alabama1865', '1865', '4', '22', '0', 'Legislative', 'Each member of the General Assembly shall receive from the public treasury such compensation for his services as may be fixed by law ; but no increase of compensation shall take effect during the session at which such increase shall have been made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_23_0_1865', 'Alabama1865', '1865', '4', '23', '0', 'Legislative', 'When vacancies happen in either house, the Governor, or the person exercising the powers of Governor for the time being, shall issue writs of election to fill such vacancies.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_24_0_1865', 'Alabama1865', '1865', '4', '24', '0', 'Legislative', 'The house of representatives shall have the sole power of preferring impeachments ; all impeachments shall be tried by the senate ; the senators, when sitting for that purpose, shall be on oath or affirmation ; and no person shall be convicted under an impeachment, without the concurrence of two thirds of the senators present.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_25_0_1865', 'Alabama1865', '1865', '4', '25', '0', 'Legislative', 'It shall be the duty of the General Assembly to pass such laws as may be necessary and proper to decide differences by arbitrators, to be appointed by the parties, who may choose that summary mode of adjustment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_26_0_1865', 'Alabama1865', '1865', '4', '26', '0', 'Legislative', 'It shall be the duty of the General Assembly, from time to time, as circumstances may require, to frame and adopt a penal code, founded on principles of reformation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_27_0_1865', 'Alabama1865', '1865', '4', '27', '0', 'Legislative', 'It shall also be the duty of the General Assembly, within five years after the adoption of this constitution, and within every subsequent period of ten years, to make provision by law for the revision, digesting, and promulgation of all the public statutes of this State, both civil and criminal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_28_0_1865', 'Alabama1865', '1865', '4', '28', '0', 'Legislative', 'The General Assembly shall have power to pass such penal laws as they may deem expedient to suppress the evil practice of duelling, extending to disqualification to hold office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_29_0_1865', 'Alabama1865', '1865', '4', '29', '0', 'Legislative', 'It shall be the duty of the General Assembly to regulate by law the cases in which deductions shall be made from the salaries of public officers, for neglect of duty in their official capacities, and the amount of such deductions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_30_0_1865', 'Alabama1865', '1865', '4', '30', '0', 'Legislative', 'Divorces from the bonds of matrimony shall not be granted, but in the cases by law provided for, and by suit in chancery ; but decrees in chancery for divorce shall be final, unless appealed from, in the manner prescribed by law, within three months from the date of the enrollment thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_31_0_1865', 'Alabama1865', '1865', '4', '31', '0', 'Legislative', 'It shall be the duty of the General Assembly, at its next session, and from time to time thereafter as it may deem proper, to enact laws prohibiting the intermarriage of white persons with negroes, or with persons of mixed blood, declaring such marriages null and void ab initio, and making the parties to any such marriage subject to criminal prosecutions, with such penalties as may be by law prescribed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_32_0_1865', 'Alabama1865', '1865', '4', '32', '0', 'Legislative', 'The General Assembly shall make provision by law for obtaining correct knowledge of the several objects proper for improvement in relation to the roads and navigable waters in this State, and for making a systematic and economical application of the means appropriated to those objects.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_33_0_1865', 'Alabama1865', '1865', '4', '33', '0', 'Legislative', 'The General Assembly shall, from time to time, enact necessary and proper laws for the encouragement of schools and the means of education ; shall take proper measures to preserve from waste or damage such lands as have been or may be granted by the United States for the use of schools in each township in this State, and apply the funds which may be raised from such lands in strict conformity with the object of such grant ; shall take like measures for the improvement of such lands as have been or may hereafter be granted by the United States to this State for the support of a seminary of learning ; and the money which may be raised from such lands, by rent, lease, or sale, or from any other quarter, for the purpose aforesaid, shall be and forever remain a fund for the exclusive support of a State university for the promotion of the arts, literature, and the sciences ; and it shall be the duty of the General Assembly to provide by law effectual means for the improvement and permanent security of the funds of such institution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_34_0_1865', 'Alabama1865', '1865', '4', '34', '0', 'Legislative', 'Not more than one bank shall be established nor more than one bank charter be renewed, at any one session of the General Assembly ; nor shall any bank be established, nor any bank charter be renewed, without the concurrence of two thirds of each house of the General Assembly, and in conformity with the following rules-that is to say : Rule 1. The stockholders shall be respectively liable for the debts of the bank in proportion to the amount of their stock. Rule 2. The remedy for the collection of debts shall be reciprocal for and against the bank. Rule 3. No bank shall commence operations, until one half of the capital stock subscribed for be actually paid in gold and silver ; which amount shall, in no case, be less than one hundred thousand dollars. Rule 4. If any bank shall neglect or refuse to pay, on demand, any bill, note, or obligation issued by the corporation, according to the promise therein expressed, the holder of such bill, note or obligation, shall be entitled to receive and recover interest thereon until paid, or until specie payments are resumed by the bank, at the rate of twelve per centum per annum from the date of such demand ; unless the General Assembly shall, by a vote of two thirds of each house thereof , sanction such suspension of specie payments. Rule 5. Whenever any bank suspends specie payments, its charter is thereby forfeited ; unless such suspension shall be sanctioned and legalized, at the next session of the General Assembly, by a vote of two thirds of each house thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_35_0_1865', 'Alabama1865', '1865', '4', '35', '0', 'Legislative', 'The General Assembly shall provide by law for organizing and disciplining the militia of this State, in such manner as they may deem expedient, not incompatible with the constitution and laws of the United States ; shall fix the rank of all staff officers, and prescribe the manner in which all officers shall be appointed or elected. Provided, that no other officers than adjutant-generals and quartermaster-generals shall be appointed by the General Assembly. And provided further, that major-generals shall appoint their aids and all division and staff officers, brigadier-generals shall appoint their aids and all other brigade staff officers, and colonels shall appoint their regimental staff officers.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_36_0_1865', 'Alabama1865', '1865', '4', '36', '0', 'Legislative', 'It shall be the duty of the General Assembly, at its next session , and from time to time thereafter, to enact such laws as will protect the freedmen of this State in the full enjoyment of all their rights of person and property , and guard them and the State against any evils that may arise from their sudden emancipation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_37_0_1865', 'Alabama1865', '1865', '4', '37', '0', 'Legislative', 'No money shall be drawn from the treasury, but in pursuance of an appropriation made by law ; and a regular statement and account of the receipts and expenditures of all public moneys shall be published annually, in such manner as may be by law directed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_38_0_1865', 'Alabama1865', '1865', '4', '38', '0', 'Legislative', 'No special law shall be enacted for the benefit of individuals or corporations, in cases which are provided for by a general law, or where the relief sought can be given by any court of this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_39_0_1865', 'Alabama1865', '1865', '4', '39', '0', 'Legislative', 'All lands liable to taxation in this State, shall be taxed in proportion to their value.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_40_0_1865', 'Alabama1865', '1865', '4', '40', '0', 'Legislative', 'No power to levy taxes shall be delegated to individuals or private corporations.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_41_0_1865', 'Alabama1865', '1865', '4', '41', '0', 'Legislative', 'The General Assembly shall not borrow or raise money on the credit of the State (except for purposes of military defense against actual or threatened invasion, rebellion, or insurrection), without the concurrence of two thirds of the members of each house ; nor shall the debts or liabilities of any corporation, person, or persons, or other State, be guarantied, nor any money, credit, or other thing, be loaned or given away, except by a like concurrence of each house ; and the votes shall in each case, be taken by yeas and nays, and be entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_42_0_1865', 'Alabama1865', '1865', '4', '42', '0', 'Legislative', 'In the event of the annexation of any foreign territory to this State, the General Assembly shall enact laws, extending to the inhabitants of the acquired territory all the rights and privileges which may be required by the terms of the acquisition ; anything in this constitution to the contrary notwithstanding.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_1_0_1865', 'Alabama1865', '1865', '5', '1', '0', 'Executive', 'The supreme executive power of this state shall be vested in a chief magistrate, who shall be styled the Governor of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_2_0_1865', 'Alabama1865', '1865', '5', '2', '0', 'Executive', 'The Governor shall be elected by the qualified electors, at the time and places at which they shall respectively vote for representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_3_0_1865', 'Alabama1865', '1865', '5', '3', '0', 'Executive', 'The returns of every election for Governor shall be sealed up, and transmitted to the seat of government, directed to the speaker of the house of representatives, who shall, during the first week of the session, open and publish them in the presence of both houses of the General Assembly. The person having the highest number of votes, shall be Governor ; but, if two or more shall be equal and highest in votes, one of them shall be chosen Governor by the joint vote of both houses. Contested elections for Governor shall be determined by both houses of the General Assembly, in such manner as shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_4_0_1865', 'Alabama1865', '1865', '5', '4', '0', 'Executive', 'The Governor shall hold his office for the term of two years from the time of his installation, and until his successor shall be qualified, but shall not be eligible for more than four years in any term of six years ; he shall be at least thirty years of age, a native citizen of the United States, and shall have resided in this State at least four years next preceding the day of his election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_5_0_1865', 'Alabama1865', '1865', '5', '5', '0', 'Executive', 'He shall, at stated times, receive a compensation for his services, which shall not be either increased or diminished during the term for which he shall have been elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_6_0_1865', 'Alabama1865', '1865', '5', '6', '0', 'Executive', 'He shall always reside, during the session of the General Assembly, at the place where their session may be held, and at other times wherever, in their opinion, the public good may require.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_7_0_1865', 'Alabama1865', '1865', '5', '7', '0', 'Executive', 'He shall be commander in chief of the army and navy of this State, and of the militia thereof, except when they shall be called into the service of the United States ; and when acting in the service of the United States, the General Assembly shall fix his rank.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_8_0_1865', 'Alabama1865', '1865', '5', '8', '0', 'Executive', 'He shall have power to call forth the militia to execute the laws of the State, to suppress insurrections, and to repel invasions ; and shall appoint his aids-de-camp.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_9_0_1865', 'Alabama1865', '1865', '5', '9', '0', 'Executive', 'He may require from the secretary of State, the comptroller of public accounts, and the State treasurer, information in writing on any subject relating to the duties of their respective offices.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_10_0_1865', 'Alabama1865', '1865', '5', '10', '0', 'Executive', 'He may, by proclamation, on extraordinary occasions, convene the General Assembly at the seat of government, or at a different place, if, since their last adjournment, that shall have become dangerous, from an enemy, or from contagious disorders ; and in case of disagreement between the two houses, with respect to the time of adjournment, he may adjourn them to such time as he may think proper, not beyond the day of the next annual meeting of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_11_0_1865', 'Alabama1865', '1865', '5', '11', '0', 'Executive', 'He shall, from time to time, give to the general Assembly information of the state of the government, and recommend to their consideration such measures as he may deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_12_0_1865', 'Alabama1865', '1865', '5', '12', '0', 'Executive', 'He shall take care that the laws are faithfully executed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_13_0_1865', 'Alabama1865', '1865', '5', '13', '0', 'Executive', 'In all criminal and penal cases, except those of treason and impeachment, he shall have power to grant reprieves and pardons, and to remit fines and forfeitures, under such rules and regulations as may be prescribed by law ; and in cases of treason, he shall have power, by and with the advice and consent of the senate, to grant reprieves and pardons, and, in the recess of the senate, he may respite the sentence until the end of the next session of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_14_0_1865', 'Alabama1865', '1865', '5', '14', '0', 'Executive', 'There shall be a great seal of the State, which shall be kept and used by the Governor officially ; and the seal now in use shall continue to be the great seal of the State, until another shall have been adopted by the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_15_0_1865', 'Alabama1865', '1865', '5', '15', '0', 'Executive', 'Vacancies that may happen in offices, the appointment of which is vested in the General Assembly, shall, during the recess of the General Assembly, be filled by the Governor, by granting commissions, which shall expire at the end of the next session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_16_0_1865', 'Alabama1865', '1865', '5', '16', '0', 'Executive', 'Every bill, which shall have passed both houses of the General Assembly, shall be presented to the Governor : if he approve, he shall sign it, but if not, he shall return it, with his objections, to the house in which it originated, who shall enter the objections at large upon the journals, and proceed to reconsider it ; if, after such reconsideration, a majority of the whole number elected to that house shall agree to pass the bill, it shall be sent, with the objections, to the other house by whom it shall likewise be reconsidered, and, if approved by a majority of the whole number elected to that house, it shall become a law ; but, in such cases, the votes of both houses shall be determined by yeas and nays, and the names of the members voting for or against the bill shall be entered on the journals of each house respectively. If any bill shall not be returned by the Governor, within five days (Sundays excepted) after it shall have been presented to him, the same shall be a law, in like manner as if he had signed it ; unless the General Assembly, by their adjournment, prevent its return, in which case it shall not be a law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_17_0_1865', 'Alabama1865', '1865', '5', '17', '0', 'Executive', 'Every order, resolution, or vote, to which the concurrence of both houses may be necessary, (except on questions of adjournment, and for bringing on elections by the two houses,) shall be presented to the Governor, and, before it shall take effect, be approved by him, or being disapproved, shall be repassed by both houses, according to the rules and limitations prescribed in the case of a bill.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_18_0_1865', 'Alabama1865', '1865', '5', '18', '0', 'Executive', 'No person shall, at one and the same time, hold the office of Governor, and any other office or commission, civil or military, either under this State, the United States, or any other State or government.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_19_0_1865', 'Alabama1865', '1865', '5', '19', '0', 'Executive', 'In case of the impeachment of the Governor, his removal from office, death, refusal to qualify, resignation, or absence from the State, the president of the senate shall exercise all the power and authority appertaining to the office of Governor, until the time appointed by the constitution for the election of Governor shall arrive, (unless the General Assembly shall provide by law for the election of a Governor to fill such vacancy,) or until the Governor who is absent or impeached shall return or be acquitted ; and if during such vacancy in the office of Governor, the president of the senate shall be impeached, removed from office, refuse to qualify, die, resign, or be absent from the State, the speaker of the house of representatives shall, in like manner, administer the government.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_20_0_1865', 'Alabama1865', '1865', '5', '20', '0', 'Executive', 'The president of the senate, and the speaker of the house of representatives, shall, during the time they respectively administer the government, receive the same compensation which the Governor would have received, if he had been employed in the duties of his office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_1_0_1865', 'Alabama1865', '1865', '6', '1', '0', 'Judicial', 'The judicial power of this State shall be vested in one supreme court, circuit courts to be held in each county of the State, and such inferior courts of law and equity, to consist of not more than five members, as the General Assembly may, from time to time, direct, ordain, and establish.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_2_0_1865', 'Alabama1865', '1865', '6', '2', '0', 'Judicial', 'Except in cases otherwise directed in this constitution, the supreme court shall have appellate jurisdiction only, which shall be co-extensive with the State, under such restrictions and regulations, not repugnant to this constitution, as may from time to time be prescribed by law. Provided, that said court shall have power to issue writs of injunction, mandamus, quo warranto, habeas corpus, and such other remedial and original writs as may be necessary to give it a general superintendence and control of inferior jurisdictions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_3_0_1865', 'Alabama1865', '1865', '6', '3', '0', 'Judicial', 'The supreme court shall be held at the seat of government ; but, if that shall have become dangerous, from an enemy or from disease, may adjourn to a different place.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_4_0_1865', 'Alabama1865', '1865', '6', '4', '0', 'Judicial', 'The State shall be divided into convenient circuits, each of which shall contain not less than three, nor more than six counties ; and for each circuit there shall be appointed a judge, who shall, after his appointment, reside in the circuit for which he may be appointed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_5_0_1865', 'Alabama1865', '1865', '6', '5', '0', 'Judicial', 'The circuit court shall have original jurisdiction in all matters, civil and criminal, within this State, not otherwise excepted in this constitution ; but in civil cases only where the matter or sum in controversy exceeds fifty dollars.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_6_0_1865', 'Alabama1865', '1865', '6', '6', '0', 'Judicial', 'A circuit court shall be held in each county in the State, at least twice in every year ; and the judges of the several circuits may hold courts for each other when they deem it expedient, and shall do so when directed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_7_0_1865', 'Alabama1865', '1865', '6', '7', '0', 'Judicial', 'The General Assembly shall have power to establish a court or courts of chancery, with original and appellate equity jurisdiction. Provided, that the judges of the several circuit courts shall have power to issue writs of injunction, returnable into the courts of chancery.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_8_0_1865', 'Alabama1865', '1865', '6', '8', '0', 'Judicial', 'The General Assembly shall have power to establish, in each county within this State, a court of probate, for the granting of letters testamentary, and of administration, and for orphans business.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_9_0_1865', 'Alabama1865', '1865', '6', '9', '0', 'Judicial', 'A competent number of justices of the peace shall be appointed in and for each county, in such mode, and for such term of office, as the General Assembly may by law direct ; whose jurisdiction, in civil cases, shall be limited to causes in which the amount in controversy shall not exceed one hundred dollars ; and in all cases tried by a justice of the peace, the right of appeal shall be secured, under such rules and regulations as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_10_0_1865', 'Alabama1865', '1865', '6', '10', '0', 'Judicial', 'The judges of the supreme court, circuit courts, and courts of chancery, shall, at stated times, receive for their services a compensation, which shall be fixed by law, and which shall not be diminished during their continuance in office ; but they shall receive no fees or perquisites of office, nor hold any office of profit or trust, under this State, the United States, or any other power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_11_0_1865', 'Alabama1865', '1865', '6', '11', '0', 'Judicial', 'Judges of the supreme court, and chancellors, shall be elected by a joint vote of both houses of the General Assembly ; judges of the circuit and probate courts, and of such other inferior courts as may be by law established, shall be elected by the qualified electors of the respective counties, cities, or districts, for which such courts may be established. Elections of judges by the people shall be held on the first Monday in May, or such other day as may be by law prescribed, not within a less period than two months of the day fixed by law for the election of Governor, members of the General Assembly, or members of Congress. Vacancies in the office of circuit judge, probate judge, or judge of any other inferior court established by law, shall be filled by the Governor ; and the person appointed by him shall hold office until the next election day by law appointed for the election of judges, and until his successor shall have been elected and qualified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_12_0_1865', 'Alabama1865', '1865', '6', '12', '0', 'Judicial', 'The judges of the several courts of this State shall hold their offices for the term of six years ; and the right of any judge to hold his office for the full term hereby prescribed, shall not be affected by any change hereafter made by law in any circuit or district, or in the mode or time of election ; but for any willful neglect of duty, or any other reasonable cause, which shall not be a sufficient ground of impeachment, the Governor shall remove any judge, on the address of two thirds of each house of the General Assembly. Provided, that the cause, or causes, for which said removal may be required, shall be stated at length in such address, and entered on the journals of each house. And provided further, that the judge intended to be removed shall be notified of such cause or causes, and shall be admitted to a hearing in his own defence, before any vote for such address ; and in all such cases, the vote shall be taken by yeas and nays, and be entered on the journals of each house respectively.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_13_0_1865', 'Alabama1865', '1865', '6', '13', '0', 'Judicial', 'No person who shall have arrived at the age of seventy years, shall be appointed or elected to, or shall continue in, the office of judge in this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_14_0_1865', 'Alabama1865', '1865', '6', '14', '0', 'Judicial', 'The judges of the supreme court shall, by virtue of their offices, be conservators of the peace throughout the State ; as also the judges of the circuit courts within their respective circuits, and the judges of the inferior courts within their respective counties.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_15_0_1865', 'Alabama1865', '1865', '6', '15', '0', 'Judicial', 'Clerks of the circuits courts, and of such inferior courts as may be by law established, shall be elected by the qualified electors in each county, for the term of four years ; and may be removed from office, for such causes, and in such manner as may be by law prescribed. Vacancies in the office of clerk shall be filled by the judge of the court, and the person so appointed shall hold office until the next general election, and until his successor is elected and qualified. Provided, that the General Assembly shall have power to annex the duties of clerk to the office of judge of any inferior court by law established.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_16_0_1865', 'Alabama1865', '1865', '6', '16', '0', 'Judicial', 'The style of all process shall be, The State of Alabama ; and all prosecutions shall be carried on in the name, and by the authority of the State of Alabama , and shall conclude, against the peace and dignity of the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_1_0_1865', 'Alabama1865', '1865', '7', '1', '0', 'Officers', 'A secretary of State, a comptroller of public accounts, and a State treasurer, shall be elected by a joint vote of both houses of the General Assembly, each of whom shall continue in office during the term of two years, shall perform all the duties that may be required of him by law, and receive such compensation as may be by law provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_2_0_1865', 'Alabama1865', '1865', '7', '2', '0', 'Officers', 'An attorney-general and as many solicitors as there are judicial circuits in the State, shall be elected by a joint vote of both houses of the General Assembly, each of whom shall hold his office for the term of four years, shall perform all the duties that may be required of him by law, and shall receive such compensation for his services as may be by law provided, which shall not be diminished during his continuance in office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_3_0_1865', 'Alabama1865', '1865', '7', '3', '0', 'Officers', 'A sheriff shall be elected in each county, by the qualified electors thereof, who shall hold his office for the term of three years, unless sooner removed, and shall not be eligible to serve, either as principal or deputy, for any two successive terms. Vacancies in the office of sheriff shall be filled by the Governor, as in other cases ; and the person so appointed shall continue in office until the next general election in the county for sheriff as by law provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_4_0_1865', 'Alabama1865', '1865', '7', '4', '0', 'Officers', 'No member of Congress, nor any person who holds any office of profit or trust under the United States, (except postmasters,) or any other State or government ; nor any person who shall have been convicted of having given or offered any bribe to procure his election or appointment; now any person who shall have been convicted of bribery, forgery, perjury, or other high crime or misdemeanor which may be by law declared to disqualify him,--shall be eligible to any office of profit or trust under this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_5_0_1865', 'Alabama1865', '1865', '7', '5', '0', 'Officers', 'All commissions shall be in the name, and by the authority of the State of Alabama ; shall be sealed with the great seal of the State, signed by the Governor, and attested by the secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_6_0_1865', 'Alabama1865', '1865', '7', '6', '0', 'Officers', 'All civil officers of this State, legislative, executive, and judicial, before they enter upon the execution of the duties of their respective offices, shall take the following oath: I solemnly swear, (or affirm, as the case may be,) that I will support the constitution of the United States , and the constitution of the State of Alabama, so long as I continue a citizen thereof ; and that I will faithfully discharge, to the best of my abilities, the duties of the office of So help me God.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_7_0_1865', 'Alabama1865', '1865', '7', '7', '0', 'Officers', 'All civil officers of the State, whether elected by the people, or by the General Assembly, or appointed by the Governor, shall be liable to impeachment for any misdemeanor in office ; but judgement in such cases shall not extend further than removal from office, and disqualification to hold any office of honor, trust, or profit, under the State ; but the party convicted shall, nevertheless, be liable and subject to indictment, trial, and punishment according to law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_1_0_1865', 'Alabama1865', '1865', '8', '1', '0', 'Suffrage and Elections', 'Every white male person, of the age of twenty-one years and upwards, who shall be a citizen of the United States, and shall have resided in this State one year next preceding the election, and the last three months thereof in the county in which he offers to vote, shall be deemed a qualified elector. Provided, that no soldier, seaman, or marine, in the regular army or navy of the United States, and no person who shall have been convicted of bribery, forgery, perjury, or other high crime or misdemeanor which may be by law declared to disqualify him, shall be entitled to vote at any election in this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_2_0_1865', 'Alabama1865', '1865', '8', '2', '0', 'Suffrage and Elections', 'In all elections by the people, the electors shall vote by ballot, until otherwise directed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_3_0_1865', 'Alabama1865', '1865', '8', '3', '0', 'Suffrage and Elections', 'Except in cases of treason, felony, or breach of the peace, electors shall be privileged from arrest during their attendance at elections, an in going to and returning from the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_4_0_1865', 'Alabama1865', '1865', '8', '4', '0', 'Suffrage and Elections', 'Returns of elections for all civil officers elected by the people, who are to be commissioned by the Governor, and also for members of the General Assembly, shall be made to the secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_1_0_1865', 'Alabama1865', '1865', '9', '1', '0', 'Amendments', 'The General Assembly may, whenever two thirds of each house shall deem it necessary, propose amendments to this constitution ; which proposed amendments shall be duly published in print, (in such manner as the General Assembly may direct,) at least three months before the next general election for representatives, for the consideration of the people ; and it shall be the duty of the several returning officers, at the next ensuing general election for representatives, to open a poll for the vote of the qualified electors on the proposed amendments, and to make a return of said vote to the secretary of State ; and if it shall thereupon appear that a majority of all the qualified electors of the State, who voted for representatives, voted in favor of the proposed amendments, and two thirds of each house of the next General Assembly, before another election, shall ratify said amendments, each house voting by yeas and nays, said amendments shall be valid, to all intents and purposes, as parts of this constitution. Provided, that said proposed amendments shall, at each of said sessions of the General Assembly, have been read three times, on three several days, in each house.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_2_0_1865', 'Alabama1865', '1865', '9', '2', '0', 'Amendments', 'After the expiration of twelve months from the adoption of this constitution, no convention shall be held, for the purpose of altering or amending the constitution of this State, unless the question of convention or no convention shall be first submitted to a vote of the qualified electors of the State, and approved by a majority of the electors voting at said election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9001_1_0_1865', 'Alabama1865', '1865', '9001', '1', '0', 'Preamble', 'PREAMBLE. We, the People of the State of Alabama, by our representatives in convention assembled ; in order to establish justice, ensure domestic tranquillity, provide for the common defence, promote the general welfare, and secure to ourselves and to our posterity the rights of life, liberty, and property ; invoking the favor and guidance of Almighty God, do ordain and establish the following constitution and form of government for the State of Alabama-that is to say:', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_1_0_1868', 'Alabama1868', '1868', '1', '1', '0', 'Bill of Rights', 'That all men are created equal; that they are endowed by their Creator with certain inalienable rights; that among these are life, liberty, and the pursuit of happiness.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_2_0_1868', 'Alabama1868', '1868', '1', '2', '0', 'Bill of Rights', 'That all persons resident in this state, born in the United States, or naturalized, or who shall have legally declared their intention to become citizens of the United States, are hereby declared citizens of the State of Alabama, possessing equal civil and political rights and public privileges.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_3_0_1868', 'Alabama1868', '1868', '1', '3', '0', 'Bill of Rights', 'That all political power is inherent in the people, and all free governments are founded on their authority, and instituted for their benefit; and that, therefore, they have, at all times, an inherent right to change their form of government, in such manner as they may deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_4_0_1868', 'Alabama1868', '1868', '1', '4', '0', 'Bill of Rights', 'That no person shall be deprived of the right to worship-God according to the dictates of his own conscience.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_5_0_1868', 'Alabama1868', '1868', '1', '5', '0', 'Bill of Rights', 'That no religion shall be established by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_6_0_1868', 'Alabama1868', '1868', '1', '6', '0', 'Bill of Rights', 'That any citizen may speak, write, and publish his sentiments on all subjects, being responsible for the abuse of that liberty.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_7_0_1868', 'Alabama1868', '1868', '1', '7', '0', 'Bill of Rights', 'That the people shall be secure in their persons, houses, papers and possessions, from unreasonable seizures or searches, and that no warrant shall issue to search any place, or to seize any person or thing without probable cause, supported by oath or affirmation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_8_0_1868', 'Alabama1868', '1868', '1', '8', '0', 'Bill of Rights', 'That in all criminal prosecutions, the accused has a right to be heard by himself and counsel, or either; to demand the nature and cause of the accusation; to have a copy thereof; to be confronted by the witnesses against him; to have compulsory process for obtaining witnesses in his favor; and in all prosecutions by indictment or information, a speedy public trial, by an impartial jury of the county or district in which the offence was committed; and that he shall not be compelled to give evidence against himself, or be deprived of his life, liberty or property, but by due process of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_9_0_1868', 'Alabama1868', '1868', '1', '9', '0', 'Bill of Rights', 'That no person shall be accused or arrested, or detained, except in cases ascertained by law, and according to the forms which the same has prescribed; and that no person shall be punished but by virtue of a law established and promulgated prior to the offence, and legally applied.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_10_0_1868', 'Alabama1868', '1868', '1', '10', '0', 'Bill of Rights', 'That no person shall, for any indictable offence, be proceeded against criminally, by information, except in cases arising in the land and naval service, or in the militia when in actual service, or by leave of the court for oppressions or misdemeanor in office: Provided, That in cases of petit larceny, assault, assault and battery, affray, unlawful assemblies, vagrancy, and other misdemeanors, the General Assembly may, by law, dispense with a grand jury, and authorize such prosecutions and proceedings before justices of the peace, or such other inferior courts as may be by law established.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_11_0_1868', 'Alabama1868', '1868', '1', '11', '0', 'Bill of Rights', 'That no person shall, for the same offence, be twice put in jeopardy of life or limb.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_12_0_1868', 'Alabama1868', '1868', '1', '12', '0', 'Bill of Rights', 'That no person shall be debarred from prosecuting or defending, before any tribunal in the state, by himself, or counsel, any civil cause to which he is a party.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_13_0_1868', 'Alabama1868', '1868', '1', '13', '0', 'Bill of Rights', 'That the right of trial by jury shall remain inviolate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_14_0_1868', 'Alabama1868', '1868', '1', '14', '0', 'Bill of Rights', 'That in prosecution for the publication of papers investigating the official conduct of officers, or men in public capacity, or when the matter published is proper for public information, the truth thereof may be given in evidence; and that in all indictments for libel, the jury shall have the right to determine the law and the facts under the direction of the court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_15_0_1868', 'Alabama1868', '1868', '1', '15', '0', 'Bill of Rights', 'That all courts shall be open, that every person, for any injury done him in his lands, goods, person or reputation, shall have a remedy by due process of law; and right and justice shall be administered without sale, denial or delay.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_16_0_1868', 'Alabama1868', '1868', '1', '16', '0', 'Bill of Rights', 'That suits may be brought against the state in such manner and in such courts as may be by law provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_17_0_1868', 'Alabama1868', '1868', '1', '17', '0', 'Bill of Rights', 'That excessive fines shall not be imposed, or cruel punishment inflicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_18_0_1868', 'Alabama1868', '1868', '1', '18', '0', 'Bill of Rights', 'That all persons shall, before conviction, be bailable by sufficient sureties, except for capital offences when the proof is evident, or the presumption great. Excessive bail shall not, in any case, be required.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_19_0_1868', 'Alabama1868', '1868', '1', '19', '0', 'Bill of Rights', 'The privilege of writ of habeas corpus shall not be suspended, except when necessary for public safety in times of rebellion or invasion.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_20_0_1868', 'Alabama1868', '1868', '1', '20', '0', 'Bill of Rights', 'That treason against the state shall consist only in levying war against it, or adhering to its enemies, giving them aid and comfort; and that no person shall be convicted of treason, except on the testimony of two witnesses to the same overt act, or his own confession in open court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_21_0_1868', 'Alabama1868', '1868', '1', '21', '0', 'Bill of Rights', 'That no person shall be attainted of treason by the General Assembly; and that no conviction shall work corruption of blood or forfeiture of estate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_22_0_1868', 'Alabama1868', '1868', '1', '22', '0', 'Bill of Rights', 'That no person shall be imprisoned for debt.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_23_0_1868', 'Alabama1868', '1868', '1', '23', '0', 'Bill of Rights', 'That no power of suspending laws shall be exercised, except by the General Assembly, or by its authority.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_24_0_1868', 'Alabama1868', '1868', '1', '24', '0', 'Bill of Rights', 'That no ex post facto law, or any law impairing the obligation of contracts, shall be made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_25_0_1868', 'Alabama1868', '1868', '1', '25', '0', 'Bill of Rights', 'That private property shall not be taken or applied for public use, unless just compensation be made therefor ; nor shall private property be taken for private use, or for the use of corporations, other than municipal, without the consent of the owner : Provided, however, That laws may be made securing to persons or corporations, the right of way over the lands of either persons or corporations, and for works of internal improvement, the right to establish depots, stations, and turnouts, but just compensation shall, in all cases, be first made to the owner.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_26_0_1868', 'Alabama1868', '1868', '1', '26', '0', 'Bill of Rights', 'That all navigable waters shall remain forever public highways, free to the citizens of the state, and of the United States, without tax, impost or toll imposed; and that no tax, toll, impost or wharfage shall be demanded or received from the owner of any merchandise or commodity for the shores, or any wharf erected on the shores, or in, or over the waters of any navigable stream, unless the same be expressly authorized by the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_27_0_1868', 'Alabama1868', '1868', '1', '27', '0', 'Bill of Rights', 'That the citizens have a right, in a peaceable manner, to assemble together for the common good and to apply to those invested with the power of government, for redress of grievances or other purposes, by petition, address or remonstrance.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_28_0_1868', 'Alabama1868', '1868', '1', '28', '0', 'Bill of Rights', 'That every citizen has a right to bear arms in defence of himself and the state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_29_0_1868', 'Alabama1868', '1868', '1', '29', '0', 'Bill of Rights', 'That no person who conscienciously scruples to bear arms shall be compelled to do so, but may pay an equivalent for personal service.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_30_0_1868', 'Alabama1868', '1868', '1', '30', '0', 'Bill of Rights', 'That no standing army shall be kept up without the consent of the General Assembly; and, in that case, no appropriation for its support shall be made for a longer term than one year and the military shall, in all cases, and at all times, be in strict subordination to the civil power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_31_0_1868', 'Alabama1868', '1868', '1', '31', '0', 'Bill of Rights', 'That no soldier shall, in time of peace, be quartered in any house, without the consent of the owner; or in time of war, but in a manner to be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_32_0_1868', 'Alabama1868', '1868', '1', '32', '0', 'Bill of Rights', 'That no title of nobility, or hereditary distinction, privilege, honor, or emolument, shall ever be granted or conferred in this state; that no property qualification shall be necessary to the election to, or holding of any office in this state, and that no office shall be created, the appointment to which shall be for a longer time than during good behavior.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_33_0_1868', 'Alabama1868', '1868', '1', '33', '0', 'Bill of Rights', 'That emigration from the state shall not be prohibited ; and that no citizen shall be exiled.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_34_0_1868', 'Alabama1868', '1868', '1', '34', '0', 'Bill of Rights', 'That temporary absence from the state shall not cause a forfeiture of residence once obtained.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_35_0_1868', 'Alabama1868', '1868', '1', '35', '0', 'Bill of Rights', 'That no form of slavery shall exist in this state; and there shall be no involuntary servitude, otherwise than for the punishment of crime, of which the party shall have been duly convicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_36_0_1868', 'Alabama1868', '1868', '1', '36', '0', 'Bill of Rights', 'The right of suffrage shall be protected by laws, regulating elections, and prohibiting, under adequate penalties, all undue influences from power, bribery, tumult or other improper conduct.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_37_0_1868', 'Alabama1868', '1868', '1', '37', '0', 'Bill of Rights', 'That this state has no right to sever its relations to the Federal Union, or to pass any law in derogation of the paramount allegiance of the citizens of this state to the government of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_38_0_1868', 'Alabama1868', '1868', '1', '38', '0', 'Bill of Rights', 'That this enumeration of certain rights shall not impair or deny others retained by the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_1_0_1868', 'Alabama1868', '1868', '2', '1', '0', 'Boundaries', 'The boundaries of this State are established and declared to be as follows--that is to say: Beginning at the point where the thirty-first degree of north latitude crosses the Perdido river; thence east to the western boundary line of the State of Georgia; thence along said line to the southern boundary line of the State of Tennessee; thence west along the southern boundary line of the State of Tennessee, crossing the Tennessee river, and on to the second intersection of said river, by said line; thence up said river to the mouth of Big Bear creek; thence by a direct line to the northwest corner of Washington county, in this state, as originally formed; thence southerly, along the line of the State of Mississippi, to the Gulf of Mexico; thence eastwardly, including all islands within six leagues of the shore, to the Perdido river, and thence up the said river to the beginning.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_2_0_1868', 'Alabama1868', '1868', '2', '2', '0', 'Boundaries', 'The General Assembly may, by a two-thirds vote of both houses thereof, arrange and designate boundaries for the several counties of this State, which boundaries shall not be altered, except by a like vote. But no new counties shall be hereafter formed of less extent than six hundred square miles; and no existing county shall be reduced to less extent than six hundred square miles; and no new county shall be formed which does not contain a sufficient number of inhabitants to entitle it to one representative under the ratio of representation existing at the time of its formation, or, unless the county or counties from which it is taken shall be left with the required number of inhabitants entitling such county or counties to separate representation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_1_0_1868', 'Alabama1868', '1868', '3', '1', '0', 'Separation of Powers', 'The powers of the government of the State of Alabama shall be divided into three distinct departments, each of which shall be confided to a separate body of magistracy, to-wit: Those which are legislative, to one; those which are executive, to another; and those which are judicial, to another.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_2_0_1868', 'Alabama1868', '1868', '3', '2', '0', 'Separation of Powers', 'No person, or collection of persons, being of one of those departments, shall exercise any power properly belonging to either of the others, except in the instances hereinafter expressly directed permitted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_1_0_1868', 'Alabama1868', '1868', '4', '1', '0', 'Legislative', 'The legislative power of this state shall be vested in a General Assembly, which shall consist of a Senate and House of Representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_2_0_1868', 'Alabama1868', '1868', '4', '2', '0', 'Legislative', 'The style of the laws of this state shall be: Be it enacted by the General Assembly of Alabama. Each law shall contain but one subject, which shall be clearly expressed in its title; and no law shall be revised or amended unless the new act contain the entire act revised, or the section or sections amended; and the section or sections so amended shall be repealed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_3_0_1868', 'Alabama1868', '1868', '4', '3', '0', 'Legislative', 'Senators and representatives shall be elected by the qualified electors, on the Tuesday after the first Monday in November. The term of office of the Senators shall be four years, and that of the Representatives two years, commencing on the day after the general election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_4_0_1868', 'Alabama1868', '1868', '4', '4', '0', 'Legislative', 'No person shall be a Representative unless he is eligible as an elector to vote for members of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_5_0_1868', 'Alabama1868', '1868', '4', '5', '0', 'Legislative', 'No person shall be a Senator, unless he be eligible as an elector to vote for members of the General Assembly, and shall be twenty-seven years of age, and shall have resided for two years within the state, and for the last year thereof within the district for which he shall be chosen.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_6_0_1868', 'Alabama1868', '1868', '4', '6', '0', 'Legislative', 'The House of Representatives, when assembled, shall choose a speaker, and its other officers; and the Senate shall choose a president, in the absence of the Lieutenant Governor, and its other officers: each House shall judge of the qualifications, elections and returns of its own members, but a contested election shall be determined in such manner as shall be directed by law. The President of the Senate and the Speaker of the House of Representatives shall remain in office until their successors are elected and qualified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_7_0_1868', 'Alabama1868', '1868', '4', '7', '0', 'Legislative', 'A majority of each house shall constitute a quorum to do business, but a smaller number may adjourn from day to day, and may compel the attendance of absent members, in such manner and under such penalties as each house may provide.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_8_0_1868', 'Alabama1868', '1868', '4', '8', '0', 'Legislative', 'Each house may determine the rules of its own proceedings, punish members for disorderly conduct, and, with the consent of two-thirds, expel a member; but not a second time for the same cause; and shall have all other powers necessary for a branch of the Legislature of a free and independent state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_9_0_1868', 'Alabama1868', '1868', '4', '9', '0', 'Legislative', 'Each house, during the session, may punish by imprisonment, any person not a member for disrespectful or disorderly behavior in its presence, or obstructing any of its proceedings: Provided, That such imprisonment shall not, at any time, exceed forty-eight hours.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_10_0_1868', 'Alabama1868', '1868', '4', '10', '0', 'Legislative', 'Each house shall keep a journal of its proceedings, and cause the same to be published immediately after its adjournment, excepting such parts as in its judgment may require secrecy, and the yeas and nays of the members of either house, on any question, shall, at the desire of one-tenth of the members present, be entered on the journals. Any member of either house shall have liberty to dissent from, or protest against, any act or resolution, which he may think injurious to the public or an individual, and have the reasons of his dissent entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_11_0_1868', 'Alabama1868', '1868', '4', '11', '0', 'Legislative', 'Members of the General Assembly, shall, in all cases, except treason, felony or breach of the peace, be privileged from arrest; and they shall not be subject to any civil process during the session of the General Assembly, nor for fifteen days next before the commencement and after the termination of each session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_12_0_1868', 'Alabama1868', '1868', '4', '12', '0', 'Legislative', 'When vacancies occur in either house, the Governor, or the person exercising the powers of the Governor, shall issue writs of elections to fill such vacancies.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_13_0_1868', 'Alabama1868', '1868', '4', '13', '0', 'Legislative', 'The doors of each house shall be open, except on such occasion as in the opinion of the house, may require secrecy.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_14_0_1868', 'Alabama1868', '1868', '4', '14', '0', 'Legislative', 'Neither house shall , without the consent of the other, adjourn for more than three days, nor to any other place than that in which they may be sitting.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_15_0_1868', 'Alabama1868', '1868', '4', '15', '0', 'Legislative', 'Bills may originate in either house, and be amended, altered or rejected by the other; but no bill shall have the force of law until on three several days it be read in each house and free discussion be allowed thereon, unless in case of urgency, four-fifths of the house in which the bill shall be pending, may deem it expedient to dispense with this rule. And every bill, having passed both houses, shall be signed by the speaker and president of their respective houses: Provided, That all bills for raising revenue shall originate in the House of Representatives, but the Senate may amend or reject them as other bills.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_16_0_1868', 'Alabama1868', '1868', '4', '16', '0', 'Legislative', 'Every bill or resolution having the force of law, to which the concurrence of both houses of the General Assembly may be necessary, except on a question of adjournment, which shall have passed both houses, shall be presented to the Governor, and if he approve, he shall sign it; if not, he shall return it with his objections, to the house in which it shall have originated, who shall enter the objections at large on the journals, and proceed to reconsider it. If after such reconsideration, a majority of the whole number of members of that house shall agree to pass it, it shall be sent, together with the objections, to the other house, by which it shall be reconsidered, and if approved by a majority of the whole number of members of that house, it shall have the same effect as if it had been signed by the Governor; but in all such cases, the votes of both houses shall be taken by yeas and nays, and the names of persons voting for and against the bill or resolution, shall be entered on the journals of both houses respectively. If the bill or resolution shall not be returned by the Governor within five days (Sundays excepted) after it shall have been presented to him, it shall have the same force and effect as if he had signed it, unless the General Assembly by its adjournment, prevent its return, in which case it shall not be a law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_17_0_1868', 'Alabama1868', '1868', '4', '17', '0', 'Legislative', 'Every order, resolution or vote, to which the concurrence of both houses may be necessary, (except on questions of adjournment, and for bringing on elections by the two houses,) shall be presented to the Governor, and before it shall take effect, be approved by him, or being disapproved, shall be re-passed by both houses, according to the rules and limitations prescribed in the case of bills.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_18_0_1868', 'Alabama1868', '1868', '4', '18', '0', 'Legislative', 'Each member of the General Assembly shall receive from the public treasury such compensation for his services as may be prescribed by law; but no increase of compensation shall take effect during the session at which such increase shall have been made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_19_0_1868', 'Alabama1868', '1868', '4', '19', '0', 'Legislative', 'No Senator or Representative shall, during the term for which he shall have been elected, be appointed to any civil office of profit under this state, which shall have been created, or the emoluments of which shall have been increased during such term, except such office as may be filled by elections by the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_20_0_1868', 'Alabama1868', '1868', '4', '20', '0', 'Legislative', 'No person who holds any lucrative office under the United States, or under this State, or any other State or government, (except postmasters, officers in the militia to whose office no annual salary is attached, justices of the peace, members of the court of county commissioners, notaries public, and commissioners of deeds;) no person who has been convicted of having given or offered any bribe to procure his election to any office; no person who has been convicted of bribery, forgery, perjury, or other high crime, or misdemeanor, which may be by law declared to disqualify him; and no person who has been a collector, or holder of any public moneys, and has failed to account for and pay over to the treasury all sums for which he may be by law accountable, shall be eligible to the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_21_0_1868', 'Alabama1868', '1868', '4', '21', '0', 'Legislative', 'The General Assembly shall meet annually, on such day as may be by law prescribed, and shall not remain in session longer than thirty days, except by a vote of two-thirds of each House.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_22_0_1868', 'Alabama1868', '1868', '4', '22', '0', 'Legislative', 'In all elections by the General Assembly, the members shall vote viva voce, and the votes shall be entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_23_0_1868', 'Alabama1868', '1868', '4', '23', '0', 'Legislative', 'All State officers may be impeached for any misdemeanor in office, but judgment shall not extend further than removal from office, and disqualification to hold office, under the authority of this State. The party impeached, whether convicted or not, shall be liable to indictment, trial and judgment, according to law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_24_0_1868', 'Alabama1868', '1868', '4', '24', '0', 'Legislative', 'The House of Representatives shall have the sole power of preferring impeachment. All impeachments shall be tried by the Senate; the Senators, when sitting for that purpose, shall be on oath or affirmation; and no person shall be convicted under an impeachment without the concurrence of two-thirds of the Senators present.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_25_0_1868', 'Alabama1868', '1868', '4', '25', '0', 'Legislative', 'It shall be the duty of the General Assembly to pass such laws as may be necessary and proper to decide differences by arbitrators, to be appointed by the parties who may choose that mode of adjustment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_26_0_1868', 'Alabama1868', '1868', '4', '26', '0', 'Legislative', 'It shall be the duty of the General Assembly , from time to time, as circumstances may require, to frame and adopt a penal code founded on principles of reformation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_27_0_1868', 'Alabama1868', '1868', '4', '27', '0', 'Legislative', 'It shall be the duty of the General Assembly, within five years after the adoption of this Constitution, and within every subsequent period of ten years, to make provision by law for the revision, digesting and promulgation of all the public statutes of this State, both civil and criminal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_28_0_1868', 'Alabama1868', '1868', '4', '28', '0', 'Legislative', 'The General Assembly shall have power to pass such penal laws as they may deem expedient, to suppress the evil practice of duelling.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_29_0_1868', 'Alabama1868', '1868', '4', '29', '0', 'Legislative', 'It shall be the duty of the General Assembly to regulate by law the cases in which deductions shall be made from the salaries of public officers for neglect of duty in their official capacities, and the amount of such deductions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_30_0_1868', 'Alabama1868', '1868', '4', '30', '0', 'Legislative', 'Divorces from the bonds of matrimony shall not be granted but in the cases by law provided for, and by suit in chancery; but decisions in chancery for divorce shall be final, unless appealed from in the manner prescribed by law, within three months from the date of the enrollment thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_31_0_1868', 'Alabama1868', '1868', '4', '31', '0', 'Legislative', 'No money shall be drawn from the treasury but in pursuance of an appropriation made by law; and a regular statement and account of the receipts and expenditures of all public moneys shall be published annually, in such manner as may be by law directed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_32_0_1868', 'Alabama1868', '1868', '4', '32', '0', 'Legislative', 'The General Assembly shall not borrow or raise money on the credit of this State, except for purposes of military defense against actual or threatened invasion, rebellion or insurrection, without the concurrence of two-thirds of the members of each house; nor shall the debts or liabilities of any corporation, person or persons, or other States be guarantied, nor any money, credit or other thing be loaned or given away, except by a like concurrence of each house; and the votes shall, in each case, be taken by the yeas and nays and be entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_33_0_1868', 'Alabama1868', '1868', '4', '33', '0', 'Legislative', 'The State shall not engage in works of internal improvement; but its credit in aid of such may be pledged by the General Assembly on undoubted security, by a vote of two-thirds of each House of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_34_0_1868', 'Alabama1868', '1868', '4', '34', '0', 'Legislative', 'It shall be the duty of the General Assembly to make adequate provisions in each county for the maintenance of the poor of this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_35_0_1868', 'Alabama1868', '1868', '4', '35', '0', 'Legislative', 'Any citizen of this State who shall, after the adoption of this Constitution, either in or out of this State, fight a duel with deadly weapons, or send, or accept a challenge so to do, or act as a second, or knowingly aid or assist in any manner those thus offending, shall be incapable of holding any office under this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_36_0_1868', 'Alabama1868', '1868', '4', '36', '0', 'Legislative', 'The General Assembly shall not have power to authorize any municipal corporation to pass any laws contrary to the general laws of the State, nor to levy a tax on real and personal property to a greater extend than two per centum of the assessed value of such property.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_37_0_1868', 'Alabama1868', '1868', '4', '37', '0', 'Legislative', 'In the event of annexation of any foreign territory to this State, the General Assembly shall enact laws extending to the inhabitants of the acquired territory, all the rights and privileges which may be required by the terms of the acquisition, anything in this Constitution to the contrary notwithstanding.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_1_0_1868', 'Alabama1868', '1868', '5', '1', '0', 'Executive', 'The Executive Department shall consist of a Governor, Lieutenant Governor, Secretary of State, Auditor, Treasurer, and Attorney General, who shall be chosen by the electors of the State, at the time and places at which they shall vote for Representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_2_0_1868', 'Alabama1868', '1868', '5', '2', '0', 'Executive', 'The Governor, Lieutenant Governor, Secretary of State, Treasurer, and Attorney General shall hold their offices for the term of two years, and the Auditor for the term of four years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_3_0_1868', 'Alabama1868', '1868', '5', '3', '0', 'Executive', 'The returns of every election for the officers named in the preceding section, shall be sealed up and transmitted to the seat of Government, by the returning officers, directed to the presiding officer of the Senate, who, during the first week of the session, shall open and publish the same in the presence of a majority of the members of the General Assembly; the person having the highest number of votes shall be declared duly elected, but if two or more shall be highest and equal in votes for the same office, one of them shall be chosen by the joint vote of both houses. Contested elections for executive officers shall be determined by both houses of the General Assembly, in such manner as shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_4_0_1868', 'Alabama1868', '1868', '5', '4', '0', 'Executive', 'The supreme executive power of this State shall be vested in the Governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_5_0_1868', 'Alabama1868', '1868', '5', '5', '0', 'Executive', 'He shall take care that the laws are faithfully executed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_6_0_1868', 'Alabama1868', '1868', '5', '6', '0', 'Executive', 'He may require information in writing, from the officers in the executive department, upon any subject relating to the duties of their respective offices.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_7_0_1868', 'Alabama1868', '1868', '5', '7', '0', 'Executive', 'He shall communicate at every session, by message to the General Assembly, the condition of the State, and recommend such measures as he shall deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_8_0_1868', 'Alabama1868', '1868', '5', '8', '0', 'Executive', 'He may, on extraordinary occasions, convene the General Assembly by proclamation, and shall state to both houses, when assembled, the purposes for which they have been convened.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_9_0_1868', 'Alabama1868', '1868', '5', '9', '0', 'Executive', 'In case of disagreement between the two houses, in respect to the time of adjournment, he shall have power to adjourn the General Assembly to such time as he may think proper, but not beyond the regular meeting thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_10_0_1868', 'Alabama1868', '1868', '5', '10', '0', 'Executive', 'He shall be commander-in-chief of the military and naval forces of the State, except when they shall be called into the service of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_11_0_1868', 'Alabama1868', '1868', '5', '11', '0', 'Executive', 'He shall have power after conviction, to grant reprieves, commutations and pardons for all offences, (except treason and cases of impeachment,) upon such conditions as he may think proper, subject, however, to such regulations as to the manner of applying for pardons as may be prescribed by law; but such pardons shall not relieve from civil or political disability. Upon conviction of treason, he may suspend the execution of the sentence, and report the same to the General Assembly at the next meeting, when the General Assembly shall either pardon, commute the sentence, direct its execution, or grant further reprieve. He shall communicate to the General Assembly at every regular session, each case of reprieve, commutation, or pardon granted, stating the name and crime of the convict, the sentence, its date; and the date of the commutation, pardon or reprieve, with his reasons therefor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_12_0_1868', 'Alabama1868', '1868', '5', '12', '0', 'Executive', 'There shall be a great seal of the State which shall be kept and used by the Governor officially, and the seal heretofore in use, shall continue to be the great seal of the State until another shall have been adopted by the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_13_0_1868', 'Alabama1868', '1868', '5', '13', '0', 'Executive', 'All grants and commissions shall be issued in the name and by the authority of the State of Alabama, sealed with the Great Seal, signed by the Governor, and countersigned by the Secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_14_0_1868', 'Alabama1868', '1868', '5', '14', '0', 'Executive', 'No member of Congress, or other person, holding office under the authority of this State, or of the United States, shall execute the office of Governor, except as herein provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_15_0_1868', 'Alabama1868', '1868', '5', '15', '0', 'Executive', 'In case of the death, impeachment, resignation, removal, or other disability of the Governor, the powers and duties of the office, for the residue of the term, or until he shall be acquitted, or the disability removed, shall devolve upon the Lieutenant Governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_16_0_1868', 'Alabama1868', '1868', '5', '16', '0', 'Executive', 'The Lieutenant Governor shall be President of the Senate, but shall vote only when the Senate is equally divided, and in case of his absence or impeachment, or when he shall exercise the office of Governor, the Senate shall choose a president pro tempore.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_17_0_1868', 'Alabama1868', '1868', '5', '17', '0', 'Executive', 'If the Lieutenant Governor, while executing the office of Governor, shall be impeached, displaced, resign or die, or otherwise become incapable of performing the duties of the office, the President of the Senate shall act as Governor until the vacancy is filled or the disability removed; and if the President of the Senate for any of the above causes shall be rendered incapable of performing the duties pertaining to the office of Governor, the same shall devolve upon the Speaker of the House of Representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_18_0_1868', 'Alabama1868', '1868', '5', '18', '0', 'Executive', 'Should the office of Secretary of State, Auditor, Treasurer, or Attorney General become vacant from any of the causes specified in the fifteenth section of this article, the governor shall fill the vacancy until the disability is removed or a successor elected and qualified. Every such vacancy shall be filled by election at the first general election that occurs more than thirty days after it shall have occurred, and the person chosen shall hold the office for the full term fixed in the second section of this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_19_0_1868', 'Alabama1868', '1868', '5', '19', '0', 'Executive', 'The officers mentioned in this article shall, at stated times, receive for their services a compensation to be established by law, which shall neither be increased or diminished during the period for which they shall have been elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_20_0_1868', 'Alabama1868', '1868', '5', '20', '0', 'Executive', 'The officers of the Executive Department and of the public institutions of the State, shall, at least five days preceding each regular session of the General Assembly, severally report to the Governor, who shall transmit such reports with his message to the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_21_0_1868', 'Alabama1868', '1868', '5', '21', '0', 'Executive', 'A Sheriff shall be elected in each county by the qualified electors thereof, who shall hold his office for the term of three years, unless sooner removed, and shall not be eligible to serve either as principal or deputy for any two successive terms. Vacancies in the office of Sheriff shall be filled by the Governor as in other cases; and the person appointed shall continue in office until the next general election in the county for Sheriff, as by law provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_1_0_1868', 'Alabama1868', '1868', '6', '1', '0', 'Judicial', 'The judicial power of the State shall be vested in the Senate sitting as a court of Impeachment, a Supreme Court, Circuit Courts, Chancery Courts, Courts of Probate, such inferior Courts of Law and Equity to consist of not more than five members, as the General Assembly may from time to time establish, and such persons as may be by law invested with powers of a judicial nature.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_2_0_1868', 'Alabama1868', '1868', '6', '2', '0', 'Judicial', 'Except in cases otherwise directed in the Constitution, the Supreme Court shall have appellate jurisdiction only, which shall be co-extensive with the State, under such restrictions and regulations not repugnant to this Constitution, as may from time to time be prescribed by law; Provided, That said court shall have power to issue writs of injunction, mandamus, habeas corpus, quo-warranto, and such other remedial and original writs as may be necessary to give it a general superintendence and control of inferior jurisdiction.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_3_0_1868', 'Alabama1868', '1868', '6', '3', '0', 'Judicial', 'The Supreme Court shall be held at the seat of government, but if that shall have become dangerous from an enemy, or from disease, it may adjourn to a different place.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_4_0_1868', 'Alabama1868', '1868', '6', '4', '0', 'Judicial', 'The State shall be divided by the General Assembly into convenient circuits, each of which shall contain not less than three nor more than eight counties; and for each circuit there shall be chosen a Judge, who shall after his election or appointment, reside in the circuit for which he shall have been chosen.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_5_0_1868', 'Alabama1868', '1868', '6', '5', '0', 'Judicial', 'The Circuit Court shall have original jurisdiction in all matters, civil and criminal, within the State, not otherwise excepted in the Constitution, but in civil cases only when the matter or sum in controversy exceeds fifty dollars: Provided, however, That the Circuit Court shall have equity jurisdiction concurrent with the Courts of Chancery in all cases for divorce, and cases in which the value of the matter in controversy does not exceed the sum of five thousand dollars.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_6_0_1868', 'Alabama1868', '1868', '6', '6', '0', 'Judicial', 'A Circuit Court shall be held in each county in the State at least twice in every year, and the Judges of the several circuits may hold courts for each other when they deem it expedient, and shall do so when directed by law: Provided, That the judges of the several Circuit Courts shall have power to issue writs of injunction returnable into Courts of Chancery.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_7_0_1868', 'Alabama1868', '1868', '6', '7', '0', 'Judicial', 'The General Assembly shall have power to establish a Court or Courts of Chancery with original and appellate jurisdiction. The State shall be divided by the General Assembly into convenient Chancery Divisions, and the Divisions into Districts; and for each Division there shall be a Chancellor, who shall, after his election or appointment, reside in the Division for which he shall have been elected or appointed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_8_0_1868', 'Alabama1868', '1868', '6', '8', '0', 'Judicial', 'A Chancery Court shall be held in each county at a place therein to be fixed by law, and the Chancellors may hold courts for each other, when they deem it expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_9_0_1868', 'Alabama1868', '1868', '6', '9', '0', 'Judicial', 'The General Assembly shall have power to establish in each county within the State a Court of Probate, with general jurisdiction for the granting of letters testamentary and of administration, and for orphans business; and the General Assembly may confer on the said courts, jurisdiction of contracts for labor, and order frequent sessions for that purpose.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_10_0_1868', 'Alabama1868', '1868', '6', '10', '0', 'Judicial', 'The Judges of the Supreme Courts, Circuit Courts, and Courts of Chancery, shall, at stated times, receive for their services a compensation which shall not be diminished during their continuance in office; but they shall receive no fees or perquisites, nor hold any office (except judicial offices) of profit or trust under this State, or the United States during the term for which they have been elected, nor under any other power during their continuance in office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_11_0_1868', 'Alabama1868', '1868', '6', '11', '0', 'Judicial', 'Judges of the Supreme Court, and Chancellors, Judges of the Circuit and Probate Courts, and of such other inferior courts as may be by law established, shall be elected by the qualified electors of the respective counties, cities, towns or districts, for which said courts may be established, on the Tuesday after the first Monday in November of each year, or such other day as may be by law prescribed. Vacancies in the office of the Circuit Judge, Judge of Probate, or Judge of any other inferior court established by law, shall be filled by the Governor; and the person appointed by him shall hold office until the next election day appointed by law for election of Judge, and until his successor shall have been elected and qualified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_12_0_1868', 'Alabama1868', '1868', '6', '12', '0', 'Judicial', 'The Judges of the several Courts of this State shall hold their office for the term of six years ; and the right of any Judge to hold his office for the full term hereby prescribed, shall not be affected by any change hereafter made by law in any Circuit or District, or in the mode or time of election ; but for any wilful neglect of duty, or any other reasonable cause which shall not be a sufficient ground of impeachment, the Governor shall remove any Judge on the address of two-thirds of each house of the General Assembly: Provided, That the cause or causes for which said removal may be required, shall be stated at length in such address, and entered on the journals of each house: And provided further, That the Judge intended to be removed shall be notified of such cause or causes, and shall be admitted to a hearing in his own defense, before any vote for such address; and in all such cases the vote shall be taken by yeas and nays, and be entered on the journal of each house respectively.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_13_0_1868', 'Alabama1868', '1868', '6', '13', '0', 'Judicial', 'A competent number of Justices and Constables shall be elected in and for each county by the qualified electors thereof; who shall hold office during such terms as may be prescribed by law. Said Justices shall have jurisdiction in all civil cases wherein the amount in controversy does not exceed one hundred dollars. In all cases tried before such Justices the right of appeal shall be secured by law: Provided, That Notaries Public appointed according to law shall be authorized and required to exercise, throughout their respective counties all the powers and jurisdiction of Justices of the Peace.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_14_0_1868', 'Alabama1868', '1868', '6', '14', '0', 'Judicial', 'The Judges of the Supreme Court shall, by virtue of their offices, be conservators of the peace throughout the State; as also the Judges of the Circuit Courts within their respective Circuits, and the Judges of the inferior courts within their respective counties.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_15_0_1868', 'Alabama1868', '1868', '6', '15', '0', 'Judicial', 'The Clerk of the Supreme Court shall be appointed by the Judges thereof; Registers in Chancery, by the Chancellors of the Divisions; and all the Clerks and Registers so appointed shall be removed by the appointing power for cause to be placed on the records of the court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_16_0_1868', 'Alabama1868', '1868', '6', '16', '0', 'Judicial', 'The Attorney General shall reside at the seat of government, and shall be the law officer of the State. During the session of the General Assembly, he shall furnish to the committees of either house, when required, drafts of bills and written opinions upon any matter under consideration of the committees, and shall perform such other duties as may be required of him by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_17_0_1868', 'Alabama1868', '1868', '6', '17', '0', 'Judicial', 'A Solicitor shall be elected in each county in this State by the qualified electors of such county, who shall reside in the county for which he is elected, and perform such duties as may be required of him by law. He shall hold office for a term of four years, and in case of vacancy, such vacancy shall be filled by the Judge of the Circuit until his successor is elected and qualified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_18_0_1868', 'Alabama1868', '1868', '6', '18', '0', 'Judicial', 'Clerks of the Circuit Court, and such inferior courts as may be by law established, shall be elected by the qualified electors in each county, for the term of six years, and may be removed from office for cause, and in such manner as may be by law prescribed. Vacancies in the office of Clerk shall be filled by the Judge of the Court, until the next general election, and until a successor shall be elected and qualified: Provided, That the General Assembly shall have power to annex the duties of Clerk to the office of Judge of any of the inferior courts by law established.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_19_0_1868', 'Alabama1868', '1868', '6', '19', '0', 'Judicial', 'The style of all processes shall be The State of Alabama, and all prosecutions shall be carried on in the name and by the authority of the State of Alabama, and shall conclude against the peace and dignity of the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_1_0_1868', 'Alabama1868', '1868', '7', '1', '0', 'Suffrage and Elections', 'In all elections by the people, the electors shall vote by ballot.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_2_0_1868', 'Alabama1868', '1868', '7', '2', '0', 'Suffrage and Elections', 'Every male person, born in the United States, and every male person who has been naturalized, or who has legally declared his intention to become a citizen of the United States, twenty-one years old or upward, who shall have resided in this State six months next preceding the election, and three months in the county in which he offers to vote, except as hereinafter provided, shall be deemed an elector: Provided, That no soldier, or sailor, or marine in the military or naval service of the United States, shall hereafter acquire a residence by reason of being stationed on duty in this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_3_0_1868', 'Alabama1868', '1868', '7', '3', '0', 'Suffrage and Elections', 'It shall be the duty of the General Assembly to provide, from time to time, for the registration of all electors; but the following classes of persons shall not be permitted to register, vote or hold office: 1st, Those, who, during the late rebellion, inflicted, or caused to be inflicted, any cruel or unusual punishment upon any soldier, sailor, marine, employee or citizen of the United States, or who, in any other way, violated the rules of civilized warfare. 2d, Those who may be disqualified from holding office by the proposed amendment to the Constitution of the United States, known as Article XIV, and those who have been disqualified from registering to vote for delegates to the Convention to frame a Constitution for the State of Alabama, under the act of Congress, to provide for the more efficient government of the rebel States, passed by Congress March 2, 1867, and the acts supplementary thereto, except such persons as aided in the reconstruction proposed by Congress, and accept the political equality of all men before the law: Provided, That the General Assembly shall have power to remove the disabilities incurred under this clause. 3d, Those who shall have been convicted of treason, embezzlement of public funds, malfeasance in office, crime punishable by law with imprisonment in the penitentiary, or bribery. 4th. Those who are idiots or insane.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_4_0_1868', 'Alabama1868', '1868', '7', '4', '0', 'Suffrage and Elections', 'All persons, before registering, must take and subscribe the following oath: I, _______, do solemnly swear (or affirm) that I will support and maintain the Constitution and laws of the United States, and the Constitution and laws of the State of Alabama; that I am not excluded from registering by any of the clauses in Sec. 3, Article 7, of the Constitution of the State of Alabama; that I will never countenance or aid in the secession of this State from the United States; That I accept the civil and political equality of all men; and agree not to attempt to deprive any person or persons, on account of race, color, or previous condition, of any political or civil right, privilege, or immunity, enjoyed by any other class of men; and furthermore, that I will not in any way injure, or countenance in others any attempt to injure, any person or persons, on account of past or present support of the government of the United States, the laws of the United States, or the principle of the political and civil equality of all men, or for affiliation with any political party.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_5_0_1868', 'Alabama1868', '1868', '7', '5', '0', 'Suffrage and Elections', 'Electors shall, in all cases except treason, felony, or breach of the peace, be privileged from arrest and civil process during their attendance at elections, and in going to and returning from the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_6_0_1868', 'Alabama1868', '1868', '7', '6', '0', 'Suffrage and Elections', 'It shall be the duty of the General Assembly, to enact adequate laws giving protection against the evils arising from the use of intoxicating liquors at elections.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_7_0_1868', 'Alabama1868', '1868', '7', '7', '0', 'Suffrage and Elections', 'Returns of elections for all civil officers elected by the people, who are to be commissioned by the Governor, and also for the members of the General Assembly, shall be made to the Secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_1_0_1868', 'Alabama1868', '1868', '8', '1', '0', 'Apportionment', 'The House of Representatives shall consist of not more than one hundred members, who shall be apportioned by the General Assembly among the several counties of the State, according to the number of inhabitants in them respectively; and to this end the General Assembly shall cause an enumeration of all the inhabitants of the State to be made in the year 1875, and every ten years thereafter, and shall make an apportionment of the representatives among the several counties at the first regular session after each enumeration; which apportionment, when made, shall not be subject to alteration until after the next census shall have been taken: Provided, That each county shall be entitled to at least one representative, And provided, further, That when two or more adjoining counties shall each have a residuum, or fraction over and above the ratio then fixed by law, which fractions, when added together, equal, or exceed that ratio, in that case the county having the largest fraction shall be entitled to one additional representative.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_2_0_1868', 'Alabama1868', '1868', '8', '2', '0', 'Apportionment', 'Until the General Assembly shall make an apportionment of the representatives among the several counties, after the first enumeration made as herein provided, the counties of Autauga, Baldwin, Bibb, Blount, Butler, Calhoun, Clay, Clarke, Cherokee, Cleburne, Crenshaw, Choctaw, Coffee, Conecuh, Coosa, Covington, Dale, DeKalb, Elmore, Fayette, Henry, Jefferson, Lauderdale, Limestone, Marshall, Marion, Monroe, Morgan, Pike, Randolph, St. Clair, Shelby, Walker, Washington and Winston, shall have one representative each; the counties of Chambers, Franklin, Greene, Hale, Jackson, Lee, Lawrence, Macon, Pickens, Russell, Talladega, Tallapoosa and Tuscaloosa, shall be entitled to two representatives each; the counties of Barbour, Bullock, Lowndes, Madison, Marengo, Perry, Sumter and Wilcox, shall be entitled to three representatives each; the counties of Dallas, Mobile and Montgomery, shall be entitled to five representatives each: Provided, That in the formation of new counties the General Assembly may apportion to each its proper representation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_3_0_1868', 'Alabama1868', '1868', '8', '3', '0', 'Apportionment', 'The whole number of Senators shall be not less than one-fourth or more than one-third of the whole number of representatives; and it shall be the duty of the General Assembly, at its first session after the making of each enumeration, as provided by section first, of this article, to fix by law the number of Senators, and to divide the State into as many senatorial districts as there are Senators; which districts shall be as nearly equal to each other as may be in the number of inhabitants, and each shall be entitled to one Senator, and no more: Provided, That no county shall be divided, and no two or more counties, which are separated entirely by a county belonging to another district, shall be joined in one district; And Provided, further, That the senatorial districts, when formed, shall not be changed until after the next enumeration shall have been taken.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_4_0_1868', 'Alabama1868', '1868', '8', '4', '0', 'Apportionment', 'At the first general election after each new apportionment, elections shall be held anew in all the senatorial districts. The senators elected, when convened at the next ensuing session of the General Assembly, shall be divided by lot into two classes, as nearly equal as may be; the seats of the senators of the 1st class shall be vacated at the expiration of two years, and those of the 2d class at the expiration of four years, from the day of election, so that (except as above provided,) one-half of the senators may be chosen biennially.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_5_0_1868', 'Alabama1868', '1868', '8', '5', '0', 'Apportionment', 'Until the General Assembly shall divide the State into senatorial districts as herein provided, the senatorial districts shall remain as follows: 1st District, Limestone and Lauderdale; 2d, Franklin and Lawrence; 3d, Morgan, Blount, Winston and Marion; 4th, Madison; 5th, Jackson, Marshall and DeKalb; 6th, Cherokee and Calhoun; 7th, Walker, Jefferson and St. Clair; 8th, Shelby and Bibb; 9th, Tuscaloosa and Fayette; 10th, Talladega and Clay; 11th; Chambers, Randolph and Cleburne; 12th, Coosa and Tallapoosa, 13th, Lee; 14th, Macon; 15th, Russell; 16th, Bullock; 17th, Barbour; 18th, Autauga and Elmore; 19th, Montgomery; 20th, Lowndes; 21st, Dallas; 22d, Perry; 23d, Hale; 24th, Greene and Pickens; 25th, Sumter; 26th, Marengo; 27th , Choctaw, Clark and Washington; 28th, Mobile; 29th, Monroe and Baldwin; 30th, Wilcox; 31st, Butler and Conecuh; 32d, Covington, Crenshaw and Pike; 33d, Coffee, Dale and Henry.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_6_0_1868', 'Alabama1868', '1868', '8', '6', '0', 'Apportionment', 'Until a new apportionment of representative to the Congress of the United States shall have been made, the Congressional Districts shall remain as stated in the Revised Code of Alabama, and after each new apportionment, the General Assembly shall divide the state into as many districts as it is allowed representatives in Congress, making such Congressional Districts as nearly equal in the number of inhabitants as may be.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_1_0_1868', 'Alabama1868', '1868', '9', '1', '0', 'Taxation', 'All taxes levied on property in this state, shall be assessed in exact proportion to the value of such property: Provided, however, That the General Assembly may levy a poll tax not to exceed one dollar and fifty cents on each poll, which shall be applied exclusively in aid of the public school fund.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_2_0_1868', 'Alabama1868', '1868', '9', '2', '0', 'Taxation', 'No power to levy taxes shall be delegated to individuals or private corporations.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_1_0_1868', 'Alabama1868', '1868', '10', '1', '0', 'Militia', 'All able-bodied male inhabitants of this state, between the ages of eighteen years and forty-five years, who are citizens of the United States, or who have declared their intention to become citizens of the United States, shall be liable to military duty in the militia of this state; but all citizens of any denomination whatever, who, from scruples of conscience, may be averse to bearing arms, shall be exempt therefrom upon such conditions as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_2_0_1868', 'Alabama1868', '1868', '10', '2', '0', 'Militia', 'The General Assembly shall provide for the organizing, arming, equipping and discipline of the militia, and for paying the same, when called into active service, in such manner as it shall deem expedient, not incompatible with the laws of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_3_0_1868', 'Alabama1868', '1868', '10', '3', '0', 'Militia', 'Officers of the militia shall be elected or appointed and commissioned in such manner as may be provided by the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_4_0_1868', 'Alabama1868', '1868', '10', '4', '0', 'Militia', 'The Governor shall be commander-in-chief of the army and navy of this state, and of the militia, except when called into the service of the United States, and shall have power to call forth the militia to execute the laws, to suppress riots, or insurrections, and to repel invasion.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_5_0_1868', 'Alabama1868', '1868', '10', '5', '0', 'Militia', 'The Governor shall nominate, and by and with the consent of the Senate, appoint one Major-General and three Brigadier-Generals: the Adjutant General, and other staff-officers to the commander-in-chief, shall be appointed by the Governor, and their commissions shall expire with the Governors term of service. No commissioned officer shall be removed from office except by the Senate, on the recommendation of the Governor, stating the grounds on which such removal is recommended, or by the decision of a court-martial pursuant to law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_6_0_1868', 'Alabama1868', '1868', '10', '6', '0', 'Militia', 'The militia may be divided into two classes, to be designated as volunteer militia and reserve militia, in such manner as shall be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_7_0_1868', 'Alabama1868', '1868', '10', '7', '0', 'Militia', 'The militia shall, in all cases, except felony, treason, or breach of the peace, be privileged from arrest during their attendance at musters and elections of officers, and in going to and returning from the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_8_0_1868', 'Alabama1868', '1868', '10', '8', '0', 'Militia', 'The officers and men commissioned and organized, shall not be entitled to, or receive any pay, rations, or emoluments when not in active service.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_1_0_1868', 'Alabama1868', '1868', '11', '1', '0', 'Education', 'The common schools, and other educational institutions of the State, shall be under the management of a Board of Education, consisting of a Superintendent of Public Instruction and two members from each Congressional District. The Governor of the State shall be ex officio a member of the Board, but shall have no vote in its proceedings.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_2_0_1868', 'Alabama1868', '1868', '11', '2', '0', 'Education', 'The Superintendent of Public Instruction shall be President of the Board of Education, and have the casting vote in case of a tie; he shall have the supervision of the public schools of the State, and perform such other duties as may be imposed upon him by the Board and the laws of the State. He shall be elected in the same manner and for the same term as the Governor of the State, and receive such salary as may be fixed by law. An office shall be assigned him in the capitol of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_3_0_1868', 'Alabama1868', '1868', '11', '3', '0', 'Education', 'The members of the Board shall hold office for a term of four years, and until their successors shall be elected and qualified. After the first election under the Constitution, the Board shall be divided into two equal classes, so that each class shall consist of one member from each District. The seats of the first class shall be vacated at the expiration of two years from the day of election, so that one-half may be chosen biennially.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_4_0_1868', 'Alabama1868', '1868', '11', '4', '0', 'Education', 'The members of the Board of Education, except the Superintendent, shall be elected by the qualified electors of the Congressional Districts in which they are chosen, at the same time and in the same manner as the members of Congress.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_5_0_1868', 'Alabama1868', '1868', '11', '5', '0', 'Education', 'The Board of Education shall exercise full legislative powers in reference to the public educational institutions of the State, and its acts, when approved by the Governor, or when re enacted by two-thirds of the Board, in case of his disapproval, shall have the force and effect of law, unless repealed by the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_6_0_1868', 'Alabama1868', '1868', '11', '6', '0', 'Education', 'It shall be the duty of the Board to establish, throughout the State, in each township, or other school district which it may created, one or more schools at which all the children of the State, between the ages of five and twenty-one years, may attend free of charge.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_7_0_1868', 'Alabama1868', '1868', '11', '7', '0', 'Education', 'No rule or law affecting the general interest of education shall be made by the Board without the concurrence of a majority of its members. The style of all acts of the Board shall be, Be it enacted by the Board of Education of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_8_0_1868', 'Alabama1868', '1868', '11', '8', '0', 'Education', 'The Board of Education shall be a body politic and corporate, by the name and style of the Board of Education of the State of Alabama. Said Board shall also be a Board of Regents of the State University, and when sitting as a Board of Regents of the University, shall have power to appoint the President and the Faculties thereof. The President of the University shall be ex officio a member of the board of Regents, but shall have no vote in its proceedings.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_9_0_1868', 'Alabama1868', '1868', '11', '9', '0', 'Education', 'The Board of Education shall meet annually at the seat of government at the same time as the General Assembly, but no session shall continue longer than twenty days, nor shall more than one session be held in the same year, unless authorized by the Governor. The members shall receive the same mileage and daily pay as the members of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_10_0_1868', 'Alabama1868', '1868', '11', '10', '0', 'Education', 'The proceeds of all lands that have been or may be granted by the United States to the State for educational purposes; of the swamp lands; and of all lands or other property given by individuals or appropriated by the state for like purposes; and of all estates of deceased persons who have died without leaving a will or heir; and all moneys which may be paid as an equivalent for exemption from military duty, shall be and remain a perpetual fund, which may be increased but not diminished, and the interest and income of which, together with the rents of all such lands as may remain unsold, and such other means, as the General Assembly may provide, shall be inviolably appropriated to educational purposes, and to no other purpose whatever.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_11_0_1868', 'Alabama1868', '1868', '11', '11', '0', 'Education', 'In addition to the amount accruing from the above sources, one-fifth of the aggregate annual revenue of the State shall be devoted exclusively to the maintenance of public schools.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_12_0_1868', 'Alabama1868', '1868', '11', '12', '0', 'Education', 'The General Assembly may give power to the authorities of the school districts to levy a poll tax on the inhabitants of the district in aid of the general school fund, and for no other purpose.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_13_0_1868', 'Alabama1868', '1868', '11', '13', '0', 'Education', 'The General Assembly shall levy a specific annual tax upon all Rail Road, Navigation, Banking, and Insurance corporations, and upon all Insurance and Foreign Bank and Exchange Agencies, and upon the profits of foreign bank bills issued in this State by any corporation, partnership or persons, which shall be exclusively devoted to the maintenance of public schools.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_14_0_1868', 'Alabama1868', '1868', '11', '14', '0', 'Education', 'The General Assembly shall, as soon as practicable, provide for the establishment of an Agricultural College, and shall appropriate the two hundred and forty thousand acres of land donated to this State for the support of such a college, by the act of Congress, passed July 2, 1862, or the money or scrip, as the case may be, arising from the sale of said land or any lands which may hereafter be granted, or appropriated for such purpose, for the support and maintenance of such college or schools, and may make the same a branch of the University of Alabama for instruction in agriculture, in the mechanic arts, and the natural sciences connected therewith, and place the same under the supervision of the Regents of the University.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_1_0_1868', 'Alabama1868', '1868', '12', '1', '0', 'Miscellaneous', 'A Bureau of Industrial Resources shall be established, to be under the management of a Commissioner, who shall be elected at the first general election, and shall hold his office for the term of four years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_2_0_1868', 'Alabama1868', '1868', '12', '2', '0', 'Miscellaneous', 'The Commissioner of Industrial Resources shall collect and condense statistical information concerning the productive industries of the state; and shall make, or cause to be made, a careful, accurate and thorough report upon the agriculture and geology of the state, and annually report such additions as the progress of scientific development and extended explorations may require. He shall, from time to time, disseminate among the people of the state such knowledge as he may deem important, concerning improved machinery and production, and for the promotion of their agricultural, manufacturing and mining interests; and shall send out to the people of the United States and foreign countries, such reports concerning the industrial resources of Alabama, as may best make known the advantages offered by the state to emigrants; and shall perform such other duties as the General Assembly may require.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_3_0_1868', 'Alabama1868', '1868', '12', '3', '0', 'Miscellaneous', 'It shall be the duty of the General assembly, at the first session after the adoption of this Constitution, to pass such laws and regulations as may be necessary for the government and protection of this bureau, and also to fix and provide for the compensation of the commissioner.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_4_0_1868', 'Alabama1868', '1868', '12', '4', '0', 'Miscellaneous', 'This bureau shall be located, and the commissioner shall reside at the capital of the state, and he shall annually make a written or printed report to the Governor of the state, to be laid before the General Assembly at each session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_5_0_1868', 'Alabama1868', '1868', '12', '5', '0', 'Miscellaneous', 'In case of the death, removal or resignation of the commissioner, the Governor, with the approval of the Senate, shall have power to appoint a commissioner for the unexpired term.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_1_0_1868', 'Alabama1868', '1868', '13', '1', '0', 'Corporations', 'Corporations may be formed under general laws, but shall not be created by special act, except for municipal purposes. All general laws and special acts passed pursuant to this section, may be altered, amended or repealed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_2_0_1868', 'Alabama1868', '1868', '13', '2', '0', 'Corporations', 'Dues from corporations shall be secured by such individual liabilities of the corporators or other means as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_3_0_1868', 'Alabama1868', '1868', '13', '3', '0', 'Corporations', 'Each stockholder in any corporation shall be liable to the amount of stock held or owned by him.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_4_0_1868', 'Alabama1868', '1868', '13', '4', '0', 'Corporations', 'The property of corporations now existing, or hereafter created, shall forever be subject to taxation the same as property of individuals, except corporations for educational and charitable purposes.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_5_0_1868', 'Alabama1868', '1868', '13', '5', '0', 'Corporations', 'No right of way shall be appropriated to the use of any corporation, until full compensation therefor be first made in money or secured by a deposit of money to the owner, irrespective of any benefit from any improvement proposed by such corporation, which compensation shall be ascertained by a jury of twelve men, in a court of record, as shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_6_0_1868', 'Alabama1868', '1868', '13', '6', '0', 'Corporations', 'The General Assembly shall not have power to establish or incorporate any bank or banking company, or monied institution, for the purpose of issuing bills of credit or bills payable to order or bearer, except under the conditions prescribed in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_7_0_1868', 'Alabama1868', '1868', '13', '7', '0', 'Corporations', 'No bank shall be established, otherwise than under a general banking law, as provided in the first section of this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_8_0_1868', 'Alabama1868', '1868', '13', '8', '0', 'Corporations', 'The General Assembly may enact a general banking law, which law shall provide for the registry and countersigning by the Governor of the State, of all paper credit designed to be created as money; and ample collateral security, convertible into specie, or the redemption of the same in gold or silver, shall be required, and such collateral security shall be under the control of such officer or officers as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_9_0_1868', 'Alabama1868', '1868', '13', '9', '0', 'Corporations', 'All bills or notes issued as money, shall be, at all times, redeemable in gold or silver, and no law shall be passed sanctioning, directly or indirectly, the suspension, by any bank or banking company, of specie payment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_10_0_1868', 'Alabama1868', '1868', '13', '10', '0', 'Corporations', 'Holders of bank notes shall be entitled, in case of insolvency, to preference of payment over all other creditors.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_11_0_1868', 'Alabama1868', '1868', '13', '11', '0', 'Corporations', 'Every bank or banking company shall be required to cease all banking operations within twenty years from the time of its organization, and promptly thereafter close its business.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_12_0_1868', 'Alabama1868', '1868', '13', '12', '0', 'Corporations', 'No bank shall receive, directly or indirectly, a greater rate of interest than shall be allowed by law to individuals for lending money.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_13_0_1868', 'Alabama1868', '1868', '13', '13', '0', 'Corporations', 'The state shall not be a stockholder in any bank, nor shall the credit of the state ever be given or lent to any banking company, association or corporation, except for the purpose of expediting the construction of railroads, or works of internal improvement, within this state, and the credit of the state shall, in no case, be given or lent without the approval of two-thirds of both houses of the General Assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_15_0_1868', 'Alabama1868', '1868', '13', '15', '0', 'Corporations', 'All corporations shall have the right to sue and shall be subject to be sued, in all courts, in like cases as natural persons.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_16_0_1868', 'Alabama1868', '1868', '13', '16', '0', 'Corporations', 'It shall be the duty of the General Assembly to provide for the organization of cities, and incorporated towns, and to restrict their power of taxation, assessment and contracting of debt.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_1_0_1868', 'Alabama1868', '1868', '14', '1', '0', 'Exemptions', 'The personal property of any resident of this state to the value of one thousand dollars, to be selected by such resident, shall be exempted from sale on execution, or other final process of any court, issued for the collection of any debt contracted after the adoption of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_2_0_1868', 'Alabama1868', '1868', '14', '2', '0', 'Exemptions', 'Every homestead, not exceeding eighty acres of land, and the dwelling and appurtenances thereon, to be selected by the owner thereof, and not in any town, city, or village, or in lieu thereof, at the option of the owner, any lot in the city, town or village, with the dwelling and appurtenances thereon, owned and occupied by any resident of this State, and not exceeding the value of two thousand dollars, shall be exempted from sale, on execution, or any other final process from a court, from any debt contracted after the adoption of this Constitution. Such exemption, however, shall not extend to any mortgage lawfully obtained, but such mortgage or other alienation of such homestead, by the owner thereof, if a married man, shall not be valid without the voluntary signature and assent of the wife of the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_3_0_1868', 'Alabama1868', '1868', '14', '3', '0', 'Exemptions', 'The homestead of a family, after the death of the owner thereof, shall be exempt from the payment of any debts contracted after the adoption of this Constitution, in all cases, during the minority of the children.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_4_0_1868', 'Alabama1868', '1868', '14', '4', '0', 'Exemptions', 'The provisions of sections one and two of this article shall not be so construed as to prevent a laborers lien for work done and performed for the person claiming such exemption, or a mechanics lien for work done on the premises.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_5_0_1868', 'Alabama1868', '1868', '14', '5', '0', 'Exemptions', 'If the owner of a homestead die, leaving a widow, but no children, the same shall be exempt, and the rents and profits thereof shall inure to her benefit.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_6_0_1868', 'Alabama1868', '1868', '14', '6', '0', 'Exemptions', 'The real and personal property of any female in this state, acquired before marriage, and all property, real and personal, to which she may afterwards be entitled by gift, grant, inheritance, or devise, shall be and remain the separate estate and property of such female, and shall not be liable for any debts, obligations, and engagements of her husband, and may be devised or bequeathed by her, the same as if she were a femme sole.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_1_0_1868', 'Alabama1868', '1868', '15', '1', '0', 'Oath of Office', 'All civil officers of this State, Legislative, Executive and Judicial, before they enter upon the execution of the duties of their respective offices, shall take the following oath: I, _______, do solemnly swear (or affirm) that I am not disfranchised by the Constitution of Alabama, or by the constitution or laws of the United States; that I will honestly and faithfully support and defend the Constitution and laws of the United States, the union of the States, and the Constitution and laws of the State of Alabama, so long as I remain a citizen thereof; and that I will honestly and faithfully discharge the duties of the office upon which I am about to enter to the best of my ability, So help me God.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_16_1_0_1868', 'Alabama1868', '1868', '16', '1', '0', 'Amendments', 'The General Assembly, whenever two-thirds of each house shall deem it necessary, may propose amendments to this Constitution, which proposed amendments shall be duly published in print at least three months before the next general election of representatives, for the consideration of the people; and it shall be the duty of the several returning officers at the next general election which shall be held for representatives, to open a poll for, and make a return to the Secretary of State for the time being, of the names of all those voting for representatives who have voted on such proposed amendments, and if thereupon it shall appear that a majority of all the citizens of the State voting for representatives have voted in favor of such proposed amendments, and two-thirds of each house of the next General Assembly shall, after such an election, and before another, ratify the same amendments, by yeas and nays, they shall be valid to all intents and purposes, as part of this Constitution: Provided, That the said proposed amendments shall, at each of the said sessions, have been read three times on three several days in each house. After the expiration of twelve months from the adoption of this Constitution, no Convention shall be held for the purpose of altering or amending the Constitution of this State, unless the question of Convention or no Convention shall be first submitted to a vote of all the electors, twenty-one years of age and upwards, and approved by a majority of the electors voting at said election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_1_0_1875', 'Alabama1875', '1875', '1', '1', '0', 'Bill of Rights', 'That all men are equally free and independent; that they are endowed by their Creator with certain inalienable rights; that among these are life, liberty, and the pursuit of happiness.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_2_0_1875', 'Alabama1875', '1875', '1', '2', '0', 'Bill of Rights', 'That all persons resident in this state, born in the United States, or naturalized, or who shall have legally declared their intention to become citizens of the United States, are hereby declared citizens of the State of Alabama, possessing equal civil and political rights.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_3_0_1875', 'Alabama1875', '1875', '1', '3', '0', 'Bill of Rights', 'That all political power is inherent in the people, and all free governments are founded on their authority, and instituted for their benefit; and that therefore they have, at all times, an inalienable and indefeasible right to change their form of government, in such manner as they may deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_4_0_1875', 'Alabama1875', '1875', '1', '4', '0', 'Bill of Rights', 'That no religion shall be established by law; that no preference shall be given by law to any religious sect, society, denomination, or mode of worship; that no one shall be compelled by law to attend any place of worship, nor to pay any tithes, taxes, or other rate, for building or repairing any place of worship, or for maintaining any minister or ministry; that no religious test shall be required as a qualification to any office or public trust under this state; and that the civil rights, privileges, and capacities of any citizen shall not be in any manner affected by his religious principles.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_5_0_1875', 'Alabama1875', '1875', '1', '5', '0', 'Bill of Rights', 'That any citizen may speak, write, and publish his sentiments on all subjects, being responsible for the abuse of that liberty.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_6_0_1875', 'Alabama1875', '1875', '1', '6', '0', 'Bill of Rights', 'That the people shall be secure in their persons, houses, papers, and possessions from unreasonable seizure or searches, and that no warrant shall issue to search any place, or to seize any person or thing, without probable cause, supported by oath or affirmation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_7_0_1875', 'Alabama1875', '1875', '1', '7', '0', 'Bill of Rights', 'That in all criminal prosecutions the accused has a right to be heard by himself and counsel, or either; to demand the nature and cause of the accusation; to have a copy thereof; to be confronted by the witnesses against him; to have compulsory process for obtaining witnesses in his favor; and, in all prosecutions by indictment, a speedy public trial by an impartial jury of the county or district in which the offense was committed; and that he shall not be compelled to give evidence against himself, nor be deprived of his life, liberty, or property, but by due process of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_8_0_1875', 'Alabama1875', '1875', '1', '8', '0', 'Bill of Rights', 'That no person shall be accused, or arrested, or detained, except in cases ascertained by law, and according to the forms which the same has prescribed; and no person shall be punished, but by virtue of a law established and promulgated prior to the offense, and legally applied.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_9_0_1875', 'Alabama1875', '1875', '1', '9', '0', 'Bill of Rights', 'That no person shall, for any indictable offense, be proceeded against criminally by information, except in cases arising in the militia and volunteer forces when in actual service, or, by leave of the court, for misfeasance, misdemeanor, extortion, and oppression in office, otherwise than is provided in this Constitution. Provided, that in cases of petit larceny, assault, assault and battery, affray, unlawful assemblies, vagrancy, and other misdemeanors, the general assembly may, by law, dispense with a grand jury, and authorize such prosecutions and proceedings before justices of the peace, or such other inferior courts as may be by law established.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_10_0_1875', 'Alabama1875', '1875', '1', '10', '0', 'Bill of Rights', 'That no person shall, for the same offense, be twice put in jeopardy of life or limb.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_11_0_1875', 'Alabama1875', '1875', '1', '11', '0', 'Bill of Rights', 'That no person shall be debarred from prosecuting or defending, before any tribunal in this state, by himself or counsel, any civil cause to which he is a party.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_12_0_1875', 'Alabama1875', '1875', '1', '12', '0', 'Bill of Rights', 'That the right of trial by jury shall remain inviolate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_13_0_1875', 'Alabama1875', '1875', '1', '13', '0', 'Bill of Rights', 'That in prosecutions for the publication of papers investigating the official conduct of officers or men in public capacity, or when the matter published is proper for public information, the truth thereof may be given in evidence; and that in all indictments for libel, the jury shall have the right to determine the law and facts, under the direction of the court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_14_0_1875', 'Alabama1875', '1875', '1', '14', '0', 'Bill of Rights', 'That all courts shall be open, and that every person, for any injury done him in his lands, goods, person, or reputation, shall have a remedy by due process of law; and right and justice shall be administered without sale, denial, or delay.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_15_0_1875', 'Alabama1875', '1875', '1', '15', '0', 'Bill of Rights', 'That the State of Alabama shall never be made defendant in any court of law or equity.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_16_0_1875', 'Alabama1875', '1875', '1', '16', '0', 'Bill of Rights', 'That excessive fines shall not be imposed, nor cruel or unusual punishments be inflicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_17_0_1875', 'Alabama1875', '1875', '1', '17', '0', 'Bill of Rights', 'That all persons shall, before conviction, be bailable by sufficient sureties, except for capital offenses when the proof is evident, or the presumption great; and that excessive bail shall not, in any case, be required.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_18_0_1875', 'Alabama1875', '1875', '1', '18', '0', 'Bill of Rights', 'That the privilege of the writ of habeas corpus shall not be suspended by the authorities of this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_19_0_1875', 'Alabama1875', '1875', '1', '19', '0', 'Bill of Rights', 'That treason against the state shall consist only in levying war against it, or adhering to its enemies, giving them aid and comfort; and that no person shall be convicted of treason, except on the testimony of two witnesses to the same overt act or his own confession in open court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_20_0_1875', 'Alabama1875', '1875', '1', '20', '0', 'Bill of Rights', 'That no person shall be attainted of treason by the general assembly; and that no conviction shall work corruption of blood, or forfeiture of estate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_21_0_1875', 'Alabama1875', '1875', '1', '21', '0', 'Bill of Rights', 'That no person shall be imprisoned for debt.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_22_0_1875', 'Alabama1875', '1875', '1', '22', '0', 'Bill of Rights', 'That no power of suspending laws shall be exercised, except by the general assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_23_0_1875', 'Alabama1875', '1875', '1', '23', '0', 'Bill of Rights', 'That no ex post facto law, or any law impairing the obligation of contracts, or making any irrevocable grants of special privileges or immunities, shall be passed by the general assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_24_0_1875', 'Alabama1875', '1875', '1', '24', '0', 'Bill of Rights', 'That the exercise of the right of eminent domain shall never be abridged, nor so construed as to prevent the general assembly from taking the property and franchises of incorporated companies, and subjecting them to public use the same as individuals; but private property shall not be taken or applied for public use, unless just compensation be first made therefor; nor shall private property be taken for private use, or for the use of corporations other than municipal, without the consent of the owner; provided, however, that the general assembly may, by law, secure to persons or corporations the right of way over the lands of other corporations, and by general laws provide for and regulate the exercise by persons and corporations of the rights herein reserved; but just compensation shall, in all cases, be first made to the owner. And provided, that the right of eminent domain shall not be so construed as to allow taxation, or forced subscription, for the benefit of railroads, or any other kind of corporations other than municipal, or for the benefit of any individual or association.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_25_0_1875', 'Alabama1875', '1875', '1', '25', '0', 'Bill of Rights', 'That all navigable waters shall remain forever public highways, free to the citizens of the state and of the United States, without tax, impost, or toll; and that no tax, toll, impost, or wharfage shall be demanded or received from the owner of any merchandise or commodity, for the use of the shores, or any wharf erected on the shores, or in or over the waters of any navigable stream, unless the same be expressly authorized by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_26_0_1875', 'Alabama1875', '1875', '1', '26', '0', 'Bill of Rights', 'That the citizens have a right in a peaceable manner to assemble together for the common good, and to apply to those invested with the power of government, for redress of grievances, or other purposes, by petition, address, or remonstrance.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_27_0_1875', 'Alabama1875', '1875', '1', '27', '0', 'Bill of Rights', 'That every citizen has a right to bear arms in defense of himself and the state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_28_0_1875', 'Alabama1875', '1875', '1', '28', '0', 'Bill of Rights', 'That no standing army shall be kept up without the consent of the general assembly; and, in that case, no appropriation for its support shall be made for a longer term than one year; and the military shall, in all cases, and at all times, be in strict subordination to the civil power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_29_0_1875', 'Alabama1875', '1875', '1', '29', '0', 'Bill of Rights', 'That no soldier shall, in time of peace, be quartered in any house, without the consent of the owner; nor in time of war, but in a manner to be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_30_0_1875', 'Alabama1875', '1875', '1', '30', '0', 'Bill of Rights', 'That no title of nobility, or hereditary distinction, privilege, honor, or emolument, shall ever be granted or conferred in this state; and that no office shall be created, the appointment to which shall be for a longer time than during good behavior.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_31_0_1875', 'Alabama1875', '1875', '1', '31', '0', 'Bill of Rights', 'That immigration shall be encouraged, emigration shall not be prohibited, and that no citizen shall be exiled.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_32_0_1875', 'Alabama1875', '1875', '1', '32', '0', 'Bill of Rights', 'That temporary absence from the state shall not cause a forfeiture of residence once obtained.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_33_0_1875', 'Alabama1875', '1875', '1', '33', '0', 'Bill of Rights', 'That no form of slavery shall exist in this state; and there shall be no involuntary servitude, otherwise than for the punishment of crime, of which the party shall have been duly convicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_34_0_1875', 'Alabama1875', '1875', '1', '34', '0', 'Bill of Rights', 'The right of suffrage shall be protected by laws regulating elections, and prohibiting, under adequate penalties, all undue influences from power, bribery, tumult, or other improper conduct.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_35_0_1875', 'Alabama1875', '1875', '1', '35', '0', 'Bill of Rights', 'The people of this state accept as final the established fact that from the federal Union there can be no secession of any state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_36_0_1875', 'Alabama1875', '1875', '1', '36', '0', 'Bill of Rights', 'Foreigners who are, or may hereafter become, bona fide residents of this state, shall enjoy the same right in respect to the possession, enjoyment, and inheritance of property, as native-born citizens.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_37_0_1875', 'Alabama1875', '1875', '1', '37', '0', 'Bill of Rights', 'That the sole object and only legitimate end of government is to protect the citizen in the enjoyment of life, liberty, and property; and when the government assumes other functions, it is usurpation and oppression.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_38_0_1875', 'Alabama1875', '1875', '1', '38', '0', 'Bill of Rights', 'No educational or property qualification for suffrage or office, nor any restraint upon the same on account of race, color, or previous condition of servitude, shall be made by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_39_0_1875', 'Alabama1875', '1875', '1', '39', '0', 'Bill of Rights', 'That this enumeration of certain rights shall not impair or deny others retained by the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_1_0_1875', 'Alabama1875', '1875', '2', '1', '0', 'Boundaries', 'The boundaries of this state are established and declared to be as follows, that is to say: Beginning at the point where the thirty-first degree of north latitude crosses the Perdido river; thence east, to the western boundary line of the State of Georgia; thence along said line to the southern boundary line of the State of Tennessee; thence west, along the southern boundary line of the State of Tennessee, crossing the Tennessee river, and on to the second intersection of said river by said line; thence up said river to the mouth of Big Bear creek; thence by a direct line to the northwest corner of Washington county, in this state, as originally formed; thence southerly, along the line of the State of Mississippi, to the Gulf of Mexico; thence eastwardly, including all islands within six leagues of the shore, to the Perdido river; thence up the said river to the beginning.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_2_0_1875', 'Alabama1875', '1875', '2', '2', '0', 'Boundaries', 'The boundaries of the several counties of this state, as heretofore established by law, are hereby ratified and confirmed. The general assembly may, by a vote of two-thirds of both houses thereof, arrange and designate boundaries for the several counties of this state, which boundaries shall not be altered, except by a like vote; but no new counties shall be hereafter formed of less extent than six hundred square miles; and no existing county shall be reduced to less extent than six hundred square miles, and no new county shall be formed which does not contain a sufficient number of inhabitants to entitle it to one representative, under the ratio of representation existing at the time of its formation, and leave the county or counties from which it is taken with the required number of inhabitants entitling such county or counties to separate representation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_1_0_1875', 'Alabama1875', '1875', '3', '1', '0', 'Separation of Powers', 'The powers of the government of the State of Alabama shall be divided into three distinct departments, each of which shall be confided to a separate body of magistracy, to wit: Those which are legislative, to one; those which are executive, to another; and those which are judicial, to another.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_2_0_1875', 'Alabama1875', '1875', '3', '2', '0', 'Separation of Powers', 'No person, or collection of persons, being of one of those departments, shall exercise any power properly belonging to either of the others, except in the instances hereinafter expressly directed or permitted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_1_0_1875', 'Alabama1875', '1875', '4', '1', '0', 'Legislative', 'The legislative power of this state shall be vested in a general assembly, which shall consist of a senate and house of representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_2_0_1875', 'Alabama1875', '1875', '4', '2', '0', 'Legislative', 'The style of the laws of this state shall be: Be it enacted by the general assembly of Alabama. Each law shall contain but one subject, which shall be clearly expressed in its title, except general appropriation bills, general revenue bills, and bills adopting a Code, digest, or revision of statutes; and no law shall be revived, amended, or the provisions thereof extended or conferred, by reference to its title only; but so much thereof as is revived, amended, extended, or conferred shall be reenacted and published at length.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_3_0_1875', 'Alabama1875', '1875', '4', '3', '0', 'Legislative', 'Senators and representatives shall be elected by the qualified electors, on the first Monday in August, eighteen hundred and seventy-six; and one-half of the senators and all the representatives shall be elected every two years thereafter, unless the general assembly shall change the time of holding elections. The terms of office of the senators shall be four years, and that of the representatives two years, commencing on the day after the general election, except as otherwise provided in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_4_0_1875', 'Alabama1875', '1875', '4', '4', '0', 'Legislative', 'Senators shall be at least twenty-seven years of age, and representatives twenty-one years of age; they shall have been citizens and inhabitants of this state for three years, and inhabitants of their respective counties or districts one year next before their election, if such county or district shall have been so long established; but if not, then of the county or district from which the same shall have been taken; and they shall reside in their respective counties or districts during their terms of service.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_5_0_1875', 'Alabama1875', '1875', '4', '5', '0', 'Legislative', 'The general assembly shall meet biennially, at the capitol, in the senate chamber and in the hall of the house of representatives (except in cases of destruction of the capitol, or epidemics, when the governor may convene them at such place in the state as he may deem best) on the day specified in this Constitution, or on such other day as may be prescribed by law; and shall not remain in session longer than sixty days at the first session held under this Constitution, nor longer than fifty days at any subsequent session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_6_0_1875', 'Alabama1875', '1875', '4', '6', '0', 'Legislative', 'The pay of the members of the general assembly shall be four dollars per day, and ten cents per mile in going to and returning from the seat of government, to be computed by the nearest usual route traveled.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_7_0_1875', 'Alabama1875', '1875', '4', '7', '0', 'Legislative', 'The general assembly shall consist of not more than thirty-three senators, and not more than one hundred members of the house of representatives; to be apportioned among the several districts and counties as prescribed in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_8_0_1875', 'Alabama1875', '1875', '4', '8', '0', 'Legislative', 'The senate, at the beginning of each regular session, and at such other times as may be necessary, shall elect one of its members president thereof; and the house of representatives, at the beginning of each regular session, shall elect one of its members as speaker; and the president of the senate and the speaker of the house of representatives shall hold their offices respectively until their successors are elected and qualified. Each house shall choose its own officers, and shall judge of the election, returns, and qualifications of its members.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_9_0_1875', 'Alabama1875', '1875', '4', '9', '0', 'Legislative', 'At the general election in the year eighteen hundred and seventy-six, senators shall be elected in the even numbered districts to serve for two years, and in the odd numbered districts to serve for four years, so that thereafter one-half the senators may be chosen biennially. Members of the house of representatives shall be elected at the general election every second year. The time of service of senators and representatives shall begin on the day after their election, except the terms of those elected in the year eighteen hundred and seventy-six, which shall not begin until the term of the present members shall have expired. Whenever a vacancy shall occur in either house, the governor for the time being shall issue a writ of election to fill such vacancy for the remainder of the term.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_10_0_1875', 'Alabama1875', '1875', '4', '10', '0', 'Legislative', 'A majority of each house shall constitute a quorum to do business; but a smaller number may adjourn from day to day, and may compel the attendance of absent members, in such manner, and under such penalties, as each house may provide.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_11_0_1875', 'Alabama1875', '1875', '4', '11', '0', 'Legislative', 'Each house shall have power to determine the rules of its proceedings, and to punish its members, or other persons, for contempt or disorderly behavior in its presence; to enforce obedience to its process; to protect its members against violence, or offers of bribes or corrupt solicitation; and, with the concurrence of two-thirds of either house, to expel a member, but not a second time for the same cause; and shall have all the powers necessary for the legislature of a free state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_12_0_1875', 'Alabama1875', '1875', '4', '12', '0', 'Legislative', 'A member of either house expelled for corruption shall not thereafter be eligible to either house, and punishment for contempt or disorderly behavior shall not bar an indictment for the same offense.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_13_0_1875', 'Alabama1875', '1875', '4', '13', '0', 'Legislative', 'Each house shall keep a journal of its proceedings, and cause the same to be published immediately after its adjournment, excepting such parts as, in its judgment, may require secrecy; and the yeas and nays of the members of either house on any question shall, at the desire of one-tenth of the members present, be entered on the journals. Any member of either house shall have liberty to dissent from, or protest against, any act or resolution which he may think injurious to the public or an individual, and have the reasons for his dissent entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_14_0_1875', 'Alabama1875', '1875', '4', '14', '0', 'Legislative', 'Members of the general assembly shall, in all cases, except treason, felony, violation of their oath of office, and breach of the peace, be privileged from arrest during their attendance at the session of their respective houses, and in going to and returning from the same; and for any speech or debate in either house, they shall not be questioned in any other place.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_15_0_1875', 'Alabama1875', '1875', '4', '15', '0', 'Legislative', 'The doors of each house shall be open, except on such occasions as, in the opinion of the house, may require secrecy.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_16_0_1875', 'Alabama1875', '1875', '4', '16', '0', 'Legislative', 'Neither house shall, without the consent of the other, adjourn for more than three days, nor to any other place than that in which they may be sitting.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_17_0_1875', 'Alabama1875', '1875', '4', '17', '0', 'Legislative', 'No senator or representative shall, during the term for which he shall have been elected, be appointed to any civil office of profit under this state, which shall have been created, or the emoluments of which shall have been increased during such term, except such offices as may be filled by election by the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_18_0_1875', 'Alabama1875', '1875', '4', '18', '0', 'Legislative', 'No person hereafter convicted of embezzlement of the public money, bribery, perjury, or other infamous crime, shall be eligible to the general assembly or capable of holding any office of trust or profit in this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_19_0_1875', 'Alabama1875', '1875', '4', '19', '0', 'Legislative', 'No law shall be passed except by bill, and no bill shall be so altered or amended on its passage through either house as to change its original purpose.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_20_0_1875', 'Alabama1875', '1875', '4', '20', '0', 'Legislative', 'No bill shall become a law until it shall have been referred to a committee of each house and returned therefrom.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_21_0_1875', 'Alabama1875', '1875', '4', '21', '0', 'Legislative', 'Every bill shall be read on three different days in each house; and no bill shall become a law unless, on its final passage, it be read at length, and the vote be taken by yeas and nays, the names of the members voting for and against the same be entered on the journals, and a majority of each house be recorded thereon as voting in its favor, except as otherwise provided in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_22_0_1875', 'Alabama1875', '1875', '4', '22', '0', 'Legislative', 'No amendment to bills by one house shall be concurred in by the other, except by a vote of a majority thereof, taken by yeas and nays, and the names of those voting for and against recorded upon the journals; and reports of committees of conference shall in like manner be adopted in each house.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_23_0_1875', 'Alabama1875', '1875', '4', '23', '0', 'Legislative', 'No special or local law shall be enacted for the benefit of individuals or corporations, in cases which are or can be provided for by a general law, or where the relief sought can be given by any court of this state; nor shall the operation of any general law be suspended by the general assembly for the benefit of any individual, corporation, or association.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_24_0_1875', 'Alabama1875', '1875', '4', '24', '0', 'Legislative', 'No local or special law shall be passed on a subject which can not be provided for by a general law, unless notice of the intention to apply therefor shall have been published in the locality where the matter or thing to be affected may be situated; which notice shall be at least twenty days prior to the introduction into the general assembly of such bill, and the evidence of such notice having been given shall be exhibited to the general assembly before such bill shall be passed; provided, that the provisions of this Constitution, as to special or local laws, shall not apply to public or educational institutions of or in this State, nor to industrial, mining, immigration, or manufacturing corporations or interests or corporations for constructing canals or improving navigable rivers or harbors of this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_25_0_1875', 'Alabama1875', '1875', '4', '25', '0', 'Legislative', 'The general assembly shall pass general laws, under which local and private interests shall be provided for and protected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_26_0_1875', 'Alabama1875', '1875', '4', '26', '0', 'Legislative', 'The general assembly shall have no power to authorize lotteries or gift enterprises for any purpose, and shall pass laws to prohibit the sale of lottery or gift enterprise tickets, or tickets in any scheme in the nature of a lottery, in this state; and all acts, or parts of acts, heretofore passed by the general assembly of this state, authorizing a lottery or lotteries, and all acts amendatory thereof, or supplemental thereto, are hereby avoided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_27_0_1875', 'Alabama1875', '1875', '4', '27', '0', 'Legislative', 'The presiding officer of each house shall, in the presence of the house over which he presides, sign all bills and joint resolutions passed by the general assembly, after the titles have been publicly read immediately before signing, and the fact of signing shall be entered on the journal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_28_0_1875', 'Alabama1875', '1875', '4', '28', '0', 'Legislative', 'The general assembly shall prescribe by law the number, duties, and compensation of the officers and employes of each house; and no payment shall be made from the state treasury, or be in any way authorized to any person, except to an acting officer or employe, elected or appointed in pursuance of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_29_0_1875', 'Alabama1875', '1875', '4', '29', '0', 'Legislative', 'No bill shall be passed giving any extra compensation to any public officer, servant, or employe, agent, or contractor, after the service shall have been rendered, or contract made; nor shall any officer of the state bind the state to the payment of any sum of money but by authority of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_30_0_1875', 'Alabama1875', '1875', '4', '30', '0', 'Legislative', 'All stationery, printing, paper, and fuel used in the legislative and other departments of government, shall be furnished, and the printing, binding, and distribution of laws, journals, department reports, and all other printing and binding, and repairing and furnishing the halls and rooms used for the meetings of the general assembly and its committees, shall be performed under contract, to be given to the lowest responsible bidder below a maximum price, and under such regulations as shall be prescribed by law; no member or officer of any department of the government shall be in any way interested in such contracts, and all such contracts shall be subject to the approval of the governor, state auditor, and state treasurer.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_31_0_1875', 'Alabama1875', '1875', '4', '31', '0', 'Legislative', 'All bills for raising revenue shall originate in the house of representatives; but the senate may propose amendments, as in other bills.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_32_0_1875', 'Alabama1875', '1875', '4', '32', '0', 'Legislative', 'The general appropriation bill shall embrace nothing but appropriations for the ordinary expenses of the executive, legislative, and judicial departments of the state, interest on the public debt, and for the public schools; all other appropriations shall be made by separate bills, each embracing but one subject.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_33_0_1875', 'Alabama1875', '1875', '4', '33', '0', 'Legislative', 'No money shall be paid out of the treasury, except upon appropriations made by law, and on warrants drawn by the proper officer in pursuance thereof; and a regular statement and account of receipts and expenditures of all public moneys shall be published annually, in such manner as may be by law directed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_34_0_1875', 'Alabama1875', '1875', '4', '34', '0', 'Legislative', 'No appropriation shall be made to any charitable or educational institution not under the absolute control of the state, other than normal schools, established by law for the professional training of teachers for the public schools of the state, except by a vote of two-thirds of all the members elected to each house.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_35_0_1875', 'Alabama1875', '1875', '4', '35', '0', 'Legislative', 'No act of the general assembly shall authorize the investment of any trust fund by executors, administrators, guardians, and other trustees, in the bonds or stock of any private corporation; and any such acts now existing are avoided, saving investments heretofore made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_36_0_1875', 'Alabama1875', '1875', '4', '36', '0', 'Legislative', 'The power to change the venue, in civil and criminal causes, is vested in the courts, to be exercised in such manner as shall be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_37_0_1875', 'Alabama1875', '1875', '4', '37', '0', 'Legislative', 'When the general assembly shall be convened in special session, there shall be no legislation upon subjects other than those designated in the proclamation of the governor calling such session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_38_0_1875', 'Alabama1875', '1875', '4', '38', '0', 'Legislative', 'No state office shall be continued or created for the inspection or measuring of any merchandise, manufacture, or commodity; but any county or municipality may appoint such officers, when authorized by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_39_0_1875', 'Alabama1875', '1875', '4', '39', '0', 'Legislative', 'No act of the general assembly changing the seat of government of the state shall become a law until the same shall have been submitted to the qualified electors of the state at a general election, and approved by a majority of such electors voting on the same; and such act shall specify the proposed new location.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_40_0_1875', 'Alabama1875', '1875', '4', '40', '0', 'Legislative', 'A member of the general assembly who shall corruptly solicit, demand, or receive, or consent to receive, directly or indirectly, for himself, for another, from any company, corporation, or person, any money, office, appointment, employment, reward, thing of value or enjoyment, or of personal advantage, or promise thereof, for his vote or official influence, or for withholding the same; or with an understanding, expressed or implied, that his vote or official action shall be in any way influenced thereby; or who shall solicit or demand any such money or other advantage, matter, or thing aforesaid, for another, as the consideration of his vote or official influence, or for withholding the same; or shall give or withhold his vote or influence, in consideration of the payment or promise of such money, advantage, matter, or thing, to another shall be guilty of bribery within the meaning of this Constitution, and shall incur the disabilities provided thereby for such offense, and such additional punishment as is, or shall be, provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_41_0_1875', 'Alabama1875', '1875', '4', '41', '0', 'Legislative', 'Any person who shall, directly or indirectly, offer, give, or promise any money or thing of value, testimonial, privilege, or personal advantage, to any executive or judicial officer, or member of the general assembly, to influence him in the performance of any of his public or official duties, shall be guilty of bribery, and be punished in such manner as shall be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_42_0_1875', 'Alabama1875', '1875', '4', '42', '0', 'Legislative', 'The offense of corrupt solicitation of members of the general assembly, or of public officers of this state, or of any municipal division thereof, and any occupation or practice of solicitation of such members, or officers, to influence their official action, shall be defined by law, and shall be punished by fine and imprisonment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_43_0_1875', 'Alabama1875', '1875', '4', '43', '0', 'Legislative', 'A member of the general assembly, who has a personal or private interest in any measure or bill, proposed or pending before the general assembly, shall disclose the fact to the house of which he is a member, and shall not vote thereon.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_44_0_1875', 'Alabama1875', '1875', '4', '44', '0', 'Legislative', 'In all elections by the general assembly, the members shall vote viva voce, and the votes shall be entered on the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_45_0_1875', 'Alabama1875', '1875', '4', '45', '0', 'Legislative', 'It shall be the duty of the general assembly to pass such laws as may be necessary and proper to decide differences by arbitrators, to be appointed by the parties who may choose that mode of adjustment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_46_0_1875', 'Alabama1875', '1875', '4', '46', '0', 'Legislative', 'It shall be the duty of the general assembly, at its first session after the ratification of this Constitution, and within every subsequent period of ten years, to make provision by law for the revision, digesting, and promulgation of the public statutes of this state of a general nature, both civil and criminal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_47_0_1875', 'Alabama1875', '1875', '4', '47', '0', 'Legislative', 'The general assembly shall pass such penal laws as they may deem expedient to suppress the evil practice of dueling.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_48_0_1875', 'Alabama1875', '1875', '4', '48', '0', 'Legislative', 'It shall be the duty of the general assembly to regulate by law the cases in which deductions shall be made from the salaries of public officers, for neglect of duty in their official capacities, and the amount of such deductions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_49_0_1875', 'Alabama1875', '1875', '4', '49', '0', 'Legislative', 'It shall be the duty of the general assembly to require the several counties of this state to make adequate provision for the maintenance of the poor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_50_0_1875', 'Alabama1875', '1875', '4', '50', '0', 'Legislative', 'The general assembly shall not have power to authorize any municipal corporation to pass any laws inconsistent with the general laws of this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_51_0_1875', 'Alabama1875', '1875', '4', '51', '0', 'Legislative', 'In the event of annexation of any foreign territory to this state, the general assembly shall enact laws extending to the inhabitants of the acquired territory all the rights and privileges which may be required by the terms of the acquisition, anything in this Constitution to the contrary notwithstanding.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_52_0_1875', 'Alabama1875', '1875', '4', '52', '0', 'Legislative', 'The general assembly shall not tax the property, real and personal, of the state, counties, or other municipal corporations, or cemeteries; nor lots in incorporated cities or towns, or within one mile of any city or town, to the extent of one acre, nor lots one mile or more distant from such cities or towns, to the extent of five acres, with the buildings thereon, when the same are used exclusively for religious worship, for schools, or for purposes purely charitable; nor such property, real or personal, to an extent not exceeding twenty-five thousand dollars in value, as may be used exclusively for agricultural or horticultural associations of a public character.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_53_0_1875', 'Alabama1875', '1875', '4', '53', '0', 'Legislative', 'The general assembly shall by law prescribe such rules and regulations as may be necessary to ascertain the value of personal and real property exempted from sale under legal process by this Constitution, and to secure the same to the claimant thereof as selected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_54_0_1875', 'Alabama1875', '1875', '4', '54', '0', 'Legislative', 'The state shall not engage in works of internal improvement, nor lend money or its credit in aid of such; nor shall the state be interested in any private or corporate enterprise, or lend money or its credit to any individual, association, or corporation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_55_0_1875', 'Alabama1875', '1875', '4', '55', '0', 'Legislative', 'The general assembly shall have no power to authorize any county, city, town, or other subdivision of this state, to lend its credit, or to grant public money or thing of value, in aid of, or to any individual, association, or corporation whatsoever, or to become a stockholder in any such corporation, association, or company, by issuing bonds, or otherwise.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_56_0_1875', 'Alabama1875', '1875', '4', '56', '0', 'Legislative', 'There can be no law of this state impairing the obligation of contracts by destroying or impairing the remedy for their enforcement; and the general assembly shall have no power to revive any right or remedy which may have become barred by lapse of time, or by any statute of this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_1_0_1875', 'Alabama1875', '1875', '5', '1', '0', 'Executive', 'The executive department shall consist of a governor, secretary of state, state treasurer, state auditor, attorney-general, and superintendent of education, and a sheriff for each county.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_2_0_1875', 'Alabama1875', '1875', '5', '2', '0', 'Executive', 'The supreme executive power of this state shall be vested in a chief magistrate, who shall be styled the governor of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_3_0_1875', 'Alabama1875', '1875', '5', '3', '0', 'Executive', 'The governor, secretary of state, state treasurer, state auditor, and attorney-general shall be elected by the qualified electors of this state, at the same time and places appointed for the election of members of the general assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_4_0_1875', 'Alabama1875', '1875', '5', '4', '0', 'Executive', 'The returns of every election for governor, secretary of state, state auditor, state treasurer, and attorney-general shall be sealed up and transmitted by the returning officers to the seat of government, directed to the speaker of the house of representatives, who shall, during the first week of the session to which said returns shall be made, open and publish them in the presence of both houses of the general assembly in joint convention. The person having the highest number of votes for either of said offices shall be declared duly elected; but if two or more shall have an equal and the highest number of votes for the same office, the general assembly, by joint vote, without delay, shall choose one of said persons for said office. Contested elections for governor, secretary of state, state auditor, state treasurer, and attorney-general, shall be determined by both houses of the general assembly, in such manner as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_5_0_1875', 'Alabama1875', '1875', '5', '5', '0', 'Executive', 'The governor, secretary of state, state treasurer, state auditor, and attorney-general shall hold their respective offices for the term of two years from the time of their installation in office, and until their successors shall be elected and qualified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_6_0_1875', 'Alabama1875', '1875', '5', '6', '0', 'Executive', 'The governor shall be at least thirty years of age when elected, and shall have been a citizen of the United States ten years, and a resident citizen of this state at least seven years next before the day of his election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_7_0_1875', 'Alabama1875', '1875', '5', '7', '0', 'Executive', 'The governor, secretary of state, state treasurer, state auditor, and attorney-general shall reside at the seat of government of this state during the time they continue in office, except in case of epidemics; and they shall receive compensation for their services, which shall be fixed by law, and which shall not be increased or diminished during the term for which they shall have been elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_8_0_1875', 'Alabama1875', '1875', '5', '8', '0', 'Executive', 'The governor shall take care that the laws be faithfully executed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_9_0_1875', 'Alabama1875', '1875', '5', '9', '0', 'Executive', 'The governor may require information in writing, under oath, from the officers of the executive department on any subject relating to the duties of their respective offices; and he may, at any time, require information in writing, under oath, from all officers and managers of state institutions, upon any subject relating to the condition, management, and expenses of their respective offices and institutions; and any such officer or manager who makes a false report, shall be guilty of perjury, and punished accordingly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_10_0_1875', 'Alabama1875', '1875', '5', '10', '0', 'Executive', 'The governor may, by proclamation, on extraordinary occasions, convene the general assembly at the seat of government, or at a different place, if, since their last adjournment, that shall have become dangerous from an enemy, or from infectious or contagious diseases; and he shall state specifically in such proclamation each matter concerning which the action of that body is deemed necessary.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_11_0_1875', 'Alabama1875', '1875', '5', '11', '0', 'Executive', 'The governor shall, from time to time, give to the general assembly information of the state of the government, and recommend to their consideration such measures as he may deem expedient; and, at the commencement of each session of the general assembly, and at the close of his term of office, give information, by written message, of the condition of the state; and he shall account to the general assembly, as may be prescribed by law, for all moneys received and paid out by him, from any funds subject to his order, with the vouchers therefor; and he shall, at the commencement of each regular session, present to the general assembly estimates of the amount of money required to be raised by taxation for all purposes.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_12_0_1875', 'Alabama1875', '1875', '5', '12', '0', 'Executive', 'The governor shall have power to remit fines and forfeitures, under such rules and regulations as may be prescribed by law, and after conviction, to grant reprieves, commutation of sentence, and pardons, except in cases of treason and impeachment; but pardons, in cases of murder, arson, burglary, rape, assault with intent to commit rape, perjury, forgery, bribery, and larceny, shall not relieve from civil and political disability, unless specifically expressed in the pardon. Upon conviction of treason, the governor may suspend the execution of the sentence, and report the same to the general assembly at the next regular session, when the general assembly shall either pardon, commute the sentence, direct its execution, or grant further reprieve. He shall communicate to the general assembly, at each regular session, each case of reprieve, commutation, or pardon granted, with his reasons therefor, stating the name and crime of the convict, the sentence, its date, and the date of the reprieve, commutation or pardon.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_13_0_1875', 'Alabama1875', '1875', '5', '13', '0', 'Executive', 'Every bill which shall have passed both houses of the general assembly, shall be presented to the governor; if he approve, he shall sign it; but, if not, he shall return it with his objections to that house in which it shall have originated, who shall enter the objections at large upon the journals; and the house to which such bill shall be returned shall proceed to reconsider it; if, after such reconsideration, a majority of the whole number elected to that house shall vote for the passage of such bill, it shall be sent, with the objections, to the other house, by which it shall likewise be reconsidered; if approved by a majority of the whole number elected to that house, it shall become a law; but in such case the votes of both houses shall be determined by yeas and nays; and the names of the members voting for or against the bill shall be entered upon the journals of each house respectively. If any bill shall not be returned by the governor within five days (Sundays excepted), after it shall have been presented to him, the same shall be a law, in like manner as if he had signed it, unless the general assembly, by their adjournment, prevent its return, in which case it shall not be a law. And every vote, order, or resolution, to which the concurrence of both houses may be necessary (except questions of adjournment, and of bringing on elections by the two houses, and of amending this Constitution), shall be presented to the governor, and, before the same shall take effect, be approved by him, or, being disapproved, shall be repassed by both houses according to the rules and limitations prescribed in the case of a bill.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_14_0_1875', 'Alabama1875', '1875', '5', '14', '0', 'Executive', 'The governor shall have power to disapprove of any item or items of any bill making appropriations of money, embracing distinct items; and the part or parts of the bill approved shall be the law, and the item or items disapproved shall be void, unless repassed according to the rules and limitations prescribed for the passage of other bills over the executive veto; and he shall, in writing, state specifically the item or items he disapproves.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_15_0_1875', 'Alabama1875', '1875', '5', '15', '0', 'Executive', 'In case of the impeachment of the governor, his removal from office, death, refusal to qualify, resignation, absence from the state, or other disability, the president of the senate shall exercise all the power and authority appertaining to the office of governor, until the time appointed for the election of governor shall arrive, or until the governor who is absent or impeached, shall return or be acquitted, or other disability be removed; and if during such vacancy in the office of governor the president of the senate shall be impeached, removed from office, refuse to qualify, die, resign, be absent from the state, or be under any other disability, the speaker of the house of representatives shall, in like manner, administer the government. If the governor shall be absent from the state over twenty days, the secretary of state shall notify the president of the senate, who shall enter upon the duties of governor; and if the governor and president of the senate shall both be absent from the state over twenty days, the secretary of state shall notify the speaker of the house of representatives; and in such case he shall enter upon and discharge the duties of governor, until the return of the governor or president of the senate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_16_0_1875', 'Alabama1875', '1875', '5', '16', '0', 'Executive', 'The president of the senate and speaker of the house of representatives shall, during the time they respectively administer the government, receive the same compensation which the governor would have received if he had been employed in the duties of his office; provided, that if the general assembly shall be in session during such absence, they, or either of them, shall receive no compensation as members of the general assembly while acting as governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_17_0_1875', 'Alabama1875', '1875', '5', '17', '0', 'Executive', 'No person shall, at one and the same time, hold the office of governor of this state and any other office, civil or military, either under this state, the United States, or any other state or government, except as otherwise provided in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_18_0_1875', 'Alabama1875', '1875', '5', '18', '0', 'Executive', 'The governor shall be commander-in-chief of the militia and volunteer forces of this state, except when they shall be called into the service of the United States, and he may call out the same to execute the laws, suppress insurrection, and repel invasion; but he need not command in person unless directed to do so by a resolution of the general assembly; and when acting in the service of the United States he shall appoint his staff, and the general assembly shall fix his rank.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_19_0_1875', 'Alabama1875', '1875', '5', '19', '0', 'Executive', 'No person shall be eligible to the office of secretary of state, state treasurer, state auditor, or attorney-general, unless he shall have been a citizen of the United States at least seven years, and shall have resided in this state at least five years next preceding his election, and shall be at least twenty-five years old when elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_20_0_1875', 'Alabama1875', '1875', '5', '20', '0', 'Executive', 'There shall be a great seal of the state, which shall be used officially by the governor; and the seal now in use shall continue to be used until another shall have been adopted by the general assembly. The said seal shall be called the Great Seal of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_21_0_1875', 'Alabama1875', '1875', '5', '21', '0', 'Executive', 'The secretary of state shall be the custodian of the seal of the state, and shall authenticate therewith all official acts of the governor, his approval of laws and resolutions excepted. He shall keep a register of the official acts of the governor, and, when necessary, shall attest them, and lay copies of the same, together with copies of all papers relative thereto, before either house of the general assembly, when required to do so, and shall perform such other duties as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_22_0_1875', 'Alabama1875', '1875', '5', '22', '0', 'Executive', 'All grants and commissions shall be issued in the name and by the authority of the State of Alabama, sealed with the great seal, and signed by the governor, and countersigned by the secretary of state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_23_0_1875', 'Alabama1875', '1875', '5', '23', '0', 'Executive', 'Should the office of secretary of state, state treasurer, state auditor, attorney-general, or superintendent of education become vacant for any cause specified in section fifteen of this article, the governor shall fill the vacancy, until the disability is removed, or a successor elected and qualified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_24_0_1875', 'Alabama1875', '1875', '5', '24', '0', 'Executive', 'The state treasurer, state auditor, and attorney-general shall perform such duties as may be prescribed by law. The state treasurer and state auditor shall, every year, at a time the general assembly may fix, make a full and complete report to the governor, showing all receipts and disbursements of revenue of every character, all claims audited and paid by the state, by items, and all taxes and revenue collected and paid into the treasury, and from what sources; and they shall make reports oftener on any matter pertaining to their office, if required to do so by the governor or the general assembly.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_25_0_1875', 'Alabama1875', '1875', '5', '25', '0', 'Executive', 'The state auditor, state treasurer, and secretary of state, shall not, after the expiration of the terms of those now in office, receive to their use any fees, costs, perquisites of office, or compensation other than their salaries as prescribed by law; and all fees that may be payable by law, for any services performed by either of such officers, shall be paid in advance into the state treasury.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_26_0_1875', 'Alabama1875', '1875', '5', '26', '0', 'Executive', 'A sheriff shall be elected in each county, by the qualified electors thereof, who shall hold his office for the term of four years, unless sooner removed, and shall be ineligible to such office as his own successor. Provided, that sheriffs elected on the first Monday in August, 1877, or at such other time as may be prescribed by law for the election in that year, shall hold their offices for the term of three years, and until their successors shall be elected and qualified. In the year 1880, at the general election for members to the general assembly, sheriffs shall be elected for four years as herein provided. Vacancies in the office of sheriff shall be filled by the governor, as in other cases; and the person appointed shall continue in office until the next general election in the county for sheriff, as provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_1_0_1875', 'Alabama1875', '1875', '6', '1', '0', 'Judicial', 'The judicial power of the state shall be vested in the senate sitting as a court of impeachment, a supreme court, circuit courts, chancery courts, courts of probate, such inferior courts of law and equity, to consist of not more than five members, as the general assembly may from time to time establish, and such persons as may be by law invested with powers of a judicial nature.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_2_0_1875', 'Alabama1875', '1875', '6', '2', '0', 'Judicial', 'Except in cases otherwise directed in the Constitution, the supreme court shall have appellate jurisdiction only, which shall be coextensive with the state, under such restrictions and regulations, not repugnant to this Constitution, as may from time to time be prescribed by law; provided, that said court shall have power to issue writs of injunction, habeas corpus, quo warranto, and such other remedial and original writs as may be necessary to give it a superintendence and control of inferior jurisdictions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_3_0_1875', 'Alabama1875', '1875', '6', '3', '0', 'Judicial', 'The supreme court shall be held at the seat of government, but if that shall become dangerous from any cause, it may adjourn to a different place.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_4_0_1875', 'Alabama1875', '1875', '6', '4', '0', 'Judicial', 'The state shall be divided by the general assembly into convenient circuits, not to exceed eight in number, unless increased by a vote of two-thirds of the members of each house of the general assembly, and no circuit shall contain less than three nor more than twelve counties; and for each circuit there shall be chosen a judge, who shall, for one year next preceding his election, and during his continuance in office, reside in the circuit for which he is elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_5_0_1875', 'Alabama1875', '1875', '6', '5', '0', 'Judicial', 'The circuit court shall have original jurisdiction in all matters, civil and criminal, within the state, not otherwise excepted in this Constitution; but in civil cases only when the matter or sum in controversy exceeds fifty dollars.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_6_0_1875', 'Alabama1875', '1875', '6', '6', '0', 'Judicial', 'A circuit court shall be held in each county in the state at least twice in every year, and the judges of the several circuits may hold courts for each other, when they deem it expedient, and shall do so when directed by law; provided, that the judges of the several circuit courts shall have power to issue writs of injunction returnable into courts of chancery.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_7_0_1875', 'Alabama1875', '1875', '6', '7', '0', 'Judicial', 'The general assembly shall have power to establish a court or courts of chancery, with original and appellate jurisdiction. The state shall be divided by the general assembly into convenient chancery divisions, not exceeding three in number, unless an increase shall be made by a vote of two-thirds of each house of the general assembly, taken by yeas and nays and entered upon the journals, and the divisions shall be divided into districts; and for each division there shall be a chancellor, who shall, at the time of his election or appointment, and during his continuance in office, reside in the division for which he shall have been elected or appointed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_8_0_1875', 'Alabama1875', '1875', '6', '8', '0', 'Judicial', 'A chancery court shall be held in each district, at a place to be fixed by law, at least once in each year; and the chancellors may hold courts for each other, when they deem it necessary.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_9_0_1875', 'Alabama1875', '1875', '6', '9', '0', 'Judicial', 'The general assembly shall have power to establish in each county within the state a court of probate, with general jurisdiction for the granting of letters testamentary and of administration, and for orphans business.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_10_0_1875', 'Alabama1875', '1875', '6', '10', '0', 'Judicial', 'The judges of the supreme court, circuit courts, and chancellors shall, at stated times, receive for their services a compensation which shall not be diminished during their official terms; but they shall receive no fees or perquisites, nor hold any office (except judicial offices) of profit or trust under this state or the United States, or any other power, during the term for which they have been elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_11_0_1875', 'Alabama1875', '1875', '6', '11', '0', 'Judicial', 'The supreme court shall consist of one chief justice and such number of associate justices as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_12_0_1875', 'Alabama1875', '1875', '6', '12', '0', 'Judicial', 'The chief justice and associate justices of the supreme court, judges of the circuit courts, and chancellors shall be elected by the qualified electors of the state, circuits, counties, and chancery divisions, for which such courts may be established, at such times as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_13_0_1875', 'Alabama1875', '1875', '6', '13', '0', 'Judicial', 'The judges of such inferior courts of law and equity as may be by law established, shall be elected or appointed in such mode as the general assembly may prescribe.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_14_0_1875', 'Alabama1875', '1875', '6', '14', '0', 'Judicial', 'The judges of the supreme court, circuit courts, and chancellors, and the judges of city courts, shall have been citizens of the United States and of this state for five years next preceding their election or appointment, and shall be not less than twenty-five years of age, and learned in law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_15_0_1875', 'Alabama1875', '1875', '6', '15', '0', 'Judicial', 'The chief justice and associate justices of the supreme court, circuit judges, chancellors, and probate judges, shall hold office for the term of six years, and until their successors are elected or appointed and qualified; and the right of such judges and chancellors to hold their offices for the full term hereby prescribed shall not be affected by any change hereafter made by law in any circuit, division, or county, or in the mode or time of election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_16_0_1875', 'Alabama1875', '1875', '6', '16', '0', 'Judicial', 'The judges of the supreme court shall, by virtue of their offices, be conservators of the peace throughout the state; the judges of the circuit courts, within their respective circuits, and the judges of the inferior courts, within their respective jurisdictions, shall, in like manner, be conservators of the peace.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_17_0_1875', 'Alabama1875', '1875', '6', '17', '0', 'Judicial', 'Vacancies in the office of any of the judges or chancellors of this state shall be filled by appointment by the governor; and such appointee shall hold his office for the unexpired term, and until his successor is elected or appointed and qualified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_18_0_1875', 'Alabama1875', '1875', '6', '18', '0', 'Judicial', 'If in any case, civil or criminal, pending in any circuit, chancery, or city court in this state, the presiding judge or chancellor shall, for any legal cause, be incompetent to try, hear, or render judgment in such cause, the parties or their attorneys of record, if it be a civil case, or the solicitor or other prosecuting officer, and the defendant or defendants, if it be a criminal case, may agree upon some disinterested person practicing in the court and learned in the law, to act as special judge or chancellor, to sit as a court, and to hear, decide, and render judgment in the same manner and to the same effect as a judge of the circuit or city court, or chancellor sitting as a court might do in such case. If the case be a civil one, and the parties or their attorneys of record do not agree, or if the case be a criminal one, and the prosecuting officer and the defendant or defendants do not agree upon a special judge or chancellor, or if either party in a civil cause is not represented in court, the clerk of the circuit or city court, or register in chancery, of the court in which said cause is pending, shall appoint the special judge or chancellor, who shall preside, try, and render judgment as in this section provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_19_0_1875', 'Alabama1875', '1875', '6', '19', '0', 'Judicial', 'The general assembly shall have power to provide for the holding of circuit and chancery courts in this state, when the judges or chancellors thereof fail to attend regular terms.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_20_0_1875', 'Alabama1875', '1875', '6', '20', '0', 'Judicial', 'No judge of any court of record in this state shall practice law in any of the courts of this state or of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_21_0_1875', 'Alabama1875', '1875', '6', '21', '0', 'Judicial', 'Registers in chancery shall be appointed by the chancellors of the divisions, and shall hold office during the term of the chancellor making such appointment; and such registers shall receive as compensation for their services only such fees and commissions as may be specifically prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_22_0_1875', 'Alabama1875', '1875', '6', '22', '0', 'Judicial', 'A clerk of the supreme court shall be appointed by the judges thereof, and shall hold office during the term of the judges making the appointment; and clerks of such inferior courts as may be established by law shall be appointed by the judges thereof, and shall hold office during the term of the judge making such appointment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_23_0_1875', 'Alabama1875', '1875', '6', '23', '0', 'Judicial', 'Clerks of the circuit court shall be elected by the qualified electors in each county for the term of six years. Vacancies in such office shall be filled by the governor for the unexpired term.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_24_0_1875', 'Alabama1875', '1875', '6', '24', '0', 'Judicial', 'The clerk of the supreme court and registers in chancery may be removed from office by the judges of the supreme court and chancellors, respectively, for cause, to be entered at length upon the records of the court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_25_0_1875', 'Alabama1875', '1875', '6', '25', '0', 'Judicial', 'A solicitor for each judicial circuit shall be elected by joint ballot of the general assembly, who shall be learned in the law, and who shall, at the time of his election, and during his continuance in office, reside in the circuit for which he is chosen, and whose term of office shall be for six years; provided, that the general assembly, at the first session thereof after the ratification of this Constitution, shall, by joint ballot, elect a solicitor for each judicial circuit of the state, whose term of office shall begin on Tuesday after the first Monday in November, eighteen hundred and seventy-six, and continue for four years; and provided, that the general assembly may, when necessary, provide for the election or appointment of county solicitors.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_26_0_1875', 'Alabama1875', '1875', '6', '26', '0', 'Judicial', 'There shall be elected by the qualified electors of each precinct of the counties not exceeding two justices of the peace and one constable. Such justices shall have jurisdiction in all civil cases wherein the amount in controversy does not exceed one hundred dollars, except in cases of libel, slander, assault and battery, and ejectment. In all cases tried before such justices the right of appeal, without prepayment of costs, shall be secured by law; provided, that the governor may appoint one notary public for each election precinct in counties, and one for each ward in cities of over five thousand inhabitants, who, in addition to the powers of notary, shall have and exercise the same jurisdiction as justices of the peace within the precincts and wards for which they are respectively appointed; and provided, that notaries public without jurisdiction may be appointed. The term of office of such justices and notaries public shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_27_0_1875', 'Alabama1875', '1875', '6', '27', '0', 'Judicial', 'An attorney-general shall be elected by the qualified electors of the state at the same time and places of election of members of the general assembly, whose term of office shall be for two years, and until his successor is elected and qualified. After his election he shall reside at the seat of government, and shall be the law officer of the state, and shall perform such duties as may be required of him by law. Sec. 28. The style of all process shall be The State of Alabama, and all prosecutions shall be carried on in the name and by the authority of the same, and shall conclude, Against the peace and dignity of the state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_1_0_1875', 'Alabama1875', '1875', '7', '1', '0', 'Impeachment', 'The governor, secretary of state, auditor, treasurer, attorney-general, superintendent of education, and judges of the supreme court may be removed from office for willful neglect of duty, corruption in office, habitual drunkenness, incompetency, or any offense involving moral turpitude while in office, or committed under color thereof, or connected therewith, by the senate, sitting as a court for that purpose, under oath or affirmation, on articles or charges preferred by the house of representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_2_0_1875', 'Alabama1875', '1875', '7', '2', '0', 'Impeachment', 'The chancellors, judges of the circuit courts, judges of the probate courts, solicitors of the circuits, and judges of inferior courts from which an appeal may be taken directly to the supreme court, may be removed from office for any of the causes specified in the preceding section, by the supreme court, under such regulations as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_3_0_1875', 'Alabama1875', '1875', '7', '3', '0', 'Impeachment', 'The sheriffs, clerks of the circuit, city, or criminal courts, tax collectors, tax assessors, county treasurers, coroners, justices of the peace, notaries public, constables, and all other county officers, mayors, and intendants of incorporated cities and towns in this state, may be removed from office for any of the causes specified in section one of this article, by the circuit, city, or criminal court of the county in which such officers hold their office, under such regulations as may be prescribed by law. Provided, that the right of trial by jury and appeal in such cases be secured.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_4_0_1875', 'Alabama1875', '1875', '7', '4', '0', 'Impeachment', 'The penalties in cases arising under the three preceding sections shall not extend beyond removal from office, and disqualification from holding office under the authority of this state, for the term for which he was elected or appointed; but the accused shall be liable to indictment, trial, and punishment, as prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_1_0_1875', 'Alabama1875', '1875', '8', '1', '0', 'Suffrage and Elections', 'Every male citizen of the United States, and every male person of foreign birth, who may have legally declared his intention to become a citizen of the United States before he offers to vote, who is twenty-one years old or upwards, possessing the following qualifications, shall be an elector, and shall be entitled to vote at any election by the people, except as hereinafter provided: First.--He shall have resided in the state at least one year immediately preceding the election at which he offers to votSecond.--He shall have resided in the county for three months, and in the precinct or ward for thirty days immediately preceding the election at which he offers to vote; provided, that the general assembly may prescribe a longer or shorter residence in any precinct in any county or in any ward in any incorporated city or town having a population of more than five thousand inhabitants, but in no case to exceed three months; and provided, that no soldier, sailor, or marine in the military or naval service of the United States, shall acquire a residence by being stationed in this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_2_0_1875', 'Alabama1875', '1875', '8', '2', '0', 'Suffrage and Elections', 'All elections by the people shall be by ballot, and all elections by persons in a representative capacity shall be viva voce.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_3_0_1875', 'Alabama1875', '1875', '8', '3', '0', 'Suffrage and Elections', 'The following classes shall not be permitted to register, vote, or hold office: First.--Those who shall have been convicted of treason, embezzlement of public funds, malfeasance in office, larceny, bribery, or other crime punishable by imprisonment in the penitentiarSecond. -Those who are idiots or insane.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_4_0_1875', 'Alabama1875', '1875', '8', '4', '0', 'Suffrage and Elections', 'Electors shall in all cases, except treason, felony, or breach of the peace, be privileged from arrest during their attendance at elections, or while going to or returning therefrom.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_5_0_1875', 'Alabama1875', '1875', '8', '5', '0', 'Suffrage and Elections', 'The general assembly shall pass laws, not inconsistent with this Constitution, to regulate and govern elections in this state, and all such laws shall be uniform throughout the state. The general assembly may, when necessary, provide by law for the registration of electors throughout the state, or in any incorporated city or town thereof, and when it is so provided, no person shall vote at any election unless he shall have registered as required by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_6_0_1875', 'Alabama1875', '1875', '8', '6', '0', 'Suffrage and Elections', 'It shall be the duty of the general assembly to pass adequate laws giving protection against the evils arising from the use of intoxicating liquors at all elections.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_7_0_1875', 'Alabama1875', '1875', '8', '7', '0', 'Suffrage and Elections', 'Returns of elections for all civil officers who are to be commissioned by the governor, except secretary of state, state auditor, state treasurer, and attorney-general, and for members of the general assembly, shall be made to the secretary of state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_1_0_1875', 'Alabama1875', '1875', '9', '1', '0', 'Apportionment', 'The whole number of senators shall be not less than one-fourth or more than one-third the whole number of representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_2_0_1875', 'Alabama1875', '1875', '9', '2', '0', 'Apportionment', 'The house of representatives shall consist of not more than one hundred members, who shall be apportioned by the general assembly among the several counties of the state, according to the number of inhabitants in them, respectively, as ascertained by the decennial census of the United States for the year eighteen hundred and eighty; which apportionment, when made, shall not be subject to alteration until the first session of the general assembly after the next decennial census of the United States shall have been taken.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_3_0_1875', 'Alabama1875', '1875', '9', '3', '0', 'Apportionment', 'It shall be the duty of the general assembly, at its first session after the taking of the decennial census of the United States in the year eighteen hundred and eighty, and after each subsequent decennial census, to fix by law the number of representatives, and apportion them among the several counties of the state; provided, that each county shall be entitled to at least one representative.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_4_0_1875', 'Alabama1875', '1875', '9', '4', '0', 'Apportionment', 'It shall be the duty of the general assembly, at its first session after the taking of the decennial census of the United States in the year eighteen hundred and eighty, and after each subsequent decennial census, to fix by law the number of senators, and to divide the state into as many senatorial districts as there are senators, which districts shall be as nearly equal to each other in the number of inhabitants as may be, and each shall be entitled to one senator, and no more; and which districts, when formed, shall not be changed until the next apportioning session of the general assembly after the next decennial census of the United States shall have been taken. No county shall be divided between two districts, and no district shall be made of two or more counties not contiguous to each other.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_5_0_1875', 'Alabama1875', '1875', '9', '5', '0', 'Apportionment', 'Should the decennial census of the United States, from any cause, not be taken, or if, when taken, the same as to this state is not full and satisfactory, the general assembly shall have power, at its first session after the time shall have elapsed for the taking of said census, to provide for an enumeration of all the inhabitants of this state, and once in each ten years thereafter, upon which it shall be the duty of the general assembly to make the apportionment of representatives and senators, as provided in this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_6_0_1875', 'Alabama1875', '1875', '9', '6', '0', 'Apportionment', 'Until the general assembly shall make an apportionment of representatives among the several counties, after the first decennial census of the United States, as herein provided, the counties of Autauga, Baldwin, Bibb, Blount, Calhoun, Chilton, Cherokee, Choctaw, Clarke, Clay, Cleburne, Coffee, Colbert, Conecuh, Coosa, Covington, Crenshaw, Dale, DeKalb, Elmore, Etowah, Escambia, Fayette, Franklin, Geneva, Henry, Lauderdale, Marion, Morgan, Monroe, Marshall, Randolph, Sanford, Shelby, St. Clair, Walker, Washington, and Winston shall each have one representative; the counties of Barbour, Bullock, Butler, Chambers, Greene, Hale, Jackson, Jefferson, Limestone, Lawrence, Lowndes, Lee, Macon, Marengo, Ferry, Pickens, Pike, Russell, Sumter, Talladega, Tallapoosa, Tuscaloosa, and Wilcox shall have each two representatives; the county of Madison shall have three representatives; the counties of Dallas and Montgomery shall have each four representatives; and the county of Mobile shall have five representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_7_0_1875', 'Alabama1875', '1875', '9', '7', '0', 'Apportionment', 'Until the general assembly shall divide the state into senatorial districts, as herein provided, the senatorial districts shall be as follows: First district, Lauderdale and Limestone; second district, Colbert and Lawrence; third district, Morgan, Winston, and Blount; fourth district, Madison; fifth district, Marshall, Jackson, and DeKalb; sixth district, Cherokee, Etowah, and St. Clair; seventh district, Calhoun and Cleburne; eighth district, Talladega and Clay; ninth district, Randolph and Chambers; tenth district, Macon and Tallapoosa; eleventh district, Bibb and Tuscaloosa; twelfth district, Franklin, Marion, Fayette, and Sanford; thirteenth district, Walker, Jefferson, and Shelby; fourteenth district, Greene and Pickens; fifteenth district, Coosa, Elmore, and Chilton; sixteenth district, Lowndes and Autauga; seventeenth district, Butler and Conecuh; eighteenth district, Ferry; nineteenth district, Choctaw, Clarke, and Washington; twentieth district, Marengo; twenty-first district, Monroe, Escambia, and Baldwin; twenty-second district, Wilcox; twenty-third district, Henry, Coffee, Dale, Geneva; twenty-fourth district, Barbour; twenty-fifth district, Pike, Crenshaw, and Covington; twenty-sixth district, Bullock; twenty-seventh district, Lee; twenty-eighth district, Montgomery; twenty-ninth district, Russell; thirtieth district, Dallas; thirty-first district, Sumter; thirty-second district, Hale; thirty-third district, Mobile.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_1_0_1875', 'Alabama1875', '1875', '10', '1', '0', 'Exemptions', 'The personal property of any resident of this state, to the value of one thousand dollars, to be selected by such resident, shall be exempted from sale on execution, or other process of any court, issued for the collection of any debt contracted since the thirteenth day of July, eighteen hundred and sixty-eight, or after the ratification of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_2_0_1875', 'Alabama1875', '1875', '10', '2', '0', 'Exemptions', 'Every homestead, not exceeding eighty acres, and the dwelling and appurtenances thereon, to be selected by the owner thereof, and not in any city, town, or village, or in lieu thereof, at the option of the owner, any lot in the city, town, or village, with the dwelling and appurtenances thereon, owned and occupied by any resident of this state, and not exceeding the value of two thousand dollars, shall be exempted from sale on execution, or any other process from a court, for any debt contracted since the thirteenth day of July, eighteen hundred and sixty-eight, or after the ratification of this Constitution. Such exemption, however, shall not extend to any mortgage lawfully obtained, but such mortgage or other alienation of such homestead, by the owner thereof, if a married man, shall not be valid without the voluntary signature and assent of the wife to the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_3_0_1875', 'Alabama1875', '1875', '10', '3', '0', 'Exemptions', 'The homestead of the family, after the death of the owner thereof shall be exempt from the payment of any debts contracted since the thirteenth day of July, one thousand eight hundred and sixty-eight, or after the ratification of this Constitution, in all cases, during the minority of the children.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_4_0_1875', 'Alabama1875', '1875', '10', '4', '0', 'Exemptions', 'The provisions of sections one and two of this article shall not be so construed as to prevent a laborers lien for work done and performed for the person claiming such exemption, or a mechanics lien for work done on the premises.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_5_0_1875', 'Alabama1875', '1875', '10', '5', '0', 'Exemptions', 'If the owner of a homestead die, leaving a widow, but no children, such homestead shall be exempt, and the rents and profits thereof shall inure to her benefit.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_6_0_1875', 'Alabama1875', '1875', '10', '6', '0', 'Exemptions', 'The real and personal property of any female in this state, acquired before marriage, and all property, real and personal, to which she may afterwards be entitled by gift, grant, inheritance, or devise, shall be and remain the separate estate and property of such female, and shall not be liable for any debts, obligations, and engagements of her husband, and may be devised or bequeathed by her, the same as if she were a femme sole.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_7_0_1875', 'Alabama1875', '1875', '10', '7', '0', 'Exemptions', 'The right of exemptions hereinbefore secured may be waived by an instrument in writing, and when such waiver relates to realty, the instrument must be signed by both the husband and the wife, and attested by one witness.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_1_0_1875', 'Alabama1875', '1875', '11', '1', '0', 'Taxation', 'All taxes levied on property in this state shall be assessed in exact proportion to the value of such property; provided, however, the general assembly may levy a poll-tax, not to exceed one dollar and fifty cents on each poll, which shall be applied exclusively in aid of the public school fund in the county so paying the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_2_0_1875', 'Alabama1875', '1875', '11', '2', '0', 'Taxation', 'No power to levy taxes shall be delegated to individuals or private corporations.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_3_0_1875', 'Alabama1875', '1875', '11', '3', '0', 'Taxation', 'After the ratification of this Constitution, no new debt shall be created against, or incurred by, this state, or its authority, except to repel invasion or suppress insurrection, and then only by a concurrence of two-thirds of the members of each house of the general assembly, and the vote shall be taken by yeas and nays and entered on the journals; and any act creating or incurring any new debt against this state, except as herein provided for, shall be absolutely void; provided, the governor may be authorized to negotiate temporary loans, never to exceed one hundred thousand dollars, to meet deficiencies in the treasury; and until the same is paid, no new loan shall be negotiated; provided, further, that this section shall not be so construed as to prevent the issuance of bonds in adjustment of existing state indebtedness.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_4_0_1875', 'Alabama1875', '1875', '11', '4', '0', 'Taxation', 'The general assembly shall not have the power to levy, in any one year, a greater rate of taxation than three-fourths of one per centum on the value of the taxable property within this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_5_0_1875', 'Alabama1875', '1875', '11', '5', '0', 'Taxation', 'No county in this state shall be authorized to levy a larger rate of taxation, in any one year, on the value of the taxable property therein, than one-half of one per centum; provided, that to pay debts existing at the ratification of this Constitution, an additional rate of one-fourth of one per centum may be levied and collected which shall be exclusively appropriated to the payment of such debts or the interest thereon; provided, further, that to pay any debt or liability now existing against any county, incurred for the erection of the necessary public buildings or other ordinary county purposes, or that may hereafter be created for the erection of necessary public buildings or bridges, any county may levy and collect such special taxes as may have been or may hereafter be authorized by law, which taxes so levied and collected shall be applied exclusively to the purposes for which the same were so levied and collected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_6_0_1875', 'Alabama1875', '1875', '11', '6', '0', 'Taxation', 'The property of private corporations, associations, and individuals of this state, shall forever be taxed at the same rate; provided, this section shall not apply to institutions or enterprises devoted exclusively to religious, educational, or charitable purposes.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_7_0_1875', 'Alabama1875', '1875', '11', '7', '0', 'Taxation', 'No city, town, or other municipal corporation other than provided for in this article, shall levy or collect a larger rate of taxation, in any one year, on the property thereof, than one-half of one per centum of the value of such property, as assessed for state taxation during the preceding year; provided, that for the payment of debts existing at the time of the ratification of this Constitution and the interest thereon, an additional rate of one per centum may be collected, to be applied exclusively to such indebtedness; and provided, this section shall not apply to the city of Mobile, which city may, until the first day of January, one thousand eight hundred and seventy-nine, levy a tax not to exceed the rate of one per centum, and from and after that time a tax not to exceed the rate of three-fourths of one per centum, to pay expenses of the city government, and may also, until the first day of January, one thousand eight hundred and seventy-nine, levy a tax not to exceed the rate of one per centum, and from and after that time, a tax not to exceed the rate of three-fourths one per centum, to pay existing indebtedness of said city, and the interest thereon.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_8_0_1875', 'Alabama1875', '1875', '11', '8', '0', 'Taxation', 'At the first session of the general assembly after the ratification of this Constitution, the salaries of the following officers shall be reduced at least twenty-five per centum, viz.: governor, secretary of state, state auditor, state treasurer, attorney-general, superintendent of education, judges of the supreme and circuit courts, and chancellors; and after said reduction, the general assembly shall not have the power to increase the same, except by a vote of a majority of all the members elected to each house, taken by yeas and nays, and entered on the journals; provided, this section shall not apply to any of said officers now in office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_9_0_1875', 'Alabama1875', '1875', '11', '9', '0', 'Taxation', 'The general assembly shall not have the power to require the counties or other municipal corporations to pay any charges which are now payable out of the state treasury.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_1_0_1875', 'Alabama1875', '1875', '12', '1', '0', 'Militia', 'All able-bodied male inhabitants of this state, between the ages of eighteen years and forty-five years, who are citizens of the United States, or have declared their intention to become such citizens, shall be liable to military duty in the militia of the state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_2_0_1875', 'Alabama1875', '1875', '12', '2', '0', 'Militia', 'The general assembly, in providing for the organization, equipment, and discipline of the militia, shall conform as nearly as practicable to the regulations for the government of the armies of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_3_0_1875', 'Alabama1875', '1875', '12', '3', '0', 'Militia', 'Each company and regiment shall elect its own company and regimental officers; but if any company or regiment shall neglect to elect such officers within the time prescribed by law, they may be appointed by the governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_4_0_1875', 'Alabama1875', '1875', '12', '4', '0', 'Militia', 'Volunteer organizations of infantry, cavalry, and artillery may be formed in such manner, and under such restrictions, and with such privileges, as may be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_5_0_1875', 'Alabama1875', '1875', '12', '5', '0', 'Militia', 'The militia and volunteer forces shall, in all cases, except treason, felony, and breach of the peace, be privileged from arrest during their attendance at musters, parades, and elections, and in going to and returning from the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_6_0_1875', 'Alabama1875', '1875', '12', '6', '0', 'Militia', 'The governor shall, except as otherwise provided herein, be commander-in-chief of the militia and volunteer forces of the state, except when in the service of the United States, and shall, with the advice and consent of the senate, appoint all general officers, whose terms of office shall be for four years. The governor, the generals, and regimental and battalion commanders shall appoint their own staffs, as may be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_7_0_1875', 'Alabama1875', '1875', '12', '7', '0', 'Militia', 'The general assembly shall provide for the safe keeping of the arms, ammunition, and accoutrements, military records, banners, and relics of the state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_8_0_1875', 'Alabama1875', '1875', '12', '8', '0', 'Militia', 'The officers and men of the militia and volunteer forces shall not be entitled to, or receive any pay, rations, or emoluments, when not in active service.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_1_0_1875', 'Alabama1875', '1875', '13', '1', '0', 'Education', 'The general assembly shall establish, organize, and maintain a system of public schools throughout the state, for the equal benefit of the children thereof between the ages of seven and twenty-one years; but separate schools shall be provided for the children of citizens of African descent.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_2_0_1875', 'Alabama1875', '1875', '13', '2', '0', 'Education', 'The principal of all funds arising from the sale or other disposition of lands or other property, which has been or may hereafter be granted or intrusted to this state, or given by the United States, for educational purposes, shall be preserved inviolate and undiminished; and the income arising therefrom shall be faithfully applied to the specific objects of the original grants or appropriations.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_3_0_1875', 'Alabama1875', '1875', '13', '3', '0', 'Education', 'All lands or other property given by individuals, or appropriated by the state for educational purposes, and all estates of deceased persons who die without leaving a will or heir, shall be faithfully applied to the maintenance of the public schools.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_4_0_1875', 'Alabama1875', '1875', '13', '4', '0', 'Education', 'The general assembly shall also provide for the levying and collection of an annual poll tax, not to exceed one dollar and fifty cents on each poll, which shall be applied to the support of the public schools in the counties in which it is levied and collected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_5_0_1875', 'Alabama1875', '1875', '13', '5', '0', 'Education', 'The income arising from the sixteenth section trust fund, the surplus revenue fund, until it is called for by the United States government, and the funds enumerated in sections three and four of this article, with such other moneys, to be not less than one hundred thousand dollars per annum, as the general assembly shall provide by taxation or otherwise, shall be applied to the support and maintenance of the public schools; and it shall be the duty of the general assembly to increase, from time to time, the public school fund, as the condition of the treasury and the resources of the state will admit.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_6_0_1875', 'Alabama1875', '1875', '13', '6', '0', 'Education', 'Not more than four per cent of all moneys raised, or which may hereafter be appropriated for the support of public schools, shall be used or expended otherwise than for the payment of teachers employed in such schools; provided, that the general assembly may, by a vote of two-thirds of each house, suspend the operation of this section.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_7_0_1875', 'Alabama1875', '1875', '13', '7', '0', 'Education', 'The supervision of the public schools shall be vested in a superintendent of education, whose powers, duties, term of office, and compensation shall be fixed by law. The superintendent of education shall be elected by the qualified voters of the state in such manner, and at such time, as shall be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_8_0_1875', 'Alabama1875', '1875', '13', '8', '0', 'Education', 'No money raised for the support of the public schools of the state shall be appropriated to, or used for, the support of any sectarian or denominational school.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_9_0_1875', 'Alabama1875', '1875', '13', '9', '0', 'Education', 'The State University and the Agricultural and Mechanical College shall each be under the management and control of a board of trustees. The board for the university shall consist of two members from the congressional district in which the university is located, and one from each of the other congressional districts in the state. The board for the Agricultural and Mechanical College shall consist of two members from the congressional district in which the college is located, and one from each of the other congressional districts in the state. Said trustees shall be appointed by the governor, by and with the advice and consent of the senate, and shall hold office for a term of six years, and until their successors shall be appointed and qualified. After the first appointment each board shall be divided into three classes, as nearly equal as may be. The seats of the first class shall be vacated at the expiration of two years, and those of the second class in four years, and those of the third class at the end of six years, from the date of appointment, so that one-third may be chosen biennially. No trustee shall receive any pay or emolument, other than his actual expenses incurred in the discharge of his duties as such. The governor shall be ex officio president, and the superintendent of education ex officio a member of each of said boards of trustees.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_10_0_1875', 'Alabama1875', '1875', '13', '10', '0', 'Education', 'The general assembly shall have no power to change the location of the State University, or the Agricultural and Mechanical College, as now established by law, except upon a vote of two-thirds of the members of the general assembly, taken by yeas and nays, and entered upon the journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_11_0_1875', 'Alabama1875', '1875', '13', '11', '0', 'Education', 'The provisions of this article, and of any act of the general assembly, passed in pursuance thereof, to establish, organize, and maintain a system of public schools throughout the state, shall apply to Mobile county only so far as to authorize and require the authorities designated by law to draw the portion of the funds to which said county will be entitled for school purposes, and to make reports to the superintendent of education, as may be prescribed by law. And all special incomes and powers of taxation, as now authorized by law for the benefit of public schools in said county, shall remain undisturbed until otherwise provided by the general assembly; provided, that separate schools for each race shall always be maintained by said school authorities.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_1_0_1875', 'Alabama1875', '1875', '14', '1', '0', 'Corporations', 'Private Corporations. Section 1. Corporations may be formed under general laws, but shall not be created by special act, except for municipal, manufacturing, mining, immigration, industrial, and educational purposes, or for constructing canals, or improving navigable rivers and harbors of this state, and in cases where, in the judgment of the general assembly, the objects of the corporation can not be attained under general laws. All general laws and special acts passed pursuant to this section may be altered, amended, or repealed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_2_0_1875', 'Alabama1875', '1875', '14', '2', '0', 'Corporations', 'All existing charters or grants of special or exclusive privileges, under which a bona fide organization shall not have taken place and business been commenced in good faith, at the time of the ratification of this Constitution, shall thereafter have no validity.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_3_0_1875', 'Alabama1875', '1875', '14', '3', '0', 'Corporations', 'The general assembly shall not remit the forfeiture of the charter of any corporation now existing, or alter or amend the same, or pass, any general or special law for the benefit of such corporation, other than in execution of a trust created by law or by contract, except upon the condition that such corporation shall thereafter hold its charter subject to the provisions of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_4_0_1875', 'Alabama1875', '1875', '14', '4', '0', 'Corporations', 'No foreign corporation shall do any business in this state without having at least one known place of business and an authorized agent or agents therein; and such corporation may be sued in any county where it does business by service of process upon an agent anywhere in this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_5_0_1875', 'Alabama1875', '1875', '14', '5', '0', 'Corporations', 'No corporation shall engage in any business other than that expressly authorized in its charter.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_6_0_1875', 'Alabama1875', '1875', '14', '6', '0', 'Corporations', 'No corporation shall issue stock or bonds except for money, labor done, or money or property actually received; and all fictitious increase of stock or indebtedness shall be void. The stock and bonded indebtedness of corporations shall not be increased, except in pursuance of general laws, nor without the consent of the persons holding the larger amount in value of stock, first obtained at a meeting to be held after thirty days notice given in pursuance of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_7_0_1875', 'Alabama1875', '1875', '14', '7', '0', 'Corporations', 'Municipal and other corporations and individuals invested with the privilege of taking private property for public use, shall make just compensation for the property taken, injured, or destroyed by the construction or enlargement of its works, highways, or improvements, which compensation shall be paid before such taking, injury, or destruction. The general assembly is hereby prohibited from depriving any person of an appeal from any preliminary assessment of damages against any such corporations or individuals made by viewers, or otherwise; and the amount of such damages in all cases of appeal shall, on the demand of either party, be determined by a jury according to law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_8_0_1875', 'Alabama1875', '1875', '14', '8', '0', 'Corporations', 'Dues from private corporations shall be secured by such means as may be prescribed by law; but in no case shall any stockholder be individually liable otherwise than for the unpaid stock owned by him or her.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_9_0_1875', 'Alabama1875', '1875', '14', '9', '0', 'Corporations', 'No corporation shall issue preferred stock without the consent of the owners of two-thirds of the stock of said corporation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_10_0_1875', 'Alabama1875', '1875', '14', '10', '0', 'Corporations', 'The general assembly shall have the power to alter, revoke, or amend any charter of incorporation now existing, and revocable at the ratification of this Constitution, or any that may hereafter be created, whenever, in their opinion, it may be injurious to the citizens of the state; in such manner, however, that no injustice shall be done to the corporators. No law hereafter enacted shall create, renew, or extend the charter of more than one corporation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_11_0_1875', 'Alabama1875', '1875', '14', '11', '0', 'Corporations', 'Any association or corporation organized for the purpose, or any individual, shall have the right to construct and maintain lines of telegraph within this state, and connect the same with other lines; and the general assembly shall, by general law of uniform operation, provide reasonable regulations to give full effect to this section. No telegraph company shall consolidate with or hold a controlling interest in the stock or bonds of any other telegraph company owning a competing line, or acquire, by purchase or otherwise, any other competing line of telegraph.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_12_0_1875', 'Alabama1875', '1875', '14', '12', '0', 'Corporations', 'All corporations shall have the right to sue, and shall be subject to be sued in all courts, in like cases as natural persons.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_13_0_1875', 'Alabama1875', '1875', '14', '13', '0', 'Corporations', 'The term corporation, as used in this article, shall be construed to include all joint stock companies, or any associations having any of the powers or privileges of corporations, not possessed by individuals or partnerships.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_14_0_1875', 'Alabama1875', '1875', '14', '14', '0', 'Corporations', 'The general assembly shall not have the power to establish or incorporate any bank or banking company, or moneyed institution, for the purpose of issuing bills of credit, or bills payable to order or bearer, except under the conditions prescribed in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_15_0_1875', 'Alabama1875', '1875', '14', '15', '0', 'Corporations', 'No banks shall be established otherwise than under a general banking law, nor otherwise than upon a specie basis.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_16_0_1875', 'Alabama1875', '1875', '14', '16', '0', 'Corporations', 'All bills or notes issued as money shall be at all times redeemable in gold or silver, and no law shall be passed sanctioning, directly or indirectly, the suspension by any bank or banking company of specie payment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_17_0_1875', 'Alabama1875', '1875', '14', '17', '0', 'Corporations', 'Holders of bank notes, and depositors who have not stipulated for interest, shall, for such notes and deposits, be entitled, in case of insolvency, to the preference of payment over all other creditors.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_18_0_1875', 'Alabama1875', '1875', '14', '18', '0', 'Corporations', 'Every bank or banking company shall be required to cease all banking operations within twenty years from the time of its organization (unless the general assembly shall extend the time), and promptly thereafter close its business; but shall have corporate capacity to sue and shall be liable to suit, until its affairs and liabilities are fully closed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_19_0_1875', 'Alabama1875', '1875', '14', '19', '0', 'Corporations', 'No bank shall receive, directly or indirectly, a greater rate of interest than shall be allowed by law to individuals for lending money.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_20_0_1875', 'Alabama1875', '1875', '14', '20', '0', 'Corporations', 'The state shall not be a stockholder in any bank, nor shall the credit of the state ever be given or loaned to any banking company, association, or corporation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_21_0_1875', 'Alabama1875', '1875', '14', '21', '0', 'Corporations', 'Railroads and Canals. Sec. 21. All railroads and canals shall be public highways, and all railroad and canal companies shall be common carriers. Any association or corporation organized for the purpose shall have the right to construct and operate a railroad between any points in this state, and to connect, at the state line, with railroads of other states. Every railroad company shall have the right with its road to intersect, connect with, or cross any other railroad, and shall receive and transport, each, the others freight, passengers, and cars, loaded or empty, without delay or discrimination.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_22_0_1875', 'Alabama1875', '1875', '14', '22', '0', 'Corporations', 'The general assembly shall pass laws to correct abuses and prevent unjust discrimination and extortion in the rates of freights and passenger tariffs on railroads, canals, and rivers in this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_23_0_1875', 'Alabama1875', '1875', '14', '23', '0', 'Corporations', 'No railroad or other transportation company shall grant free passes, or sell tickets or passes at a discount other than as sold to the public generally, to any member of the general assembly, or to any person holding office under this state or the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_24_0_1875', 'Alabama1875', '1875', '14', '24', '0', 'Corporations', 'No street passenger railway shall be constructed within the limits of any city or town without the consent of its local authorities.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_25_0_1875', 'Alabama1875', '1875', '14', '25', '0', 'Corporations', 'No railroad, canal, or other transportation company, in existence at the time of the ratification of this Constitution, shall have the benefit of any future legislation by general or special laws, other than in execution of a trust created by law, or by contract, except on the condition of complete acceptance of ail the provisions of this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_1_0_1875', 'Alabama1875', '1875', '15', '1', '0', 'Oath of Office', 'All members of the general assembly, and all officers, executive and judicial, before they enter upon the execution of the duties of their respective offices, shall take the following oath or affirmation, to wit: I,.........., solemnly swear (or affirm, as the case may be) that I will support the Constitution of the United States, and the Constitution of the State of Alabama, so long as I continue a citizen thereof; and that I will faithfully and honestly discharge the duties of the office upon which I am about to enter, to the best of my ability. So help me God. Which oath may be administered by the presiding officer of either house of the general assembly, or by any officer authorized by law to administer an oath.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_16_1_0_1875', 'Alabama1875', '1875', '16', '1', '0', 'Miscellaneous', 'No person holding an office of profit under the United States, except postmasters whose annual salary does not exceed two hundred dollars, shall, during his continuance in such office, hold any office of profit under this state; nor shall any person hold two offices of profit at one and the same time under this state, except justices of the peace, constables, notaries public, and commissioners of deeds.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_16_2_0_1875', 'Alabama1875', '1875', '16', '2', '0', 'Miscellaneous', 'It is made the duty of the general assembly to enact all laws necessary to give effect to the provisions of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_17_1_0_1875', 'Alabama1875', '1875', '17', '1', '0', 'Amendments', 'The general assembly may, whenever two-thirds of each house shall deem it necessary, propose amendments to this Constitution, which, having been read on three several days in each house, shall be duly published in such manner as the general assembly may direct, at least three months before the next general election for representatives, for the consideration of the people; and it shall be the duty of the several returning officers, at the next general election which shall be held for representatives, to open a poll for the vote of the qualified electors on the proposed amendments, and to make return of said vote to the secretary of state; and, if it shall thereupon appear that a majority of all qualified electors of the state, who voted at said election, voted in favor of the proposed amendments, said amendments shall be valid, to all intents and purposes, as parts of this Constitution; and the result of such election shall be made known by proclamation of the governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_17_2_0_1875', 'Alabama1875', '1875', '17', '2', '0', 'Amendments', 'No convention shall hereafter be held for the purpose of altering or amending the Constitution of this state, unless the question of convention or no convention shall first be submitted to a vote of all the electors of the state, and approved by a majority of those voting at said election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_1_0_1875', 'Alabama1875', '1875', '9003', '1', '0', 'Schedule', 'That all laws in force at the ratification of this Constitution, and not inconsistent therewith, shall remain in full force, until altered or repealed by the general assembly; and all rights, actions, prosecutions, claims, and contracts of this state, counties, individuals, or bodies corporate, not inconsistent with this Constitution, shall continue to be as valid as if this Constitution had not been ratified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_2_0_1875', 'Alabama1875', '1875', '9003', '2', '0', 'Schedule', 'That all bonds executed by or to any officer of this state, all recognizances, obligations, and all other instruments executed to this state, or any subdivision or municipality thereof, before the ratification of this Constitution, and all fines, taxes, penalties, and forfeitures due and owing to this state, or any subdivision, or to any municipality thereof; and all writs, suits, prosecutions, claims and causes of action, except as herein otherwise provided, shall continue and remain unaffected by the ratification of this Constitution. All indictments which may have been found, or which may hereafter be found, for any crime or offense committed before the ratification of this Constitution, shall be proceeded upon in the same manner as if this Constitution had not been ratified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_3_0_1875', 'Alabama1875', '1875', '9003', '3', '0', 'Schedule', 'That all the executive and judicial officers, and all other officers in this state, who shall have been elected at the election held in this state, on third day of November, eighteen hundred and seventy-four, or who may have been appointed since that time, and all members of the present general assembly, and all that may hereafter be elected members of the present general assembly, and all other officers holding office at the time of the ratification of this Constitution, except such as hold office under any act of the general assembly, shall continue in office and exercise the duties thereof until their respective terms shall expire, as provided by the present Constitution and laws of this state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_4_0_1875', 'Alabama1875', '1875', '9003', '4', '0', 'Schedule', 'This Constitution shall be submitted to the qualified electors of this state for ratification or rejection, as authorized and required by an act of the general assembly of this state, entitled An act to provide for the calling of a convention to revise and amend the Constitution of this state, approved nineteenth day of March, anno domini eighteen hundred and seventy-five.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_5_0_1875', 'Alabama1875', '1875', '9003', '5', '0', 'Schedule', 'If at said election the said Constitution shall be found to have been ratified by a majority of all the qualified electors voting at said election, the said new Constitution, so ratified, shall go into effect as the new Constitution of the State of Alabama within the time stated in the proclamation of the governor, and shall thereafter be binding and obligatory as such upon all the people of this state, according to the provisions of said act, approved nineteenth day of March, anno domini eighteen hundred and seventy-five.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_6_0_1875', 'Alabama1875', '1875', '9003', '6', '0', 'Schedule', 'That instead of the publication as required by section twelve of said act, the governor of the state is hereby authorized to take such steps as will give general publicity and circulation to this Constitution in as economical manner as practicable.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_7_0_1875', 'Alabama1875', '1875', '9003', '7', '0', 'Schedule', 'That all laws requiring an enumeration of the inhabitants of this state during the year eighteen hundred and seventy-five are hereby avoided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_8_0_1875', 'Alabama1875', '1875', '9003', '8', '0', 'Schedule', 'That the board of education of this state is hereby abolished.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_9_0_1875', 'Alabama1875', '1875', '9003', '9', '0', 'Schedule', 'The salaries of the executive and judicial, and all other officers of this state who may be holding office at the time of the ratification of this Constitution, and the pay of the present members of the general assembly, shall not be affected by the provisions of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_1_0_1901', 'Alabama1901', '1901', '1', '1', '0', 'Bill of Rights', 'That all men are equally free and independent; that they are endowed by their Creator with certain inalienable rights; that among these are life, liberty and the pursuit of happiness.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_2_0_1901', 'Alabama1901', '1901', '1', '2', '0', 'Bill of Rights', 'That all political power is inherent in the people, and all free governments are founded on their authority and instituted for their benefit; and that, therefore, they have at all times an inalienable and indefeasible right to change their form of government in such manner as they may deem expedient.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_3_0_1901', 'Alabama1901', '1901', '1', '3', '0', 'Bill of Rights', 'That no religion shall be established by law; that no preference shall be given by law to any religious sect, society, denomination or mode of worship; that no one shall be compelled by law to attend any place of worship; nor to pay any tithes, taxes, or other rates for building or repairing any place of worship, or for maintaining any minister or ministry; that no religious test shall be required as a qualification to any office or public trust under this State; and that the civil rights, privileges, and capacities of any citizen shall not be in any manner affected by his religious principles.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_4_0_1901', 'Alabama1901', '1901', '1', '4', '0', 'Bill of Rights', 'That no law shall ever be passed to curtail or restrain the liberty of speech or of the press; and any person may speak, write and publish his sentiments on-all subjects, being responsible for the abuse of that liberty.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_5_0_1901', 'Alabama1901', '1901', '1', '5', '0', 'Bill of Rights', 'That the people shall be secure in their persons, houses, papers and possessions from unreasonable seizures or searches, and that no warrants shall issue to search any place or to seize any person or thing without probable cause, supported by oath or affirmation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_6_0_1901', 'Alabama1901', '1901', '1', '6', '0', 'Bill of Rights', 'That in all criminal prosecutions the accused has a right to be heard b himself and counsel, or either; to demand the nature and cause of the accusation and to have a copy thereof; to be confronted by the witnesses against him; to have compulsory process for obtaining witnesses in his favor; to testify in all cases in his own behalf if he elects so to do; and in all prosecutions by indictment, a speedy public trial, by an impartial jury of the county or district in which the offense was committed; and he shall not be compelled to give evidence against himself, nor be deprived of life, liberty or property, except by due process of law; but the Legislature may, by a general law, provide for a change of venue at the instance of the defendant in all prosecutions by indictment, and such change of venue on application of the defendant, may be heard and determined without the personal presence of the defendant so applying therefor; provided, that at the time of the application for the change of venue, the defendant is imprisoned in jail or some legal place of confinement.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_7_0_1901', 'Alabama1901', '1901', '1', '7', '0', 'Bill of Rights', 'That no person shall be accused, or arrested, or detained, except in cases ascertained by law, and according to the form which the same has prescribed; and no person shall be punished but by virtue of a law established and promulgated prior to the offense and legally applied.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_8_0_1901', 'Alabama1901', '1901', '1', '8', '0', 'Bill of Rights', 'That no person shall for any indictable offense be proceeded against criminally by information, except in cases arising in the militia and volunteer forces when in actual service, or when assembled under arms as a military organization, or, by leave of the court, for misfeasance, misdemeanor, extortion and oppression in office, otherwise than is provided in this Constitution; provided, that in cases of misdemeanor, the Legislature may by law dispense with a Grand Jury and authorize such prosecutions and proceedings before Justices of the Peace or such other inferior courts as may be by law established.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_9_0_1901', 'Alabama1901', '1901', '1', '9', '0', 'Bill of Rights', 'That no person shall, for the same offense, be twice put in jeopardy of life or limb; but courts may, for reasons fixed by law, discharge juries from the consideration of any case, and no person shall gain any advantage by reason of such discharge of the jury.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_10_0_1901', 'Alabama1901', '1901', '1', '10', '0', 'Bill of Rights', 'That no person shall be barred from prosecuting or defending before any tribunal in this State, by himself or counsel, any civil cause to which he is a party.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_11_0_1901', 'Alabama1901', '1901', '1', '11', '0', 'Bill of Rights', 'That the right of trial by jury shall remain inviolate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_12_0_1901', 'Alabama1901', '1901', '1', '12', '0', 'Bill of Rights', 'That in all prosecutions for libel or for the publication of papers investigating the official conduct of officers or men in public capacity, or when the matter published is proper for public information, the truth thereof may be given in evidence; and that in all indictments for libel, the jury shall have the right to determine the law and the facts under the direction of the court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_13_0_1901', 'Alabama1901', '1901', '1', '13', '0', 'Bill of Rights', 'That all courts shall be open; and that every person for any injury done him in his lands, goods, person or reputation, shall have a remedy by due process of law; and right and justice shall be administered without sale, denial or delay.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_14_0_1901', 'Alabama1901', '1901', '1', '14', '0', 'Bill of Rights', 'That the State of Alabama, shall never be made a defendant in any court of law or equity.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_15_0_1901', 'Alabama1901', '1901', '1', '15', '0', 'Bill of Rights', 'That excessive fines shall not be imposed nor cruel or unusual punishment inflicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_16_0_1901', 'Alabama1901', '1901', '1', '16', '0', 'Bill of Rights', 'That all persons shall, before conviction, be bailable by sufficient sureties, except for capital offenses, when the proof is evident or the presumption great; and that excessive ball shall not in ally case be required.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_17_0_1901', 'Alabama1901', '1901', '1', '17', '0', 'Bill of Rights', 'That the privilege of the writ of habeas corpus shall not be suspended by the authorities of this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_18_0_1901', 'Alabama1901', '1901', '1', '18', '0', 'Bill of Rights', 'That treason against the State shall consist only in levying war against it, or adhering to its enemies, giving them aid and comfort; and that no person shall be convicted of treason, except on the testimony of two witnesses to the same overt let, or his own confession in open court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_19_0_1901', 'Alabama1901', '1901', '1', '19', '0', 'Bill of Rights', 'That no person shall be attainted of treason by the Legislature; and no conviction shall work corruption of blood or forfeiture of estate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_20_0_1901', 'Alabama1901', '1901', '1', '20', '0', 'Bill of Rights', 'That no person shall be imprisoned for debt.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_21_0_1901', 'Alabama1901', '1901', '1', '21', '0', 'Bill of Rights', 'That no power of suspending laws shall be exercised except by the Legislature.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_22_0_1901', 'Alabama1901', '1901', '1', '22', '0', 'Bill of Rights', 'That no ex post facto law, nor any law impairing the obligation of contracts, or making any irrevocable or exclusive grants of special privileges or immunities, shall be passed by the Legislature; and every grant of a franchise, privilege or immunity, shall forever remain subject to revocation, alteration or amendment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_23_0_1901', 'Alabama1901', '1901', '1', '23', '0', 'Bill of Rights', 'That the exercise of the right of eminent domain shall never be abridged nor so construed as to prevent the Legislature from taking the property and franchises of incorporated companies, and subjecting them to public use in the same manner in which the property and franchises of individuals are taken and subjected; but private property shall not be taken for, or applied to, public use, unless just compensation be first made therefor; nor shall private property be taken for private use, or for the use of corporations, other than municipal, without the consent of the owner; provided, however, the Legislature may by law secure to persons or corporations the right of way over the lands of other persons or corporations, and by general laws provide for and regulate the exercise by persons and corporations of the rights herein reserved; but just compensation shall in all cases be first made to the owner; and provided, that the right of eminent domain shall not be so construed as to allow taxation or forced subscription for the benefit of railroads or any other kind of corporations, other than municipal, or for the benefit of any individual or association.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_24_0_1901', 'Alabama1901', '1901', '1', '24', '0', 'Bill of Rights', 'That all navigable waters shall remain forever public highways, free to the citizens of the State and the United States, without tax, impost or toll; and that no tax, toll, impost or wharfage shall be demanded or received from the owner of any merchandise or commodity for the use of the shores or any wharf erected on the shores, or in or over the waters, of any navigable stream, unless the same be expressly authorized by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_25_0_1901', 'Alabama1901', '1901', '1', '25', '0', 'Bill of Rights', 'That the citizens have a right, in a peaceable manner, to assemble together for the common good, and to apply to those invested with the power of government for redress or grievances or other purposes, by petition, address or remonstrance.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_26_0_1901', 'Alabama1901', '1901', '1', '26', '0', 'Bill of Rights', 'That every citizen has a right to bear arms in defense of himself and the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_27_0_1901', 'Alabama1901', '1901', '1', '27', '0', 'Bill of Rights', 'That no standing army shall be kept up without the consent of the Legislature, and, in that case, no appropriation for its support shall be made for a longer term than one year; and the military shall, in all cases, and at all times, be in strict subordination to the civil power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_28_0_1901', 'Alabama1901', '1901', '1', '28', '0', 'Bill of Rights', 'That no soldier shall, in time of peace, be quartered in any house without the consent of the owner, nor, in time of war, but in a manner to be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_29_0_1901', 'Alabama1901', '1901', '1', '29', '0', 'Bill of Rights', 'That no title of nobility or hereditary distinction, privilege, honor or emolument, shall ever be granted or conferred in this State; and that no office shall be created, the appointment to which shall be for a longer time than during good behavior.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_30_0_1901', 'Alabama1901', '1901', '1', '30', '0', 'Bill of Rights', 'That immigration shall be encouraged; emigration shall not be prohibited, and no citizen shall be exiled.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_31_0_1901', 'Alabama1901', '1901', '1', '31', '0', 'Bill of Rights', 'That temporary absence from the State shall not cause a forfeiture of residence once obtained.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_32_0_1901', 'Alabama1901', '1901', '1', '32', '0', 'Bill of Rights', 'That no form of slavery shall exist in this State; and there shall not be any involuntary servitude, otherwise than for the punishment of crime, of which the party shall have been duly convicted.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_33_0_1901', 'Alabama1901', '1901', '1', '33', '0', 'Bill of Rights', 'The privilege of suffrage shall be protected by laws regulating elections and prohibiting, under adequate penalties, all undue influences from power, bribery, tumult or other improper conduct.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_34_0_1901', 'Alabama1901', '1901', '1', '34', '0', 'Bill of Rights', 'Foreigners who are, or may hereafter become, bona fide residents of this State, shall enjoy the same rights in respect to the possession, enjoyment and inheritance of property as native born citizens.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_35_0_1901', 'Alabama1901', '1901', '1', '35', '0', 'Bill of Rights', 'That the sole object and only legitimate end of government is to protect the citizen in the enjoyment of life, liberty and property, and when the government assumes other functions it is usurpation and oppression.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_1_36_0_1901', 'Alabama1901', '1901', '1', '36', '0', 'Bill of Rights', 'That this enumeration of certain rights shall not impair or deny others retained by the people; and, to guard against any encroachments on the rights herein retained, we declare that everything in this Declaration of Rights is excepted out of the general powers of government, and shall forever remain inviolate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_37_0_1901', 'Alabama1901', '1901', '2', '37', '0', 'Boundaries', 'The boundaries of this State are established and declared to be as follows, that is to say: Beginning at the point where the thirty-first degree of north latitude crosses the Perdido river; thence east, to the western boundary line of the State of Georgia; thence along said line to the southern boundary line of the State of Tennessee; thence west, along the southern boundary line of the State of Tennessee, crossing the Tennessee river, and on to the second intersection of said river by said line; thence tip said river to the mouth of Big Bear creek; thence by a direct line to the northwest corner of Washington county, in this State, as originally formed; thence Southwardly along the line of the State of Mississippi, to the Gulf of Mexico; thence eastwardly, including all islands within six leagues of the shore, to the Perdido river; thence up the said river to the beginning; provided that the limits and jurisdi ction of this State shall extend to and include any other land and territory hereafter acquired by contract or agreement with other States, or otherwise, although such land and territory are not included within the boundaries hereinbefore designated.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_38_0_1901', 'Alabama1901', '1901', '2', '38', '0', 'Boundaries', 'The boundaries of the several counties of this State, as they now exist, are hereby ratified and confirmed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_39_0_1901', 'Alabama1901', '1901', '2', '39', '0', 'Boundaries', 'The Legislature may by a vote of two-thirds of each House thereof arrange and designate boundaries for the several counties of this State, which boundaries shall not be altered, except by a like vote; but no new county shall be formed hereafter of less extent than six hundred square miles, and no existing county shall be reduced to less than six hundred square miles; and no new county shall be formed unless it shall contain a sufficient number of inhabitants to entitle it to one Representative under the ratio of representation existing at the time of its formation, and leave the county or counties from which it is taken with the required number of inhabitants to entitle such county or counties, each, to separate representation; provided, that out of the counties of Henry, Dale and Geneva a new county of less than six hundred square miles may be formed under the provisions of this article, so as to leave said counties of Henry, Dale and Geneva with not less than five hundred square miles each.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_40_0_1901', 'Alabama1901', '1901', '2', '40', '0', 'Boundaries', 'No county line shall be altered or changed, or, in the event of the creation of new counties, shall be established, so as to run within seven miles of the county court house of any old county.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_2_41_0_1901', 'Alabama1901', '1901', '2', '41', '0', 'Boundaries', 'No court house or county site shall be removed except by a majority vote of the qualified electors of said county, voting at an election held for such purpose, and when an election has once been held no other election shall be held for such purpose until the expiration of four years; provided, that the county site of Shelby county shall remain at Columbiana, unless removed by a vote of the people as provided for in an act entitled, An Act to provide for the permanent location of the county site of Shelby county, Alabama, by a vote of the qualified electors of said county, approved the 9th day of February, 1899, and the act amendatory thereof, approved the 20th day February, 1899, or by an election held under the provisions of this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_42_0_1901', 'Alabama1901', '1901', '3', '42', '0', 'Separation of Powers', 'The powers of the government of the State of Alabama shall be divided into three distinct departments, each of which shall be confided to a separate body of magistracy, to-wit: That which are legislative, to one; those which are executive, to another; and those which are judicial, to another.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_3_43_0_1901', 'Alabama1901', '1901', '3', '43', '0', 'Separation of Powers', 'In the government of this State, except in the instances in this Constitution hereinafter expressly directed or permitted, the legislative department shall never exercise the executive and judicial powers, or either of them; the executive shall never exercise the legislative and judicial powers, or either of them; the judicial shall never exercise the legislative and executive powers, or either of them; to the end that it may be a government of laws and not of men.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_44_0_1901', 'Alabama1901', '1901', '4', '44', '0', 'Legislative', 'The legislative power of this State shall be vested in a Legislature, which shall consist of a Senate and a House of Representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_45_0_1901', 'Alabama1901', '1901', '4', '45', '0', 'Legislative', 'The style of the laws of this State shall be: Be it enacted by the Legislature of Alabama, which need not be repeated, but the act shall be divided into sections for convenience, according to substance; and the sections designated merely by figures. Each law shall contain but one subject, which shall be clearly expressed in its title, except general appropriation bills, general revenue bills, and bills adopting a code, digest, or revision of statutes; and no law shall be revived, amended, or the provisions thereof extended or conferred, by reference to its title only; but so much thereof as is revived, amended, extended, or conferred, shall be re-enacted and published at length.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_46_0_1901', 'Alabama1901', '1901', '4', '46', '0', 'Legislative', 'Senators and Representatives shall be elected by the qualified electors on the first Tuesday after the first Monday in November, unless the Legislature shall change the time of holding elections, and in every fourth year thereafter. The terms of office of the Senators and Representatives shall commence on the day after the general election at which they are elected, and expire on the day after the general election held in the fourth year after their election, except as otherwise provided in this Constitution. At the general election in the year nineteen hundred and two all the Representatives, together with the Senators for the even numbered districts and for the thirty-fifth district, shall be elected. The terms of those Senators who represent the odd numbered districts under the law in force prior to the ratification of this Constitution are hereby extended until the day after the general election in the year nineteen hundred and six; and until the expiration of his terms as hereinbefore extended, each such Senator shall represent the district established by this Constitution bearing the number corresponding with that for which he was elected. In the year nineteen hundred and six, and in every fourth year thereafter, all the Senators and Representatives shall be elected. Whenever a vacancy shall occur in either House the Governor shall issue a writ of election to fill such vacancy for the remainder of the term.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_47_0_1901', 'Alabama1901', '1901', '4', '47', '0', 'Legislative', 'Senators shall be at least twenty-five years of age, and Representatives twenty-one years of age at the time of their election. They shall have been citizens and residents of this State for three years, and residents of their respective counties or districts for one year next before their election, if such county or district shall have been so long established; but if not, then of the county or district from which the same shall have been taken; and they shall reside in their respective counties or districts during their terms of office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_48_0_1901', 'Alabama1901', '1901', '4', '48', '0', 'Legislative', 'The Legislature shall meet quadrennially at the Capitol, in the Senate chamber, and in the Hall of the House of Representatives, on the second Tuesday in January next succeeding their election, or on such other day as may be prescribed by law; and shall not remain in session longer than sixty days at the first session held under this Constitution, nor longer than fifty days at any subsequent session. If at any time it should from any cause become impossible or dangerous for the Legislature to meet or remain at the Capitol or for the Senate to meet or remain in the Senate Chamber, or for the Representatives to meet or remain in the Hall of the House of Representatives, the Governor may convene the Legislature, or remove it, after it has convened, to some other place, or may designate some other place for the sitting of the respective Houses, or either of them, as necessity may require.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_49_0_1901', 'Alabama1901', '1901', '4', '49', '0', 'Legislative', 'The pay of the members of the Legislature shall be four dollars per day, and ten cents per mile in going to and returning from the seat of government, to be computed by the nearest usual route traveled.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_50_0_1901', 'Alabama1901', '1901', '4', '50', '0', 'Legislative', 'The Legislature shall consist of not more than thirty-five Senators, and not more than one hundred and five members of the House of Representatives, to be apportioned among the several districts and counties as prescribed in this Constitution; provided that in addition to the above number of Representatives each new county hereafter created shall be entitled to one Representative.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_51_0_1901', 'Alabama1901', '1901', '4', '51', '0', 'Legislative', 'The Senate, at the beginning of each regular session, and at such other times as may be necessary, shall elect one of its members president pro tem thereof, to preside over its deliberations in the absence of the Lieutenant-Governor; and the House of Representatives, at the beginning of each regular session, and at such other times as may be necessary, shall elect one of its members as Speaker; and the President of the Senate and the Speaker of the House of Representatives shall hold their offices, respectively, until their successors are elected and qualified. In case of the temporary disability of either of said presiding officers, the House to which he belongs may elect one of its members to preside over that House, and to perform all the duties of such officer during the continuance of his disability; and such temporary officer, while performing duty as such, shall receive the same compensation to which the permanent officer is entitled by law, and no other. Each House shall choose its own officers, and shall judge of the election, returns and qualifications of its members.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_52_0_1901', 'Alabama1901', '1901', '4', '52', '0', 'Legislative', 'A majority of each House shall constitute a quorum to do business; but a smaller number may adjourn from day to day and compel the attendance of absent members, in such manner, and under such penalties as each House may provide.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_53_0_1901', 'Alabama1901', '1901', '4', '53', '0', 'Legislative', 'Each House shall have power to determine the rules of its proceedings, and to punish its members and other persons, for contempt or disorderly behavior in its presence ; to enforce obedience to its processes; to protect its members against violence or offers of bribery or corrupt solicitation; and, with the concurrence of two-thirds of the House, to expel a member, but not a second time for the same offense; and the two Houses shall have all the powers necessary for the Legislature of a free State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_54_0_1901', 'Alabama1901', '1901', '4', '54', '0', 'Legislative', 'A member of the Legislature expelled for corruption shall not thereafter be eligible to either House; and punishment for contempt or disorderly behavior shall not bar an indictment for the same offense.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_55_0_1901', 'Alabama1901', '1901', '4', '55', '0', 'Legislative', 'Each House shall keep a Journal of its proceedings, and cause the same to be published immediately after its adjournment, excepting such parts as, in its judgment, may require secrecy; and the yeas and nays of the members of either House on any question shall, at the request of one-tenth of the members present, be entered on the Journal. Any member of either House shall have liberty to dissent from or protest against, any act or resolution which he may think injurious to the public, or to an individual, and have the reason for his dissent entered on the Journal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_56_0_1901', 'Alabama1901', '1901', '4', '56', '0', 'Legislative', 'Members of the Legislature shall in all cases, except treason, felony, violation of their oath of office, and breach of the peace, be privileged from arrest during their attendance at the session of their respective houses, and in going to and returning from the same; and for any speech or debate in either House they shall not be questioned in any other place.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_57_0_1901', 'Alabama1901', '1901', '4', '57', '0', 'Legislative', 'The doors of each House shall be opened except on such occasions as, in the opinion of the House, may require secrecy; but no person shall be admitted to the floor of either House while & same is in session, except members of the Legislature, officers and employees of the two Houses, the Governor and his secretaries, representatives of the press and other persons to whom either House, by unanimous vote, may extend the privileges of its floor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_58_0_1901', 'Alabama1901', '1901', '4', '58', '0', 'Legislative', 'Neither House shall, without consent of the other, adjourn for more than three days, nor to any other place than that in which they may be sitting, except as otherwise provided in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_59_0_1901', 'Alabama1901', '1901', '4', '59', '0', 'Legislative', 'No Senator or Representative shall, during the term for which he shall have been elected, be appointed to any office of profit under this State, which shall have been created, or the emoluments of which shall have been increased during such term, except such offices as may be filled by election by the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_60_0_1901', 'Alabama1901', '1901', '4', '60', '0', 'Legislative', 'No person convicted of embezzlement of the public money, bribery, perjury, or other infamous crime, shall be eligible to the Legislature or capable of holding any office of trust or profit in this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_61_0_1901', 'Alabama1901', '1901', '4', '61', '0', 'Legislative', 'No law shall be passed except by bill, and no bill shall be so altered or amended on its passage through either House as to change the original purpose.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_62_0_1901', 'Alabama1901', '1901', '4', '62', '0', 'Legislative', 'No bill shall become a law until it shall have been referred to a standing committee of each House, acted upon by such committee in session, and returned therefrom, which facts shall affirmatively appear upon the Journal of each House.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_63_0_1901', 'Alabama1901', '1901', '4', '63', '0', 'Legislative', 'Every bill shall be read on three different days in each House, and no bill shall become a law unless on its final passage it be read at length, and the vote to be taken by yeas and nays, the names of the members voting for and against the same be entered upon the Journal, and a majority of each House be recorded thereon as voting in its favor, except as otherwise provided in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_64_0_1901', 'Alabama1901', '1901', '4', '64', '0', 'Legislative', 'No amendment to bills shall be adopted except by a majority of the House wherein the same is offered, nor unless the amendment, with the names of those voting for and against the same, shall be entered at length on the Journal of the House in which the same is adopted; and no amendment to bills by one House shall be concurred in by the other, unless a vote be taken by yeas and nays, and the names of the members voting for and against the same be recorded at length on the Journal; and no report of a committee of conference shall be adopted in either House except upon a vote taken by yeas and nays and entered on the Journal as herein provided for the adoption of amendments.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_65_0_1901', 'Alabama1901', '1901', '4', '65', '0', 'Legislative', 'The Legislature shall have no power to authorize lotteries or gift enterprises for any purpose, and shall pass laws to prohibit the sale in this State of lottery or gift enterprise tickets, or tickets in any scheme in the nature of a lottery; and all acts or parts of acts heretofore passed by the Legislature of this State, authorizing a lottery or lotteries, and all acts amendatory thereof, or supplemental thereto, are hereby avoided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_66_0_1901', 'Alabama1901', '1901', '4', '66', '0', 'Legislative', 'The presiding officer of each House shall, in the presence of the House over which he presides, sign all bills and joint resolutions passed by the Legislature, after the same shall have been publicly read at length immediately before signing, and the fact of reading and signing shall be entered upon the Journal; but the reading at length may be dispensed with by a two-thirds vote of a quorum present, which fact shall also be entered on the Journal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_67_0_1901', 'Alabama1901', '1901', '4', '67', '0', 'Legislative', 'The Legislature shall prescribe by law the number, duties and compensation of the officers and employees of each House, and no payment shall be made from the State Treasury or be in any way authorized to any person except to an acting officer or employe elected or appointed in pursuance of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_68_0_1901', 'Alabama1901', '1901', '4', '68', '0', 'Legislative', 'The legislature shall have no power to grant, or to authorize or require any county or municipal authority to grant, nor shall any county or municipal authority have power to grant, any extra compensation, fee or allowance to any public officer, servant or employee, agent or contractor, after service shall have been rendered or contract made; nor to increase or decrease the fees and compensation of such officers, during their terms of office; nor shall any officer of the State bind the State to the payment of any sum. of money, but by authority of law; provided this section shall not apply to allowances, made by commissioners court, or boards of revenue to county officers for ex-officio services, nor prevent the Legislature, from increasing or diminishing at any time the allowance to sheriffs or other officers for feeding, transferring or guarding prisoners.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_69_0_1901', 'Alabama1901', '1901', '4', '69', '0', 'Legislative', 'All stationery, printing, paper and fuel used in the legislative and other departments of government, shall be furnished, and the printing, binding and distribution of laws, Journals, department reports, and all other printing, binding and repairing, and furnishing the halls and rooms used for the meeting of the Legislature and its committees, shall be performed, under contract, to be given to the lowest responsible bidder below a maximum price, and under such regulations as shall be prescribed by law; no member or officer of any department of the government shall be in any way interested in such contracts, and all such contracts shall be subject to the approval of the Governor, Auditor, and Treasurer.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_70_0_1901', 'Alabama1901', '1901', '4', '70', '0', 'Legislative', 'All bills for raising revenue shall originate in the House of Representatives. The Governor, Auditor and Attorney General shall, before each regular session of the Legislature, prepare a general revenue bill, to be submitted to the Legislature for its information, and the Secretary of State shall have printed for the use of the Legislature a sufficient number of copies of the bill so prepared, which the Governor shall transmit to the House of Representatives as soon as organized to be used or dealt with as that House may elect. The Senate may propose amendments to revenue bills. No revenue bill shall be passed during the last five days of the session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_71_0_1901', 'Alabama1901', '1901', '4', '71', '0', 'Legislative', 'The general appropriation bill shall embrace nothing but appropriations for the ordinary expenses of the Executive, Legislative and Judicial departments of the State, for interest on the public debt, and for the public schools. The salary of no officer or employe shall be increased in such bill, nor shall any appropriation be made therein for any officer or employe, unless his Employment and the amount of his salary have already been provided for by law. All other appropriations shall be made by separate bills, each embracing but one subject.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_72_0_1901', 'Alabama1901', '1901', '4', '72', '0', 'Legislative', 'No money shall be paid out of the Treasury except upon appropriations made by law, and on warrants drawn by the proper officer in pursuance thereof; and a regular statement and account of receipts an expenditures of all public moneys shall be published annually, in such manner as may be by law directed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_73_0_1901', 'Alabama1901', '1901', '4', '73', '0', 'Legislative', 'No appropriation shall be made to any charitable or educational institution not under the absolute control of the State, other than normal schools established by law for the professional training of teachers for the public schools of the State, except by a vote of two-thirds of all the members elected to each House.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_74_0_1901', 'Alabama1901', '1901', '4', '74', '0', 'Legislative', 'No act of the Legislature shall authorize the investment of any trust funds by executors, administrators, guardians or other trustees in the bonds or stocks of any private corporation; and any such acts now existing are avoided, saving investments heretofore made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_75_0_1901', 'Alabama1901', '1901', '4', '75', '0', 'Legislative', 'The power to change the venue in civil and criminal causes is vested in the courts, to be exercised in such manner as shall be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_76_0_1901', 'Alabama1901', '1901', '4', '76', '0', 'Legislative', 'When the Legislature shall be convened in special session, there shall be no legislation upon subjects other than those designated in the proclamation of the Governor calling such session, except by a vote of two-thirds of each House. Special sessions shall be limited to thirty days.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_77_0_1901', 'Alabama1901', '1901', '4', '77', '0', 'Legislative', 'No State office shall be continued or created for the inspection or measuring of any merchandise, manufacture or commodity; but any county or municipality may appoint such officers when authorized by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_78_0_1901', 'Alabama1901', '1901', '4', '78', '0', 'Legislative', 'No act of the Legislature changing the seat of government of the State shall become a law until the same shall have been submitted to the qualified electors of the State, at a general election, and approved by a majority of such electors voting on the same; and such act shall specify the proposed new location.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_79_0_1901', 'Alabama1901', '1901', '4', '79', '0', 'Legislative', 'A member of the Legislature who shall solicit, demand or receive or consent to receive, directly or indirectly, for himself or for another, from any company, corporation, association or person, any money, office, appointment, employment, reward, thing of value or enjoyment, or of personal advantage, or promise thereof, for his vote or official influence, or for withholding the same; or with an understanding, expressed or implied, that his vote, or official action, shall be in any way influenced thereby; or who shall solicit or demand any such money or other advantage, matter or thing aforesaid, for another as the consideration for his vote, or influence, or for withholding the same; or shall give or withhold his vote or influence, in consideration of the payment or promise of such money, advantage, matter or thing to another, shall be guilty of bribery within the meaning of this Constitution, and shall incur the disabilities and penalties provided thereby for such offense, and such additional punishment as is or shall be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_80_0_1901', 'Alabama1901', '1901', '4', '80', '0', 'Legislative', 'Any person who shall, directly or indirectly, offer, give or promise any money, or thing of value, testimonial, privilege or personal advantage, to any executive or judicial officer or member of the. Legislature, to influence him in the performance of any of his public or official duties, shall be guilty of bribery, and be punished in such manner as may be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_81_0_1901', 'Alabama1901', '1901', '4', '81', '0', 'Legislative', 'The offense of corrupt solicitation of members of the Legislature, or of public officers of this State, or of any municipal division thereof, and any occupation or practice of solicitation of such members or officers, to influence their official action, shall be defined by law, and shall be punished by fine and imprisonment in the penitentiary; and the Legislature shall provide for the trial and punishment of the offenses enumerated in the two preceding sections, and shall require the judges to give the same specially in charge to the grand juries in all the counties of this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_82_0_1901', 'Alabama1901', '1901', '4', '82', '0', 'Legislative', 'A member of the Legislature who has a personal or private interest in any measure or bill proposed or pending before the Legislature, shall disclose the fact to the House of which he is a member, and shall not vote thereon.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_83_0_1901', 'Alabama1901', '1901', '4', '83', '0', 'Legislative', 'In all elections by the Legislature the members shall vote viva voce, and the votes shall be entered on the Journal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_84_0_1901', 'Alabama1901', '1901', '4', '84', '0', 'Legislative', 'It shall be the duty of the Legislature to pass such laws as may be necessary and proper to decide differences by arbitrators to appointed by the parties who may choose that mode of adjustment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_85_0_1901', 'Alabama1901', '1901', '4', '85', '0', 'Legislative', 'It shall be the duty of the Legislature, at its first session after the ratification of this Constitution, and within every subsequent period of twelve years, to make provision by law for revising, digesting and promulgating the public statues of this State of a general nature, both civil and criminal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_86_0_1901', 'Alabama1901', '1901', '4', '86', '0', 'Legislative', 'The Legislature shall. pass such penal laws as it may deem expedient to suppress the evil practice of dueling.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_87_0_1901', 'Alabama1901', '1901', '4', '87', '0', 'Legislative', 'It shall be the duty of the Legislature to regulate by law the cases in which deduction shall be made from the salaries or compensation of public officers for neglect of duty in their official capacities, and the amount of such deduction.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_88_0_1901', 'Alabama1901', '1901', '4', '88', '0', 'Legislative', 'It shall be the duty of the Legislature to require the several counties of this State to make adequate provision for maintenance of the poor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_89_0_1901', 'Alabama1901', '1901', '4', '89', '0', 'Legislative', 'The Legislature shall not have power to authorize any municipal corporation to pass any laws inconsistent with the general laws of this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_90_0_1901', 'Alabama1901', '1901', '4', '90', '0', 'Legislative', 'In the event of the annexation of any foreign territory to this State, the Legislature shall enact laws extending to the inhabitants of the acquired territory all the rights and privileges which may be required by the terms of acquisition not inconsistent with this Constitution. Should the State purchase such foreign territory, the Legislature, with the approval of the Governor, shall be authorized to expend any money in the Treasury not otherwise appropriated, and, if necessary, to provide also for the issuance of State bonds, to pay for the purchase of such foreign territory.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_91_0_1901', 'Alabama1901', '1901', '4', '91', '0', 'Legislative', 'The Legislature shall not tax the property, real or personal, of the State, counties or other municipal corporations, or cemeteries; nor lots in incorporated cities or towns, or within one mile of any city or town to the extent of one acre; nor lots one mile or more distant from such cities or towns, to the extent of five acres, with the buildings thereon, when same are used exclusively for religious worship, for schools, or for purposes purely charitable.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_92_0_1901', 'Alabama1901', '1901', '4', '92', '0', 'Legislative', 'The Legislature shall by law prescribe such rules and regulations as may be necessary to ascertain the value of real and personal property exempted from sale under legal process by this Constitution, and to secure the same to the claimant thereof as selected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_93_0_1901', 'Alabama1901', '1901', '4', '93', '0', 'Legislative', 'The State shall not engage in works of internal improvement, nor lend money or its credit in aid of such; nor shall the State be interested in any private or corporate enterprise, or lend money or its credit to any individual, association or corporation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_94_0_1901', 'Alabama1901', '1901', '4', '94', '0', 'Legislative', 'The Legislature shall not have power to authorize any county, city, town, or other subdivision of this State to lend its credit, or to grant public money or thing of value in aid of, or to, any individual, association or corporation whatsoever, or to become a stockholder in any such corporation, association or company, by issuing bonds or otherwise.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_95_0_1901', 'Alabama1901', '1901', '4', '95', '0', 'Legislative', 'There can be no law of this State impairing the obligation of contracts by destroying or impairing the remedy for their enforcement; and the Legislature shall have no power to revive any right or remedy which may have become barred by lapse of time, or by any statute of this State. After suit has been commenced on any cause of action, the Legislature shall have no power to take away such cause of action, or destroy any existing defense to such suit.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_96_0_1901', 'Alabama1901', '1901', '4', '96', '0', 'Legislative', 'The Legislature shall not enact any law not applicable to all the counties in the State, regulating costs and charges of courts, or fees, commissions or allowances of public officers.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_97_0_1901', 'Alabama1901', '1901', '4', '97', '0', 'Legislative', 'The Legislature shall not authorize payment to any person of the salary of a deceased officer beyond the date of his death.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_98_0_1901', 'Alabama1901', '1901', '4', '98', '0', 'Legislative', 'The Legislature shall not retire any officer on pay, or part pay, or make any grant to such retiring officer.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_99_0_1901', 'Alabama1901', '1901', '4', '99', '0', 'Legislative', 'Lands belonging to or under the control of the State shall never be donated directly or indirectly to private corporations, associations, or individuals, or railroad companies; nor shall such lands be sold to corporations or associations for a less price than that for which they are subject to sale to individuals; provided, that nothing contained in this section shall prevent the Legislature from granting a right of way, not exceeding one hundred and twenty-five feet in width, as a mere easement, for railroads or telegraph or telephone lines across State lands, and the Legislature shall never dispose of the land covered by such right of way except subject to such easement.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_100_0_1901', 'Alabama1901', '1901', '4', '100', '0', 'Legislative', 'No obligation or liability of any person, association or corporation held or owned by this State, or by any county or other municipality thereof, shall ever be remitted, released or postponed, or in any way diminished, by the Legislature; nor shall such liability or obligation be extinguished except by payment thereof; nor shall such liability or obligation be exchanged or transferred except upon payment of its face value; provided, that this section shall not prevent the Legislature from providing by general law for the compromise of doubtful claims.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_101_0_1901', 'Alabama1901', '1901', '4', '101', '0', 'Legislative', 'No State or county official shall, at any time during his term of office, accept either directly or indirectly any fee, money, office, appointment, employment, reward or thing value, or of personal advantage, or the promise thereof, to lobby for or against any measure pending before the Legislature, or to give or withhold his influence to secure the passage or defeat of any such measure.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_102_0_1901', 'Alabama1901', '1901', '4', '102', '0', 'Legislative', 'The Legislature shall never pass any law to authorize or legalize any marriage between any white person and a negro, or a descendant of a negro.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_103_0_1901', 'Alabama1901', '1901', '4', '103', '0', 'Legislative', 'The Legislature shall provide by law for the regulation, prohibition or reasonable restraint of common carriers, partnerships, associations, trusts, monopolies, and combinations of capital, so as to prevent them or any of them from making scarce articles of necessity, trade or commerce, or from increasing unreasonably the cost thereof to the consumer, or preventing reasonable competition in any calling, trade or business.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_104_0_1901', 'Alabama1901', '1901', '4', '104', '0', 'Legislative', 'The Legislature shall not pass a special, private or local law in any of the following cases: (1) Granting a divorce; (2) Relieving any minor of the disabilities of non-age; (3) Changing the name of any corporation, association, or individual; (4) Providing for the adopting or legitimizing of any child; (5) Incorporating a city, town or village; (6) Granting a charter to any corporation, association, or individual; (7) Establishing rules of descent or distribution; (8) Regulating the time within which a civil or criminal action may be begun; (9) Exempting any individual, private corporation or association from the operation of any general law; (10) Providing for the sale of the property of any individual or estate; (11) Changing or locating a county seat (12) Providing for a change of venue in any case; (13) Regulating the rate of interest; (14) Fixing the punishment of crime; (15) Regulating either the assessment or collection of taxes, except in connection with the readjustment, renewal, or extension of existing municipal indebtedness created prior to the ratification of the Constitution of eighteen hundred and seventy-five; (16) Giving effect to an invalid will, deed or other instrument; (17) Authorizing any county, city, town, village, district or other political subdivision of county, to issue bonds or other securities unless the issuance of said bonds or other securities shall have been authorized before the enactment of such local or special law, by a vote of the duly qualified electors of such county, township, city, town, village, district or other political subdivision of a county, at an election held for such purpose, in the manner that may be prescribed by law; provided, the Legislature may without such election, pass special laws to refund bonds issued before the date of the ratification of this Constitution; (18) Amending, confirming or extending the charter of any private municipal corporation, or remitting the forfeiture thereof; provided, this shall not prohibit the Legislature from altering or rearranging the boundaries of any city, town or village; (19) Creating, extending or impairing any lien; (20) Chartering or licensing any ferry, road or bridge; (21) Increasing the jurisdiction and fees of justices of the peace, or the fees of constables; (22) Establishing separate school districts; (23) Establishing separate stock districts; (24) Creating, increasing or decreasing fees, percentages or allowances of public officers; (25) Exempting property from taxation or from levy or sale; (26) Exempting any person from jury, road or other civil duty; (27) Donating any lands owned by or under control of the State to any person or corporation; (28) Remitting fines, penalties or forfeitures; (29) Providing for the conduct of elections or designating places of voting, or changing the boundaries of wards, precincts or districts, except in the event of the organization of new counties, or the changing of the lines of old counties; (30) Restoring the right to vote to persons convicted of infamous crimes, or crimes involving moral turpitude; (31) Declaring who shall be liners between precincts or between counties. 104. The Legislature shall pass general laws for the cases enumerated in this section, provided that nothing in this section or article shall affect the right of the Legislature to enact local laws regulating or prohibiting the liquor traffic; but no such local law shall be enacted unless notice shall have been given as required in Section 106 of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_105_0_1901', 'Alabama1901', '1901', '4', '105', '0', 'Legislative', 'No special, private or local law, except a law fixing the time of holding courts, shall be enacted in any case which is provided for by a general law, or when the relief sought can be given by any court of this State; and the courts and not the Legislature, shall judge as to whether the matter of said law is provided for by a general law, and as to whether the relief sought can be given by any court; nor shall the Legislature indirectly, enact any such special, private or local law by the partial repeal of a general law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_106_0_1901', 'Alabama1901', '1901', '4', '106', '0', 'Legislative', 'No special, private or local haw shall be passed on any subject not enumerated in Section 104 of this Constitution, except in reference to fixing the time of holding courts, unless notice of the intention to apply therefor shall have been published, without cost to the State, in the county or counties where the matter or thing to be affected may be situated, which notice shall state the substance of the proposed law and be published at least once a week for four consecutive weeks in some newspaper published in such county or counties, or if there is no newspaper published therein, then by posting the said notice for four consecutive weeks at five different places in the county or counties prior to the introduction of the bill; and proof by affidavit that said notice has been given shall be exhibited to each House of the Legislature, and said proof spread upon the Journal. The courts shall pronounce void every special, private or local law which the Journals do not affirmatively show was passed in accordance with the provisions of this section.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_107_0_1901', 'Alabama1901', '1901', '4', '107', '0', 'Legislative', 'The Legislature shall not, by special, private or local law, repeal or modify any special, private or local law except upon notice being given and shown as provided in the last preceding section.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_108_0_1901', 'Alabama1901', '1901', '4', '108', '0', 'Legislative', 'The operation of a general law shall not be suspended for the benefit of any individual, private corporation or association; nor shall any individual, private corporation or association be exempted from the operation of any general law except as in this article otherwise provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_109_0_1901', 'Alabama1901', '1901', '4', '109', '0', 'Legislative', 'The Legislature shall pass general laws under which local and private interests shall be provided for and protected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_110_0_1901', 'Alabama1901', '1901', '4', '110', '0', 'Legislative', 'A general law within the meaning of this article is a law which apples to the whole State; a local law is a law which applies to any political subdivision or subdivisions of the State less than the whole; a special or private law within the meaning of this article is one which applies to an individual, association or corporation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_4_111_0_1901', 'Alabama1901', '1901', '4', '111', '0', 'Legislative', 'No bill introduced as a general law in either House of the Legislature shall be so amended on its passage as to become a special, private or local law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_112_0_1901', 'Alabama1901', '1901', '5', '112', '0', 'Executive', 'The Executive department shall consist of a Governor, Lieutenant-Governor, Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education, Commissioner of Agriculture and Industries, and a Sheriff for each county.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_113_0_1901', 'Alabama1901', '1901', '5', '113', '0', 'Executive', 'The supreme executive power of this State shall be vested in a chief magistrate, who shall be styled The Governor of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_114_0_1901', 'Alabama1901', '1901', '5', '114', '0', 'Executive', 'The Governor, Lieutenant-Governor, Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education and Commissioner of Agriculture and Industries, shall be elected by the qualified electors of the State at the same time and places appointed for the election of members of the Legislature in the year nineteen hundred and two, and in every fourth year thereafter.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_115_0_1901', 'Alabama1901', '1901', '5', '115', '0', 'Executive', 'The returns of every election for Governor, Lieutenant-Governor, Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education and Commissioner of Agriculture and Industries shall be sealed up and transmitted by the returning officers to the seat of government, directed to the Speaker of the House of Representatives, who shall, during the first week of the session to which such returns shall be made, open and publish them in the presence of both Houses of the Legislature in joint convention; but the Speakers duty and the duty of the joint convention shall be purely ministerial, The result of the election shall be ascertained and declared by the Speaker from the face of the returns without delay. The person having the highest number of votes for any one of said offices shall be declared duly elected; but if two or more persons shall have an equal and the highest number of votes for the same office, the Legislature by joint vote, without delay, shall choose one of said persons for said office. Contested elections for Governor, Lieutenant-Governor, Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education and Commissioner of Agriculture and Industries shall be determined by both Houses of the Legislature in such manner as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_116_0_1901', 'Alabama1901', '1901', '5', '116', '0', 'Executive', 'The Governor, Lieutenant-Governor, Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education and Commissioner of Agriculture and Industries, elected after the ratification of this Constitution, shall hold, their respective offices for the term of four years from the first Monday after the second Tuesday in January next succeeding their election, and until their successor shall be elected and qualified. After the first election under this Constitution no one of said officers shall be eligible as his own successor; and the Governor shall not be eligible to election or appointment to any office under this State, or to the Senate of the United States during his term, and within one year after the expiration thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_117_0_1901', 'Alabama1901', '1901', '5', '117', '0', 'Executive', 'The Governor and Lieutenant-Governor shall each be at least thirty years of age when elected, and shall have been citizens of the United States ten years and resident citizens of this State at least seven years next before the date of their election. The Lieutenant-Governor shall be ex-officio President of the Senate, but shall have no right to vote except in the event of a tie.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_118_0_1901', 'Alabama1901', '1901', '5', '118', '0', 'Executive', 'The Governor, Lieutenant-Governor, Attorney-General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education and Commissioner of Agriculture and Industries shall receive compensation to be fixed by law, which shall not be increased or diminished during the term for which they shall have been elected, and shall, except the Lieutenant-Governor, reside at the State Capital during the time they continue in office, except during epidemics. The compensation of the Lieutenant-Governor shall be the same as that received by the Speaker of the House, except while serving as Governor, during which time his compensation shall be the same as that allowed the Governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_119_0_1901', 'Alabama1901', '1901', '5', '119', '0', 'Executive', 'If the Legislature, at the session next after the ratification of this Constitution, shall enact a law increasing the salary of the Governor, such increase shall become effective and apply to the first Governor elected after the ratification of this Constitution, if the Legislature shall so determined.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_120_0_1901', 'Alabama1901', '1901', '5', '120', '0', 'Executive', 'The Governor shall take care that the laws be faithfully executed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_121_0_1901', 'Alabama1901', '1901', '5', '121', '0', 'Executive', 'The Governor may require information in writing, under oath, from the officers of the executive department, named in this article, or created by statute, on any subject relating to the duties of their respective offices; and he may at any time require information in writing, under oath, from all officers and managers of State institutions, upon any subject relating to the condition, management and expenses of their respective offices and institutions. Any such officer or manager who makes a wilfully false report or fails without sufficient excuse to make the required report on demand, is guilty of an impeachable offense.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_122_0_1901', 'Alabama1901', '1901', '5', '122', '0', 'Executive', 'The Governor may, by proclamation, on extraordinary occasions, convene the Legislature at the seat of government, or at a different place if, since their last adjournment, that shall have become dangerous from an enemy, insurrection, or other lawless outbreak, or from any infectious or contagious disease; and he shall state specifically in such proclamation each matter concerning which the action of that body is deemed necessary.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_123_0_1901', 'Alabama1901', '1901', '5', '123', '0', 'Executive', 'The Governor shall, from time to time, give the Legislature information of the state of the government, and recommend for its consideration such measures as he may deem expedient; and at the commencement of each regular session of the Legislature, and at the close of his term of office, he shall give information by written message of the condition of the State; and he shall account to the Legislature, as may be prescribed by law, for all moneys received and paid out by him or by his order; and at the commencement of each regular session he shall present to the Legislature estimates of the amount of money required to be raised by taxation for all purposes.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_124_0_1901', 'Alabama1901', '1901', '5', '124', '0', 'Executive', 'The Governor shall have power to remit fines and forfeitures under such rules and regulations as may be prescribed by law; and, after conviction, to grant reprieves, paroles, commutations of sentence and pardons, except in cases of impeachment. The Attorney General, Secretary of State, and State Auditor shall constitute a Board of Pardons, who shall meet on the call of the Governor, and before whom shall be laid all recommendations or petitions, for pardon, commutation or parole, in cases of felony; and the board shall hear them in open session, and give their opinion thereon in writing to the Governor, after which or on the failure of the board to advise for more than sixty days, the Governor may grant or refuse the commutation, parole or pardon, as to him seems best for the public interest. He shall communicate to the Legislature at each session every remission of fines and forfeitures, and every reprieve, commutation, parole or pardon, with his reasons therefor, in the opinions of the Board of Pardons in each case required to be referred, stating the name and crime of the convict, the sentence, its date, and the date of reprieve, commutation, parole or pardon. Pardons in cases of felony and other offenses involving moral turpitude shall not relieve from civil and political disabilities, unless approved by the Board of Pardons and specifically expressed in the pardon.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_125_0_1901', 'Alabama1901', '1901', '5', '125', '0', 'Executive', 'Every bill which shall have passed both Houses of the Legislature, except as otherwise provided in this Constitution, shall be presented to the Governor; if he approve, he shall sign it; but if not, he shall return it with his objections to the House in which it originated, which shall enter the objections at large upon the Journal and proceed to reconsider it. If the Governors message proposes no amendment which would remove his objections to the bill, the House in which the bill originated may proceed to reconsider, and if a majority of the whole number elected to that House vote for the passage of the bill, it shall be sent to the other House, which shall in like manner reconsider, and if a majority of the whole number elected to that House vote for the passage of the bill, the same shall become a law, notwithstanding the Governors veto. If the Governors message proposes amendment, which would remove his objections, the House to which it is sent may so amend the bill and send it with the Governors message to the other House, which may adopt but cannot amend, said amendment; and both Houses concurring in the amendment, the bill shall again be sent to the Governor and acted on by him as other bills. If the House to which the bill is returned refuses to make such amendment, it shall proceed to reconsider; and if a majority of the whole number elected to that House shall vote for the passage of the bill, it shall be sent with the objections to the other House, by which it shall likewise be reconsidered, and if approved by a majority of the whole number elected to that House, it shall become a law. If the House to which the bill is returned makes the amendment and the other House declines to pass the same, that House shall proceed to reconsider, as though the bill had originated therein, and such proceedings shall be taken thereon as above provided. In every such case the vote of both Houses shall be determined by yeas and nays, and the names of the members voting for or against the bill shall be entered upon the Journals of each House respectively. If any bill shall not be returned by the Governor within six days, Sundays excepted, after it shall have been presented, the same shall become a law in like manner as if he had signed it, unless the Legislature, by its adjournment, prevent the return, in which case it shall not be a law; but when return is prevented by recess, such bill must be returned to the House in which it originated within two days after the reassembling, otherwise it shall become a law, but bills presented to the Governor within five days before the final adjournment of the Legislature may be approved by the Governor at any time within ten days after such adjournment, and if approved and deposited with the Secretary of State within that time shall become law. Every vote, order, or resolution to which concurrence of both Houses may be necessary, except on questions of adjournment and the bringing on of elections by the two Houses, and amending this Constitution, shall be presented to the Governor; and, before the same shall take effect, be approved by him; or, being disapproved, shall be repassed by both Houses according to the rules and limitations prescribed in the case of a bill.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_126_0_1901', 'Alabama1901', '1901', '5', '126', '0', 'Executive', 'The Governor shall have power to approve or disapprove any item or items of any appropriation bill embracing distinct items, and the part or parts of the bill approved shall be the law, and the item or items disapproved shall be void, unless repassed according to the rules and limitations prescribed for the passage of bills over the executive veto; and he shall in writing state specifically the item or items he disapproves, setting the same out in full in his message, but in such case the enrolled bill shall not be returned with the Governors objection.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_127_0_1901', 'Alabama1901', '1901', '5', '127', '0', 'Executive', 'In case of the Governors removal from office, death or resignation, the Lieutenant-Governor shall become Governor. If both the Governor and Lieutenant-Governor be removed from office, die, or resign more than sixty days prior to the next general election at which any State officers are to be elected, a Governor and Lieutenant-Governor shall be elected at such election for the unexpired term, and in the event of a vacancy in the office, caused by the removal from office, death or resignation of the Governor and Lieutenant-Governor, pending such vacancy, and until their successors shall be elected and qualified, the office of Governor shall be held and administered by either the President pro tem of the Senate, Speaker of the House of Representatives, Attorney General, State Auditor, Secretary of State, or State Treasurer in the order herein named. In case of the impeachment of the Governor, his absence from the State for more than twenty days, unsoundness of mind, or other disability, the power and authority of the office shall, until the Governor is acquitted, return to the State, or is restored to his mind, or relieved from other disability, devolve in the order herein named upon the Lieutenant-Governor, President pro tem of the Senate, Speaker of the House of Representatives, Attorney General, State Auditor, Secretary of State and State Treasurer. If any of these officers be under any of the disabilities herein specified, the office of Governor shall be administered in the order named by such of these officers as may be free from such disability. If the Governor shall be absent from the State over twenty days, the Secretary of State shall notify the Lieutenant-Governor, who shall enter upon the duties of Governor; if both the Governor and Lieutenant-Governor shall be absent from the State over twenty days, the Secretary of State shall notify the President pro tem of the Senate, who shall enter upon the duties of Governor, and so on, in case of such absence, he shall notify each of the other officers named in their order, who shall discharge the duties of the office until the Governor or other officer entitled to administer the office in succession to the Governor returns. If the Governor-elect fails or refuses from any cause to qualify, the Lieutenant-Governor-elect shall qualify and exercise the duties of Governor until the Governor elect qualifies; and in the event both the Governor-elect and the Lieutenant-Governor-elect from any cause fail to qualify, the President pro tem of the Senate, the Speaker of the House of Representatives, the Attorney General, State Auditor, Secretary of State and State Treasurer shall in like manner, in the order named, administer the office until the Governor-elect or Lieutenant-Governor-elect qualifies.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_128_0_1901', 'Alabama1901', '1901', '5', '128', '0', 'Executive', 'If the Governor or other officer administering the office shall appear to be of unsound mind, it shall be the duty of the Supreme Court of Alabama, at any regular term, or at any special term, which it is hereby authorized to call for that purpose, upon request in writing, verified by their affidavits, of any two of the officers named in Section 127 of this Constitution, not next in succession to the office of Governor, to ascertain the mental condition of the Governor or other officer administering the office, and if he is adjudged to be of unsound mind, to so decree, a copy of which decree, duly certified, shall be filed in the office of Secretary of State; and in the event of such adjudication it shall be the duty of the officer next in succession to perform the duties of the office until the Governor or other officer administering the office is restored to his mind. If the incumbent denies that the Governor or other person entitled to administer the office has been restored to his mind, the Supreme Court, at the instance of any officer named in Section 127 of this Constitution, shall ascertain the truth concerning the same, and if the officer has been restored to his mind, shall so adjudge and file a duly certified copy of its decree with the Secretary of State; and in the event of such adjudication, the office shall be restored to him. The Supreme Court shall prescribe the method of taking testimony and the rules of practice in which proceedings, which rules shall include a provision for the service of notice of such proceedings on the Governor or person acting as Governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_129_0_1901', 'Alabama1901', '1901', '5', '129', '0', 'Executive', 'The Lieutenant-Governor, President pro tem of the Senate, Speaker of the House, Attorney General, State Auditor, Secretary of State or State Treasurer, while administering the office of Governor, shall receive like compensation as that prescribed by law for the Governor, and no other.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_130_0_1901', 'Alabama1901', '1901', '5', '130', '0', 'Executive', 'No person shall at the same time hold the office of Governor and any other office, civil or military, under this State, or the United States, or any other State or government, except as otherwise provided in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_131_0_1901', 'Alabama1901', '1901', '5', '131', '0', 'Executive', 'The Governor shall be commander-in-chief of the militia and volunteer forces of this State, except when they shall be called into the service of the United States, and he may call out the same to execute the laws, suppress insurrection and repel invasion, but need not command in person unless directed to do so by resolution of the Legislature; and when acting in the service of the United States, he shall appoint his staff, and the Legislature shall fix his rank.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_132_0_1901', 'Alabama1901', '1901', '5', '132', '0', 'Executive', 'No person shall be eligible to the office of Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education, or Commissioner of Agriculture and Industries, unless he shall have been a citizen of the United States at least seven years, and shall have resided in this State at least five years next preceding his election, and shall be at least twenty-five years old when elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_133_0_1901', 'Alabama1901', '1901', '5', '133', '0', 'Executive', 'There shall be a seal of the State which shall be used officially by the Governor, and the seal now in use shall continue to be used until another shall have been adopted by the Legislature. The seal shall be called The Great Seal of the State of Alabama.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_134_0_1901', 'Alabama1901', '1901', '5', '134', '0', 'Executive', 'The Secretary of State shall be the custodian of the Great Seal of the State, and shall authenticate therewith all official acts of the Governor, except his approval of laws, resolutions, appointments to office and administrative orders. He shall keep a register of the official acts of the Governor, and when necessary, shall attest them and lay copies of same together with copies of all papers relative thereto, before either House of the Legislature when required to do so, and shall perform such other duties as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_135_0_1901', 'Alabama1901', '1901', '5', '135', '0', 'Executive', 'All grants and commissions shall be issued in the name and by the authority of the State of Alabama, sealed with the Great Seal of the State, signed by the Governor and countersigned by the Secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_136_0_1901', 'Alabama1901', '1901', '5', '136', '0', 'Executive', 'Should the office of Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education, or Commissioner of Agriculture and Industries become vacant from any cause the Governor shall fill such vacancy until the disability is removed or a successor elected and qualified. In case any of said officers shall become of unsound mind, such unsoundness shall be ascertained by the Supreme Court upon the suggestion of the Governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_137_0_1901', 'Alabama1901', '1901', '5', '137', '0', 'Executive', 'The Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education, and Commissioner of Agriculture and Industries shall perform such duties as may be prescribed by law. The State Treasurer and State Auditor shall every year, at a time fixed by the Legislature, make a full and complete report to the Governor, showing the receipts and disbursements of every character, all claims audited and paid out, by items, and all taxes and revenues collected and paid into the treasury, and the sources thereof. They shall make reports oftener upon any matters pertaining to their offices, if required by the Governor or the Legislature. The Attorney General, State Auditor, Secretary of State, State :Treasurer, and Commissioner of Agriculture and Industries shall not receive to their use any fees, costs, perquisites of office or other compensation than the salaries prescribed by law, and all fees that may be payable for any services performed by such officers shall be at once paid into the State Treasury.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_5_138_0_1901', 'Alabama1901', '1901', '5', '138', '0', 'Executive', 'A Sheriff shall be elected in each county by the qualified electors thereof, who shall hold office for a term of four years, unless sooner removed, and he shall be ineligible to such office as his own successor; provided, that the terms of all Sheriffs expiring ill the year nineteen hundred and four are hereby extended until the time of the expiration of the terms of the other executive officers of this State in the year nineteen hundred and seven, unless sooner removed. Whenever any prisoner is taken from jail, or from the custody of any Sheriff or his deputy, and put to death, or suffers grievous bodily harm, owing to the neglect, connivance, cowardice, or other grave fault of the Sheriff, such Sheriff may be impeached under Section 174 of the Constitution. If the Sheriff be impeached, and thereupon convicted, he shall not be eligible to hold an office in this State during the time for which he had been elected or appointed to serve as Sheriff.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_139_0_1901', 'Alabama1901', '1901', '6', '139', '0', 'Judicial', 'The judicial power of the State shall be vested in the Senate, sitting as a court of impeachment, a Supreme Court, Circuit Courts, Chancery Courts, Courts of Probate, such courts of law and equity inferior to the Supreme Court, and to consist of not more than 5 members, as the Legislature from time to time may establish, and such persons as may be by law invested with powers of a judicial nature; but no court of general jurisdiction, at law or in equity, or both, shall hereafter be established in and for any one country having a population of less than twenty thousand, according to the next preceding Federal census, and property assessed for taxation at a less valuation than three million five hundred thousand dollars.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_140_0_1901', 'Alabama1901', '1901', '6', '140', '0', 'Judicial', 'Except in cases otherwise directed in this Constitution, the Supreme Court shall have appellate jurisdiction only, which shall be coextensive with the State, under such restrictions and regulations, not repugnant to this Constitution, as may from time to time be prescribed by law, except where jurisdiction over appeals is vested in some inferior court, and made final therein; provided, that the Supreme Court shall have power to issue writs of injunction, habeas corpus, quo warranto, and such other remedial and original writs as may be necessary to give it a general superintendence and control of inferior jurisdictions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_141_0_1901', 'Alabama1901', '1901', '6', '141', '0', 'Judicial', 'The Supreme Court shall be held at the seat of government, but if that shall become dangerous from any cause, it may convene at or adjourn to another place.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_142_0_1901', 'Alabama1901', '1901', '6', '142', '0', 'Judicial', 'Except as otherwise authorized in this article, the State shall be divided into convenient circuits. For each circuit there shall be chosen a judge, who shall, for one year next preceding his election during his continuance in office, reside in the circuit for which he is elected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_143_0_1901', 'Alabama1901', '1901', '6', '143', '0', 'Judicial', 'The Circuit Court shall have original jurisdiction in all matters civil and criminal within the State not otherwise excepted in this Constitution; but in civil cases, other than suits for libel, slander, assault and battery, and ejectment, it shall have no original jurisdiction except where the matter or sum in controversy exceeds fifty dollars.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_144_0_1901', 'Alabama1901', '1901', '6', '144', '0', 'Judicial', 'A Circuit Court, or a court having the jurisdiction of the Circuit Court, shall be held in each county in the State at least twice in each year, and judges of the several courts mentioned in this section may hold court for each other when they deem it expedient, and shall do so when directed by law. The judges of the several courts mentioned in this section shall have power to issue writs of injunction, returnable to the Court of Chancery, or courts having the jurisdiction of Courts of Chancery.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_145_0_1901', 'Alabama1901', '1901', '6', '145', '0', 'Judicial', 'The Legislature shall have power to establish a Court or Courts of Chancery, with original and appellate jurisdiction, except as otherwise authorized in this article. The State shall be divided by the Legislature into convenient Chancery divisions; each division shall be divided into districts, and for each division there shall be a chancellor, who shall have resided in the division for which he shall be elected or appointed for one year next preceding his election or appointment, and shall reside therein during his continuance in office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_146_0_1901', 'Alabama1901', '1901', '6', '146', '0', 'Judicial', 'A Chancery Court, or a court having the jurisdiction of the Chancery Court, shall be held in each district, at a place to be fixed by law, at least twice in each year, and the chancellors may hold court for each other when they deem it necessary, and shall do so when directed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_147_0_1901', 'Alabama1901', '1901', '6', '147', '0', 'Judicial', 'Any county having a population of twenty thousand or more, according to the next preceding Federal census, and also taxable property of three million five hundred thousand dollars or more in value, according to the next preceding assessment of property for State and county taxation, need not be included in any circuit or chancery division; but if the value of its taxable property shall be reduced below that limit, or if its population shall be reduced below that number, the Legislature shall include such county in a circuit and chancery division, or either, embracing more than one county. No Circuit or Chancery division shall contain less than three counties, unless there be embraced therein a county having a population of twenty thousand or more, and taxable property of three million five hundred thousand dollars or more in value.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_148_0_1901', 'Alabama1901', '1901', '6', '148', '0', 'Judicial', 'The Legislature may confer upon the Circuit Court or the Chancery Court the jurisdiction of both of said courts. In counties having two or more courts of record, the Legislature may provide for the consolidation of all or any such courts of record, except the Probate Court, with or without separate divisions, and a sufficient number of judges for the transaction of the business of such consolidated court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_149_0_1901', 'Alabama1901', '1901', '6', '149', '0', 'Judicial', 'The Legislature shall have power to establish in each county a court of probate, with general jurisdiction of orphans business and with power to grant letters testamentary and administration; provided, that whenever any court having equity powers has taken jurisdiction of the settlement of any estate, it shall have power to do all things necessary for the settlement of such estate, including the appointment and removal of administrators, executors, guardians, and trustees, and including action upon the resignation of either of them.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_150_0_1901', 'Alabama1901', '1901', '6', '150', '0', 'Judicial', 'The Justices of the Supreme Court, Chancellors and the Judges of the Circuit Courts, and other courts of record, except Probate Courts, shall, at stated times, receive for their services a compensation which shall not be diminished during their official terms; they shall receive no fees or perquisites, nor hold any office, except judicial offices, of profit or trust under this State or the United States, or any other government during the time for which they have been elected or appointed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_151_0_1901', 'Alabama1901', '1901', '6', '151', '0', 'Judicial', 'The Supreme Court shall consist of one Chief Justice, and such number of Associate Justices as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_152_0_1901', 'Alabama1901', '1901', '6', '152', '0', 'Judicial', 'The Chief Justice and Associate Justices of the Supreme Court, Judges of the Circuit Courts, Judges of the Probate Courts, and Chancellors shall be elected by the qualified electors of the State, circuits, counties and chancery divisions, for which such courts may be established, at such times as may be prescribed by law, except as herein otherwise provided.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_153_0_1901', 'Alabama1901', '1901', '6', '153', '0', 'Judicial', 'The Judges of such inferior courts of law and equity as may be by law established, shall be elected or appointed in such mode as the Legislature may prescribe.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_154_0_1901', 'Alabama1901', '1901', '6', '154', '0', 'Judicial', 'Chancellors and Judges of all courts of record shall have been citizens of the United States and of this State for five years next preceding their election or appointment, and shall not be less than twenty-five years of age, and, except Judges of Probate, shall be learned in the law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_155_0_1901', 'Alabama1901', '1901', '6', '155', '0', 'Judicial', 'Except as otherwise provided in this article, the Chief Justice and Associate Justices of the Supreme Court, Circuit Judges, Chancellors, and Judges of Probate, shall hold office for the term of six years, and until their successors are elected or appointed and qualified; and the right of such Judge and Chancellors to hold their offices for the full term hereby prescribed shall not be affected by any change hereafter made by law in any circuit, division, or county, or in the mode of time of election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_156_0_1901', 'Alabama1901', '1901', '6', '156', '0', 'Judicial', 'The Chief Justice and Associate Justices of the Supreme Court shall be chosen at an election to be held at the time and places fixed by law for the election of members of the House of Representatives of the Congress of the United States, until the Legislature shall by law change the time of holding such election. The term of office of the Chief Justice, who shall be elected in the year nineteen hundred and four, shall be as provided in the last preceding section. The successors of two of the Associate Justices elected in the year nineteen hundred and four shall be elected in the year nineteen hundred and six, and the successors of the other two Associate Justices elected in nineteen hundred and four, shall be elected in the year nineteen hundred and eight. The Associate Justices of said court elected in the year nineteen hundred and four shall draw or cast lots among themselves to determine which of them shall hold office for the terms ending, respectively, in the years nineteen hundred and six and nineteen hundred and eight, and until their respective successors are elected or appointed and qualified. The result of such determination shall be certified to the Governor by such Associate Justices, or a majority of them, prior to the first day of January nineteen hundred and five, and such certificate shall be entered upon the minutes of the court. In the event of the failure of said Associate Justices to make and certify such determination, the Governor shall designate the terms for which they shall respectively hold office, as above provided, and shall issue his proclamation accordingly. In the event of an increase or reduction by law of the number of Associate Justices of the Supreme Court, the Legislature shall, as nearly as may be, provide for the election each second year, of one-third of the members of said court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_157_0_1901', 'Alabama1901', '1901', '6', '157', '0', 'Judicial', 'All judicial officers within their respective jurisdictions shall, by virtue of their offices, be conservators of the peace.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_158_0_1901', 'Alabama1901', '1901', '6', '158', '0', 'Judicial', 'Vacancies in the office of any of the Justices of the Supreme Court or Judges who hold office by election, or Chancellors of this State, shall be filled by appointment by the Governor. The appointee shall hold his office until the next general election for any State officer held at least six months after the vacancy occurs, and until his successor is elected and qualified; the successor chosen at such election shall hold office for the unexpired term and until his successor is elected and qualified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_159_0_1901', 'Alabama1901', '1901', '6', '159', '0', 'Judicial', 'Whenever any new circuit or chancery division is created, the Judge or Chancellor therefor shall be elected at the next general election for any State officer for a term to expire at the next general election for Circuit Judge and Chancellor; provided, that if said new circuit or chancery division is created more than six months before such general election for any State officer, the Governor shall appoint some one as Judge or Chancellor, as the case may be, to hold the office until such election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_160_0_1901', 'Alabama1901', '1901', '6', '160', '0', 'Judicial', 'If in any case, civil or criminal, pending in any Circuit Court, Chancery Court, or in any court of general jurisdiction having any part of the jurisdiction of a Circuit and a Chancery Court, or either of them in this State, the presiding judge or chancellor shall, for any legal cause, be incompetent to try, hear or render judgment in such case, the parties, or their attorneys of record, if it be a civil case, or the solicitor or prosecuting officer, and the defendant or defendants, if it be a criminal case, may agree upon some disinterested person practicing in the court and learned in the law, to act as special judge or chancellor to sit as a court, and to hear, decide and render judgment in the same manner and to the same effect as such incompetent Chancellor or Judge could have rendered but for such incompetency. If the case be a civil one, and the parties or their attorneys of record do not agree; or if it be a criminal one, and the prosecuting officer and the defendant or defendants do not agree upon a special judge or chancellor, or if either party in a civil cause is not represented in court, the Register in Chancery or the clerk of such Circuit or other court in which said cause is pending, shall appoint a special judge or chancellor, who shall preside, try and render judgment as in this section provided. The Legislature may prescribe other methods for supplying special judges in such cases.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_161_0_1901', 'Alabama1901', '1901', '6', '161', '0', 'Judicial', 'The Legislature shall have power to provide for the holding of Chancery and Circuit Courts, and for the holding of courts having the jurisdiction of Circuit and Chancery Courts, or either of them, when the Chancellors or Judges thereof fail to attend regular terms.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_162_0_1901', 'Alabama1901', '1901', '6', '162', '0', 'Judicial', 'No Judge of any court of record in this State shall practice law in any of the courts of this State, or of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_163_0_1901', 'Alabama1901', '1901', '6', '163', '0', 'Judicial', 'Registers in Chancery shall be appointed by the Chancellors of the respective divisions, and shall have been at least twelve months before their appointment, and shall be at the time of their appointment and during their continuance in office, resident citizens of the district for which they are appointed. They shall hold office for the term for which the Chancellor making such appointment was elected or appointed. Such registers shall receive as compensation for their services only such fees and commissions as may be specifically prescribed by law, which fees shall be uniform throughout the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_164_0_1901', 'Alabama1901', '1901', '6', '164', '0', 'Judicial', 'The clerk of the Supreme Court shall be appointed by the Judges thereof, and shall hold office for the term of six years; and the clerks of such inferior courts as may be established by law shall be selected in such manner as the Legislature may provide.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_165_0_1901', 'Alabama1901', '1901', '6', '165', '0', 'Judicial', 'Clerks of the Circuit Court shall be elected by the qualified electors in each county for the term of six years, and may, when appointed by the Chancellor, also fill the office of Register in Chancery. Vacancies in such office of clerk shall be filled by the Judge of the Circuit Court for the unexpired term.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_166_0_1901', 'Alabama1901', '1901', '6', '166', '0', 'Judicial', 'The clerk of the Supreme Court and Registers in Chancery may be removed from office by the Justices of the Supreme Court, and by the Chancellors, respectively, for cause, to be entered at length upon the minutes of the court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_167_0_1901', 'Alabama1901', '1901', '6', '167', '0', 'Judicial', 'A Solicitor for each judicial circuit or other territorial subdivision prescribed by the Legislature, shall be elected by the qualified electors of those counties in such circuit or other territorial subdivision in which such Solicitor prosecutes criminal cases, and such Solicitor shall be learned in the law, and shall at the time of his election and during his continuance in office, reside in a county (in the circuit) in which he prosecutes criminal cases, or other territorial subdivision for which he is elected, and his term of office shall be four years, and he shall receive no other compensation than a salary, to be prescribed by law, which shall not be increased during the term for which he was elected; provided, that this article shall not operate to abridge the term of any Solicitor now in office; and, provided further, that the Solicitor elected in the year nineteen hundred and four shall hold office for six years, and until their successors are elected and qualified; and provided further, that the Legislature may provide by law for the appointment by the Governor or the election by the qualified electors of a county for a Solicitor for any county.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_168_0_1901', 'Alabama1901', '1901', '6', '168', '0', 'Judicial', 'In each precinct not lying within, or partly within, any city or incorporated town of more than fifteen hundred inhabitants, there shall be elected by the qualified electors of such precinct not exceeding two Justices of the Peace, and one Constable. Where one or more precincts lie within, or partly within, a city or incorporated town having more than fifteen hundred inhabitants, the Legislature may provide by law for the election of not more than two Justices of the Peace and one Constable, for each of such precincts, or an inferior court for such precinct or precincts, in lieu of all Justices of the Peace therein. Justices of the Peace, and the inferior courts in this section provided for, shall have jurisdiction in all civil cases where the amount in controversy does not exceed one hundred dollars, except in cases of libel, slander, assault and battery and ejectment. The Legislature may provide by law what fees may be charged by Justices of the Peace and Constables, which fees shall be uniform throughout the State. The right of appeal from any judgment of a Justice of the Peace, or from any inferior court authorized by this section, without the repayment of costs, and also the term of office of such Justices, any of the Judges of such inferior courts, and of Notaries Public, shall be provided for by law. The Governor may appoint Notaries Public without the powers of a Justice of the Peace, and may, except where otherwise provided by an act of the Legislature, appoint not more than one Notary Public with all of the powers and jurisdiction of a Justice of the Peace for each precinct in which the election of Justices of the Peace shall be authorized.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_169_0_1901', 'Alabama1901', '1901', '6', '169', '0', 'Judicial', 'In all prosecutions for rape and assault with intent to ravish, the court may, in its discretion, exclude from the court room all persons, except such as may be necessary in the conduct of the trial.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_170_0_1901', 'Alabama1901', '1901', '6', '170', '0', 'Judicial', 'The style of all processes shall be The State of Alabama, and all prosecutions shall be carried on in the name and by the authority of the same, and shall conclude Against the peace and dignity of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_171_0_1901', 'Alabama1901', '1901', '6', '171', '0', 'Judicial', 'The Legislature shall have the power to abolish any court, except the Supreme Court and the Probate Courts, whenever its jurisdiction and functions have been conferred upon some other court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_6_172_0_1901', 'Alabama1901', '1901', '6', '172', '0', 'Judicial', 'Nothing in this article shall be so construed as to abridge the term of office of any officer now in office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_173_0_1901', 'Alabama1901', '1901', '7', '173', '0', 'Impeachment', 'The Governor, Lieutenant-Governor, Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education, Commissioner of Agriculture and Industries, and Justices of the Supreme Court may be removed from office for wilful neglect of duty, corruption in office, incompetency, or intemperance in the use of intoxicating liquors or narcotics to such an extent, in view of the dignity of the office and importance of its duties, as unfits the officer for the discharge of such duties, or for any offense involving moral turpitude while in office, or committed under color thereof, or connected therewith, by the Senate sitting as a court of impeachment, under oath or affirmation, on articles or charges preferred by the House of Representatives. When the Governor or Lieutenant-Governor is impeached, the Chief Justice, or if he be absent or disqualified, then one of the Associate Justices of the Supreme Court, to be selected by it, shall preside over the Senate when sitting as a court of impeachment. If at any time when the Legislature is not in session, a majority of all the members elected to the House of Representatives shall certify in writing to the Secretary of State their desire to meet to consider the impeachment of the Governor, Lieutenant Governor, or other officer administering the office of Governor, it shall be the duty of the Secretary of State immediately to notify the Speaker of the House, who shall within tell days after receipt of such notice, summon the members of the House by publication in some newspaper published at the Capital to assemble at the Capitol on a day to be fixed by the Speaker, not later than fifteen days after the receipt of the notice to him from the Secretary of State, to consider the impeachment of the Governor, Lieutenant-Governor or other officer administering the Office of Governor. If the House of Representatives prefer articles of impeachment, the Speaker of the House shall forthwith notify the Lieutenant-Governor, unless he be the officer impeached, in which event he shall notify the Secretary of State, who shall summon in the manner herein above provided for, the members of the Senate to assemble at the Capitol on a day to be named in said summons, not later than ten days after receipt of the notice from the Speaker of the House, for the purpose of organizing as a court of impeachment. The Senate, when thus organized, shall hear and try such articles of impeachment against the Governor, Lieutenant-Governor or other officer administering the office of Governor, as may be preferred by the House of Representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_174_0_1901', 'Alabama1901', '1901', '7', '174', '0', 'Impeachment', 'The Chancellors, Judges of the Circuit Courts, Judges of the Probate Courts, and Judges of other courts from which an appeal may be taken directly to the Supreme Court, and Solicitors and Sheriffs, may be removed from office for any of the causes specified in the preceding section or elsewhere in this Constitution, by the Supreme Court, under such regulations as may be prescribed by law. The Legislature may provide for the impeached or removal of other officers than those named in this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_175_0_1901', 'Alabama1901', '1901', '7', '175', '0', 'Impeachment', 'The clerks of the Circuit Courts, or courts of like jurisdiction, and of Criminal Courts, Tax Collectors, Tax Assessors, County Treasurers, County Superintendents of Education, Judges of inferior courts created under authority of Section 168 of this Constitution, Coroners, Justices of the Peace, Notaries Public, Constables, and all other county officers, Mayors, Intendants and all other officers of incorporated cities and towns in this State, may be removed from office, for any of the causes specified in Section 173 of this Constitution, by the Circuit or other courts of like jurisdiction or a Criminal Court of the county in which such officers hold their office, under such regulations as may be prescribed by law; provided, that the right of trial by jury and appeal in such cases shall be secured.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_7_176_0_1901', 'Alabama1901', '1901', '7', '176', '0', 'Impeachment', 'The penalties in cases arising under the three preceding sections shall not extend beyond removal from office, and disqualifications from holding office, under the authority of this State, for the term for which the officer was elected or appointed; but the accused shall be liable to indictment and punishment as prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_177_0_1901', 'Alabama1901', '1901', '8', '177', '0', 'Suffrage and Elections', 'Every male citizen of this State, who is a citizen of the United States, and every male resident of foreign birth, who, before the ratification of this Constitution, shall have legally declared his intention to become a citizen of the United States, twenty-one years old or upward, not laboring under any of the disabilities named in this article, and possessing the qualifications required by it, shall be an elector, and shall be entitled to vote at any election by the people; provided, that all foreigners who have legally declared their intention of becoming citizens of the United States, shall, if they fail to become citizens thereof at the time they are entitled to become such, cease to have the right to vote until they become such citizens.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_178_0_1901', 'Alabama1901', '1901', '8', '178', '0', 'Suffrage and Elections', 'To entitle a person to vote at any election by the people, he shall have resided in the State at least two years, in the county one year, and in the precinct or ward three months, immediately preceding the election at which be offers to vote, and he shall have been duly registered as an elector, and shall have paid on or before the first day of February next preceding the date of the election at which he offers to vote all poll taxes due from him for the year nineteen hundred and one, an for each subsequent year; provided, that any elector who, within three months next preceding the date of the election at which he offers to vote, has removed from one precinct or ward to another precinct or ward in the same county, incorporated town or city, shall have the right to vote in the precinct or ward from which he has so removed, if he would have been entitled to vote in such precinct or ward but for such removal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_179_0_1901', 'Alabama1901', '1901', '8', '179', '0', 'Suffrage and Elections', 'All elections by the people shall be by ballot, and all elections by persons in a representative capacity shall be viva voce.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_180_0_1901', 'Alabama1901', '1901', '8', '180', '0', 'Suffrage and Elections', 'The following male citizens of this State, who are citizens of the United States, and every male resident of foreign birth, who, before the ratification of this Constitution, shall have legally declared his intention to become a citizen of the United States, and who shall not have had an opportunity to perfect his citizenship prior to the twentieth day of December, nineteen hundred and two, twenty-one years old or upwards, who, if their place of residence shall remain unchanged, will have, at the date of the next general election the qualifications as to residents prescribed in Section 178 of this Constitution, and who are not disqualified under Section 182 of this Constitution, shall, upon application be entitled to register as electors prior to the twentieth day of December, nineteen hundred and two, namely: First - All who have honorably served in the land or naval forces of the United States in the war of 1812, or in the war with Mexico, or in any war with the Indians, or in the war between the States, or in the war with Spain, or who honorably served in the land or naval forces of the Confederate States, or of the State of Alabama in the war between the States; oSecond.- The lawful descendants of persons who honorably served in the land or naval forces of the United States in the war of the American Revolution, or in the war of 1812, or in the war with Mexico, or in any war with the Indians, or in the war between the States, or in the land or naval forces of the Confederate States, or of the State of Alabama in the war between the States; oThird.- All persons who are of good character and who understand the duties and obligations of citizenship under a republican form of government.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_181_0_1901', 'Alabama1901', '1901', '8', '181', '0', 'Suffrage and Elections', 'After the first day of January, nineteen hundred and three, the following persons, and no others, who, if their place of residence shall remain unchanged, will have, at the date of the next general election, the qualifications as to residence prescribed in Section 178 of this Constitution, shall be qualified to register as electors, provided, they shall not be disqualified under Section 182 of this Constitution. First - Those who can read and write any article of the Constitution of the United States in the English language, and who are physically unable to work; and those who can read and write any article of the Constitution of the United States in the English language, and who have worked or been regularly engaged in some lawful employment, business or occupation, trade or calling for the greater part of the twelve months next preceding the time they offer to register; and those who are unable to read and write, if such inability is due solely to physical disability; oSecond.- The owner in good faith, in his own right, or the husband of a woman who is the owner in good faith, in her own right, of forty acres of land situate in this State, upon which they reside; or the owner in good faith, in his own right, or the husband of any woman who is the owner in good faith, in her own right, of real estate, situate in this State, assessed for taxation at the value of three hundred dollars or more, or the owner in good faith, in his own right, or the husband of a woman who is the owner in good faith, in her own right, of personal property in this State assessed for taxation at three hundred dollars or more; provided, that the taxes due upon such real or personal property for the year next preceding the year in which he offers to register shall have been paid, unless the assessment shall have been legally contested and is undetermined.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_182_0_1901', 'Alabama1901', '1901', '8', '182', '0', 'Suffrage and Elections', 'The following persons shall be disqualified both from registering and from voting, namely: All idiots and insane persons; those who shall by reason of conviction of crime be disqualified from voting at the time of the ratification of this Constitution; those who shall be convicted of treason, murder, arson, embezzlement, malfeasance in office, larceny, receiving stolen property, obtaining property or money under false pretenses, perjury, subordination of perjury, robbery, assault with intent to rob, burglary, forgery, bribery, assault and battery on the wife, bigamy, living in adultery, sodomy, incest, rape, miscegenation, crime against nature, or any crime punishable by imprisonment in the penitentiary, or of any infamous crime or crime involving moral turpitude; also any person who shall be convicted as a vagrant or tramp, or of selling or offering to sell his vote or the vote of another, or of making or offering to make false return in any election by the people or in any primary election to procure the nomination or election of any person to any office, or of suborning any witness or registrar to secure the registration of any person as an elector.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_183_0_1901', 'Alabama1901', '1901', '8', '183', '0', 'Suffrage and Elections', 'No person shall be qualified to vote or participate in any primary election, party convention, mass meeting or other method of party action of any political party or faction, who shall not possess the qualifications prescribed in this article for an elector, or who shall be disqualified from voting under the provisions of this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_184_0_1901', 'Alabama1901', '1901', '8', '184', '0', 'Suffrage and Elections', 'No person, not registered and qualified as an elector under the provisions of this article, shall vote at the general election in nineteen hundred and two, or at any subsequent State, county, or municipal election, general, local or special; but the provisions of this article shall not apply to any election held prior to the general election in the year nineteen hundred and two.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_185_0_1901', 'Alabama1901', '1901', '8', '185', '0', 'Suffrage and Elections', 'Any elector whose right to vote shall be challenged for any legal cause before an election officer, shall be required to swear or affirm that the matter of the challenge is untrue before his vote shall be received, and any one who willfully swears or affirms falsely thereto shall be guilty of perjury, and upon conviction thereof shall be imprisoned in the penitentiary for not less than one nor more than five years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_186_0_1901', 'Alabama1901', '1901', '8', '186', '0', 'Suffrage and Elections', 'The Legislature shall provide by law for the registration, after the first day of January, nineteen hundred and three, of all qualified electors. Until the first day of January, nineteen hundred and three, all electors shall be registered under and in accordance with the requirements of this section, as follows: First - Registration shall be conducted in each county by a board of three reputable and suitable persons resident in the county, who shall not hold any elective office during their term, to be appointed within sixty days after the ratification of this Constitution, by the Governor, Auditor and Commissioner of Agriculture and Industries, or by a majority of them, acting as a board of appointment. If one or more of the persons appointed on such board of registration shall refuse, neglect, or be unable to qualify or serve, or if a vacancy or vacancies occur in the membership of the board of registrars from any cause, the Governor, Auditor and Commissioner of Agriculture and Industries, or a majority of them, acting as a board of appointment, shall make other appointments to fill such board. Each registrar shall receive two dollars per day, to be paid by the State, and disbursed by the several Judges of Probate, for each entire days attendance upon the sessions of the board. Before entering upon the performance of the duties of his office, each registrar shall take the same oath required of the judicial officers of the State, which oath may be administered by any person authorized by law to administer oaths. The oath shall be in writing and subscribed by the registrar, and filed in the office of the Judge of Probate of the countSecond.- Prior to the first day of August, nineteen hundred and two, the Board of Registrars in each county shall visit each precinct at least once, and oftener, if necessary to make a complete registration of all persons entitled to register, and shall remain there at least one day from eight oclock in the morning until sunset. They shall give at least twenty days notice of the time when, and the place in the precinct where, they will attend to register applicants for registration, by bills posted at five or more public places in each election precinct, and by advertisement once a week for three successive weeks in a newspaper, if there be one published in the county. Upon failure to give such notice, or to attend any appointment made by them in any precinct, they shall, after like notice, fill new appointments therein; but the time consumed by the board in completing such registration shall not exceed sixty working days in any county, except that in counties of more than nine hundred square miles in area, such board may consume seventy-five working days in completing the registration, and except that in counties in which there is any city of eight thousand or more inhabitants, the board may remain in Session, in addition to the time hereinbefore prescribed, not more than three successive weeks in each of such cities; and thereafter the board may sit from time to time in each of such cities not more than one week in each month, and except that in the county of Jefferson the board may hold an additional session of not exceeding five consecutive days duration for each session, in each town or city of more than one thousand and less than eight thousand inhabitants. No person shall be registered except at the county site or in the precinct in which he resides. The registrars shall issue to each person registered a certificate of registratioThird.- The Board of Registrars shall not register any person between the first day of August, nineteen hundred and two, and the Friday next preceding the day of election in November, nineteen hundred and two. On Friday and Saturday next preceding the day of election in November, nineteen hundred and two, they shall sit in the court house of each county during such days, and shall register all applicants having the qualifications prescribed by Section 180 of this Constitution, and not disqualified under Section 182, who shall have reached the age of twenty-one years after the first day of August, nineteen hundred and two, or who shall prove to the reasonable satisfaction of the board that, by reason of physical disability or unavoidable absence from the county, they had no opportunity to register prior to the first day of August, nineteen hundred and two, and they shall not on such days register any other persons. When there are two or more court houses in a county, the registrars may sit during such two days at the court house they may select, but shall give ten days notice, by bills posted at each of the court houses, designating the court house at which they will siFourth.- The Board of Registrars shall hold sessions at the court house of their respective counties during the entire third week in November, nineteen hundred and two, and for six working days next prior to the twentieth day of December nineteen hundred and two, during which sessions they shall register all persons applying who possess the qualifications prescribed in Section 180 of this Constitution, and who shall not be disqualified under Section 182. In counties where there are more than two court houses the Board of Registrars shall divide the time equally between them. The Board of Registrars shall give notice of the time and place of such sessions by posting notices at each court house in their respective counties, and at each voting place and at three other public places in the county, and by publication once a week for two consecutive weeks in a newspaper, if one be published in the county; such notices to be posted and such publications to be commenced as early as practicable in the first week of November, nineteen hundred and two. Failure on the part of the registrars to conform to the provisions of this article as to the giving of the required notices shall not invalidate any registration made by them. Fifth - The Board of Registrars shall have power to examine, under oath or affirmation, all applicants for registration, and to take testimony touching the qualifications of such applicants. Each member of such board is authorized to administer the oath to be taken by the applicants and witnesses, which shall be in the following form, and subscribed by the person making it, and preserved by the board, namely: I solemnly swear (or affirm) that in the matter of the application of ____ ____ for registration as an elector, I will speak the truth, the whole truth, and nothing but the truth, so help me God. Any person who upon such examination makes any wilfully false statement in reference to any material matter touching the qualification of any applicant for registration, shall be guilty of perjury, and upon conviction thereof, shall be imprisoned in the penitentiary for not less than one nor more than five years. Sixth - The action of the majority of the Board of Registrars shall be the action of the board and a majority of the board shall constitute a quorum for the transaction of all business. Any person to whom registration is denied shall have the right of appeal, without giving security for costs, within thirty days after such denial, by filing a petition in the Circuit Court or court of like jurisdiction held for the county in which he seeks to register, to have his qualifications as an elector determined. Upon the filing of the petition the clerk of the court shall give notice thereof to any Solicitor authorized to represent the State in said county, whose duty it shall be to appear and defend against the petition on behalf of the State. Upon such trial the court shall charge the jury only as to what constitutes the qualifications that entitle the applicant to become an elector at the time he applied for registration, and the jury shall determine the weight and effect of the evidence and return a verdict. From the judgment rendered an appeal will lie to the Supreme Court in favor of the petitioner, to be taken within thirty days. Final judgment in favor of the petitioner shall entitle him to registration as of the date of his application to the registrars. Seventh - The Secretary of State shall, at the expense of the State, have prepared and shall furnish to the registrars and judges of probate of the several counties a sufficient number of registration books and of blank forms of the oath, certificates of registration and notices required to be given by the registrars. The cost of the publication in newspapers of the notices required to be given by the registrars shall be paid by the State, the bills therefor to be rendered to the Secretary of State and approved by him. Eighth - Any person who registers for another, or who registers more than once, and any registrar who enters the name of any person on the list of registered voters, without such person having made application in person under oath on a form provided for that purpose, or who knowingly registers any person more than once, or who knowingly enters a name upon the registration list as the name of a voter, without any one of that name applying to register, shall be guilty of a felony, and upon conviction thereof shall be imprisoned in the penitentiary for not less than one nor more than five years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_187_0_1901', 'Alabama1901', '1901', '8', '187', '0', 'Suffrage and Elections', 'The Board of Registrars in each county shall, on or before the first day of February, nineteen hundred and three, or as soon thereafter as practicable, file in the office of the Judge of Probate in their county, a complete list, sworn to by them, of all persons registered in their county, showing the age of such persons so registered, with the precinct or ward in which each of such persons resides set opposite the name of such person, and shall also file a like list in the of the Secretary of State. The Judge of Probate shall, on or before the first day of March, nineteen hundred and three, or as soon thereafter as practicable, cause to be made from such list in duplicate, in the books furnished by the Secretary of State, an alphabetical list by precincts of the persons shown by the list of registrars to have been registered in the county, and shall file one of such alphabetical lists in the office of the Secretary of State; for which services by the Judges of Probate compensation shall be provided by the Legislature. The Judges of Probate shall keep both the original list filed by the registrars and the alphabetical list made therefrom as records in the office of the Judge of Probate of the county. Unless he shall become disqualified under the provisions of this article, any one who shall register prior to the first day of January, nineteen hundred and three, shall remain an elector during life, and shall not be required to register again unless he changes his residence, in which event he may register again on production of his certificate. The certificate of the registrars or of the Judge of Probate or of the Secretary of State shall be sufficient evidence to establish the fact of such life registration. Such certificate shall be issued free of charge to the elector, and the Legislature shall provide by law for the renewal of such certificate when lost, mutilated or destroyed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_188_0_1901', 'Alabama1901', '1901', '8', '188', '0', 'Suffrage and Elections', 'From and after the first day of January, nineteen hundred and three, any applicant for registration may be required to state under oath, to be administered by the registrar or by any person authorized by law to administer oaths, where he lived during the five years next preceding the time at which he applies to register, and the name or names by which he was known during that period, and the name of his employer or employers, if any, during such period. Any applicant for registration who refuses to state such facts, or any of them, shall not be entitled to register, and any person so offering to register, who wilfully makes a false statement in regard to such matters, or any of them, shall be guilty of perjury, and upon conviction thereof shall be imprisoned in the penitentiary for not less than one nor more than five years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_189_0_1901', 'Alabama1901', '1901', '8', '189', '0', 'Suffrage and Elections', 'In the trial of any contested election, and in proceedings to investigate any election, and in criminal prosecutions for violations of the election laws, no person other than a defendant in such criminal prosecutions shall be allowed to withhold his testimony on the ground that he may criminate himself or subject himself to public infamy; but such person shall not be prosecuted for any offense arising out of the transactions concerning which he testified, but may be prosecuted for perjury committed on such examination.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_190_0_1901', 'Alabama1901', '1901', '8', '190', '0', 'Suffrage and Elections', 'The Legislature shall pass laws not inconsistent with this Constitution to regulate and govern elections, and all such laws shall be uniform throughout the State; and shall provide by law for the manner of holding elections and of ascertaining the result of the same, and shall provide general registration laws not inconsistent with the provisions of this article, for the registration of all qualified electors from and after the first day of January, nineteen hundred and three. The Legislature shall also make provision by law, not inconsistent with this article, for the regulation of primary elections, and for punishing frauds at the same, but shall not make primary elections compulsory. The Legislature shall by law provide for purging the registration list of the names of those who die, become insane, or convicted of crime, or otherwise disqualified as electors under the provisions of this Constitution, and of any names which may have been fraudulently entered on such list by the registrars; provided, that a trial by jury may be had on the demand of any person whose name is proposed to be stricken from the list.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_191_0_1901', 'Alabama1901', '1901', '8', '191', '0', 'Suffrage and Elections', 'It shall be the duty of the Legislature to pass adequate laws giving protection against the evils arising from the use of intoxicating liquors at all elections.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_192_0_1901', 'Alabama1901', '1901', '8', '192', '0', 'Suffrage and Elections', 'Electors shall in all cases, except treason, felony or breach of the peace, be privileged from arrest during their attendance at elections, or while going to or returning therefrom.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_193_0_1901', 'Alabama1901', '1901', '8', '193', '0', 'Suffrage and Elections', 'Returns of elections for members of the Legislature and for all civil officers who are to be commissioned by the Governor, except the Attorney General, State Auditor, Secretary of State, State Treasurer, Superintendent of Education, and Commissioner of Agriculture and Industries, shall be made to the Secretary of State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_194_0_1901', 'Alabama1901', '1901', '8', '194', '0', 'Suffrage and Elections', 'The poll tax mentioned in this article shall be one dollar and fifty cents upon each male inhabitant of the State, over the age of twenty-one years, and under the age of forty-five years, who would not now be exempt by law; but the Legislature is authorized to increase the maximum age fixed in this section to not more than sixty years. Such poll tax shall become due and payable on the first day of October in each year, and become delinquent on the first day of the next succeeding February , but no legal process, nor any fee or commission shall be allowed for the collection thereof. The Tax Collector shall make returns of poll tax collections separate from other collections.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_195_0_1901', 'Alabama1901', '1901', '8', '195', '0', 'Suffrage and Elections', 'Any person who shall pay the poll tax of another, or advance him money for that purpose in order to influence his vote, shall be guilty of bribery and upon conviction therefor shall be imprisoned in the penitentiary for not less than one nor more than five years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_8_196_0_1901', 'Alabama1901', '1901', '8', '196', '0', 'Suffrage and Elections', 'If any section or subdivision of this article shall for any reason be, or be held by any court of competent jurisdiction and of final resort to be, invalid, inoperative or void, the residue of this article shall not be thereby invalidated or affected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_197_0_1901', 'Alabama1901', '1901', '9', '197', '0', 'Apportionment', 'The whole number of Senators shall be not less than one-fourth, or more than one-third of the whole number of Representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_198_0_1901', 'Alabama1901', '1901', '9', '198', '0', 'Apportionment', 'The House of Representatives shall consist of not more than one hundred and five members unless new counties shall be created, in which event each new county shall be entitled to one Representative. The members of the House of Representatives shall be apportioned by the Legislature among the several counties of the State, according to the number of inhabitants in them respectively, as ascertained by the decennial census of the United States, which apportionment when made shall not be subject to alteration until the next session of the Legislature after the next decennial census of the United States shall have been taken.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_199_0_1901', 'Alabama1901', '1901', '9', '199', '0', 'Apportionment', 'It shall be the duty of the Legislature at its first session after the taking of the decennial census of the United States in the year nineteen hundred and ten, and after each subsequent decennial census, to fix by law the number of Representatives, and apportion them among the several counties of the State, according to the number of inhabitants in them respectively; provided, that each county shall be entitled to at least one Representative.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_200_0_1901', 'Alabama1901', '1901', '9', '200', '0', 'Apportionment', 'It shall be the duty of the Legislature at its first session after taking the decennial census of the United States in the year nineteen hundred and ten, and after each subsequent decennial census, to fix by law the number of Senators and to divide the State into as many Senatorial districts as there are Senators, which districts shall be as nearly equal to each other in the number of inhabitants as may be, and each shall be entitled to one Senator, and no more; and such districts when formed, shall not be changed until the next apportioning session of the Legislature, after the next decennial census of the United States shall have been taken; provided, that counties created after the next preceding apportioning session of the Legislature may be attached to Senatorial districts. No county shall be divided between two districts, and no district shall be made tip of two or more counties not contiguous to each other.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_201_0_1901', 'Alabama1901', '1901', '9', '201', '0', 'Apportionment', 'Should any decennial census of the United States not be taken, or if when taken, the same, as to this State, be not full and satisfactory, the Legislature shall have power at its first session after the time shall have elapsed for the taking of said census, to provide for an enumeration of all the inhabitants of this State, upon which it shall be the duty of the Legislature to make the apportionment of Representatives and Senators as provided for in this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_202_0_1901', 'Alabama1901', '1901', '9', '202', '0', 'Apportionment', 'Until the Legislature shall make an apportionment of Representatives among the several counties, as provided in the preceding section, the counties of Autauga, Baldwin, Bibb, Blount, Cherokee, Chilton, Choctaw, Clay, Cleburne, Coffee, Colbert, Conecuh, Coosa, Covington, Crenshaw, Cullman, Dale, DeKalb, Escambia, Fayette, Franklin, Geneva, Greene, Lamar, Lawrence, Limestone, Macon, Marion, Marshall, Monroe, Pickens, Randolph, St. Clair, Shelby, Washington, and Winston, shall each have one Representative; the counties of Barbour, Bullock, Butler, Calhoun, Chambers, Clarke, Elmore, Etowah, Hale, Henry, Jackson, Lauderdale, Lee, Lowndes, Madison, Marengo, Morgan, Perry, Pike, Russell, Sumter, Talladega, Tallapoosa, Tuscaloosa, Walker and Wilcox, shall each have two Representatives; the counties of Dallas and Mobile shall each have three Representatives; the county of Montgomery shall have four Representatives; and the county of Jefferson shall have seven Representatives.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9_203_0_1901', 'Alabama1901', '1901', '9', '203', '0', 'Apportionment', 'Until the Legislature shall divide the State into Senatorial districts, as herein provided, the Senatorial districts shall be as follows: First district, Lauderdale and LimestonSecond.district, Lawrence and MorgaThird.district, Blount, Cullman, and WinstoFourth.district, Madison; Fifth district, Jackson and Marshall; Sixth district, Etowah and St. Clair; Seventh district, Calhoun; Eighth district, Talladega; Ninth district, Chambers and Randolph; Tenth district, Tallapoosa and Elmore; Eleventh district, Tuscaloosa; Twelfth district, Fayette, Lamar and Walker; Thirteenth district, Jefferson; Fourteenth district, Pickens and Sumter; Fifteenth district, Autauga, Chilton and Shelby; Sixteenth district, Lowndes; Seventeenth district, Butler, Conecuh and Covington; Eighteenth district, Bibb and Perry; Nineteenth district, Choctaw, Clarke, and Washington; Twentieth district, Marengo; Twenty-first district, Baldwin, Escambia and Monroe; Twenty-second district, Wilcox; Twenty-third district, Dale and Geneva; Twenty-fourth district, Barbour; Twenty-fifth district, Coffee, Crenshaw and Pike; Twenty-sixth district, Bullock and Macon, Twenty-seventh district, Lee and Russell; Twenty-eighth district, Montgomery; Twenty-nineth district, Cherokee and DeKalb; Thirtieth district, Dallas; Thirty-first district, Colbert, Franklin and Marion; Thirty-second district, Greene and Hale; Thirty-third district, Mobile; Thirty-fourth district, Cleburne, Clay and Coosa; Thirty-fifth district, Henry.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_204_0_1901', 'Alabama1901', '1901', '10', '204', '0', 'Exemptions', 'The personal property of any resident of this State, to the value of one thousand dollars, to be selected by such resident, shall be exempt from sale on execution or other process of any court, issued for the collection of any debt contracted since the thirteenth day of July, eighteen hundred and sixty-eight, or after the ratification of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_205_0_1901', 'Alabama1901', '1901', '10', '205', '0', 'Exemptions', 'Every homestead, not exceeding eighty acres and the dwellings and appurtenances thereon, to be selected by the owner thereof, and not in any city, town or village; or in lieu thereof, at the option of the owner, any lot in a city, town or village, with the dwelling and appurtenances thereon, owned and occupied by any resident of this State, and not exceeding the value of two thousand dollars, shall be exempt from sale on execution, or any other process from a court; for any debt contracted since the thirteenth day of July, eighteen hundred and sixty-eight, or after the ratification of this Constitution. Such exemption, however, shall not extend to any mortgage lawfully obtained, but such mortgage or other alienation of said homestead by the owner thereof, if a married man, shall not be valid without the voluntary signature and assent of the wife to the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_206_0_1901', 'Alabama1901', '1901', '10', '206', '0', 'Exemptions', 'The homestead of a family, after the death of the owner thereof, shall be exempt from the payment of any debts contracted since the thirteenth day of July, eighteen hundred and sixty-eight, or after the ratification of this Constitution, in all cases, during the minority of the children.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_207_0_1901', 'Alabama1901', '1901', '10', '207', '0', 'Exemptions', 'The provisions of Sections 204 and 205 of this Constitution shall not be so construed as to prevent a laborers lien for work done and performed for the person claiming such exemption, or a mechanics lien for work done on the promises.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_208_0_1901', 'Alabama1901', '1901', '10', '208', '0', 'Exemptions', 'If the owner of the homestead die, leaving a widow, but no children, such homestead shall be exempt, and the rents and profits thereof shall inure to her benefit.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_209_0_1901', 'Alabama1901', '1901', '10', '209', '0', 'Exemptions', 'The real and personal property of any female in this State, acquired before marriage, and all property, real and personal, to which she may afterwards be entitled by gift, grant, inheritance or devise, shall be and remain the separate estate and property of such female, and shall not be liable for any debts, obligations or engagements of her husband, and may be devised or bequeathed by her, the same as if she were a feme sole.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_10_210_0_1901', 'Alabama1901', '1901', '10', '210', '0', 'Exemptions', 'The right of exemption hereinbefore secured may be waived by an instrument in writing, and when such waiver relates to realty, the instrument must be signed by both the husband and wife, and attested by one witness.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_211_0_1901', 'Alabama1901', '1901', '11', '211', '0', 'Taxation', 'All taxes levied on Property in this State shall be assessed in exact proportion to the value of such property, but no tax shall be assessed upon any debt for rent or hire of real or personal property, while owned by the landlord or hirer during the current year of such rental or hire, if such real or personal property be assessed at its full value.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_212_0_1901', 'Alabama1901', '1901', '11', '212', '0', 'Taxation', 'The power to levy taxes shall not be delegated to individuals, or private corporations or associations.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_213_0_1901', 'Alabama1901', '1901', '11', '213', '0', 'Taxation', 'After the ratification of this Constitution, no new debt shall be created against or incurred by this State, or its authority, except to repel invasion or suppress insurrection, and then only by a concurrence, of two-thirds of the members of each House of the Legislature, and the vote shall be taken by yeas and nays, and entered on the Journals; and any act creating or incurring any new debt against this State, except as herein provided for, shall be absolutely void; provided, the Governor may be authorized to negotiate temporary loans, never to exceed three hundred thousand dollars, to meet the deficiencies in the treasury, and until the same is paid no new loan shall be negotiated; provided further, that this section shall not be so construed as to prevent the issuance of bonds for the purpose of refunding the existing bonded indebtedness of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_214_0_1901', 'Alabama1901', '1901', '11', '214', '0', 'Taxation', 'The Legislature shall not have the power to levy, in any one year it greater rate of taxation than sixty-five one hundreths of one per centum on the value of the taxable property within this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_215_0_1901', 'Alabama1901', '1901', '11', '215', '0', 'Taxation', 'No county in this State shall be authorized to levy a greater rate of taxation, in any one year, on the value of the taxable property therein, than one-half of per centum; provided, that to pay debts existing on the sixth day of December, eighteen hundred and seventy-five, an additional rate of one-fourth of one per centum may be levied and collected, which shall be appropriated exclusively to the payment of such debts and the interest thereon; provided further, that to pay any debt or liability now existing against any county, incurred for the erection, construction or maintenance of the necessary public buildings or bridges, or that may hereafter be created for the erection of necessary public buildings, bridges or roads, any county may levy and collect such special taxes, not to exceed one-fourth of one per centum, as may have been or may hereafter be authorized by law, which taxes, so levied and collected, shall be applied exclusively to the purposes for which the same were so levied an collected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_216_0_1901', 'Alabama1901', '1901', '11', '216', '0', 'Taxation', 'No city, town, village, or other municipal corporation, other than as provided in this article, shall levy or collect a higher rate of taxation in any one year on the property situated therein than one-half of one per centum of the value of such property as assessed for State taxation during the preceding gear; provided that for the purpose of paying debts existing on the sixth day of December, eighteen hundred and seventy-five, and the interest thereon, a tax of one per centum may be levied and collected, to be applied exclusively to the payment of such indebtedness; and provided further, that this section shall not apply to the city of Mobile, which city may from and after the ratification of this Constitution levy a tax not to exceed the rate of three- fourths of one per centum to pay the expenses of the city government, and may also levy a tax not to exceed three-fourths of one per centum to pay the debt existing on the sixth day of December, eighteen hundred and seventy-five, with interest thereon, or any renewal of such debt; and provided further, that this section shall not apply to the cities of Birmingham, Huntsville and Bessemer, and the town of Andalusia which cities and town may levy and collect a tax not to exceed one-half of one per centum, in addition to the tax of one-half of one per centum as hereinbefore allowed to be levied and collected, such special tax to be applied exclusively to the payment of interest on bonds of said cities of Birmingham, Huntsville and Bessemer and town of Andalusia, respectively heretofore issued in pursuance of law, or now authorized by be issued, and for a sinking fund to pay off said bonds at maturity thereof; and provided further, that this section shall not apply to the city of Montgomery, which city shall have the right to levy and collect a tax of not exceeding one-half of one per centum per annum upon the value of taxable property therein, as fixed for State taxation, for general purposes, and an additional tax of not exceeding in three-fourths of one per centum per annum upon the value of the property therein, as fixed for State taxation, to be devoted exclusively to the payment of its public debt, interest thereon, and renewals thereof, and to the maintenance of its public schools, and public conveniences; and provided further, that this section shall not apply to Troy, Attalla, Gadsden, Woodlawn, Brewton, Pratt City, Ensley, Wylam, and Avondale, which cities and towns from and after the ratification of this Constitution, levy and collect an additional tax of not exceeding one-half of one per centum; and provided further, that this section shall not apply to the cities of Decatur, New Decatur, and Cullman, which cities may from and after the ratification of this Constitution, levy and collect an additional tax of not exceeding three-tenths of one per centum per annum; such special tax of said city of Decatur to be applied exclusively for the public schools, public school buildings, and public improvements; and such special tax of New Decatur and Cullman to be applied exclusively for educational purposes, and to be expended under their respective boards of Public School Trustees; but this additional tax shall not be levied by Troy, Attalla, Gadsden, Woodlawn, Brewton, Pratt City, Ensley, Wylam, Avondale, Decatur, New Decatur, or Cullman unless authorized by a majority vote of the qualified electors voting a special election held for the purpose of ascertaining whether or not said tax shall be levied; and provided further, that the purposes for which such special tax is sought to be levied shall be stated in such election call, and, if authorized, the revenue derived from such special tax shall be used for no other purpose than that stated; and provided further, that the additional tax authorized to be levied by the city of Troy, when so levied and collected, shall be used exclusively in the payment of the bonds and interest coupons thereon, hereafter issued in the adjustment of the present bonded indebtedness of said city; and provided further, that the additional tax authorized to be levied and collected by the city of Attalla shall, when so levied and collected, be used exclusively in the payment of bonds to the amount of not exceeding twenty-five thousand dollars, and the interest coupons thereon, hereafter to be issued in the adjustment of the present indebtedness of said city; provided further, that the governing boards of said cities, which are authorized to levy an additional tax, after the holding of an election as aforesaid, are hereby authorized to provide by ordinance the necessary machinery for the holding of said election and declaring the result thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_217_0_1901', 'Alabama1901', '1901', '11', '217', '0', 'Taxation', 'The property of private corporations, associations and individuals of this State shall forever be taxed at the same rate; provided, this section shall not apply to institutions devoted exclusively to religious, educational or charitable purposes.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_218_0_1901', 'Alabama1901', '1901', '11', '218', '0', 'Taxation', 'The Legislature shall not have the power to require counties or other municipal corporations to pay any charges which are now payable out of the State treasury.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_11_219_0_1901', 'Alabama1901', '1901', '11', '219', '0', 'Taxation', 'The Legislature may levy a tax of not more than two and one-half per centum of the value of all estates, real and personal, money, public and private securities of every kind, in this State passing from any person who may die seized and possessed thereof, or of any part of such estate, money or securities, or interest therein transferred by the intestate laws of this State or by will, deed, grant, bargain, sale or gift, made or intended to take effect in possession after death of the grantor, devisor, or donor, to any person or persons, bodies politic or corporate, in trust or otherwise, other than to or for the use of the father, mother, husband, wife, brothers, sisters, children or lineal descendants of the grantor, devisor, donor or intestate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_220_0_1901', 'Alabama1901', '1901', '12', '220', '0', 'Corporations', 'No person, firm, association or corporation shall be authorized or permitted to use the streets, avenues, alleys or public places of any city, town or village for the construction or operation of any public utility or private enterprise, without first obtaining the consent of the proper authorities of such city, town or village.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_221_0_1901', 'Alabama1901', '1901', '12', '221', '0', 'Corporations', 'The Legislature shall not enact any law which will permit any person, firm, corporation or association to pay a privilege, license or other tax to the State of Alabama, and relieve him or it from the payment of all other privilege and license taxes in the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_222_0_1901', 'Alabama1901', '1901', '12', '222', '0', 'Corporations', 'The Legislature, after the ratification of this Constitution shall have authority to pass general laws authorizing the counties, cities, towns, villages, districts or other political subdivisions of counties to issue bonds, but no bonds shall be issued under authority of a general law unless such issue of bonds be first authorized by a majority vote by ballot of the qualified voters of such county, city, town, village, district, or other political subdivision of a county, voting upon such proposition. The ballot used at such election shall contain the words For ----- bond issue, and Against ----- bond issue, (the character of the bond to be shown in the blank space,) and the voter shall indicate his choice by placing a cross mark before or after the one or the other. This section shall not apply to the renewal, refunding or reissue of bonds lawfully issued, nor to the issuance of bonds in cases where the same have been authorized by laws enacted prior to the ratification of this Constitution, nor shall this section apply to obligations incurred or bonds to be issued to procure means to pay for street and sidewalk improvements or sanitary or storm water sewers, the cost of which is to be assessed, in whole or in part, against the property abutting said improvements or drained by such sanitary or storm water sewers.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_223_0_1901', 'Alabama1901', '1901', '12', '223', '0', 'Corporations', 'No city, town or other municipality shall make any assessment for the cost of sidewalks or street paving, or for the cost of the construction of any sewers against property abutting in such street or sidewalk so paved, or drained by such sewers, in excess of the increased value of such property by reason of the special benefits derived from such improvements.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_224_0_1901', 'Alabama1901', '1901', '12', '224', '0', 'Corporations', 'No county shall become indebted in an amount including present indebtedness, greater than three and one-half per centum of the assessed value of the property therein; provided, this limitation shall not affect any existing indebtedness in excess of such three and one-half per centum, which has already been created or authorized by existing law to be created; provided, that any county which has already incurred a debt exceeding three and one-half per centum of the assessed value of the property therein, shall be authorized to incur an indebtedness of one and a half per centum of the assessed value of such property in addition to the debt already existing. Nothing herein contained shall prevent any county from issuing bonds, or other obligations, to fund or refund any indebtedness now existing or authorized by existing laws to be created.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_225_0_1901', 'Alabama1901', '1901', '12', '225', '0', 'Corporations', 'No city, town or other municipal corporation having a population of less than six thousand, except as hereinafter provided, shall become indebted in an amount, including present indebtedness, exceeding five per centum of the assessed value of the property therein, except for the construction or purchase of water works, gas or electric lighting plants, or sewerage or for the improvement of streets, for which purposes and additional indebtedness not exceeding three per centum may be created; provided, this limitation shall not affect any debt now authorized by law to be created, nor any temporary loans to be paid within one year, made in anticipation of the collection of taxes, not exceeding one-fourth of the annual revenues of such city or town. All towns and cities having a population of six thousand or more, also Gadsden, Ensley, Decatur, and New Decatur, are hereby authorized to become indebted in an amount, including present indebtedness not exceeding seven per centum of the assessed valuation of the property therein, provided that there shall not be included in the limitation of the indebtedness of such last described cities and towns the following classes of indebtedness, to-wit: Temporary loans, to be paid within one year, made in anticipation of the collection of taxes, and not exceeding one-fourth of the general revenues bonds or other obligations already issued, or which may hereafter be issued for the purpose of acquiring, providing or constructing school houses, water works and sewers; and obligations incurred and bonds issued for street or sidewalk improvements, where the cost of the same, in whole or in part, is to be assessed against the property abutting said improvements; provided, that the proceeds of all obligations issued as herein provided, in excess of said seven per centum shall not be used for any purpose other than that for which said obligations were issued. Nothing contained in this article shall prevent the funding or refunding of existing indebtedness. This section shall not apply to the cities of Sheffield and Tuscumbia.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_226_0_1901', 'Alabama1901', '1901', '12', '226', '0', 'Corporations', 'No city, town or village, whose present indebtedness exceeds the limitation imposed by this Constitution, shall be allowed to become indebted in any further amount, except as otherwise provided in this Constitution, until such indebtedness shall be reduced within such limit; provided, however, that nothing herein contained shall prevent any municipality, except the city of Gadsden, from issuing bonds already authorized by law; provided further, this section shall not apply to the cities of Sheffield and Tuscumbia.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_227_0_1901', 'Alabama1901', '1901', '12', '227', '0', 'Corporations', 'Any person, firm, association or corporation who may construct or operate any public utility along or across the public streets of any city, town or village, under any privilege or franchise permitting such construction or operation, shall be liable to abutting proprietors for the actual damage done to the abutting property on account of such construction or operation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_228_0_1901', 'Alabama1901', '1901', '12', '228', '0', 'Corporations', 'No city or town having a population of more than six thousand shall have authority to grant to any person, firm, corporation or association the right to use its streets, avenues, alleys, or public places for the construction or operation of water works, gas works, telephone or telegraph line, electric light or power plants, steam or other heating plants, street railroads, or any other public utility, except railroads other than street railroads for a longer period than thirty years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_229_0_1901', 'Alabama1901', '1901', '12', '229', '0', 'Corporations', 'The Legislature shall pass no special act conferring corporate powers, but it shall pass general laws tinder which corporations may be organized and corporate powers obtained, subject, nevertheless, to repeal at the will of the Legislature; and shall pass general laws under which charters may be altered or amended. The Legislature shall, by general law, provide for the payment to the State of Alabama of a franchise tax by corporations organized under the laws of this State, which shall by in proportion to the amount of capital stock; but strictly benevolent, educational or religious corporations shall not be required to pay such a tax. The charter of any corporation shall be subject to amendment, alteration or repeal under general laws.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_230_0_1901', 'Alabama1901', '1901', '12', '230', '0', 'Corporations', 'All existing charters, under which a bona fide organization shall not have taken place and business commenced in good faith within twelve months from the time of the ratification of this Constitution, shall thereafter have no validity.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_231_0_1901', 'Alabama1901', '1901', '12', '231', '0', 'Corporations', 'The Legislature shall not remit the forfeiture of the charter of any corporation. now existing, or alter or amend the same, nor pass any general or special law for the benefit of such corporation, other than in execution of a trust created by law or by contract, except upon condition that such corporation shall thereafter hold its charter subject to the provisions of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_232_0_1901', 'Alabama1901', '1901', '12', '232', '0', 'Corporations', 'No foreign corporation shall do any business in this State without having at least one known place of business and an authorized agent or agents therein, and without filing with the Secretary of State a certified copy of its articles of incorporation or association. Such corporation may be sued in any county where it does business, by service of process upon an agent anywhere in the State. The Legislature shall, by general law, provide for the payment to the State of Alabama of a franchise tax by such corporation, but such franchise tax shall be based on the actual amount of capital employed in this State. Strictly benevolent, educational or religious corporations shall not be required to pay such a tax.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_233_0_1901', 'Alabama1901', '1901', '12', '233', '0', 'Corporations', 'No corporation shall engage in any business other than that expressly authorized in its charter or articles of incorporation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_234_0_1901', 'Alabama1901', '1901', '12', '234', '0', 'Corporations', 'No corporation shall issue stock or bonds except for money, labor done or property actually received; and all fictitious increase of stock or indebtedness shall be void. The stock and bonded indebtedness of corporations shall not be increased except in pursuance of general laws, nor without the consent of the persons holding the larger amount in value of stock, first obtained at a meeting to be held after thirty days notice, given in pursuance of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_235_0_1901', 'Alabama1901', '1901', '12', '235', '0', 'Corporations', 'Municipal and other corporations and individuals invested with the privilege of taking property for public use, shall make just compensation, to be ascertained as may be provided by law, for the property taken, injured or destroyed by the construction or enlargement of its works, highways or improvements, which compensation shall be paid before such taking, injury or destruction. The Legislature is hereby prohibited from denying the right of appeal from any preliminary assessment of damages against any such corporations or individuals made by viewers or otherwise, but such appeal shall not deprive those who have obtained the judgment of condemnation from a right of entry, provided the amount of damages assessed shall have been paid in the court in money, and a bond shall have been given in not less than double the amount of the damages assessed, with good and sufficient sureties, to pay such damages as the property owner may sustain; and the amount of damages in all cases of appeals shall, on the demand of either party, be determined by a Jury according to law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_236_0_1901', 'Alabama1901', '1901', '12', '236', '0', 'Corporations', 'Dues from private corporations shall be secured by such means as may be prescribed by law; but in no case shall any stockholder be individually liable otherwise than for the unpaid stock owned by him or her.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_237_0_1901', 'Alabama1901', '1901', '12', '237', '0', 'Corporations', 'No corporation shall issue preferred stock without the consent of the owners of two-thirds of the stock of said corporation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_238_0_1901', 'Alabama1901', '1901', '12', '238', '0', 'Corporations', 'The Legislature shall have the power to alter, amend or revoke any charter of incorporation now existing and revocable at the ratification of this Constitution, or any that may be hereafter created, whenever, in its opinion, such charter may be injurious to the citizens of this State, in such manner, however, that no injustice shall be done to the stockholders.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_239_0_1901', 'Alabama1901', '1901', '12', '239', '0', 'Corporations', 'Any association or corporation organized for the purpose, or any individual, shall have the right construct and maintain lines of telegraph and telephone within this State, and connect the same with other lines; and the Legislature shall, by general law of uniform operation, provide reasonable regulations to give full effect to this section. No telegraph or telephone company shall consolidate with or hold a controlling interest in the stock or bonds of any other telegraph or telephone company owning a complete line, or acquire, by purchase or otherwise, any other competing line of telegraph or telephone.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_240_0_1901', 'Alabama1901', '1901', '12', '240', '0', 'Corporations', 'All corporations shall have the right to sue, and shall be subject to be sued, in all courts in like cases as natural persons.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_241_0_1901', 'Alabama1901', '1901', '12', '241', '0', 'Corporations', 'The term corporation, as used in this article, shall be construed to include all joint stock companies, and all associations having any of the powers or privileges of corporations not possessed by individuals or partnerships.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_242_0_1901', 'Alabama1901', '1901', '12', '242', '0', 'Corporations', 'All railroads and canals, not constructed and used exclusively for private purposes, shall be public highways, and all railroads and canal companies shall be common carriers. Any association or corporation organized for the purpose shall have the right to construct and operate a railway between any points in this State, and connect at the State line with railroads of other States. Every railroad company shall have the right with its road to intersect, connect with, or cross any other railroad, and each shall receive and transport the freight, passengers and cars, loaded or empty, of the others, without delay or discrimination.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_243_0_1901', 'Alabama1901', '1901', '12', '243', '0', 'Corporations', 'The power and authority of regulating railroad freight and passenger tariffs, the locating and building of passenger and freight depots, correcting abuses, preventing unjust discrimination and extortion and requiring reasonable and just rates of freight and passenger tariffs, are hereby conferred upon the Legislature, whose duty it shall be to pass laws from time to time regulating freight and passenger tariffs, to prohibit unjust discrimination on the various railroads, canals and rivers of the State, and to prohibit the charging of other than just and reasonable rates, and enforce the same by adequate penalties.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_244_0_1901', 'Alabama1901', '1901', '12', '244', '0', 'Corporations', 'No railroad or other transportation company or corporation shall grant free passes or sell tickets or passes at a discount, other than as sold to the public generally, to any member of the Legislature, or to any officer exercising judicial functions under the laws of this State; and any such member or officer receiving such pass or ticket for himself or procuring the same for another, shall be guilty of a misdemeanor, and upon conviction shall be fined not exceeding five hundred dollars and, at the discretion of the court trying the case, in addition to such fine, may be imprisoned for a term not exceeding six months, and upon conviction, shall be subject to impeachment and removal from office. The courts having jurisdiction shall give this law specially in charge to the Grand Juries, and when the evidence is sufficient to authorize an indictment, the Grand Jury must present a true bill. The Circuit Court or any court of like jurisdiction in any county into or through which such member or officer is transported by the use of such prohibited pass or ticket, shall have jurisdiction of the case, provided, only one prosecution shall be had or the same offense; and provided further, that the trial and judgment for one offense shall not bar a prosecution for another offense, when the same pass or ticket is used; and provided further, that nothing herein shall prevent a member of the Legislature who is a bona fide employe of a railroad or other transportation company or corporation at the time of his election, from accepting or procuring for himself or another, not a member of the Legislature, or officer exercising judicial functions, a free pass over the railroads or other transportation company or corporation by which he is employed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_245_0_1901', 'Alabama1901', '1901', '12', '245', '0', 'Corporations', 'No railroad company shall give or pay any rebate, or a bonus in the nature thereof, directly or indirectly, or do any act to mislead or deceive the public as to the real rates charged or received for freights or passage; and any such payments shall be illegal and void, and these prohibitions shall be enforced by suitable penalties.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_12_246_0_1901', 'Alabama1901', '1901', '12', '246', '0', 'Corporations', 'No railroad, canal or transportation company in existence at the time of the ratification of this Constitution shall have the benefit of any future legislation by general or special laws, other than in execution of a trust created by law or by contract, except on condition of complete acceptance of all the provisions of this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_247_0_1901', 'Alabama1901', '1901', '13', '247', '0', 'Banks', 'The Legislature shall not have the power to establish or incorporate any bank or banking company, or moneyed institution, for the purpose of issuing bills of credit, or bills payable to order or bearer, except under the conditions prescribed in this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_248_0_1901', 'Alabama1901', '1901', '13', '248', '0', 'Banks', 'No bank shall be established otherwise than under a general banking law, nor other than upon a specie basis; provided, that any bank may be established with authority to issue bills to circulate as money in an amount equal to the face value of bonds of the United States, or of this State, convertible into specie at their face value, which shall, before such bank is authorized to issue bills for circulation, be deposited with the State Treasurer, or other depository prescribed by law, in an amount equal to the aggregate of such proposed issue, with power in such treasurer or depository to dispose of any or all of such bonds for a sufficient amount of specie to redeem the circulating notes of such bank at any time and without delay, should such bank suspend specie payment or fail to redeem its notes on demand.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_249_0_1901', 'Alabama1901', '1901', '13', '249', '0', 'Banks', 'All bills or notes issued as money shall be at all times redeemable in gold or silver, and no law shall be passed sanctioning, directly or indirectly, the suspension by any bank or banking company of specie payment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_250_0_1901', 'Alabama1901', '1901', '13', '250', '0', 'Banks', 'Holders of bank notes, and depositors who have not stipulated for interest, shall, for such notes and deposits, be entitled, in case of insolvency, to preference of payment over all other creditors; provided, this section shall apply to all banks, whether incorporated or not.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_251_0_1901', 'Alabama1901', '1901', '13', '251', '0', 'Banks', 'Every bank or banking company shall be required to cease all banking operations within twenty years from the time of its organization, unless the time be extended by law, and promptly thereafter close its business; but after it has closed its business it shall have corporate capacity to sue and shall be liable to suits until its affairs and liabilities are fully closed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_252_0_1901', 'Alabama1901', '1901', '13', '252', '0', 'Banks', 'No bank shall receive, directly or indirectly, a greater rate of interest than shall be allowed by law to individuals for lending money.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_253_0_1901', 'Alabama1901', '1901', '13', '253', '0', 'Banks', 'Neither the State nor any political subdivision thereof shall be a stockholder in any bank, nor shall the credit of the State or any political subdivision thereof ever be given or lent to any banking company, association or corporation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_254_0_1901', 'Alabama1901', '1901', '13', '254', '0', 'Banks', 'The Legislature shall by appropriate laws provide for the examination, by some public officer, of all banks and banking institutions and trust companies engaged in banking business in this State; and each of such banks and banking companies or institutions shall, through its president or such other officer as the Legislature may designate, make a report under oath of its resources and liabilities at least twice a year.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_13_255_0_1901', 'Alabama1901', '1901', '13', '255', '0', 'Banks', 'The provisions of this article shall apply to all banks except National banks, and to all trust companies and individuals doing a banking business, whether incorporated or not.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_256_0_1901', 'Alabama1901', '1901', '14', '256', '0', 'Education', 'The Legislature shall establish, organize and maintain a liberal system of public schools throughout the State for the benefit of the Children thereof between the ages of seven and twenty-one years. The public school fund shall be apportioned to the several counties in proportion to the number of school children of school age therein, and shall be so apportioned to the schools in the districts or townships in the county as to provide, as nearly as practicable, school terms of equal duration in such school districts or townships. Separate schools shall be provided for white and colored children, and no child of either race shall be permitted to attend a school of the other race.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_257_0_1901', 'Alabama1901', '1901', '14', '257', '0', 'Education', 'The principal of all funds arising from the sale or other disposition of lands or other property, which as been or may hereafter be granted or entrusted to this State of given by the United States for educational purposes shall be preserved inviolate and undiminished; and the income arising there faithfully applied to the specific object of the original grants or appropriations.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_258_0_1901', 'Alabama1901', '1901', '14', '258', '0', 'Education', 'All lands or other property given by individuals, or appropriated by the State for educational purposes, and all estates of deceased persons who die without leaving a will or heir shall be faithfully applied to the maintenance of the public schools.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_259_0_1901', 'Alabama1901', '1901', '14', '259', '0', 'Education', 'All poll taxes collected in this State shall be applied to the support of the public schools in the respective counties where collected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_260_0_1901', 'Alabama1901', '1901', '14', '260', '0', 'Education', 'The income arising from the Sixteenth Section trust fund, the surplus revenue fund, until it is called for by the United States government, and the funds enumerated in Sections 257 and 258 of this Constitution, together with a special annual tax of thirty cents on each one hundred dollars of taxable property in this State, which the Legislature shall levy, shall be applied to the support and maintenance of the public schools, and it shall be the duty of the Legislature to increase the public school fund from time to time, as the necessity therefor and the condition of the treasury and the resources of the State may justify; provided, that nothing herein contained shall be so construed as to authorize the Legislature to levy in any one year a greater rate of State taxation for all purposes, including schools, than sixty-five cents on each one hundred dollars worth of taxable property; and provided further, that nothing herein contained shall prevent the Legislature from first providing for the payment of the bonded indebtedness of the State and interest thereon out of all the revenues of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_261_0_1901', 'Alabama1901', '1901', '14', '261', '0', 'Education', 'Not more than four per cent. of all moneys raised, or which may hereafter be appropriated for the support of public schools, shall be used or expended otherwise than for the payment of teachers employed in such schools; provided, that the Legislature may, by a vote of two-thirds of each House, suspend the operation of this section.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_262_0_1901', 'Alabama1901', '1901', '14', '262', '0', 'Education', 'The supervision of the public schools shall be vested in a Superintendent of Education, whose powers, duties and compensation shall be fixed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_263_0_1901', 'Alabama1901', '1901', '14', '263', '0', 'Education', 'No money raised for the support of the public schools, shall be appropriated to or used for the support of any sectarian or denominational school.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_264_0_1901', 'Alabama1901', '1901', '14', '264', '0', 'Education', 'The State University shall be under the management and control of a board of trustees which shall consist of two members from the Congressional district in which the University is located, one from each of the other Congressional districts in the State, the Superintendent of Education and the Governor who shall be ex-officio president of the board. The members of the Board of Trustees as now constituted shall hold office until their respective terms expire under existing law, and until their successors shall be elected and confirmed as hereinafter required. Successors to those trustees whose terms expire in nineteen hundred and two shall hold office until nineteen hundred and seven; successors to those trustees whose terms expire in nineteen hundred and four shall hold office until nineteen hundred and eleven; successors to those trustees whose terms expire in nineteen hundred and six shall hold office until nineteen hundred and fifteen; and thereafter their successors shall hold office for a term of twelve years. When the term of any member of such board shall expire, the remaining members of the board shall by secret ballot elect his successor; provided, that any trustee so elected shall hold office from the date of his election until his confirmation or rejection by the Senate, and, if confirmed, until the expiration of the term for which he was elected, and until his successor is elected. At every meeting of the Legislature the Superintendent of Education shall certify to the Senate the names of all who have been so elected since the last session of the Legislature, and the Senate shall confirm or reject them, as it shall determine is for the best interest of the University. If it reject the names of any members, it shall thereupon elect trustees in the stead of those rejected. In case of a vacancy on said board by death or resignation of a member, or from any cause other than the expiration or his term of office, the board shall elect his successor who shall hold office until the next session of the Legislature. No trustee shall receive any pay or emolument other than his actual expenses incurred in the discharge of his duties as such.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_265_0_1901', 'Alabama1901', '1901', '14', '265', '0', 'Education', 'After the ratification of this Constitution there shall be paid, out of the treasury of this State, at the time and in the manner provided by law, the sum of not less than thirty-six thousand dollars per annum as interest on the funds of the University of Alabama, heretofore covered into the treasury, for the maintenance and support of said institution; provided, that the Legislature shall have the power at any time they deem proper for the best interest of said University to abolish the military system at said institution, or reduce the said system to a department of instruction, and that such action on the part of the Legislature shall not cause any diminution of the amount of the annual interest payable out of the treasury for the support and maintenance of said University.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_266_0_1901', 'Alabama1901', '1901', '14', '266', '0', 'Education', 'The Alabama Polytechnic Institute, formerly called the Agricultural and Mechanical College, shall be under the management and control of a Board of Trustees, which shall consist of two members from the Congressional district in which the institute is located, and one from each of the other Congressional districts in the State, the State Superintendent of Education, and the Governor, who shall be ex-officio president of the board. The trustees shall be appointed by the Governor, by and with the advice and consent of the Senate, and shall hold office for a term of twelve years, and until their successors shall be appointed and qualified. The board shall be divided into three classes, as nearly equal as may be, so that one-third may be chosen quadrennially. Vacancies occurring in the office of trustees from death or resignation, and the vacancies regularly occurring in the year nineteen hundred and five, shall be filled by the Governor, and such appointee shall hold office until the next meeting of the Legislature. Successors to those trustees whose terms expire in nineteen hundred and three shall hold office until nineteen hundred and eleven; successors to those whose terms of office expire in nineteen hundred and five shall hold office until nineteen hundred and fifteen; and successors to those whose terms of office expire in nineteen hundred and seven shall hold office until nineteen hundred and nineteen. No trustee shall receive any pay or emolument other than his actual expenses incurred in the discharge of his duties as such.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_267_0_1901', 'Alabama1901', '1901', '14', '267', '0', 'Education', 'The Legislature shall not have power to change the location of the State University, or the Alabama Polytechnic Institute, or the Alabama school for the Deaf and Blind, or the Alabama Girls Industrial school, as now established by law, except upon a vote of two-thirds of the Legislature taken by yeas and nays and entered upon the Journals.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_268_0_1901', 'Alabama1901', '1901', '14', '268', '0', 'Education', 'The Legislature shall provide for taking a school census by townships and districts throughout the State not oftener than once in two years, and shall provide for the punishment of all persons or officers making false or fraudulent enumerations and returns; provided, the State Superintendent of Education may order and supervise the taking of a new census in any township, district or county whenever he may have reasonable cause to believe that false or fraudulent returns have been made.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_269_0_1901', 'Alabama1901', '1901', '14', '269', '0', 'Education', 'The several counties in this State shall have power to levy and collect a special tax not exceeding ten cents on each one hundred dollars of taxable property in such counties, for the support of public schools; provided, that the rate of such tax, the time it is to continue, and the purpose thereof, shall have been first submitted to a vote of the qualified electors of the county, and voted for by three-fifths of those voting at such election; but the rate of such special tax shall not increase the rate of taxation, State and county combined, in any one year, to more than one dollar and twenty-five cents on each one hundred dollars of taxable property; excluding, however, all special county taxes for public buildings, roads, bridges and the payment of debts existing at the ratification of the Constitution of eighteen hundred and seventy-five. The funds arising from such special school tax shall be so apportioned and paid through the proper school officials to the several schools in the townships and districts in the county that the school terms of the respective schools shall be extended by such supplement as nearly the same length of time as practicable; provided, that this section shall not apply to the cities of Decatur, New Decatur and Cullman.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_14_270_0_1901', 'Alabama1901', '1901', '14', '270', '0', 'Education', 'The provisions of this article and of any act of the Legislature passed in pursuance thereof to establish, organize and maintain a system of public schools throughout the State, shall apply to Mobile county, only so far as to authorize and require the authorities designated by law to draw the portions of the funds to which said county shall be entitled for school purposes and to make reports to the Superintendent of Education as may be prescribed by law; and all special incomes and powers of taxation as now authorized by law for the benefit of public schools in said county shall remain undisturbed until otherwise provided by the Legislature; provided, that separate schools for each race shall always be maintained by said school authorities.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_271_0_1901', 'Alabama1901', '1901', '15', '271', '0', 'Militia', 'The Legislature shall have power to declare who shall constitute the militia of the State, and to provide for organizing, arming and disciplining the same; and the Legislature may provide for the organization of a State and Naval Militia.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_272_0_1901', 'Alabama1901', '1901', '15', '272', '0', 'Militia', 'The Legislature, in providing for the organization, equipment and discipline of the militia, shall conform as nearly as practicable to the regulations for the government of the armies of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_273_0_1901', 'Alabama1901', '1901', '15', '273', '0', 'Militia', 'Each company and regiment shall elect its own company and regimental officers; but if any company or regiment shall neglect to elect such officers within the time prescribed by law, they may be appointed by the Governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_274_0_1901', 'Alabama1901', '1901', '15', '274', '0', 'Militia', 'Volunteer organizations of infantry, cavalry, and artillery and naval militia may be formed in such manner and under such restrictions and with such privileges as may be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_275_0_1901', 'Alabama1901', '1901', '15', '275', '0', 'Militia', 'The militia and volunteer forces shall, in all cases, except treason, felony and breach of the peace, be privileged from arrest during their attendance at musters, parades and elections, and in going to and returning from the same.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_276_0_1901', 'Alabama1901', '1901', '15', '276', '0', 'Militia', 'The Governor shall, with the advice and consent of the Senate, appoint all general officers, whose terms of office shall be four years. The Governor, the generals and regimental and battalion commanders shall appoint their own staffs, as may be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_277_0_1901', 'Alabama1901', '1901', '15', '277', '0', 'Militia', 'The Legislature shall provide for the safe keeping of the arms, ammunition and accouterments and military records, banners and relics of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_15_278_0_1901', 'Alabama1901', '1901', '15', '278', '0', 'Militia', 'The officers and men of the militia and volunteer forces shall not be entitled to or receive any pay, rations or emoluments when not in active service.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_16_279_0_1901', 'Alabama1901', '1901', '16', '279', '0', 'Oath of Office', 'All members of the Legislature, and all officers, executive and judicial, before they enter upon the execution of the duties of their respective offices, shall take the following oath or affirmation: I, ----- -----, solemnly swear (or affirm, as the case may be), that I will support the Constitution of the United States, and the Constitution of the State of Alabama, so long as I continue a citizen thereof; and that I will faithfully and honestly discharge the duties of the office upon which I am about to enter, to the best of my ability. So help me God. The oath may be administered by the presiding officer of either House of the Legislature, or by any officer authorized by law to administer an oath.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_17_280_0_1901', 'Alabama1901', '1901', '17', '280', '0', 'Miscellaneous', 'No person holding an office of profit under the United States, except postmasters, whose annual salaries do not exceed two hundred dollars, shall during his continuance in such office hold any office of profit under this State; nor, unless otherwise provided in this Constitution, shall any person hold two offices of profit at one and the same time under this State, except Justices of the Peace, Constables, Notaries Public and Commissioners of Deeds.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_17_281_0_1901', 'Alabama1901', '1901', '17', '281', '0', 'Miscellaneous', 'The salary, fees or compensation of any officer holding any civil office of profit under this State or any county or municipality thereof, shall not be increased or diminished during the term for which he shall have been elected or appointed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_17_282_0_1901', 'Alabama1901', '1901', '17', '282', '0', 'Miscellaneous', 'It is made the duty of the Legislature to enact all laws necessary to give effect to the provisions of this Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_17_283_0_1901', 'Alabama1901', '1901', '17', '283', '0', 'Miscellaneous', 'The act of the General Assembly of Alabama, entitled, An Act to consolidate and adjust the bonded debt of the State of Alabama, approved February 18th, 1895, and an act amendatory thereof entitled, An Act to amend Section 6 of an act to consolidate and adjust the bonded debt of the State of Alabama, approved February 18th, 1895, which said last named act was approved February 16th, 1899, are hereby made valid, and both of said acts shall have the full force and effect of law, except insofar as they authorize the redemption before maturity of the bonds authorized by said acts to be issued. The Governor is authorized and empowered to act under the same and to carry out all the provisions thereof; provided, that the bonds authorized to be issued by said acts and issued thereunder may be made payable at any time, not exceeding fifty years from the date thereof, and shall not be redeemable until their maturity.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_18_284_0_1901', 'Alabama1901', '1901', '18', '284', '0', 'Amendments', 'Amendments may be proposed to this Constitution by the Legislature in the manner following: The proposed amendments shall be read in the House in which they originate on three several days, and if upon the third reading three-fifths of all the members elected to that House shall vote in favor thereof, the proposed amendments shall be sent to the other House, in which they shall likewise be read on three several days, and if upon the third reading three-fifths of all the members elected to that House shall vote in favor of the proposed amendments, the Legislature shall order an election by the qualified electors of the State upon the proposed amendments, to be held either at the general election next succeeding the session of the Legislature at which the amendments are proposed or upon another day appointed by the Legislature not less than three months after the final adjournment of the session of the Legislature at which the amendments were proposed. Notice of such election, together with the proposed amendments, shall be given by proclamation of the Governor, which shall be published in every county in such manner as the Legislature shall direct, for at least eight successive weeks next preceding the day appointed for such election. On the day so appointed an election shall be held for the vote of the qualified electors of the State upon the proposed amendments. If such election be held on the day of the general election, the officers of such general election shall open a poll for the vote of the qualified electors upon the proposed amendments; if it be held on a day other than that of a general election, officers for such election shall be appointed, and the election shall be held in all things in accordance with the law governing general elections. In all elections upon such proposed amendments, the votes cast thereat shall be canvassed, tabulated and returns thereof be made to the Secretary of State, and counted , in the same manner as in elections for Representatives to the Legislature; and if it shall thereupon appear that a majority of the qualified electors who voted at such election upon the proposed amendments voted in favor of the same, such amendments shall be valid to all intents and purposes as parts of this Constitution. The results of such election shall be made known by proclamation of the Governor. Representation in the Legislature shall be based upon population, and such basis of representation shall not be changed by constitutional amendment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_18_285_0_1901', 'Alabama1901', '1901', '18', '285', '0', 'Amendments', 'Upon the ballots used at all elections provided for in Section 284 of this Constitution, the substance or subject matter of each proposed amendment shall be so printed that the nature thereof shall be clearly indicated. Following each proposed amendment on the ballot shall be printed the word Yes and immediately under that shall be printed the word No. The choice of the elector shall be indicated by a cross mark made by him or under his direction opposite the word expressing his desire, and no amendment shall be adopted unless it receives the affirmative vote of a majority of all the qualified electors who vote at such election.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_18_286_0_1901', 'Alabama1901', '1901', '18', '286', '0', 'Amendments', 'No convention shall hereafter be held for the purpose of altering or amending the Constitution of this State, unless after the Legislature by a vote of the majority of all the members elected to each House, has passed an act or resolution calling a Convention for such purpose, the question of Convention or No Convention shall be first, submitted to a vote of all the qualified electors of the State and approved by a majority of those voting at such election. No act or resolution of the Legislature calling a convention for the purpose of altering or amending the Constitution of this State, shall be repealed, except upon the vote of a majority of all the members elected to each House at the same session at which such act or resolution was passed; provided, nothing herein contained shall be construed as restricting the jurisdiction and power of the convention, when duly assembled in pursuance of this section, to establish such ordinances and to do and perform such things as to the convention may seem necessary or proper for the purpose of altering, revising, or amending the existing Constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_18_287_0_1901', 'Alabama1901', '1901', '18', '287', '0', 'Amendments', 'All votes of the Legislature upon proposed amendments to this Constitution, and upon bills or resolutions calling a Convention for the purpose of altering or amending the Constitution of this State, shall be taken by yeas and nays and entered on the Journals. No act or resolution of the Legislature passed in accordance with the provisions of this article, proposing amendments to this Constitution, or calling a convention for the purpose of altering or amending the Constitution of this State, shall be submitted for the approval of the Governor, but shall be valid without his approval.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9001_1_0_1901', 'Alabama1901', '1901', '9001', '1', '0', 'Preamble', 'We, the people of the State of Alabama, in order to establish justice, insure domestic tranquility and secure the blessings of liberty to ourselves and our posterity, invoking the favor and guidance of Almighty God, do ordain and establish the following Constitution and form of government for the State of Alabama:', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_1_0_1901', 'Alabama1901', '1901', '9003', '1', '0', 'Schedule', 'That all laws in force at the ratification of this Constitution and not inconsistent therewith, shall remain in full force until altered or repealed by the Legislature; and all rights, actions, prosecutions, claims and contracts of the State, counties, municipal corporations, individuals, or bodies corporate, not inconsistent with this Constitution, shall continue to be valid as if this Constitution had not been ratified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_2_0_1901', 'Alabama1901', '1901', '9003', '2', '0', 'Schedule', 'That all bonds executed by or to any officer of this State, all recognizances, obligations, and all other instruments executed to this State, or to any subdivision or municipality thereof, before the ratification of this Constitution, and all fines, taxes, penalties, and forfeitures due and owing to the State, or any subdivision or municipality thereof, and all writs, suits, prosecutions, claims and causes of action, except as herein otherwise provided, shall continue and remain unaffected by the ratification of this Constitution. All indictments which have been found, or which may hereafter be found, for any crime or offense committed before the ratification of this Constitution shall be proceeded upon in the same manner as if this Constitution had not been ratified.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_3_0_1901', 'Alabama1901', '1901', '9003', '3', '0', 'Schedule', 'That all the executive and judicial officers and all other officers in this State, who were elected at the elections held in this State on the first Monday in August in the years eighteen hundred and ninety-eight and nineteen hundred, who have been appointed since that time, and all members of the present General Assembly and all who may be hereafter elected members of the present General Assembly, and all other officers holding office at the time of the ratification of this Constitution, shall, except as otherwise provided in this Constitution, continue in office and exercise the duties thereof until their respective terms shall expire as provided by the Constitution of eighteen hundred and seventy-five or the laws of this State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_4_0_1901', 'Alabama1901', '1901', '9003', '4', '0', 'Schedule', 'This Constitution shall be submitted to the qualified electors of this State for ratification or rejection, as authorized and required by an act of the General Assembly of this State, entitled An Act to provide for holding a convention to revise and amend the Constitution of this State, approved the eleventh day of December, nineteen hundred; and no elector shall be deprived of his right to vote at tho election to be held for such purpose by reason of his not being registered.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_5_0_1901', 'Alabama1901', '1901', '9003', '5', '0', 'Schedule', 'That instead of the publication as required by the act to provide for holding a convention to revise and amend the Constitution, approved the eleventh day of December, nineteen hundred, the Governor of this State is hereby authorized to take such steps as will give general publicity and circulation to this Constitution in a manner as economical as practicable.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AL_9003_6_0_1901', 'Alabama1901', '1901', '9003', '6', '0', 'Schedule', 'The salaries of the Executive and Judicial and all other officers of this State, who may be holding office at the time of the ratification of this Constitution, and the payment of the present members of the General Assembly, shall not be affected by the provisions of this Constitution. Done by the people of Alabama, through their delegates in convention assembled in the hall of the House of Representatives, at Montgomery, Alabama, this the third day of September, Anno Domini, nineteen hundred and one.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_1_0_1959', 'Alaska1959', '1959', '1', '1', '0', 'Bill of Rights', 'This constitution is dedicated to the principles that all persons have a natural right to life, liberty, the pursuit of happiness, and the enjoyment of the rewards of their own industry; that all persons are equal and entitled to equal rights, opportunities, and protection under the law; and that all persons have corresponding obligations to the people and to the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_2_0_1959', 'Alaska1959', '1959', '1', '2', '0', 'Bill of Rights', 'All political power is inherent in the people. All government originates with the people, is founded upon their will only, and is instituted solely for the good of the people as a whole.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_3_0_1959', 'Alaska1959', '1959', '1', '3', '0', 'Bill of Rights', 'No person is to be denied the enjoyment of any civil or political right because of race, color, creed, or national origin. The legislature shall implement this section.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_4_0_1959', 'Alaska1959', '1959', '1', '4', '0', 'Bill of Rights', 'No law shall be made respecting an establishment of religion, or prohibiting the free exercise thereof.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_5_0_1959', 'Alaska1959', '1959', '1', '5', '0', 'Bill of Rights', 'Every person may freely speak, write, and publish on all subjects, being responsible for the abuse of that right.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_6_0_1959', 'Alaska1959', '1959', '1', '6', '0', 'Bill of Rights', 'The right of the people peaceably to assemble, and to petition the government shall never be abridged.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_7_0_1959', 'Alaska1959', '1959', '1', '7', '0', 'Bill of Rights', 'No person shall be deprived of life, liberty, or property, without due process of law. The right of all persons to fair and just treatment in the course of legislative and executive investigations shall not be infringed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_8_0_1959', 'Alaska1959', '1959', '1', '8', '0', 'Bill of Rights', 'No person shall be held to answer for a capital, or otherwise infamous crime, unless on a presentment or indictment of a grand jury, except in cases arising in the armed forces in time of war or public danger. Indictment may be waived by the accused. In that case the prosecution shall be by information. The grand jury shall consist of at least twelve citizens, a majority of whom concurring may return an indictment. The power of grand juries to investigate and make recommendations concerning the public welfare or safety shall never be suspended.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_9_0_1959', 'Alaska1959', '1959', '1', '9', '0', 'Bill of Rights', 'No person shall be put in jeopardy twice for the same offense. No person shall be compelled in any criminal proceeding to be a witness against himself.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_10_0_1959', 'Alaska1959', '1959', '1', '10', '0', 'Bill of Rights', 'Treason against the State consists only in levying war against it, or in adhering to its enemies, giving them aid and comfort. No person shall be convicted of treason, unless on the testimony of two witnesses to the same overt act, or on confession in open court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_11_0_1959', 'Alaska1959', '1959', '1', '11', '0', 'Bill of Rights', 'In all criminal prosecutions, the accused shall have the right to a speedy and public trial, by an impartial jury of twelve, except that the legislature may provide for a jury of not more than twelve nor less than six in courts not of record. The accused is entitled to be informed of the nature and cause of the accusation; to be released on bail, except for capital offenses when the proof is evident or the presumption great; to be confronted with the witnesses against him; to have compulsory process for obtaining witnesses in his favor, and to have the assistance of counsel for his defense.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_12_0_1959', 'Alaska1959', '1959', '1', '12', '0', 'Bill of Rights', 'Excessive bail shall not be required, nor excessive fines imposed, nor cruel and unusual punishments inflicted. Penal administration shall be based oon the principle of reformation and the need for protecting the public..0', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_13_0_1959', 'Alaska1959', '1959', '1', '13', '0', 'Bill of Rights', 'The privilege of the writ of habeas corpus shall not be suspended, unless when in cases of rebellion or actual or imminent invasion, the public safety requires it..0', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_14_0_1959', 'Alaska1959', '1959', '1', '14', '0', 'Bill of Rights', 'The right of the people to be secure in their persons, houses and other property, papers, and effects, against unreasonable searches and seizures, shall not be violated. No warrants shall issue, but upon probable cause, supported by oath or affirmation, and particularly describing the place to be searched, and the persons or things to be seized.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_15_0_1959', 'Alaska1959', '1959', '1', '15', '0', 'Bill of Rights', 'No bill of attainder or ex post facto law shall be passed. No law impairing the obligation of contracts, and no law making any irrevocable grant of special privileges or immunities shall be passed. No conviction shall work corruption of blood or forfeiture of estate.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_16_0_1959', 'Alaska1959', '1959', '1', '16', '0', 'Bill of Rights', 'In civil cases where the amount in controversy exceeds two hundred fifty dollars, the right of trial by a jury of twelve is preserved to the same extent as it existed at common law. The legislature may make provision for a verdict by not less than three-fourths of the jury and, in courts not of record, may provide for a jury of not less than six or more than twelve.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_17_0_1959', 'Alaska1959', '1959', '1', '17', '0', 'Bill of Rights', 'There shall be no imprisonment for debt. This section does not prohibit civil arrest of absconding debtors.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_18_0_1959', 'Alaska1959', '1959', '1', '18', '0', 'Bill of Rights', 'Private property shall not be taken or damaged for public use without just compensation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_19_0_1959', 'Alaska1959', '1959', '1', '19', '0', 'Bill of Rights', 'A well- regulated militia being necessary to the security of a free state, the right of the people to keep and bear arms shall not be infringed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_20_0_1959', 'Alaska1959', '1959', '1', '20', '0', 'Bill of Rights', 'No member of the armed forces shall in time of peace be quartered in any house without the consent of the owner or occupant, or in time of war except as prescribed by law. The military shall be in strict subordination to the civil power.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_1_21_0_1959', 'Alaska1959', '1959', '1', '21', '0', 'Bill of Rights', 'The enumeration of rights in this constitution shall not impair or deny others retained by the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_1_0_1959', 'Alaska1959', '1959', '2', '1', '0', 'Legislative', 'The legislative power of the State is vested in a legislature consisting of a senate with a membership of twenty and a house of representatives with a membership of forty.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_2_0_1959', 'Alaska1959', '1959', '2', '2', '0', 'Legislative', 'A member of the legislature shall be a qualified voter who has been a resident of Alaska for at least three years and of the district from which elected for at least one year, immediately preceding his filing for office. A senator shall be at least twenty-five years of age and a representative at least twenty-one years of age.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_3_0_1959', 'Alaska1959', '1959', '2', '3', '0', 'Legislative', 'Legislators shall be elected at general elections. Their terms begin on the fourth Monday of the January following election unless otherwise provided by law. The term of representatives shall be two years, and the term of senators, four years. One-half of the senators shall be elected every two years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_4_0_1959', 'Alaska1959', '1959', '2', '4', '0', 'Legislative', 'A vacancy in the legislature shall be filled for the unexpired term as provided by law. If no provision is made, the governor shall fill the vacancy by appointment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_5_0_1959', 'Alaska1959', '1959', '2', '5', '0', 'Legislative', 'No legislator may hold any other office or position of profit under the United States or the State. During the term for which elected and for one year thereafter, no legislator may be nominated, elected, or appointed to any other office or position of profit which has been created, or the salary or emoluments of which have been increased, while he was a member. This section shall not prevent any person from seeking or holding the office of governor, secretary of state, or member of Congress. This section shall not apply to employment by or election to a constitutional convention.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_6_0_1959', 'Alaska1959', '1959', '2', '6', '0', 'Legislative', 'Legislators may not be held to answer before any other tribunal for any statement made in the exercise of their legislative duties while the legislature is in session. Members attending, going to, or returning from legislative sessions are not subject to civil process and are privileged from arrest except for felony or breach of the peace.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_7_0_1959', 'Alaska1959', '1959', '2', '7', '0', 'Legislative', 'Legislators shall receive annual salaries. They may receive a per diem allowance for expenses while in session and are entitled to travel expenses going to and from sessions. Presiding officers may receive additional compensation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_8_0_1959', 'Alaska1959', '1959', '2', '8', '0', 'Legislative', 'The legislature shall convene in regular session each year on the fourth Monday in January, but the month and day may be changed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_9_0_1959', 'Alaska1959', '1959', '2', '9', '0', 'Legislative', 'Special sessions may be called by the governor or by vote of two-thirds of the legislators. The vote may be conducted by the legislative council or as prescribed by law. At special sessions called by the governor, legislation shall be limited to subjects designated in his proclamation calling the session or to subjects presented by him. Special sessions are limited to thirty days.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_10_0_1959', 'Alaska1959', '1959', '2', '10', '0', 'Legislative', 'Neither house may adjourn or recess for longer than three days unless the other concurs. If the two houses cannot agree on the time of adjournment and either house certifies the disagreement to the governor, he may adjourn the legislature.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_11_0_1959', 'Alaska1959', '1959', '2', '11', '0', 'Legislative', 'There shall be a legislative council, and the legislature may establish other interim committees. The council and other interim committees may meet between legislative sessions. They may perform duties and employ personnel as provided by the legislature. Their members may receive an allowance for expenses while performing their duties.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_12_0_1959', 'Alaska1959', '1959', '2', '12', '0', 'Legislative', 'The houses of each legislature shall adopt uniform rules of procedure. Each house may choose its officers and employees. Each is the judge of the election and qualifications of its members and may expel a member with the concurrence of two-thirds of its members. Each shall keep a journal of its proceedings. A majority of the membership of each house constitutes a quorum to do business, but a smaller number may adjourn from day to day and may compel attendance of absent members. The legislature shall regulate lobbying.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_13_0_1959', 'Alaska1959', '1959', '2', '13', '0', 'Legislative', 'Every bill shall be confined to one subject unless it is an appropriation bill or one codifying, revising, or rearranging existing laws. Bills for appropriations shall be confined to appropriations. The subject of each bill shall be expressed in the title. The enacting clause shall be: Be it enacted by the Legislature of the State of Alaska.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_14_0_1959', 'Alaska1959', '1959', '2', '14', '0', 'Legislative', 'The legislature shall establish the procedure for enactment of bills into law. No bill may become law unless it has passed three readings in each house on three separate days, except that any bill may be advanced from second to third reading on the same day by concurrence of three-fourths of the house considering it. No bill may become law without an affirmative vote of a majority of the membership of each house. The yeas and nays on final passage shall be entered in the journal.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_15_0_1959', 'Alaska1959', '1959', '2', '15', '0', 'Legislative', 'The governor may veto bills passed by the legislature. He may, by veto, strike or reduce items in appropriation bills. He shall return any vetoed bill, with a statement of his objections, to the house of origin.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_16_0_1959', 'Alaska1959', '1959', '2', '16', '0', 'Legislative', 'Upon receipt of a veto message, the legislature shall meet immediately in joint session and reconsider passage of the vetoed bill or item. Bills to raise revenue and appropriation bills or items, although vetoed, become law by an affirmative vote of three-fourths of the membership of the legislature. Bills vetoed after the adjournment of the first regular session of the legislature shall be reconsidered by the legislature sitting as one body no later than the fifth day of the next regular or special session of that legislature.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_17_0_1959', 'Alaska1959', '1959', '2', '17', '0', 'Legislative', 'A bill becomes law if, while the legislature is in session, the governor neither signs nor vetoes it within fifteen days, Sundays excepted, after its delivery to him. If the legislature is not in session and the governor neither signs nor vetoes a bill within twenty days, Sundays excepted, after its delivery to him, the bill becomes law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_18_0_1959', 'Alaska1959', '1959', '2', '18', '0', 'Legislative', 'Laws passed by the legislature become effective ninety days after enactment. The legislature may, by concurrence of two-thirds of the membership of each house, provide for another effective date.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_19_0_1959', 'Alaska1959', '1959', '2', '19', '0', 'Legislative', 'The legislature shall pass no local or special act if a general act can be made applicable. Whether a general act can be made applicable shall be subject to judicial determination. Local acts necessitating appropriations by a political subdivision may not become effective unless approved by a majority of the qualified voters voting thereon in the subdivision affected.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_20_0_1959', 'Alaska1959', '1959', '2', '20', '0', 'Legislative', 'All civil officers of the State are subject to impeachment by the legislature. Impeachment shall originate in the senate and must be approved by a two-thirds vote of its members. The motion for impeachment shall list fully the basis for the proceeding. Trial on impeachment shall be conducted by the house of representatives. A supreme court justice designated by the court shall preside at the trial. Concurrence of two-thirds of the members of the house is required for a judgment of impeachment. The judgment may not extend beyond removal from office, but shall not prevent proceedings in the courts on the same or related charges.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_2_21_0_1959', 'Alaska1959', '1959', '2', '21', '0', 'Legislative', 'The legislature shall establish procedures for suits against the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_1_0_1959', 'Alaska1959', '1959', '3', '1', '0', 'Executive', 'The executive power of the State is vested in the governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_2_0_1959', 'Alaska1959', '1959', '3', '2', '0', 'Executive', 'The governor shall be at least thirty years of age and a qualified voter of the State. He shall have been a resident of Alaska at least seven years immediately preceding his filing for office, and he shall have been a citizen of the United States for at least seven years.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_3_0_1959', 'Alaska1959', '1959', '3', '3', '0', 'Executive', 'The governor shall be chosen by the qualified voters of the State at a general election. The candidate receiving the greatest number of votes shall be governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_4_0_1959', 'Alaska1959', '1959', '3', '4', '0', 'Executive', 'The term of office of the governor is four years, beginning at noon on the first Monday in December following his election and ending at noon on the first Monday in December four years later.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_5_0_1959', 'Alaska1959', '1959', '3', '5', '0', 'Executive', 'No person who has been elected governor for two full successive terms shall be again eligible to hold that office until one full term has intervened.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_6_0_1959', 'Alaska1959', '1959', '3', '6', '0', 'Executive', 'The governor shall not hold any other office or position of profit under the United States, the State, or its political subdivisions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_7_0_1959', 'Alaska1959', '1959', '3', '7', '0', 'Executive', 'There shall be a secretary of state. He shall have the same qualifications as the governor and serve for the same term. He shall perform such duties as may be prescribed by law and as may be delegated to him by the governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_8_0_1959', 'Alaska1959', '1959', '3', '8', '0', 'Executive', 'The secretary of state shall be nominated in the manner provided by law for nominating candidates for other elective offices. In the general election the votes cast for a candidate for governor shall be considered as cast also for the candidate for secretary of state running jointly with him. The candidate whose name appears on the ballot jointly with that of the successful candidate for governor shall be elected secretary of state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_9_0_1959', 'Alaska1959', '1959', '3', '9', '0', 'Executive', 'In case of the temporary absence of the governor from office, the secretary of state shall serve as acting governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_10_0_1959', 'Alaska1959', '1959', '3', '10', '0', 'Executive', 'If the governor-elect dies, resigns, or is disqualified, the secretary of state elected with him shall succeed to the office of governor for the full term. If the governor- elect fails to assume office for any other reason, the secretary of state elected with him shall serve as acting governor, and shall succeed to the office if the governor-elect does not assume his office within six months of the beginning of the term.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_11_0_1959', 'Alaska1959', '1959', '3', '11', '0', 'Executive', 'In case of a vacancy in the office of governor for any reason, the secretary of state shall succeed to the office for the remainder of the term.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_12_0_1959', 'Alaska1959', '1959', '3', '12', '0', 'Executive', 'Whenever for a period of six months, a governor has been continuously absent from office or has been unable to discharge the duties of his office by reason of mental or physical disability, the office shall be deemed vacant. The procedure for determining absence and disability shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_13_0_1959', 'Alaska1959', '1959', '3', '13', '0', 'Executive', 'Provision shall be made by law for succession to the office of governor and for an acting governor in the event that the secretary of state is unable to succeed to the office or act as governor. No election of a secretary of state shall be held except at the time of electing a governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_14_0_1959', 'Alaska1959', '1959', '3', '14', '0', 'Executive', 'When the secretary of state succeeds to the office of governor, he shall have the title, powers, duties and emoluments of that office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_15_0_1959', 'Alaska1959', '1959', '3', '15', '0', 'Executive', 'The compensation of the governor and the secretary of state shall be prescribed by law and shall not be diminished during their term of office, unless by general law applying to all salaried officers of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_16_0_1959', 'Alaska1959', '1959', '3', '16', '0', 'Executive', 'The governor shall be responsible for the faithful execution of the laws. He may, by appropriate court action or proceeding brought in the name of the State, enforce compliance with any constitutional or legislative mandate, or restrain violation of any constitutional or legislative power, duty, or right by any officer, department, or agency of the State or any of its political subdivisions. This authority shall not be construed to authorize any action or proceeding against the legislature.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_17_0_1959', 'Alaska1959', '1959', '3', '17', '0', 'Executive', 'Whenever the governor considers it in the public interest, he may convene the legislature, either house, or the two houses in joint session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_18_0_1959', 'Alaska1959', '1959', '3', '18', '0', 'Executive', 'The governor shall, at the beginning of each session, and may at other times, give the legislature information concerning the affairs of the State and recommend the measures he considers necessary.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_19_0_1959', 'Alaska1959', '1959', '3', '19', '0', 'Executive', 'The governor is commander-in-chief of the armed forces of the State. He may call out these forces to execute the laws, suppress or prevent insurrection or lawless violence, or repel invasion. The governor, as provided by law, shall appoint all general and flag officers of the armed forces of the State, subject to confirmation by a majority of the members of the legislature in joint session. He shall appoint and commission all other officers.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_20_0_1959', 'Alaska1959', '1959', '3', '20', '0', 'Executive', 'The governor may proclaim martial law when the public safety requires it in case of rebellion or actual or imminent invasion. Martial law shall not continue for longer than twenty days without the approval of a majority of the members of the legislature in joint session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_21_0_1959', 'Alaska1959', '1959', '3', '21', '0', 'Executive', 'Subject to procedure prescribed by law, the governor may grant pardons, commutations, and reprieves, and may suspend and remit fines and forfeitures. This power shall not extend to impeachment. A parole system shall be provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_22_0_1959', 'Alaska1959', '1959', '3', '22', '0', 'Executive', 'All executive and administrative offices, departments, and agencies of the state government and their respective functions, powers, and duties shall be allocated by law among and within not more than twenty principal departments, so as to group them as far as practicable according to major purposes. Regulatory, quasi-judicial, and temporary agencies may be established by law and need not be allocated within a principal department.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_23_0_1959', 'Alaska1959', '1959', '3', '23', '0', 'Executive', 'The governor may make changes in the organization of the executive branch or in the assignment of functions among its units which he considers necessary for efficient administration. Where these changes require the force of law, they shall be set forth in executive orders. The legislature shall have sixty days of a regular session, or a full session if of shorter duration, to disapprove these executive orders. Unless disapproved by resolution concurred in by a majority of the members in joint session, these orders become effective at a date thereafter to be designated by the governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_24_0_1959', 'Alaska1959', '1959', '3', '24', '0', 'Executive', 'Supervision. Each principal department shall be under the supervision of the governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_25_0_1959', 'Alaska1959', '1959', '3', '25', '0', 'Executive', 'The head of each principal department shall be a single executive unless otherwise provided by law. He shall be appointed by the governor, subject to confirmation by a majority of the members of the legislature in joint session, and shall serve at the pleasure of the governor, except as otherwise provided in this article with respect to the secretary of state. The heads of all principal departments shall be citizens of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_26_0_1959', 'Alaska1959', '1959', '3', '26', '0', 'Executive', 'When a board or commission is at the head of a principal department or a regulatory or quasi-judicial agency, its members shall be appointed by the governor, subject to confirmation by a majority of the members of the legislature in joint session, and may be removed as provided by law. They shall be citizens of the United States. The board or commission may appoint a principal executive officer when authorized by law, but the appointment shall be subject to the approval of the governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_3_27_0_1959', 'Alaska1959', '1959', '3', '27', '0', 'Executive', 'The governor may make appointments to fill vacancies occurring during a recess of the legislature, in offices requiring confirmation by the legislature. The duration of such appointments shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_1_0_1959', 'Alaska1959', '1959', '4', '1', '0', 'Judicial', 'The judicial power of the State is vested in a supreme court, a superior court, and the courts established by the legislature. The jurisdiction of courts shall be prescribed by law. The courts shall constitute a unified judicial system for operation and administration. Judicial districts shall be established by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_2_0_1959', 'Alaska1959', '1959', '4', '2', '0', 'Judicial', 'The supreme court shall be the highest court of the State, with final appellate jurisdiction. It shall consist of three justices, one of whom is chief justice. The number of justices may be increased by law upon the request of the supreme court.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_3_0_1959', 'Alaska1959', '1959', '4', '3', '0', 'Judicial', 'The superior court shall be the trial court of general jurisdiction and shall consist of five judges. The number of judges may be changed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_4_0_1959', 'Alaska1959', '1959', '4', '4', '0', 'Judicial', 'Supreme court justices and superior court judges shall be citizens of the United States and of the State, licensed to practice law in the State, and possessing any additional qualifications prescribed by law. Judges of other courts shall be selected in a manner, for terms, and with qualifications prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_5_0_1959', 'Alaska1959', '1959', '4', '5', '0', 'Judicial', 'The governor shall fill any vacancy in an office of supreme court justice or superior court judge by appointing one of two or more persons nominated by the judicial council.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_6_0_1959', 'Alaska1959', '1959', '4', '6', '0', 'Judicial', 'Each supreme court justice and superior court judge shall, in the manner provided by law, be subject to approval or rejection on a nonpartisan ballot at the first general election held more than three years after his appointment. Thereafter, each supreme court justice shall be subject to approval or rejection in a like manner every tenth year, and each superior court judge, every sixth year.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_7_0_1959', 'Alaska1959', '1959', '4', '7', '0', 'Judicial', 'The office of any supreme court justice or superior court judge becomes vacant ninety days after the election at which he is rejected by a majority of those voting on the question, or for which he fails to file his declaration of candidacy to succeed himself.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_8_0_1959', 'Alaska1959', '1959', '4', '8', '0', 'Judicial', 'The judicial council shall consist of seven members. Three attorney members shall be appointed for six-year terms by the governing body of the organized state bar. Three non-attorney members shall be appointed for six-year terms by the governor subject to confirmation by a majority of the members of the legislature in joint session. Vacancies shall be filled for the unexpired term in like manner. Appointments shall be made with due consideration to area representation and without regard to political affiliation. The chief justice of the supreme court shall be ex-officio the seventh member and chairman of the judicial council. No member of the judicial council, except the chief justice, may hold any other office or position of profit under the United States or the State. The judicial council shall act by concurrence of four or more members and according to rules which it adopts.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_9_0_1959', 'Alaska1959', '1959', '4', '9', '0', 'Judicial', 'The judicial council shall conduct studies for improvement of the administration of justice, and make reports and recommendations to the supreme court and to the legislature at intervals of not more than two years. The judicial council shall perform other duties assigned by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_10_0_1959', 'Alaska1959', '1959', '4', '10', '0', 'Judicial', 'Whenever the judicial council certifies to the governor that a supreme court justice appears to be so incapacitated as substantially to prevent him form performing his judicial duties, the governor shall appoint a board of three persons to inquire into the circumstances, and may on the boards recommendation retire the justice. Whenever a judge on another court appears to be so incapacitated as substantially to prevent him form performing his judicial duties, the judicial council shall recommend to the supreme court that the judge be placed under early retirement. After notice and hearing, the supreme court by majority vote of its members may retire the judge.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_11_0_1959', 'Alaska1959', '1959', '4', '11', '0', 'Judicial', 'Justices and judges shall be retired at the age of seventy except as provided in this article. The basis and amount of retirement pay shall be prescribed by law. Retired judges shall render no further service on the bench except for special assignments as provided by court rule.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_12_0_1959', 'Alaska1959', '1959', '4', '12', '0', 'Judicial', 'Impeachment of any justice or judge for malfeasance or misfeasance in the performance of his official duties shall be according to procedure prescribed for civil officers.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_13_0_1959', 'Alaska1959', '1959', '4', '13', '0', 'Judicial', 'Justices, judges, and members of the judicial council shall receive compensation as prescribed by law. Compensation of justices and judges shall not be diminished during their terms of office, unless by general law applying to all salaried officers of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_14_0_1959', 'Alaska1959', '1959', '4', '14', '0', 'Judicial', 'Supreme court justices and superior court judges while holding office may not practice law, hold office in a political party, or hold any other office or position of profit under the United States, the State, or its political subdivisions. Any supreme court justice or superior court judge filing for another elective public office forfeits his judicial position.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_15_0_1959', 'Alaska1959', '1959', '4', '15', '0', 'Judicial', 'The supreme court shall make and promulgate rules governing the administration of all courts. It shall make and promulgate rules governing practice and procedure in civil and criminal cases in all courts. These rules may be changed by the legislature by two-thirds vote of the members elected to each house.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_4_16_0_1959', 'Alaska1959', '1959', '4', '16', '0', 'Judicial', 'The chief justice of the supreme court shall be the administrative head of all courts. He may assign judges from one court or division thereof to another for temporary service. The chief justice shall, with the approval of the supreme court, appoint an administrative director to serve at his pleasure and to supervise the administrative operations of the judicial system.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_5_1_0_1959', 'Alaska1959', '1959', '5', '1', '0', 'Suffrage and Elections', 'Every citizen of the United States who is at least nineteen years of age, who meets registration residency requirements which may be prescribed by law, and who is qualified to vote under this article, may vote in any state or local election. He shall have been, immediately preceding the election, a thirty day resident of the election district in which he seeks to vote, except that for purposes of voting for President and Vice President of the United States other residency requirements may be prescribed by law. He shall be able to read or speak the English language as prescribed by law, unless prevented by physical disability. Additional voting qualifications may be prescribed by law for bond issue elections of political subdivisions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_5_2_0_1959', 'Alaska1959', '1959', '5', '2', '0', 'Suffrage and Elections', 'No person may vote who has been convicted of a felony involving moral turpitude unless his civil rights have been restored. No person may vote who has been judicially determined to be of unsound mind unless the disability has been removed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_5_3_0_1959', 'Alaska1959', '1959', '5', '3', '0', 'Suffrage and Elections', 'Methods of voting, including absentee voting, shall be prescribed by law. Secrecy of voting shall be preserved. The procedure for determining election contests, with right of appeal to the courts, shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_5_4_0_1959', 'Alaska1959', '1959', '5', '4', '0', 'Suffrage and Elections', 'The legislature may provide a system of permanent registration of voters, and may establish voting precincts within election districts.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_5_5_0_1959', 'Alaska1959', '1959', '5', '5', '0', 'Suffrage and Elections', 'General elections shall be held on the second Tuesday in October of every even-numbered year, but the month and day may be changed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_1_0_1959', 'Alaska1959', '1959', '6', '1', '0', 'Apportionment', 'Members of the house of representatives shall be elected by the qualified voters of the respective election districts. Until reapportionment, election districts and the number of representatives to be elected from each district shall be as set forth in Section 1 of Article XIV.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_2_0_1959', 'Alaska1959', '1959', '6', '2', '0', 'Apportionment', 'Members of the senate shall be elected by the qualified voters of the respective senate districts. Senate districts shall be as set forth in Section 2 of Article XIV, subject to changes authorized in this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_3_0_1959', 'Alaska1959', '1959', '6', '3', '0', 'Apportionment', 'The Governor and the senate shall reapportion the house of representatives and the senate immediately following the official reporting of each decennial census of the United States. Reapportionment shall be based upon the civilian population within each election district as reported by the census.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_4_0_1959', 'Alaska1959', '1959', '6', '4', '0', 'Apportionment', 'Reapportionment shall be by the method of equal proportions, except that each election district having the major fraction of the quotient obtained by dividing total civilian population by forty shall have one representative.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_5_0_1959', 'Alaska1959', '1959', '6', '5', '0', 'Apportionment', 'Should the total civilian population within any election district fall below one-half of the quotient, the district shall be attached to an election district within its senate district, and the reapportionment for the new district shall be determined as provided in section 4 of this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_6_0_1959', 'Alaska1959', '1959', '6', '6', '0', 'Apportionment', 'The Governor may further redistrict by changing the size and area of election districts, subject to the limitations of this article. Each new district so created shall be formed of contiguous and compact territory containing as nearly as practicable a relatively integrated socio-economic area. Each shall contain a population at least equal to the quotient obtained by dividing the total civilian population state by forty. Consideration may be given to local government boundaries. Drainage and other geographic features shall be used in describing boundaries wherever possible.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_7_0_1959', 'Alaska1959', '1959', '6', '7', '0', 'Apportionment', 'The senate districts, described in Section 2 of Article XIV, may be modified to reflect changes in election districts. A district, although modified, shall retain its total number of senators and its approximate perimeter.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_8_0_1959', 'Alaska1959', '1959', '6', '8', '0', 'Apportionment', 'The governor shall appoint a reapportionment board to act in an advisory capacity to him. It shall consist of five members, none of whom may be public employees or officials. At least one member each shall be appointed from the Southeastern, Southcentral, Central, and Northwestern Senate districts. Appointments shall be made without regard to political affiliation. Board members shall be compensated.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_9_0_1959', 'Alaska1959', '1959', '6', '9', '0', 'Apportionment', 'The board shall elect one of its members chairman and may employ temporary assistants. Concurrence of three members is required for a ruling or determination, but a lesser number may conduct hearings or otherwise act for the board.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_10_0_1959', 'Alaska1959', '1959', '6', '10', '0', 'Apportionment', 'Within ninety days following the official reporting of each decennial census, the board shall submit to the governor a plan for reapportionment and redistricting as provided in this article. Within ninety days after receipt of the plan, the governor shall issue a proclamation of reapportionment and redistricting. An accompanying statement shall explain any changes from the plan of the board. The reapportionment and redistricting shall be effective for the election of members of the legislature until the official reporting of the next decennial census.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_6_11_0_1959', 'Alaska1959', '1959', '6', '11', '0', 'Apportionment', 'Any qualified voter may apply to the superior court to compel the governor, by mandamus or otherwise, to perform his reapportionment duties or to correct any error in redistricting or reapportionment. Application to compel the governor to perform his reapportionment duties must be filed within thirty days of the expiration of either of the two ninety-day periods specified in this article. Application to compel correction of any error in redistricting must be filed within thirty days following the proclamation. Original jurisdiction in these matters is hereby vested in the superior court. On appeal, the cause shall be reviewed by the supreme court upon the law and the facts.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_7_1_0_1959', 'Alaska1959', '1959', '7', '1', '0', 'Welfare', 'The legislature shall by general law establish and maintain a system of public schools open to all children of the State, and may provide for other public educational institutions. Schools and institutions so established shall be free from sectarian control. No money shall be paid from public funds for the direct benefit of any religious or other private educational institution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_7_2_0_1959', 'Alaska1959', '1959', '7', '2', '0', 'Welfare', 'The University of Alaska is hereby established as the state university and constituted a body corporate. It shall have title to all real and personal property now or hereafter set aside for or conveyed to it. Its property shall be administered and disposed of according to law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_7_3_0_1959', 'Alaska1959', '1959', '7', '3', '0', 'Welfare', 'The University of Alaska shall be governed by a board of regents. The regents shall be appointed by the governor, subject to confirmation by a majority of the members of the legislature in joint session. The board shall, in accordance with law, formulate policy and appoint the president of the university. He shall be the executive officer of the board.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_7_4_0_1959', 'Alaska1959', '1959', '7', '4', '0', 'Welfare', 'The legislature shall provide for the promotion and protection of public health.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_7_5_0_1959', 'Alaska1959', '1959', '7', '5', '0', 'Welfare', 'The legislature shall provide for public welfare.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_1_0_1959', 'Alaska1959', '1959', '8', '1', '0', 'Natural Resources', 'It is the policy of the State to encourage the settlement of its land and the development of its resources by making them available for maximum use consistent with the public interest.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_2_0_1959', 'Alaska1959', '1959', '8', '2', '0', 'Natural Resources', 'The legislature shall provide for the utilization, development, and conservation of all natural resources belonging to the State, including land and waters, for the maximum benefit of its people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_3_0_1959', 'Alaska1959', '1959', '8', '3', '0', 'Natural Resources', 'Wherever occurring in their natural state, fish, wildlife, and waters are reserved to the people for common use.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_4_0_1959', 'Alaska1959', '1959', '8', '4', '0', 'Natural Resources', 'Fish, forests, wildlife, grasslands, and all other replenishable resources belonging to the State shall be utilized, developed, and maintained on the sustained yield principle, subject to preferences among beneficial uses.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_5_0_1959', 'Alaska1959', '1959', '8', '5', '0', 'Natural Resources', 'The legislature may provide for facilities, improvements, and services to assure greater utilization, development, reclamation, and settlement of lands, and to assure fuller utilization and development of the fisheries, wildlife, and waters.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_6_0_1959', 'Alaska1959', '1959', '8', '6', '0', 'Natural Resources', 'Lands and interests therein, including submerged and tidal lands, possessed or acquired by the State, and not used or intended exclusively for governmental purposes, constitute the state public domain. The legislature shall provide for the selection of lands granted to the State by the United States, and for the administration of the state public domain.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_7_0_1959', 'Alaska1959', '1959', '8', '7', '0', 'Natural Resources', 'The legislature may provide for the acquisition of sites, objects, and areas of natural beauty or of historic, cultural, recreational, or scientific value. It may reserve them from the public domain and provide for their administration and preservation for the use, enjoyment, and welfare of the people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_8_0_1959', 'Alaska1959', '1959', '8', '8', '0', 'Natural Resources', 'The legislature may provide for the leasing of, and the issuance of permits for exploration of, any part of the public domain or interest therein, subject to reasonable concurrent uses. Leases and permits shall provide, among other conditions, for payment by the party at fault for damage or injury arising from noncompliance with terms governing concurrent use, and for forfeiture in the event of breach of conditions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_9_0_1959', 'Alaska1959', '1959', '8', '9', '0', 'Natural Resources', 'Subject to the provisions of this section, the legislature may provide for the sale or grant of state lands, or interests therein, and establish sales procedures. All sales or grants shall contain such reservations to the State of all resources as may be required by Congress or the State and shall provide for access to these resources. Reservation of access shall not unnecessarily impair the owners use, prevent the control of trespass, or preclude compensation for damages.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_10_0_1959', 'Alaska1959', '1959', '8', '10', '0', 'Natural Resources', 'No disposals or leases of state lands, or interests therein, shall be made without prior public notice and other safeguards of the public interest as may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_11_0_1959', 'Alaska1959', '1959', '8', '11', '0', 'Natural Resources', 'Discovery and appropriation shall be the basis for establishing a right in those minerals reserved to the State which, upon the date of ratification of this constitution by the people of Alaska, were subject to location under the federal mining laws. Prior discovery, location, and filing, as prescribed by law, shall establish a prior right to these minerals and also a prior right to permits, leases, and transferable licenses for their extraction. Continuation of these rights shall depend upon the performance of annual labor, or the payment of fees, rents, or royalties, or upon other requirements as may be prescribed by law. Surface uses of land by a mineral claimant shall be limited to those necessary for the extraction or basic processing of the mineral deposits, or for both. Discovery and appropriation shall initiate a right, subject to further requirements of law, to patent of mineral lands if authorized by the State and not prohibited by Congress. The provisions of this section shall apply to all other minerals reserved to the State which by law are declared subject to appropriation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_12_0_1959', 'Alaska1959', '1959', '8', '12', '0', 'Natural Resources', 'The legislature shall provide for the issuance, types and terms of leases for coal, oil, gas, oil shale, sodium, phosphate, potash, sulfur, pumice, and other minerals as may be prescribed by law. Leases and permits giving the exclusive right of exploration for these minerals for specific periods and areas, subject to reasonable concurrent exploration as to different classes of minerals, may be authorized by law. Like leases and permits giving the exclusive right of prospecting by geophysical, geochemical, and similar methods for all minerals may also be authorized by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_13_0_1959', 'Alaska1959', '1959', '8', '13', '0', 'Natural Resources', 'All surface and subsurface waters reserved to the people for common use, except mineral and medicinal waters, are subject to appropriation. Priority of appropriation shall give prior right. Except for public water supply, an appropriation of water shall be limited to stated purposes and subject to preferences among beneficial uses, concurrent or otherwise, as prescribed by law, and to the general reservation of fish and wildlife.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_14_0_1959', 'Alaska1959', '1959', '8', '14', '0', 'Natural Resources', 'Free access to the navigable or public waters of the State, as defined by the legislature, shall not be denied any citizen of the United States or resident of the State, except that the legislature may by general law regulate and limit such access for other beneficial uses or public purposes.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_15_0_1959', 'Alaska1959', '1959', '8', '15', '0', 'Natural Resources', 'No exclusive right or special privilege of fishery shall be created or authorized in the natural waters of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_16_0_1959', 'Alaska1959', '1959', '8', '16', '0', 'Natural Resources', 'No person shall be involuntarily divested of his right to the use of waters, his interests in lands, or improvements affecting either, except for a superior beneficial use or public purpose and then only with just compensation and by operation of law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_17_0_1959', 'Alaska1959', '1959', '8', '17', '0', 'Natural Resources', 'Laws and regulations governing the use or disposal of natural resources shall apply equally to all persons similarly situated with reference to the subject matter and purpose to be served by the law or regulation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_8_18_0_1959', 'Alaska1959', '1959', '8', '18', '0', 'Natural Resources', 'Proceedings in eminent domain may be undertaken for private ways of necessity to permit essential access for extraction or utilization of resources. Just compensation shall be made for property taken or for resultant damages to other property rights.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_1_0_1959', 'Alaska1959', '1959', '9', '1', '0', 'Taxation', 'The power of taxation shall never be surrendered. This power shall not be suspended or contracted away, except as provided in this article.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_2_0_1959', 'Alaska1959', '1959', '9', '2', '0', 'Taxation', 'The lands and other property belonging to citizens of the United States residing without the State shall never be taxed at a higher rate than the lands and other property belonging to the residents of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_3_0_1959', 'Alaska1959', '1959', '9', '3', '0', 'Taxation', 'Standards for appraisal of all property assessed by the State or its political subdivisions shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_4_0_1959', 'Alaska1959', '1959', '9', '4', '0', 'Taxation', 'The real and personal property of the State or its political subdivisions shall be exempt from taxation under conditions and exceptions which may be provided by law. All, or any portion of, property used exclusively for non-profit religious, charitable, cemetery, or educational purposes, as defined by law, shall be exempt from taxation. Other exemptions of like or different kind may be granted by general law. All valid existing exemptions shall be retained until otherwise provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_5_0_1959', 'Alaska1959', '1959', '9', '5', '0', 'Taxation', 'Private leaseholds, contracts, or interests in land or property owned or held by the United States, the State, or its political subdivisions, shall be taxable to the extent of the interests.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_6_0_1959', 'Alaska1959', '1959', '9', '6', '0', 'Taxation', 'No tax shall be levied, or appropriation of public money made, or public property transferred, nor shall the public credit be used, except for a public purpose.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_7_0_1959', 'Alaska1959', '1959', '9', '7', '0', 'Taxation', 'The proceeds of any state tax or license shall not be dedicated to any special purpose, except when required by the federal government for state participation in federal programs. This provision shall not prohibit the continuance of any dedication for special purposes existing upon the date of ratification of this section by the people of Alaska.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_8_0_1959', 'Alaska1959', '1959', '9', '8', '0', 'Taxation', 'No state debt shall be contracted unless authorized by law for capital improvements and ratified by a majority of the qualified voters of the State who vote on the question. The State may, as provided by law and without ratification, contract debt for the purpose of repelling invasion, suppressing insurrection, defending the State in war, meeting natural disasters, or redeeming indebtedness outstanding at the time this constitution becomes effective. .0', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_9_0_1959', 'Alaska1959', '1959', '9', '9', '0', 'Taxation', 'No debt shall be contracted by any political subdivision of the State, unless authorized for capital improvements by its governing body and ratified by a majority vote of those qualified to vote and voting on the question.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_10_0_1959', 'Alaska1959', '1959', '9', '10', '0', 'Taxation', 'The State and its political subdivisions may borrow money to meet appropriations for any fiscal year in anticipation of the collection of the revenues for that year, but all debt so contracted shall be paid before the end of the next fiscal year.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_11_0_1959', 'Alaska1959', '1959', '9', '11', '0', 'Taxation', 'The restrictions on contracting debt do not apply to debt incurred through the issuance of revenue bonds by a public enterprise or public corporation of the State or a political subdivision, when the only security is the revenues of the enterprise or corporation. The restrictions do not apply to indebtedness to be paid from special assessments on the benefited property, nor do they apply to refunding indebtedness of the State or its political subdivisions.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_12_0_1959', 'Alaska1959', '1959', '9', '12', '0', 'Taxation', 'The governor shall submit to the legislature, at a time fixed by law, a budget for the next fiscal year setting forth all proposed expenditures and anticipated income of all departments, offices, and agencies of the State. The governor, at the same time, shall submit a general appropriation bill to authorize the proposed expenditures, and a bill or bills covering recommendations in the budget for new or additional revenues.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_13_0_1959', 'Alaska1959', '1959', '9', '13', '0', 'Taxation', 'No money shall be withdrawn from the treasury except in accordance with appropriations made by law. No obligation for the payment of money shall be incurred except as authorized by law. Unobligated appropriations outstanding at the end of the period of time specified by law shall be void.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_9_14_0_1959', 'Alaska1959', '1959', '9', '14', '0', 'Taxation', 'The legislature shall appoint an auditor to serve at its pleasure. He shall be a certified public accountant. The auditor shall conduct post-audits as prescribed by law and shall report to the legislature and to the governor.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_1_0_1959', 'Alaska1959', '1959', '10', '1', '0', 'Local Government', 'The purpose of this article is to provide for maximum local self-government with a minimum of local government units, and to prevent duplication of tax levying jurisdictions. A liberal construction shall be given to the powers of local government units.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_2_0_1959', 'Alaska1959', '1959', '10', '2', '0', 'Local Government', 'All local government powers shall be vested in boroughs and cities. The State may delegate taxing powers to organized boroughs and cities only.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_3_0_1959', 'Alaska1959', '1959', '10', '3', '0', 'Local Government', 'The entire State shall be divided into boroughs, organized or unorganized. They shall be established in a manner and according to standards provided by law. The standards shall include population, geography, economy, transportation, and other factors. Each borough shall embrace an area and population with common interests to the maximum degree possible. The legislature shall classify boroughs and prescribe their powers and functions. Methods by which boroughs may be organized, incorporated, merged, consolidated, reclassified, or dissolved shall be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_4_0_1959', 'Alaska1959', '1959', '10', '4', '0', 'Local Government', 'The governing body of the organized borough shall be the assembly, and its composition shall be established by law or charter. Each city of the first class, and each city of any other class designated by law, shall be represented on the assembly by one or more members of its council. The other members of the assembly shall be elected from and by the qualified voters resident outside such cities.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_5_0_1959', 'Alaska1959', '1959', '10', '5', '0', 'Local Government', 'Service areas to provide special services within an organized borough may be established, altered, or abolished by the assembly, subject to the provisions of law or charter. A new service area shall not be established if, consistent with the purposes of this article, the new service can be provided by an existing service area, by incorporation as a city, or by annexation to a city. The assembly may authorize the levying of taxes, charges, or assessments within a service area to finance the special services.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_6_0_1959', 'Alaska1959', '1959', '10', '6', '0', 'Local Government', 'The legislature shall provide for the performance of services it deems necessary or advisable in unorganized boroughs, allowing for maximum local participation and responsibility. It may exercise any power or function in an unorganized borough which the assembly may exercise in an organized borough.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_7_0_1959', 'Alaska1959', '1959', '10', '7', '0', 'Local Government', 'Cities shall be incorporated in a manner prescribed by law, and shall be a part of the borough in which they are located. Cities shall have the powers and functions conferred by law or charter. They may be merged, consolidated, classified, reclassified, or dissolved in the manner provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_8_0_1959', 'Alaska1959', '1959', '10', '8', '0', 'Local Government', 'Council. The governing body of a city shall be the council.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_9_0_1959', 'Alaska1959', '1959', '10', '9', '0', 'Local Government', 'The qualified voters of any borough of the first class or city of the first class may adopt, amend, or repeal a home rule charter in a manner provided by law. In the absence of such legislation, the governing body of a borough or city of the first class shall provide the procedure for the preparation and adoption or rejection of the charter. All charters, or parts or amendments of charters, shall be submitted to the qualified voters of the borough or city, and shall become effective if approved by a majority of those who vote on the specific question.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_10_0_1959', 'Alaska1959', '1959', '10', '10', '0', 'Local Government', 'The legislature may extend home rule to other boroughs and cities.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_11_0_1959', 'Alaska1959', '1959', '10', '11', '0', 'Local Government', 'A home rule borough or city may exercise all legislative powers not prohibited by law or by charter.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_12_0_1959', 'Alaska1959', '1959', '10', '12', '0', 'Local Government', 'A local boundary commission or board shall be established by law in the executive branch of the state government. The commission or board may consider any proposed local government boundary change. It may present proposed changes to the legislature during the first ten days of any regular session. The change shall become effective forty-five days after presentation or at the end of the session, whichever is earlier, unless disapproved by a resolution concurred in by a majority of the members of each house. The commission or board, subject to law, may establish procedures whereby boundaries may be adjusted by local action.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_13_0_1959', 'Alaska1959', '1959', '10', '13', '0', 'Local Government', 'Agreements, including those for cooperative or joint administration of any functions or powers, may be made by any local government with any other local government, with the State, or with the United States, unless otherwise provided by law or charter. A city may transfer to the borough in which it is located any of its powers or functions unless prohibited by law or charter, and may in like manner revoke the transfer.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_14_0_1959', 'Alaska1959', '1959', '10', '14', '0', 'Local Government', 'An agency shall be established by law in the executive branch of the state government to advise and assist local governments. It shall review their activities, collect and publish local government information, and perform other duties prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_10_15_0_1959', 'Alaska1959', '1959', '10', '15', '0', 'Local Government', 'Special service districts existing at the time a borough is organized shall be integrated with the government of the borough as provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_11_1_0_1959', 'Alaska1959', '1959', '11', '1', '0', 'Referendum', 'The people may propose and enact laws by the initiative, and approve or reject acts of the legislature by the referendum.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_11_2_0_1959', 'Alaska1959', '1959', '11', '2', '0', 'Referendum', 'An initiative or referendum is proposed by an application containing the bill to be initiated or the act to be referred. The application shall be signed by not less than one hundred qualified voters as sponsors, and shall be filed with the secretary of state. If he finds it in proper form he shall so certify. Denial of certification shall be subject to judicial review.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_11_3_0_1959', 'Alaska1959', '1959', '11', '3', '0', 'Referendum', 'After certification of the application, a petition containing a summary of the subject matter shall be prepared by the secretary of state for circulation by the sponsors. If signed by qualified voters, equal in number to ten per cent of those who voted in the preceding general election and resident in at least two-thirds of the election districts of the State, it may be filed with the secretary of state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_11_4_0_1959', 'Alaska1959', '1959', '11', '4', '0', 'Referendum', 'An initiative petition may be filed at any time. The secretary of state shall prepare a ballot title and proposition summarizing the proposed law, and shall place them on the ballot for the first statewide election held more than one hundred- twenty days after adjournment of the legislative session following the filing. If, before the election, substantially the same measure has been enacted, the petition is void.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_11_5_0_1959', 'Alaska1959', '1959', '11', '5', '0', 'Referendum', 'A referendum petition may be filed only within ninety days after adjournment of the legislative session at which the act was passed. The secretary of state shall prepare a ballot title and proposition summarizing the act and shall place them on the ballot for the first statewide election held more than one hundred-eighty days after adjournment of that session.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_11_6_0_1959', 'Alaska1959', '1959', '11', '6', '0', 'Referendum', 'If a majority of the votes cast on the proposition favor its adoption, the initiated measure is enacted. If a majority of the votes cast on the proposition favor the rejection of an act referred, it is rejected. The secretary of state shall certify the election returns. An initiated law becomes effective ninety days after certification, is not subject to veto, and may not be repealed by the legislature within two years of its effective date. It may be amended at any time. An act rejected by referendum is void thirty days after certification. Additional procedures for the initiative and referendum may be prescribed by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_11_7_0_1959', 'Alaska1959', '1959', '11', '7', '0', 'Referendum', 'The initiative shall not be used to dedicate revenues, make or repeal appropriations, create courts, define the jurisdiction of courts or prescribe their rules, or enact local or special legislation. The referendum shall not be applied to dedications of revenue, to appropriations, to local or special legislation, or to laws necessary for the immediate preservation of the public peace, health, or safety.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_11_8_0_1959', 'Alaska1959', '1959', '11', '8', '0', 'Referendum', 'All elected public officials in the State, except judicial officers, are subject to recall by the voters of the State or political subdivision from which elected. Procedures and grounds for recall shall be prescribed by the legislature.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_1_0_1959', 'Alaska1959', '1959', '12', '1', '0', 'General Provisions', 'The State of Alaska shall consist of all the territory, together with the territorial waters appurtenant thereto, included in the Territory of Alaska upon the date of ratification of this constitution by the people of Alaska.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_2_0_1959', 'Alaska1959', '1959', '12', '2', '0', 'General Provisions', 'The State and its political subdivisions may cooperate with the United States and its territories, and with other states and their political subdivisions on matters of common interest. The respective legislative bodies may make appropriations for this purpose.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_3_0_1959', 'Alaska1959', '1959', '12', '3', '0', 'General Provisions', 'Service in the armed forces of the United States or of the State is not an office or position of profit as the term is used in this constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_4_0_1959', 'Alaska1959', '1959', '12', '4', '0', 'General Provisions', 'No person who advocates, or who aids or belongs to any party or organization or association which advocates, the overthrow by force or violence of the government of the United States or of the State shall be qualified to hold any public office of trust or profit under this constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_5_0_1959', 'Alaska1959', '1959', '12', '5', '0', 'General Provisions', 'All public officers, before entering upon the duties of their offices, shall take and subscribe to the following oath or affirmation: I do solemnly swear (or affirm) that I will support and defend the Constitution of the United States and the Constitution of the State of Alaska, and that I will faithfully discharge my duties as . . . . . . . . to the best of my ability. The legislature may prescribe further oaths or affirmations.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_6_0_1959', 'Alaska1959', '1959', '12', '6', '0', 'General Provisions', 'The legislature shall establish a system under which the merit principle will govern the employment of persons by the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_7_0_1959', 'Alaska1959', '1959', '12', '7', '0', 'General Provisions', 'Membership in employee retirement systems of the State or its political subdivisions shall constitute a contractual relationship. Accrued benefits of these systems shall not be diminished or impaired.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_8_0_1959', 'Alaska1959', '1959', '12', '8', '0', 'General Provisions', 'The enumeration of specified powers in this constitution shall not be construed as limiting the powers of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_9_0_1959', 'Alaska1959', '1959', '12', '9', '0', 'General Provisions', 'The provisions of this constitution shall be construed to be self-executing whenever possible.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_10_0_1959', 'Alaska1959', '1959', '12', '10', '0', 'General Provisions', 'Titles and subtitles shall not be used in construing this constitution. Personal pronouns used in this constitution shall be construed as including either sex.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_11_0_1959', 'Alaska1959', '1959', '12', '11', '0', 'General Provisions', 'As used in this constitution, the terms by law and by the legislature, or variations of these terms, are used interchangeably when related to law-making powers. Unless clearly inapplicable, the law-making powers assigned to the legislature may be exercised by the people through the initiative, subject to the limitations of Article XI.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_12_0_1959', 'Alaska1959', '1959', '12', '12', '0', 'General Provisions', 'The State of Alaska and its people forever disclaim all right and title in or to any property belonging to the United States or subject to its disposition, and not granted or confirmed to the State or its political subdivisions, by or under the act admitting Alaska to the Union. The State and its people further disclaim all right or title in or to any property, including fishing rights, the right or title to which may be held by or for any Indian, Eskimo, or Aleut, or community thereof, as that right or title is defined in the act of admission. The State and its people agree that, unless otherwise provided by Congress, the property, as described in this section, shall remain subject to the absolute disposition of the United States. They further agree that no taxes will be imposed upon any such property, until otherwise provided by the Congress. This tax exemption shall not apply to property held by individuals in fee without restrictions on alienation.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_12_13_0_1959', 'Alaska1959', '1959', '12', '13', '0', 'General Provisions', 'All provisions of the act admitting Alaska to the Union which reserve rights or powers to the United States, as well as those prescribing the terms or conditions of the grants of lands or other property, are consented to fully by the State and its people.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_13_1_0_1959', 'Alaska1959', '1959', '13', '1', '0', 'Amendments', 'Amendments to this constitution may be proposed by a two-thirds vote of each house of the legislature. The secretary of state shall prepare a ballot title and proposition summarizing each proposed amendment, and shall place them on the ballot for the next statewide election. If a majority of the votes cast on the proposition favor the amendment, it shall be adopted. Unless otherwise provided in the amendment, it becomes effective thirty days after the certification of the election returns by the secretary of state.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_13_2_0_1959', 'Alaska1959', '1959', '13', '2', '0', 'Amendments', 'The legislature may call constitutional conventions at any time.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_13_3_0_1959', 'Alaska1959', '1959', '13', '3', '0', 'Amendments', 'If during any ten-year period a constitutional convention has not been held, the secretary of state shall place on the ballot for the next general election the question: Shall there be a Constitutional Convention? If a majority of the votes cast on the question are in the negative, the question need not be placed on the ballot until the end of the next ten-year period. If a majority of the votes cast on the question are in the affirmative, delegates to the convention shall be chosen at the next regular statewide election, unless the legislature provides for the election of the delegates at a special election. The secretary of state shall issue the call for the convention. Unless other provisions have been made by law, the call shall conform as nearly as possible to the act calling the Alaska Constitutional Convention of 1955, including, but not limited to, number of members, districts, election and certification of delegates, and submission and ratification of revisions and ordinances. The appropriation provisions of the call shall be self-executing and shall constitute a first claim on the state treasury.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_13_4_0_1959', 'Alaska1959', '1959', '13', '4', '0', 'Amendments', 'Constitutional conventions shall have plenary power to amend or revise the constitution, subject only to ratification by the people. No call for a constitutional convention shall limit these powers of the convention.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_14_1_0_1959', 'Alaska1959', '1959', '14', '1', '0', 'Apportionment', 'Members of the house of representatives shall, until reapportionment, be elected from the election districts and in the numbers shown below: Number of District Name of District Number of Representatives 1 Prince of Wales 1 2 Ketchikan 2 3 Wrangell-Petersburg 1 4 Sitka 2 5 Juneau 2 6 Lynn Canal-Icy Straits 1 7 Cordova-McCarthy 1 8 Valdez-Chitina-Whittier 1 9 Palmer-Wasilla-Talkeetna 1 10 Anchorage 8 11 Seward 1 12 Kenai-Cook Inlet 1 13 Kodiak 2 14 Aleutian Islands 1 15 Bristol Bay 1 16 Bethel 1 17 Kuskokwim 1 18 Yukon-Koyukuk 1 19 Fairbanks 5 20 Upper Yukon 1 21 Barrow 1 22 Kobuk 1 23 Nome 2 24 Wade Hampton 1', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_14_2_0_1959', 'Alaska1959', '1959', '14', '2', '0', 'Apportionment', 'Members of the senate shall be elected from the senate districts and in the number shown below: Name of District Composed of Election Districts Number of Senators A. Southeastern 1, 2, 3, 4, 5, and 6 2 B. Ketchikan-Prince of Wales 1 and 2 1 C. Wrangell-Petersburg-Sitka 3 and 4 1 D. Juneau-Yakutat 5 and 6 1 E. Southcentral 7, 8, 9, 10, 11, 12, 13, and 14 2 F. Cordova-Valdez 7 and 8 1 G. Anchorage-Palmer 9 and 10 1 H. Seward-Kenai 11 and 12 1 I. Kodiak-Aleutians 13 and 14 1 J. Central 15, 16, 17, 18, 19, and 20 2 K. Bristol Bay-Bethel 15 and 16 1 L. Yukon-Kuskokwim 17 and 18 1 M. Fairbanks-Fort Yukon 19 and 20 1 N. Northwestern 21, 22, 23, and 24 2 O. Barrow-Kobuk 21 and 22 1 P. Nome-Wade Hampton 23 and 24 1', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_14_3_0_1959', 'Alaska1959', '1959', '14', '3', '0', 'Apportionment', 'The election districts set forth in Section 1 shall include the following territory: 1. Prince of Wales: All of Prince of Wales, Dall, Forrester, Suemez, Baker, Lulu, Noyes, Warren, Kosciusko and the Kashevarof Islands as well as adjacent off-shore islands. 2. Ketchikan: That area of the mainland drained by streams flowing into Revillagigedo Channel, Behm Canal, Burroughs Bay, and east side of Clarence Strait from the southernmost point of the Alaska-British Columbia boundary line to and including Lemesurier Point; and those islands south of Ernest Sound and east of Clarence Strait, including Revillagigedo, Gravina, Annette, and Duke Islands, and other adjacent smaller islands. 3. Wrangell-Petersburg: That area of the mainland north of Election District No. 2 and south of, and including, the area draining into Frederick Sound to Cape Fanshaw on the north, and partly bounded on the north by a line drawn between Cape Fanshaw and the north side of Pybus bay; that area of Admiralty Island drained by the streams flowing into Frederick Sound; that area of Baranof Island drained by streams flowing into Chatham Strait to but not including that area drained by streams flowing into Peril Starit; and including Kupereanof. Mitkof, Kulu and Coronation Islands and other smaller adjacent islands. 4. Sitka: Those parts of Admiralty, Chichagof and Baranof Islands not included in Election Districts No. 3, 5 and 6; and Kruzof Island and other smaller adjacent islands. 5. Juneau: The mainland north of Election District No. 3 up to and including the area drained by streams flowing into Berners Bay on the north; and that area of Admiralty Island north of Election District No. 3 and drained by streams flowing into Stephens Passage, Seymour Canal, Lynn Canal, and their tributaries; and including Douglas, Shelter and Benjamin Islands, and other smaller adjacent islands. 6. Lynn Canal- Icy Straits: That part of the mainland, not included in Election district No. 5, drained by streams flowing into Lynn Canal, Glacier Bay, Icy Strait, Cross Sound and their tributaries, and the Pacific Ocean, to and including the area drained into Icy Bay to the west; those parts of Admiralty and Chichagof Islands drained by streams flowing onto Icy Strait, Cross Sound, and their tributaries; and Yakobi, Lemesurier, and Pleasant Islands, and other smaller adjacent islands. 7. Cordova-McCarthy: That area draining into the Gulf of Alaska and Prince William Sound, from but not including the area draining into the south side of Icy Bay on the east to Knowles Head on the west, including Hawkins, Hinchinbrook, Kayak, and Middleton Islands, and other smaller adjacent islands; and that area drained by the Copper River and its tributaries up to and not including the Tiekel River on the west, and up to and including the Chitina River on the east. 8. Valdez-Chitina-Whittier: That area drained by all streams flowing into Prince William Sound from Cape Junken on the the west to Knowles Head on the east, including Montague, Latouche, and Knight Islands, and other smaller adjacent islands; and all of the area drained by the Copper River and its tributaries above and including the Tiekel River on the west, and above but not including the Chitina River on the east. 9. Pamer-Wasilla-Talkeetna: That area from and including Susitna on the south, drained by the Susitna River and its tributaries; and that area drained by the Little Susitna River from and including Flat Lake on the south; and that area draining into Knik Arm from and including Fish Creek and its tributaries on the west side of Knik Arm, to and including the area draining into the Knik River from the north, and from the south to the highway bridge. 10. Anchorage: That area around Turnagain Arm and east of Knik Arm drained by streams flowing into Turnagain Arm and Knik Arm, from and including Placer River on the south, to and including the Knik River highway bridge on the north; that area east of Knik Arm and north of Cook Inlet drained by Goose Creek and its tributaries on the east, and the Little Susitna River south of Flat Lake, and the Susitna River south of but not including Susitna; the area west of Cook Inlet drained by Ivan, Lewis, Theodore Rivers and their tributaries, to but not including Beluga River on the south. 11. Seward: That part of the Kenai Peninsula draining into the Gulf of Alaska from Gore Point on the west to Cape Junken on the east; and the area draining into Turnagain Arm from and including the drainage of Resurrection Creek on the west but not including Placer River on the east, and to and including the confluence of the Kenai and Russian Rivers on the west. 12. Kenai-Cook Inlet: That area of Kenai Peninsula drained by streams flowing into the Gulf of Alaska, Cook Inlet, and Turnagain Arm, from and including the area drained into Port Dick on the south to Gore Point, to but not including Resurrection Creek on the north, and the area east of the confluence of the Kenai and Russian Rivers; and that area west of Cook Inlet drained by all streams flowing into Cook Inlet drained by all streams flowing into Cook Inlet from Cape Douglas on the south, to and including the Beluga River; including Elizabeth Island and adjacent Islands in Cook Inlet. 13. Kodiak: The part of the Alaska Peninsula drained by all streams flowing into the Pacific Ocean from Cape Douglas on the east to but not including Kujulik Bay on the west; and all adjacent off-shore islands, including the Semidi Islands, and Kodiak, Afognak, Trinity, Chirikof Islands, and other smaller islands in the immediate vicinity, such as the Barren Islands and the Chugach Islands. 14. Aleutian Islands: The part of the Alaska Peninsula west of including the drainage of Meshik River and Kujulik Bay; and all of the Aleutian and Pribilof Islands and adjacent off-shore Islands west of and excluding the Semidi Islands and Sutwik Island. 15. Bristol Bay: The area drained by all streams flowing into Bristol Bay from Cape Newenham on the west to but not including the Meshik River on the south. 16. Bethel: The area drained by all streams flowing into Baird Inlet, Etolin Strait, and Kuskokwim Bay; that area drained by the Kuskokwim River and its tributaries up to, and including, the area drained by the Tuluksak River on the east bank of the Kuskokwim River; and the area drained by tributaries up to the opposite point on the west bank of the Kuskokwim River; and including Nunivak Island and St. Matthew Island and adjacent islands. 17. Kuskokwim: The area drained by the Kuskokwim River and its tributaries above and not including the area drained by the Tuluksak River and its tributaries on the east bank; and the area drained by tributaries above the opposite point on the west bank of the Kuskokwim River; and the area drained by the Yukon River from Tuckers Slough, to but not including the area drained by the Khotol River. 18. Yukon-Koyokuk: The area drained by all streams and their tributaries flowing into the Yukon River from and including Khotol River on the west to and including Hess Creek on the east; and that area drained by the Tanana River and its tributaries up to but not including Clear Creek, near Blair Lakes, on the east; and that part of Goldstream Creek up to but not including Nugget Creek and Spinach Creek; and that portion drained by the Chatanika River up to but not including Vault Creek. 19. Fairbanks: That area drained by the Tanana River and its tributaries from and including Clear Creek, near Blair Lakes, on the west, to the Alaska-Canada boundary on the east; and also that area drained by Goldstream Creek and its tributaries from, and including, Nugget Creek and Spinach Creek; and that portion drained by the Chatanika River and its tributaries up stream from and including Vault Creek. 20. Upper Yukon: That area drained by the Yukon River and its tributaries from, but not including Hess Creek on the west, to the Alaska-Canada boundary; and that area drained by streams flowing into the Arctic Ocean from, but not including, Kuparuk River on the west, to the Alaska boundary. 21. Barrow: The area drained by all streams flowing into the Arctic Ocean from Cape Lisburne on the west, to and including the area drained by the Kuparuk River and its tributaries on the east. 22. Kobuk: The area drained by all streams flowing into the Arctic Ocean and Kotzebue Sound, from Cape Lisburne on the north, to and including the area drained by the Goodhope River and its tributaries on the south. 23. Nome: That part of the Seward Peninsula and adjacent areas drained by all streams flowing into the Kotzebue Sound, Bering Strait and Norton Sound, from, but not including, the area drained by the Goodhope River and its tributaries on the north, to but not including, the area drained by the Pastolik River on the south; and King, Little Diomede, St. Lawrence, Sledge, and Stuart Islands, as well as adjacent offshore islands. 24. Wade Hampton: The area drained by the lower Yukon River and its tributaries, from Tuckers Slough to the mouth of the Bering Sea; and the area drained by all streams flowing into the Bering Sea and Norton Sound, from and including Hazen Bay on the south, to and including the Pastolik River on the north.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_1_0_1959', 'Alaska1959', '1959', '15', '1', '0', 'Schedule', 'All laws in force in the Territory of Alaska on the effective date of this constitution and consistent therewith shall continue in force until they expire by their own limitation, are amended, or repealed.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_2_0_1959', 'Alaska1959', '1959', '15', '2', '0', 'Schedule', 'Except as otherwise provided in this constitution, all rights, titles, actions, suits, contracts, and liabilities and all civil, criminal, or administrative proceedings shall continue unaffected by the change from territorial to state government, and the State shall be the legal successor to the Territory in these matters.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_3_0_1959', 'Alaska1959', '1959', '15', '3', '0', 'Schedule', 'Cities, school districts, health districts, public utility districts, and other local subdivisions of government existing on the effective date of this constitution shall continue to exercise their powers and functions under existing law, pending enactment of legislation to carry out the provisions of this constitution. New local subdivisions of government shall be created only in accordance with this constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_4_0_1959', 'Alaska1959', '1959', '15', '4', '0', 'Schedule', 'All officers of the Territory, or under its laws, on the effective date of this constitution shall continue to perform the duties of their offices in a manner consistent with this constitution until they are superseded by officers of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_5_0_1959', 'Alaska1959', '1959', '15', '5', '0', 'Schedule', 'Residence, citizenship, or other qualifications under the Territory may be used toward the fulfillment of corresponding qualifications required by this constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_6_0_1959', 'Alaska1959', '1959', '15', '6', '0', 'Schedule', 'When the people of the Territory ratify this constitution and it is approved by the duly constituted authority of the United States, the governor of the Territory shall, within thirty days after receipt of the official notification of such approval, issue a proclamation and take necessary measures to hold primary and general elections for all state elective offices provided for by this constitution.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_7_0_1959', 'Alaska1959', '1959', '15', '7', '0', 'Schedule', 'The primary election shall take place not less than forty nor more than ninety days after the proclamation by the governor of the Territory. The general election shall take place not less than ninety days after the primary election. The elections shall be governed by this constitution and by applicable territorial laws.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_8_0_1959', 'Alaska1959', '1959', '15', '8', '0', 'Schedule', 'The officers to be elected at the first general election shall include two senators and one representative to serve in the Congress of the United States, unless senators and a representative have been previously elected and seated. One senator shall be elected for the long term and one senator for the short term, each term to expire on the third day of January in an odd-numbered year to be determined by authority of the United States. The term of the representative shall expire on the third day of January in the odd-numbered year immediately following his assuming office. If the first representative is elected in an even-numbered year to take office in that year, a representative shall be elected at the same time to fill the full term commencing on the third day of January of the following year, and the same person may be elected for both terms.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_9_0_1959', 'Alaska1959', '1959', '15', '9', '0', 'Schedule', 'Terms of First Governor and Secretary of state. The First Governor and Secretary of state shall hold office for a term beginning with the day on which they assume office and ending at noon on the first Monday in December of the even-numbered year following the next presidential election. This term shall count as a full term for purposes of determining eligibility for reelection only if it is four years or more in duration', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_10_0_1959', 'Alaska1959', '1959', '15', '10', '0', 'Schedule', 'At the first State general election, one senator shall be chosen for a two-year term from each of the following senate districts, described in Section 2 of Article XIV: A, B, D, E, G, I, J, L, N, and O. At the same election, one senator shall be chosen for a four-year term from each of the following senate districts described in Section 2 of Article XIV: A, C, E, F, H, J, K, M, N, and P.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_11_0_1959', 'Alaska1959', '1959', '15', '11', '0', 'Schedule', 'The first State legislators shall hold office for a term beginning with the day on which they assume office and ending at noon on the fourth Monday in January after the next general election, except that senators elected for four-year terms shall serve an additional two years thereafter. If the first general election is held in an even-numbered year, it shall be deemed to be the general election for that year.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_12_0_1959', 'Alaska1959', '1959', '15', '12', '0', 'Schedule', 'The returns of the first general election shall be made, canvassed, and certified in the manner prescribed by law. The governor of the Territory shall certify the results to the President of the United States.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_13_0_1959', 'Alaska1959', '1959', '15', '13', '0', 'Schedule', 'When the President of the United States issues a proclamation announcing the results of the election, and the State has been admitted into the Union, the officers elected and qualified shall assume office.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_14_0_1959', 'Alaska1959', '1959', '15', '14', '0', 'Schedule', 'The governor shall call a special session of the first state legislature within thirty days after the presidential proclamation unless a regular session of the legislature falls within that period. The special session shall not be limited as to duration.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_15_0_1959', 'Alaska1959', '1959', '15', '15', '0', 'Schedule', 'The provisions of Section 5 of Article II shall not prohibit any member of the first State legislature from holding any office or position created during his first term.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_16_0_1959', 'Alaska1959', '1959', '15', '16', '0', 'Schedule', 'The first members of the judicial council shall, notwithstanding Section 8 of Article IV, be appointed for terms as follows: three attorney members for one, three, and five years respectively, and three non-attorney members for two, four, and six years respectively. The six members so appointed shall, in accordance with Section 5 of Article IV, submit to the governor nominations to fill the initial vacancies on the superior court and the supreme court, including the office of chief justice. After the initial vacancies on the superior and supreme courts are filled, the chief justice shall assume his seat on the judicial council.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_17_0_1959', 'Alaska1959', '1959', '15', '17', '0', 'Schedule', 'Until the courts provided for in Article IV are organized, the courts, their jurisdiction, and the judicial system shall remain as constituted on the date of admission unless otherwise provided by law. When the State courts are organized, new actions shall be commenced and filed therein, and all causes, other than those under the jurisdiction of the United States, pending in the courts existing on the date of admission, shall be transferred to the proper state court as though commenced, filed, or lodged in those courts in the first instance, except as otherwise provided by law.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_18_0_1959', 'Alaska1959', '1959', '15', '18', '0', 'Schedule', 'The debts and liabilities of the Territory of Alaska shall be assumed and paid by the State, and debts owed to the Territory shall be collected by the State. Assets and records of the Territory shall become the property of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_19_0_1959', 'Alaska1959', '1959', '15', '19', '0', 'Schedule', 'The first reapportionment of the house of representatives shall be made immediately following the official reporting of the 1960 decennial census, or after the first regular legislative session if the session occurs thereafter, notwithstanding the provision as to time contained in Section 3 of Article VI. All other provisions of Article VI shall apply in the first reapportionment.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_20_0_1959', 'Alaska1959', '1959', '15', '20', '0', 'Schedule', 'The capital of the State of Alaska shall be at Juneau.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_21_0_1959', 'Alaska1959', '1959', '15', '21', '0', 'Schedule', 'The seal of the Territory, substituting the word State for Territory, shall be the seal of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_22_0_1959', 'Alaska1959', '1959', '15', '22', '0', 'Schedule', 'The flag of the Territory shall be the flag of the State.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_23_0_1959', 'Alaska1959', '1959', '15', '23', '0', 'Schedule', 'Citizens who legally voted in the general election of November 4, 1924, and who meet the residence requirements for voting, shall be entitled to vote notwithstanding the provisions of Section 1 of Article V.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_24_0_1959', 'Alaska1959', '1959', '15', '24', '0', 'Schedule', 'Ordinance No. 1 on ratification of the constitution, Ordinance No. 2 on the Alaska-Tennessee Plan, and Ordinance No. 3 on the abolition of fish traps, adopted by the Alaska Constitutional Convention and appended to this constitution, shall be submitted to the voters and if ratified shall become effective as provided in each ordinance.', '0', 'mparoca', 'mparoca');
INSERT INTO `constitutionsdb`.`sections` (`section_id`, `constitution_id`, `section_year`, `article_num`, `section_num`, `part_num`, `section_topic`, `section_text`, `is_deleted`, `created_by`, `updated_by`) VALUES ('AK_15_25_0_1959', 'Alaska1959', '1959', '15', '25', '0', 'Schedule', 'This constitution shall take effect immediately upon the admission of Alaska into the Union as a state.', '0', 'mparoca', 'mparoca');
INSERT INTO sections (section_id, constitution_id, section_year, article_num, section_num, part_num, section_topic, 
